INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(1, 'Đối thủ của Việt Nam ráo riết chuẩn bị cho vòng loại World Cup', 'UAE đi chuyên cơ và thuê trọn trung tâm thể thao hiện tại Serbia để tập luyện, chuẩn bị cho loạt trận quyết định ở vòng loại World Cup 2022', '          <p class=\"Normal\">\"UAE sẽ tập luyện 12 ngày tại Trung tâm của Liên đoàn bóng đá Serbia. Cơ sở vật chất tại đây rất tuyệt vời, với nhiều sân bóng, phòng gym, bể bơi và các dụng cụ hiện đại bậc nhất phục vụ cho bóng đá\", Liên đoàn bóng đá UAE thông báo.</p>\r\n<p class=\"Normal\">Trong thời gian lưu trú tại Serbia, dự kiến đến ngày 23/8, UAE tập trung phục hồi thể lực cho các cầu thủ, các bài chiến thuật và đá hai trận giao hữu với các CLB của Serbia.</p>\r\n<p class=\"Normal\">UAE đạt sáu điểm sau bốn trận, đứng thứ tư tại bảng G - ít hơn năm điểm và đá ít hơn một trận so với đầu bảng Việt Nam. Họ sẽ lần lượt gặp Malaysia (ngày 8/10), Indonesia (13/10), Thái Lan (12/11) và Việt Nam (17/11).</p>\r\n<p class=\"Normal\">Hối cuối tháng 7, UAE đã <a href=\"https://vnexpress.net/doi-thu-cua-viet-nam-thue-hlv-tung-vao-tu-ket-world-cup-4135105.html\" rel=\"dofollow\">ký hợp đồng với HLV Jorge Luis Pinto </a>- người từng dẫn dắt nhiều CLB và đội tuyển quốc gia khu vực Nam Mỹ và Caribbean. Năm 2014, ông gây tiếng vang khi giúp Costa Rica, đội tuyển vốn không được đánh giá cao, vào tới tứ kết World Cup.</p>\r\n<p class=\"Normal\">Trên đất Brazil năm đó, thầy trò Pinto lần lượt thắng Uruguay 3-1, Italy 1-0 rồi hoà Anh 0-0 để đứng nhất \"Bảng tử thần\". Vào vòng 1/8, Costa Rica thắng tiếp Hy Lạp bằng loạt luân lưu. Ở tứ kết, họ hoà Hà Lan 0-0 qua 120 phút, nhưng thua trong loạt đá cân não.</p>\r\n<p class=\"Normal\">Liên đoàn bóng đá UAE cho HLV Pinto toàn quyền chọn trợ lý, trong đó có một tên tuổi đáng chú ý là Eduardo Nino. Ông từng là thủ môn của đội tuyển Colombia, khi chuyển sang làm công tác huấn luyện cũng từng dự World Cup 2014 và 2018.</p>\r\n<p class=\"Normal\">UAE cũng nâng cấp chất lượng đội hình bằng việc nhập tịch cầu thủ. Trong lần hội quân này họ có sự góp mặt của Sebastian Tagliabue. Tiền đạo 34 tuổi từng nhiều năm chơi bóng ở Chile, Colombia và Saudi Arabia. Anh tới UAE khoác áo CLB Abu Adhabia năm 2013 và là cầu thủ ngoại tốt nhất giải đấu này với 158 bàn trong 173 trận ra sân.</p>\r\n<p class=\"Normal\">\"Thật tuyệt vời khi được khoác áo UAE. Tôi đang hoà nhập tốt và hy vọng sẽ sớm ghi bàn thắng cho đội tuyển\", chân sút mới được nhập tịch của UAE chia sẻ.</p>\r\n<p class=\"Normal\">UAE từng lọt vào vòng loại cuối cùng World Cup 2018 – khu vực châu Á, nhưng không thể đoạt vé tới Nga khi chỉ đứng thứ tư, sau Nhật Bản, Saudi Arabia và Australia.</p>\r\n        ', '2020-08-10', 11, 0, 'Thường', 1, 'binbin'),
(2, 'Công Phượng bị cạnh tranh gay gắt', 'Dẫn đầu danh sách ghi bàn của CLB TP HCM sau 11 vòng đấu, tuy nhiên Công Phượng sẽ đối đầu với hai thử thách lớn đến từ hai tân binh đến từ đội tuyển Costa Rica là Arial và Ortiz', '          <p>Sau khi bộ đôi Ariel Rodriguez và Guillermo Ortiz vừa gia nhập CLB TP HCM, bên cạnh sự phấn khích của người hâm mộ, một câu hỏi đặt ra là với những cầu thủ khoác áo đội tuyển Costa Rica, một quốc gia ở Nam Mỹ vốn nổi tiếng với <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> hoa mỹ, liệu Công Phượng có còn chỗ đứng?</p>\r\n<p>Sau 11 vòng đấu, Công Phượng cho thấy anh là cầu thủ quan trọng nhất trên hàng tiền đạo của CLB TP HCM với 4 bàn thắng. Tiền đạo trưởng thành từ lò HAGL là một trong hai chân sút Việt Nam đứng trong tốp 15 người đang dẫn đầu cuộc đua vua phá lưới <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> 2020. Vấn đề là bây giờ, Công Phượng đang có sự cạnh tranh gay gắt khi đội nhà mang về 2 ngoại binh chất lượng, với tổng chi phí chuyển nhượng được trang Transfermarkt định giá lên đến 1 triệu USD.</p>\r\n        ', '2020-08-10', 2, 0, 'Thường', 1, 'hiuhiu'),
(3, 'VFF có dám phạt bầu Đệ?', 'Chỉ 24 giờ sau khi gửi đơn xin không đá tiếp V-League 2020, chủ tịch CLB Thanh Hóa Nguyễn Văn Đệ xin rút lại và... chơi tiếp', '          <p>Ông Đệ mở diễn đàn với những lập luận dễ xuôi tai do CLB khó khăn về tài chính, trong khi mùa giải chưa biết đến khi nào có thể trở lại. Nhưng nếu các nhà làm giải cho ít tiền hỗ trợ qua mùa dịch thì không bỏ giải nữa.</p>\r\n<p>Chủ tịch một CLB chuyên nghiệp đại diện cho tỉnh lớn mà nói thật cứ như đùa, vì gần như ngay lập tức ông Đệ xin rút lại lá đơn, nghĩa là rút lại những câu nói trong lá đơn ấy. Thật ra ông Đệ đơn thuần phát ngôn dưới danh nghĩa cá nhân vì đơn giản đội bóng không phải của ông. Người ta gọi ông Đệ là bầu cho vui chứ thực chất ông chưa xứng đáng với danh xưng đúng nghĩa ấy.</p>\r\n<p>Hóa ra ông chủ tịch CLB Thanh Hóa dường như xem cuộc chơi chẳng khác gì cái chợ, muốn vào thì vào, muốn ra thì ra. Cũng may các cấp cao hơn có thẩm quyền thực sự với CLB đã nhanh chóng “gõ” xuống chấn chỉnh để phần nào gỡ gạc cho uy tín của <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> xứ Thanh có ông trưởng đoàn rất ngông và cao hứng còn cầm cả sa bàn chiến thuật chỉ đạo chuyên môn cho cầu thủ.</p>\r\n<p>Ông Đệ bào chữa việc một đại diện VFF rỉ tai ông sẽ chi một ít tiền giúp đỡ CLB nên Thanh Hóa không dừng cuộc chơi nữa vì đã đạt mục đích.</p>\r\n<p>Bầu Đệ lên tiếng đòi bỏ giải rồi lại rút đơn nói chơi tiếp đúng vào thời điểm VFF mới thông báo sẽ xử lý nghiêm những ai phát ngôn hoặc hành động gây tổn hại hình ảnh giải đấu, làm xấu <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a>.</p>\r\n<p>Vậy VFF có dám phạt ông Nguyễn Văn Đệ hay không? Nếu không sẽ khó trách sau này những người làm bóng đá cứ huênh hoang tuyên bố rồi xem V-League như cái chợ, buồn thì nghỉ đá, vui thì chơi lại</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thunthun'),
(4, 'Văn Hậu trở về Việt Nam với chiếc áo V-Leahue chật chội', 'Câu chuyện hậu trường cho biết Văn Hậu đến với Heerenveen theo bản hợp đồng cho mượn một năm là một tay sắp xếp và giới của cựu trung vệ Hà Lan từng khoác áo nhiều CLB V-League Danny Van Bakel.', '          <p>Chàng rể Việt Bakel cho rằng chiếc áo <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> quá chật chội với <a class=\"TextlinkBaiviet\" title=\"Đoàn Văn Hậu\" href=\"https://www.24h.com.vn/doan-van-hau-c48e5200.html\">Đoàn Văn Hậu</a> nên Hậu cần tìm nơi thử thách cao hơn để tiếp tục phát triển. Tất nhiên là thế, <a class=\"TextlinkBaiviet\" title=\"Văn Hậu\" href=\"https://www.24h.com.vn/doan-van-hau-c48e5200.html\">Văn Hậu</a> có thể hình, sức mạnh, lối chơi bóng hiện đại, quan trọng hơn là rất trẻ nên việc đi tìm “đỉnh chuyên môn” của mình là điều hợp lý.</p>\r\n<p>Lần trở về này, Văn Hậu sẽ khoác áo CLB cũ Hà Nội đá V-League hay tiếp tục tìm kiếm những đợt thử sức khác kiểu Công Phượng, Xuân Trường từng sang Nhật, Hàn Quốc, Bỉ, Thái Lan…</p>\r\n<p>Đoàn Văn Hậu mới 21 tuổi nhưng đã trải qua đầy đủ các đội tuyển ở mọi lứa tuổi của bóng đá Việt Nam. Dày dạn thế nhưng Hậu vẫn đủ tuổi đá SEA Games 31 năm 2021. Một mẫu cầu thủ hiếm với thể hình đẹp, thể lực, sức mạnh dồi dào cùng lối chơi đơn giản nhưng rất hiệu quả. Hậu được xem là một mẫu hậu vệ hiện đại công thủ toàn diện với tuổi đời còn rất trẻ.</p>\r\n<p>Tuổi 21 và có một thể hình, cơ địa, thể chất cực tốt Đoàn Văn Hậu có quyền tiếp tục tìm kiếm cho mình những điểm tựa, những giải đấu cao để phát triển thay vì lại đóng khung trong V-League. Hy vọng sau một năm ở Hà Lan dù chỉ có 4 phút đá trong đội chuyên nghiệp Heerenveen nhưng Văn Hậu không nhụt chí và không bị dị ứng với những giải đấu ở đẳng cấp cao.</p>\r\n<p>Hậu cần được học hỏi nhiều hơn thay vì chơi trong nhà khi đã chạm trần.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(5, 'Trường ban trọng tài Dương Văn Hiền đã nói điều gì với bầu Hiển?', 'Ông Dương Văn Hiền, trưởng ban trọng tài của VFF lần đầu tiên lên tiếng về cuộc nói chuyện với bầu Hiển trên sân vận động Hàng Đẫy, sau một trận đấu với CLB Hà Nội.', '          <p>Sau khi trận đấu giữa TP.HCM và Hà Nội thuộc vòng 11 <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> 2020 kết thúc với chiến thắng đậm 3-0 dành cho đội bóng nhà bầu Hiển, HLV và cầu thủ đội chủ nhà phản ứng mạnh mẽ với các quyết định của tổ trọng tài. Thậm chí, lãnh đạo CLB TP.HCM còn gửi công văn đến VFF và VPF để kiến nghị thay Trưởng Ban trọng tài Dương Văn Hiền.</p>\r\n<p>Còn với các cổ động viên TP.HCM, họ chia sẻ trên khắp các trang mạng xã hội hình ảnh Trưởng Ban trọng tài Dương Văn Hiền “thì thầm” trò chuyện với ông bầu Đỗ Quang Hiển của CLB Hà Nội, sau một trận đấu của nhà đương kim vô địch V-League trên sân vận động Hàng Đẫy.</p>\r\n<p>Ông Dương Văn Hiền và các trọng tài bị chỉ trích “ép” CLB TP.HCM và thiên vị cho đội Hà Nội.</p>\r\n<p>Chia sẻ trong một chương trình mới đây của nhà báo Minh Hải, ông Dương Văn Hiền đã lần đầu tiết lộ về cuộc nói chuyện với bầu Hiển gây xôn xao dư luận: “Việc phân công trọng tài, tôi không chịu áp lực từ ai hết. Chưa bao giờ CLB Hà Nội đá lại để thua 2-3 trận sân nhà như mùa giải này và trọng tài cũng có lúc bắt thiệt cho đội Hà Nội. Tôi và các trọng tài làm việc công tâm.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Tôi gặp bất cứ huấn luyện viên hay lãnh đạo CLB nào cũng vậy. Tôi nhớ, lúc đó ông Hiển xuống sân gặp tôi và có chào tôi. Tôi và ông Hiển có bắt tay nhau. Nhưng tất cả chỉ dừng lại ở chuyện chào hỏi, xã giao. Chẳng lẽ, giờ ông Hiển xuống bắt tay, tôi lại không chào lại.</p>\r\n<p>Lúc đó, tôi nhớ chúng tôi chỉ chào hỏi, chúc mừng nhau hay đại loại thế. Tôi và ông Hiển chỉ nói 2-3 câu với nhau rồi thôi”. </p>\r\n<p>Nói thêm về trận đấu giữa TP.HCM và Hà Nội, Trưởng Ban trọng tài cho biết ông là người TP.HCM, sinh ra và lớn lên ở TP.HCM. Ngày hôm đó, ông Hiền có mặt trên khán đài sân Thống Nhất dự khán trận đấu, nhưng các trọng tài đã không “bênh” riêng gì đội nào mà…mắc lỗi với cả hai CLB. Từ đó, Trưởng Ban trọng tài nhận định cấp dưới của mình chỉ sai sót về mặt con người, do tâm lý chưa tốt chứ không có vấn đề gì về tư tưởng.</p>\r\n<p>Bên cạnh đó, Trưởng Ban trọng tài Dương Văn Hiền nhấn mạnh ông không bao giờ né báo chí hay lãnh đạo đội nào hết. Sếp của Ban trọng tài VFF còn dẫn chứng việc ông từng gặp cả ông Nguyễn Hồng Thanh (Chủ tịch CLB SLNA), ngay tại khách sạn nơi đội bóng xứ Nghệ đóng quân khi làm khách trước Hải Phòng ở V-League cách đây 2 năm.</p>\r\n<p>Ông Hiền nói tiếp: “Năm mà tôi vừa lên thay anh Nguyễn Văn Mùi, tôi đã trực tiếp gặp anh Nguyễn Hồng Thanh ở khách sạn nơi SLNA đóng quân tại Hải Phòng. Tôi đã dám đi đến khách sạn gặp anh Hồng Thanh, để hẹn uống nước.</p>\r\n<p>Lúc đó, tôi có nói với anh Hồng Thanh rằng hình ảnh anh em trọng tài đã thấp rồi, mà Ban trọng tài không thể một sớm một chiều vực dậy chuyên môn. Chúng tôi mong lãnh đạo các đội bóng động viên cầu thủ phản ứng có chừng mực, để tránh ảnh hưởng tâm lý các trọng tài.</p>\r\n<p>Nói thật, khi ấy có nhà báo nào vô tình chụp hình tôi ngồi với anh Hồng Thanh ở khách sạn đội SLNA, không biết chuyện gì sẽ xảy ra. Nhưng tôi nghĩ mình cây ngay không sợ chết đứng”. </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thonthon'),
(6, 'ĐT Việt Nam & U23 Việt Nam tập trung, thầy Park không \"ngồi chơi, xơi nước\"', 'HLV Park Hang Seo phải dời lịch tập trung của đội tuyển Việt Nam và U23 Việt Nam, do diễn biến phức tạp của tình hình dịch Covid-19.', '          <p>Do ảnh hưởng của dịch Covid-19, hầu hết các giải bóng đá trong nước đều đã phải tạm hoãn mà chưa hẹn ngày trở lại. Mới đây, Ban xử lý các tình huống khẩn cấp của Liên đoàn bóng đá Đông Nam Á (AFF) đã họp để thống nhất đề xuất lên Hội đồng AFF để dời lịch tổ chức AFF Cup 2020 sang tháng 4/2021.</p>\r\n<p>Ngoài việc AFF Cup có khả năng cao không thể tổ chức trong năm 2020, các trận đấu của <a class=\"TextlinkBaiviet\" title=\"ĐT Việt Nam\" href=\"https://www.24h.com.vn/doi-tuyen-viet-nam-c48e3410.html\">ĐT Việt Nam</a> ở bảng G vòng loại World Cup 2022 khu vực châu Á cũng chưa chắc chắn có thể diễn ra như lịch dự kiến của AFC (ĐT Việt Nam lần lượt gặp Malaysia, Indonesia và UAE trong tháng 10 và tháng 11/2020).</p>\r\n<p>Trong bối cảnh các hoạt động bóng đá trong nước và giải đấu quốc tế cấp đội tuyển gần như “đóng băng”, HLV <a class=\"TextlinkBaiviet\" title=\"Park Hang Seo\" href=\"https://www.24h.com.vn/hlv-park-hang-seo-c48e5068.html\">Park Hang Seo</a> và đội ngũ trợ lý người Hàn Quốc có nguy cơ tiếp tục phải “ngồi chơi, xơi nước” từ nay đến hết năm 2020.</p>\r\n<p>Bởi vì dịch Covid-19 mà đợt tập trung đầu tiên của ĐT Việt Nam trong năm 2020 không thể diễn ra như kế hoạch đã đề ra trước đó. Ban đầu, HLV Park Hang Seo cùng VFF đã chuẩn bị để ĐT Việt Nam và U23 Việt Nam cùng lúc tập trung tại Trung tâm đào tạo bóng đá trẻ VFF từ ngày 10 đến 16/8.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\">ĐT Việt Nam tập trung với quân số khoảng 30 cầu thủ, để chuẩn bị cho các trận đấu vòng loại World Cup 2022 khu vực châu Á. Trong khi việc U23 Việt Nam hội quân nằm trong kế hoạch dài hơi để hướng đến SEA Games 2021 trên sân nhà.</div>\r\n</div>\r\n<p>Trước diễn biến mới của dịch Covid-19 tại Việt Nam, VFF đã thảo luận với HLV Park Hang Seo để dời lịch đợt tập trung kể trên của ĐT Việt Nam và U23 Việt Nam. Cụ thể, các ĐTQG Việt Nam có thể hội quân ở Trung tâm đào tạo bóng đá trẻ VFF từ ngày 14 đến ngày 21/8.</p>\r\n<p>Dù vậy, như lời của một lãnh đạo VFF, chưa có gì đảm bảo để đợt tập trung của ĐT Việt Nam và U23 Việt Nam có thể diễn ra theo đúng kế hoạch. “Đây mới chỉ là kế hoạch dự kiến của các đội tuyển quốc gia. VFF và HLV Park Hang Seo sẽ theo sát tình hình dịch Covid-19 trong thời gian tới để chủ động trong mọi tình huống”, một lãnh đạo của VFF cho hay.</p>\r\n<p>Trong thời gian vừa qua, HLV Park Hang Seo và đội ngũ trợ lý ở các đội tuyển đã toả đi khắp các sân cỏ trên cả nước để tuyển quân cho ĐT Việt Nam và U23 Việt Nam. Các đợt tập trung ngắn hạn như trên là cơ sở để thầy Park và cộng sự rà soát lực lượng để hướng đến mục tiêu quan trọng trong năm 2020, 2021 của bóng đá Việt Nam.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(7, 'Đội Công Phượng chính thức mua ngoại binh triệu đô, khi nào ra mắt?', 'Chiều 4/8, CLB TP.HCM chính thức công bố hoàn tất hợp đồng chiêu mộ bộ đôi ngoại binh đến từ Costa Rica là Ariel Rodiguez  và Jose Ortiz.', '          <p>Chiều ngày 4/8, trên trang chủ của CLB TP.HCM chính thức giới thiệu bộ đôi tân binh đến từ Costa Rica. Đó là Jose Guillermo Ortiz và Ariel Rodriguez. Hai cầu thủ ngoại kể trên đã đặt bút ký vào bản hợp đồng để thi đấu cho CLB TP.HCM và khoác lên mình chiếc áo đấu của đội Á quân <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a>.</p>\r\n<p>Chi tiết hợp đồng giữa CLB TP.HCM và hai ngoại binh người Costa Rica không được tiết lộ. Nhưng theo tìm hiểu, Jose Guillermo Ortiz và Ariel Rodriguez sẽ thi đấu cho đội chủ sân Thống Nhất đến hết mùa giải 2021.</p>\r\n<p>Tại CLB TP.HCM, Jose Guillermo Ortiz sẽ khoác áo số 27, trong khi áo số 77 được Ariel Rodriguez chọn. Thông tin từ lãnh đạo CLB TP.HCM cho hay, Jose Guillermo Ortiz và Ariel Rodriguez đã vượt qua buổi kiểm tra y tế trước khi chính thức ký hợp đồng để trở thành đồng đội của Công Phượng.</p>\r\n<p>Hai cầu thủ đến từ Costa Rica sẽ có buổi tập đầu tiên trong màu áo CLB TP.HCM vào thứ Hai tuần sau (ngày 10/8). Đó cũng là thời điểm Công Phượng và các cầu thủ TP.HCM hội quân, sau thời gian “xả trại” để phòng, chống dịch Covid-19.</p>\r\n<p>Hai tân binh của đội Á quân V-League có mặt tại TP.HCM từ giữa tháng 7/2020. Để tuân thủ quy định về phòng, chống dịch Covid-19, Jose Guillermo Ortiz và Ariel Rodriguez vừa phải hoàn thành 14 ngày cách ly trước khi ký hợp đồng với CLB TP.HCM.</p>\r\n<p>Chủ tịch kiêm HLV trưởng Nguyễn Hữu Thắng đánh giá cao chuyên môn của bộ đôi tuyển thủ Costa Rica. Ban huấn luyện CLB TP.HCM hy vọng với sự góp mặt của Jose Guillermo Ortiz và Ariel Rodriguez trên hàng công, đội bóng mà Công Phượng đang đầu quân sẽ trở lại với cuộc đua vô địch ở giai đoạn tiếp theo của V-League 2020.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(8, 'Vì sao HLV ngoại khó thành công ở V-League?', 'Việc HLV Chung Hae-Seong rời CLB TP.HCM một lần nữa cho thấy việc HLV ngoại rất khó thành công ở V-League.', '          <p>Nguyên nhân của thực trạng này là gì khi nó dường như không đơn thuần xuất phát từ vấn đề chuyên môn?</p>\r\n<p>Sau vòng 11 <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> 2020, HLV Chung Hae-seong đã chính thức nói lời chia tay CLB TP HCM. Ông Chung sau đó được mời giữ chức Giám đốc kỹ thuật tại đội bóng chủ sân Thống Nhất nhưng ông đã từ chối. Chỉ trong 3 năm, riêng CLB TP HCM đã khiến 3 HLV nước ngoài mất việc. Trước đó, HLV Alain Fiard (Pháp) và HLV Toshiya Miura (Nhật Bản) cũng phải ra đi trong thất bại.</p>\r\n<p>Hồi đầu mùa giải năm nay, CLB Thanh Hóa sau khi để thua 3 trận liên tiếp cũng đã sa thải HLV người Ý Fabio Lopez. Trước đó, chính đội bóng xứ Thanh cũng nói lời chia tay HLV Mihail Cucchiaroni (Romania) hay HLV Ljupko Petrovic (Serbia). Hiện nay, V-League 2020 chỉ còn duy nhất HAGL đang dùng thầy ngoại - HLV Lee Tae-hoon. Mặc dù vậy, ông Lee không thể giúp đội bóng phố Núi trở thành một thế lực và dấu ấn chiến thuật cũng nhạt nhòa.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Nhìn rộng hơn, trong lịch sử V-League, có rất nhiều nhà cầm quân ngoại quốc tới hành nghề nhưng chỉ 2 người thực sự thành công. Đó là HLV Arjhan Somgamsak (vô địch V-League 2003, 2004 cùng HAGL) và Henrique Calisto (vô địch V-League 2005, 2006 cùng Đồng Tâm Long An). Thành tích cao nhất cho một HLV ngoại tại V-League kể từ năm 2006 chỉ là ngôi Á quân (HLV Chung Hae-seong, mùa 2019 cùng TP HCM; HLV Petrovic, mùa 2016 cùng Thanh Hóa). Chỉ tính riêng 10 năm qua, có 23 nhà cầm quân nước ngoài tới V-League và 16 người trong số đó không thể trụ lại quá 1 mùa giải.</p>\r\n<p>Chuyên gia Lê Thế Thọ cho rằng, điều này hết sức bình thường bởi những HLV ngoại đa phần có phong cách chuyên nghiệp còn các CLB Việt Nam lại chưa đáp ứng được yêu cầu đó. “Ở nước ngoài, HLV được toàn quyền chuyên môn còn ở Việt Nam thì không. Ông bầu, nhà tài trợ vẫn thường có thói quen chỉ đạo chiến thuật, tác động nhân sự”, ông Thọ nhận xét.</p>\r\n<p>“Cầu thủ nước ngoài tập ra tập, chơi ra chơi nhưng cầu thủ Việt Nam vừa tập vừa chơi. Rồi cả việc phối hợp giữa các bộ phận, các cá nhân có trách nhiệm ở một đội bóng cũng kém nghiêm túc. Chuyện đến muộn là bình thường nhưng người nước ngoài họ không thế, họ yêu cầu phải đúng giờ. Lâu ngày sẽ nảy sinh chuyện phản ứng hoặc chống đối ngầm. Nhưng suy cho cùng, cả hệ thống <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> đều thiếu chuyên nghiệp chứ chẳng riêng các CLB, thế nên để thay đổi thì không dễ”, ông Thọ nói thêm.</p>\r\n<p>Cũng theo chuyên gia Lê Thế Thọ, một nguyên nhân nữa là các HLV ngoại tới Việt Nam làm việc đa phần không thuộc hàng xuất sắc. “Nếu họ giỏi thì họ đã không thất nghiệp ở đất nước của họ, họ đã tìm tới những nền <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> cao hơn, phát triển hơn. Tổng hòa hai yếu tố tôi vừa nêu thì sẽ hiểu tại sao V-League không phải là đất lành với HLV ngoại”, ông Thọ nói.</p>\r\n<p>Trong khi đó, HLV Triệu Quang Hà đánh giá, yếu tố văn hóa là rào cản lớn nhất khiến HLV ngoại khó thích ứng tại V-League. “Giữa văn hóa phương Tây và văn hóa phương Đông có sự khác biệt lớn. Sự khác biệt về văn hóa sẽ tạo ra khoảng cách giữa HLV và cầu thủ, HLV và ê kíp hỗ trợ”, ông Hà nói và phân tích thêm: “Các HLV ngoại đa phần đều không nhận được sự hỗ trợ, ủng hộ tối đa từ lãnh đạo đội bóng cũng như các trợ lý người Việt và thậm chí cả cầu thủ”.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'binbin'),
(9, 'Ngã Ngửa Bầu Đệ và CLB Thanh Hóa lại không bỏ V-League', 'Bầu Đệ công bố sẽ không bỏ giải V-League như công văn gửi đến VFF và VPF chiều 5/8.', '          <p>“VFF và VPF đã gọi điện cho chúng tôi để trao đổi. Họ cho biết sẽ sắp xếp để ban điều hành giải đấu và các CLB cùng ngồi lại với nhau, bàn bạc tìm giải pháp thời gian tới. Như vậy, khi mà VFF và VPF đưa ra giải pháp hợp lý, CLB Thanh Hoá sẽ tiếp tục thi đấu các trận đấu ở <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> theo lịch của ban tổ chức”.</p>\r\n<p>Đó là chia sẻ mới nhất từ ông bầu Nguyễn Văn Đệ của CLB Thanh Hoá chiều ngày 6/8. Bầu Đệ xác nhận CLB Thanh Hoá sẽ không bỏ giải như công văn gửi đến VFF và VPF chiều 5/8. Các cầu thủ Thanh Hoá được lệnh tiếp tục tập trung để tập luyện, chờ ngày V-League thi đấu trở lại.</p>\r\n<p>Theo tìm hiểu, ngoài lý do đã được VFF và VPF lắng nghe và chia sẻ khó khăn, CLB Thanh Hoá quyết định rút lại công văn xin không tiếp tục thi đấu các trận đấu còn lại ở V-League 2020 theo chỉ đạo của Sở Văn hoá, Thể thao và Du lịch tỉnh Thanh Hoá.</p>\r\n<p>Chiều tối ngày 5/8, ông bầu Nguyễn Văn Đệ của CLB Thanh Hoá gây ngỡ ngàng làng <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> khi ký công văn gửi đến VFF và VPF để xin “không tiếp tục tham gia thi đấu ở V-League 2020”. Lý do bầu Đệ đưa ra để đội bóng xứ Thanh có thể bỏ giải là khó khăn về tài chính, trong khi V-League 2020 chưa biết đến khi nào có thể trở lại.</p>\r\n<p>Bên cạnh đó, trong công văn của CLB Thanh Hoá vẫn có thêm “điều khoản” để ra điều kiện với VFF và VPF rằng họ sẽ không bỏ giải nếu được hỗ trợ về mặt tài chính. “Không riêng gì CLB Thanh Hoá mà nhiều đội bóng khác ở V-League đang gặp rất nhiều khó khăn về tài chính.</p>\r\n<p>Trong tình cảnh này, chúng tôi chưa thể giảm lương cầu thủ như đợt giải đấu bị hoãn lần trước. Đội bóng còn phải nuôi biết bao nhiêu con người, miệng ăn núi lở. Khi VFF hay VPF chưa thể đưa ra các giải pháp kịp thời, chúng tôi phải hành động chứ không thể chờ mãi được”, bầu Đệ chia sẻ.</p>\r\n<p>Xác nhận với phóng viên, Tổng thư ký VFF Lê Hoài Anh cho biết Liên đoàn bóng đá Việt Nam đã nhận được công văn kể trên của CLB Thanh Hoá. Ban thư ký của VFF cũng đã báo cáo lên thường trực VFF để xin ý kiến chỉ đạo về vụ việc CLB Thanh Hoá xin không tham gia các vòng đấu còn lại của V-League 2020.</p>\r\n<p>Một lãnh đạo cấp cao khác của VFF chia sẻ rằng: “Việc các giải bóng đá đang phải tạm hoãn vì Covid-19 là tình huống bất khả kháng. VFF và VPF cần sự chung tay của các đội bóng để cùng vượt qua khó khăn hiện tại.</p>\r\n<p>Ngoài ra, VFF luôn lắng nghe những góp ý, đề xuất từ phía các đội bóng. Về trường hợp của CLB Thanh Hoá xin không tham gia các vòng đấu còn lại của V-League 2020, thường trực VFF sẽ họp bàn trong thời gian tới để đưa ra phương án xử lý phù hợp nhất có thể.”</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'binbin'),
(10, 'Bình Dương thua toàn diện trên sân nhà, HLV Hiếu vẫn chê Thanh Hóa đá kém', 'HLV trưởng của Bình Dương - Đỗ Công Hiếu vẫn không đề cao đội bóng Thanh Hóa dù nhận thất bại trước đối thủ ngay trên sân nhà tại vòng 10 V-League.', '          <p>Dù bị Thanh Hóa đánh bại, HLV trưởng đội bóng Bình Dương – ông Nguyễn Thanh Sơn vẫn khẳng định đội khách thi đấu không có gì đặc sắc: “Tôi thấy Thanh Hóa đá rất bình thường, chúng tôi bắt bài được hết. Trận này chúng tôi thua là do lỗi cá nhân của các cầu thủ nhà. Nếu không, chúng tôi chắc chắn chỉ từ hòa đến thắng”.</p>\r\n<p>“Một phần nữa khiến cầu thủ của chúng tôi chơi không tốt đó là do áp lực phải thắng trận đấu này để cạnh tranh suất trong top 8 cho giai đoạn 2. Mà nhiều cầu thủ của tôi còn trẻ nên tâm lý không vững. Hàng công chúng tôi cũng nôn nóng nên bỏ lỡ nhiều cơ hội ngon ăn”, ông Sơn nói thêm.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\">HLV trưởng Bình Dương vẫn lạc quan về cơ hội vào top 8 của đội nhà. Ông Sơn cho biết đội bóng của mình sẽ cố gắng lấy được nhiều điểm nhất trong 3 trận còn lại.</div>\r\n<div class=\"banner_inread\">\r\n<p>Trong khi đó, HLV trưởng Thanh Hóa Nguyễn Thành Công bày tỏ niềm hạnh phúc trước chiến thắng này. Ông cho rằng chính tinh thần chiến đấu lăn xả của các cầu thủ đã mang về <a class=\"TextlinkBaiviet\" title=\"kết quả\" href=\"https://www.24h.com.vn/ket-qua-bong-da-c140.html\">kết quả</a> mỹ mãn.</p>\r\n<p>“Trước trận đấu này, chúng tôi chỉ đặt mục tiêu giành 1 điểm nhưng các cầu thủ của tôi còn làm tốt hơn. Tôi không đặt sức ép cho học trò dù ở trận đấu nào mà chỉ cố gắng qua từng trận. Đây là chiến thắng rất quan trọng giúp chúng tôi có cơ hội cạnh tranh cho top 8”, ông Công nói.</p>\r\n</div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(11, 'Công Phượng lên núi làm...nông dân, đầu tư vào nông nghiệp?', 'Trong lúc V-League 2020 vẫn đang tạm hoãn vì tình hình căng thẳng của dịch covid-19, Công Phượng có dịp ghé thăm nhà của người bạn Võ Huy Toàn ở TP.Đà Lạt.', '          <p>Mới đây, cầu thủ chạy cánh Võ Huy Toàn của CLB TP.HCM đã đăng tải hình ảnh anh cùng với người đồng đội Nguyễn Công Phượng cùng nhau đi hái bơ, chặt mía thu hút sự chú ý của đông đảo giới cầu thủ lẫn người hâm mộ.</p>\r\n<p>Hình ảnh Công Phượng và Huy Toàn mang ủng lấm lem bùn đất khi khoe thành quả là những trái bơ và một cây mía, thực sự làm nhiều cổ động viên thích thú. “Kiếm thêm mùa dịch, ai cần cứ liên hệ nhé”, Huy Toàn chú thích dưới hình ảnh chụp cùng Công Phượng.</p>\r\n<p>Dưới phần bình luận, nhiều người thân của Công Phượng và Huy Toàn vào khen bộ đôi cầu thủ đang đầu quân cho CLB TP.HCM là “nông dân chính hiệu”. Thậm chí, có người còn trêu Công Phượng rằng tiền đạo sinh năm 1995 dường như đang muốn đầu tư thêm vào lĩnh vực nông nghiệp.</p>\r\n<p>Được biết, tranh thủ quãng thời gian được nghỉ ngơi trong lúc chờ <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> thi đấu trở lại, cầu thủ chạy cánh Võ Huy Toàn đã có dịp mời người đồng đội Nguyễn Công Phượng về quê nhà ở Đà Lạt chơi. Công Phượng và Huy Toàn là những người bạn thân với nhau kể từ khi cả hai cùng thi đấu trong màu áo U23 Việt Nam thi đấu tại SEA Games 2015 ở Singapore.</p>\r\n<p>Hiện tại, Công Phượng – Huy Toàn đang là đối tác ăn ý trên hàng công của đội Á quân V-League. Theo tìm hiểu, Công Phượng và đồng đội đang được CLB TP.HCM cho “xả trại”. Dự kiến, CLB TP.HCM hội quân trở lại vào ngày 10/8 và đây cũng là thời điểm bộ đôi tân binh triệu đô Ariel Rodriguez và Jose Ortiz có buổi tập đầu tiên với Công Phượng cùng đồng đội.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thunthun'),
(12, 'Chủ tịch kỳ lạ nhất V-League nói về đề xuất trao luôn cúp cho Sài Gòn FC', 'Một số CLB ở V-League đề nghị kết thúc sớm mùa giải 2020 và trao nốt ngôi vô địch cho đội đầu bảng Sài Gòn FC, khi một lần nữa giải đấu bị hoãn vô thời hạn vì dịch covid-19. Hôm nay, chủ tịch kiêm HLV trưởng của Sài Gòn FC đã lên tiếng về đề xuất trên.', '          <p>Do ảnh hưởng của dịch Covid-19, hôm 26/7 Công ty cổ phần <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> chuyên nghiệp Việt Nam (VPF) đã ra thông báo tạm dừng V-League 2020 kể từ vòng 12 và giải hạng Nhất từ vòng 10. Với việc các giải bóng đá chuyên nghiệp Việt Nam một lần nữa hoãn vô thời hạn vì Covid-19, một số CLB đã gửi công văn đến VFF và VPF đề nghị V-League 2020 kết thúc sớm ngay từ lúc này.</p>\r\n<p>Cụ thể, lãnh đạo các CLB như SLNA, Quảng Nam cùng đề xuất lên VFF và VPF về việc nên dừng hẳn giải đấu để tập trung phòng, chống dịch Covid-19. Ngoài ra, các đội bóng nhóm cuối bảng xếp hạng còn đề xuất trao chức vô địch V-League 2020 cho đội đầu bảng Sài Gòn và không có đội xuống hạng ở mùa giải này.</p>\r\n<p>Trao đổi với truyền thông ngày 28/7, ông Trần Anh Tú – Chủ tịch Hội đồng quản trị kiêm Tổng giám đốc VPF cho biết V-League 2020 chỉ huỷ trong trường hợp bất khả kháng và thời gian tổ chức giải không còn. Đến hôm nay (5/8), Chủ tịch kiêm HLV trưởng Vũ Tiến Thành của CLB Sài Gòn lần đầu chính thức lên tiếng về đề xuất kết thúc sớm V-League 2020 và trao cúp cho đội bóng Sài thành.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\"><span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>“Đã tham gia thi đấu bóng đá, ai cũng muốn vô địch. Nhưng CLB Sài Gòn không ngồi yên chờ chức vô địch. Thay vào đó, chúng tôi luôn mong muốn được tiếp tục thi đấu để chinh phục danh hiệu, nếu Chính phủ cho phép giải đấu trở lại. Tôi cũng phân tích thêm, hoãn giải đấu là chờ giải pháp tiếp theo.</p>\r\n<p>Huỷ giải đấu là huỷ bỏ kết quả từ đầu mùa giải. Còn kết thúc giải sớm là công nhận thành tích tại thời điểm đó. Vì vậy, nếu trong hoàn cảnh bất khả kháng, V-League không thể tiếp tục thì mọi người không thể phủ nhận nỗ lực của Sài Gòn FC trong giai đoạn vừa qua.</p>\r\n<p>Chúng tôi không cần một danh hiệu, nhưng đó có thể là một suất dự đấu trường AFC Champions League vào năm sau. Bởi nếu mùa giải kết thúc sớm, chúng ta cần xác định vị trí của các đội ở thời điểm hiện tại và cử ra đại diện cho Việt Nam tham dự đấu trường châu Á năm tới.</p>\r\n<p>CLB Sài Gòn đang hướng đến thành tích bất bại và vô địch giai đoạn một. Chúng tôi đã thắng cả đương kim vô địch Hà Nội và Á quân TP.HCM ngay trên sân khách. Liên đoàn bóng đá châu Á và VPF cũng đánh giá và hoan nghênh chúng tôi. Vậy thì chúng tôi cần được ghi nhận cho những nỗ lực đó”, Chủ tịch kiêm HLV Vũ Tiến Thành của CLB Sài Gòn cho hay.</p>\r\n<p>Ông Vũ Tiến Thành, người duy nhất ở <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> hiện tại vừa làm Chủ tịch kiêm HLV ở một đội bóng V-League, chia sẻ thêm, trong thời gian tới VPF cần họp trực tuyến với các đội bóng để chia sẻ và lắng nghe ý kiến các CLB, để lên các giải pháp và lộ trình cho giải đấu: “Tất nhiên, để V-League quay trở lại, yếu tố sức khoẻ an toàn là trên hết. Trong giai đoạn này, VFF và VPF cần phải có những cảm thông và chia sẻ tài chính với các đội bóng.</p>\r\n<p>Tại sao doanh nghiệp được Chính phủ hỗ trợ, FIFA hỗ trợ các liên đoàn thành viên mà VFF lẫn VPF lại không hỗ trợ các đội bóng trong lúc gặp khó khăn vì đại dịch? Theo tôi, trong giai đoạn giải đấu đang trì hoãn như thế này, VPF cần có một kế hoạch rõ ràng để các đội bóng nắm bắt và hoạch định tài chính của mình.</p>\r\n<p>VPF cho rằng huỷ V-League sẽ phải đền bù hợp đồng cho nhà tài trợ. Nhưng theo tôi tìm hiểu, trong một hợp đồng kinh tế, nếu hợp đồng không được thực hiện vì lý do bất khả kháng như thiên tai, dịch bệnh thì không phải đền bù hợp đồng đó. Do đó, chúng tôi muốn VPF cần xây dựng lộ trình, phán đoán thời gian kéo dài của dịch bệnh để định hướng các giải pháp cho các đội bóng chuẩn bị và thực hiện”.</p>\r\n<p>Sau 5 ngày cho các cầu thủ “xả trại”, CLB Sài Gòn vừa tập trung trở lại và rèn thể lực trong lúc chờ quyết định tiếp theo của Ban điều hành V-League 2020. Chủ tịch kiêm HLV Vũ Tiến Thành tiết lộ, dù giải đấu đang tạm hoãn nhưng CLB Sài Gòn vẫn giữ nguyên chế độ lương, thưởng với các cầu thủ và toàn bộ thành viên của đội bóng duy nhất bất bại sau 11 vòng đấu của V-League 2020.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thonthon'),
(13, 'VFF hoãn Đại hội thường niên, các giải bóng đá VN vạ lây vì dịch Covid-19', 'Chiều 4/8, VFF chính thức thông báo hoãn Đại hội thường niên VFF 2020 và tạm dừng các giải bóng đá ngoài chuyên nghiệp để phòng, chống dịch Covid-19.', '          <p>Theo kế hoạch dự kiến, Đại hội thường niên VFF năm 2020 sẽ được tổ chức vào ngày 8/8 tại Hà Nội. Bầu vị trí Phó chủ tịch VFF phụ trách tài chính là một trong những nội dung quan trọng tại Đại hội thường niên VFF năm 2020.</p>\r\n<p>Tuy nhiên, do diễn biến phức tạp của dịch Covid-19, một số đại diện lãnh đạo các đội bóng như Thanh Hoá, Quảng Nam, hay Đà Nẵng đã kiến nghị tạm hoãn đại hội. Đến chiều 4/8, Liên đoàn <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> đã lấy ý kiến các thành viên Ban chấp hành để chính thức quyết định lùi thời gian tổ chức Đại hội thường niên.</p>\r\n<p>“Được sự đồng ý của các cơ quan có thẩm quyền, Đại hội thường niên VFF năm 2020 khóa VIII, nhiệm kỳ 2018 - 2020 dự kiến được tổ chức ngày 08/8/2020 tại Hà Nội. Cho đến thời điểm hiện tại, mọi công tác chuẩn bị cho việc tổ chức Đại hội thường niên VFF năm 2020 đã hoàn tất, kể cả việc đảm bảo các yêu cầu phòng chống dịch của ngành tế.</p>\r\n<p>Căn cứ tình hình hiện tại, ngày 04/08 Ban Chấp hành VFF đã thống nhất ý kiến hoãn thời gian tổ chức Đại hội thường niên VFF năm 2020. Đại hội thường niên sẽ chuyển sang tổ chức ở thời điểm khác phù hợp để các tổ chức thành viên VFF có thể tham dự đầy đủ và đảm bảo thành công cho Đại hội, sự điều chỉnh này cũng được VFF thông báo đến FIFA, AFC và AFF”, thông báo của VFF ghi rõ.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\">Cùng lúc với việc ra quyết định tạm hoãn vô thời hạn Đại hội thường niên VFF, Liên đoàn bóng đá Việt Nam đã thông báo tạm dừng toàn bộ các giải bóng đá ngoài chuyên nghiệp để tăng cường phòng, chống dịch Covid-19.</div>\r\n</div>\r\n<p>Các giải đấu ngoài chuyên nghiệp do VFF tổ chức phải tạm hoãn vì dịch Covid-19 gồm: Lượt về giải Bóng đá hạng Nhì quốc gia – On Sports 2020; Lượt về vòng loại và vòng chung kết giải Bóng đá Vô địch U15 quốc gia 2020; Vòng chung kết giải Bóng đá Vô địch U17 quốc gia 2020; Giải Bóng đá nữ Vô địch U16 quốc gia 2020; Giải Bóng đá nữ Vô địch quốc gia – Cúp Thái Sơn Bắc 2020; Giải Bóng đá Bãi biển Vô địch quốc gia 2020; Vòng chung kết giải Bóng đá Thiếu niên và Nhi đồng toàn quốc 2020.</p>\r\n<p>Quyết định của VFF nhằm hạn chế triệt để nguy cơ lây lan; đảm bảo an toàn cho các cầu thủ, ban huấn luyện và thành viên tham dự trước tình hình phức tạp của dịch Covid-19. Trước đó, các giải bóng đá chuyên nghiệp của Việt Nam như V-League 2020, giải hạng Nhất 2020 và Cúp Quốc gia 2020 đã tạm hoãn chưa hẹn ngày trở lại vì Covid-19.</p>\r\n<p>Do ảnh hưởng của dịch Covid-19, <a class=\"TextlinkBaiviet\" title=\"ĐT Việt Nam\" href=\"https://www.24h.com.vn/doi-tuyen-viet-nam-c48e3410.html\">ĐT Việt Nam</a> không có cơ hội bảo vệ ngôi vô địch AFF Cup trong năm 2020, bởi Ban xử lý các tình huống khẩn cấp của AFF đã đề xuất dời lịch tổ chức giải sang tháng 4/2021.</p>\r\n<p>ĐT Việt Nam chỉ còn 1 giải đấu trong năm 2020 là vòng loại World Cup 2022 khu vực châu Á, nhưng chưa có gì đảm bảo để các trận đấu của thầy trò ông Park Hang Seo trước Malaysia, Indonesia và UAE có thể diễn ra đúng lịch.</p>\r\n<p>Ngoài ra, HLV Park Hang Seo đã lên kế hoạch để ĐT Việt Nam và U23 Việt Nam tập trung từ 14/8 đến 21/8 tại Hà Nội. Nhưng theo lãnh đạo VFF, đợt tập trung của các ĐTQG Việt Nam có thể một lần nữa phải thay đổi lịch. Đó là lý do đến thời điểm này VFF và HLV Park Hang Seo vẫn chưa chính thức công bố danh sách tập trung 2 đội tuyển.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(14, 'Báo Indonesia bất ngờ \"khiêu khích\" tuyển Việt Nam vs ThaiLand', 'Truyền thông xứ vạn đảo cho rằng, cả Việt Nam lẫn ThaiLand đang có sự chuẩn bị không tốt bằng Indonesia ở vòng loại World Cup 2022.', '          <p>Cụ thể, tờ Indosport mới đây đã đăng tải bài viết nói về việc Việt Nam lẫn Thái Lan đang không có được sự chuẩn bị tốt nhất cho các trận đấu tiếp theo tại vòng loại <a class=\"TextlinkBaiviet\" title=\"World Cup 2022\" href=\"https://www.24h.com.vn/world-cup-2022-c48e2601.html\">World Cup 2022</a> diễn ra vào tháng 10 và 11 sắp tới.</p>\r\n<p>\"2/4 đối thủ chung bảng G của Indonesia gồm Việt Nam và Thái Lan đang gặp phải những khó khăn nhất định bởi kế hoạch tập luyện bị cản trở bởi một vài vấn đề.</p>\r\n<p>Tuyển Thái Lan đang ngóng chờ sự trở lại của HLV Akira Nishino. Trước đó, vị chiến lược gia người Nhật dự định trở lại Thái Lan vào tháng 7/2020 nhưng vì dịch Covid-19 khiến ông vẫn chưa được cấp phép bay.</p>\r\n<p>Được biết, LĐBĐ Thái Lan (FAT) sẵn sàng chi số tiền 120.000 USD để giúp HLV Nishino có giấy phép trở lại Thái Lan. Tuy nhiên, vẫn chưa xác định cụ thể thời gian nhà cầm quân sinh năm 1955 quay lại xứ chùa Vàng.</p>\r\n<p>Trong khi đó, Việt Nam cũng gặp vấn đề như Thái Lan trong quá trình chuẩn bị. Lúc này, họ đang bị sốc bởi sự tái phát của dịch bệnh Covid-19.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Các giải đấu cũng phải hoãn vì sự an toàn của con người. HLV Park Hang-seo hẳn sẽ rất lo lắng khi kế hoạch tập trung vào tháng 8/2020 có khả năng bị hủy”, Indosport viết.</p>\r\n<p>Ở chiều ngược lại, tờ nhật báo hàng đầu Indonesia lại khá vui với sự chuẩn bị của đội nhà.</p>\r\n<p>“Khác với hai đội bóng trên, tuyển Indonesia dễ thở hơn nhờ sớm trở lại tập luyện để chuẩn bị cho ba trận còn lại tại vòng loại World Cup 2022.</p>\r\n<p>Thực tế, \"Garuda\" đã tập trung vào ngày 1/8 nhưng bị hoãn vì vẫn đang chờ các kết quả kiểm tra Covid-19”, tờ Indosport khẳng định.</p>\r\n<p>Dù có sự chuẩn bị có thể coi là tốt hơn so với Việt Nam và Thái Lan nhưng tuyển Indonesia thực tế đã bị loại sau 5 trận toàn thua ở bảng G vòng loại World Cup 2022 khu vực châu Á.</p>\r\n<p>Điều đáng chú ý, đội bóng xứ vạn đảo nhận thất bại 1-3 và 0-3 trước chính Việt Nam lẫn Thái Lan trong các cuộc đối đầu trực tiếp diễn ra vào thời điểm cuối năm 2019.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thunthun'),
(15, 'Hy vọng của HLV Park góp mặt trong danh sách tiền đạo tệ nhất V-League', 'Tiền đạo Hà Minh Tuấn gây thất vọng khi chưa thể ghi bàn sau 11 vòng đấu của V-League 2020.', '          <p>V-League 2020 đã diễn ra được 11 vòng đấu và có không ít những cầu thủ nội tạo ra những dấu ấn nhất định. Có thể kể đến các gương mặt cũ như Công Phượng, Văn Toàn hay cái tên Văn Long. Tuy nhiên, bên cạnh đó cũng có những cái tên đang gây thất vọng rất lớn. Một trong số đó phải kể đến tiền đạo Hà Minh Tuấn của CLB Quảng Nam.</p>\r\n<p>Tính ra, sau 8 trận được ra sân (4 trận đá chính) với tổng cộng 479 phút thi đấu ở mùa giải năm nay, Minh Tuấn vẫn chưa có pha lập công nào. Đây cũng là một trong những nguyên nhân khiến Quảng Nam đang đứng ở vị trí cuối bảng sau 11 vòng đấu.</p>\r\n<p>Thực tế, chấn thương gặp phải trước thềm V-League 2020 khởi tranh khiến cầu thủ gốc Đại Lộc (Quảng Nam) đang rất khó khăn trong việc tìm lại bản năng săn bàn. Bên cạnh đó, cũng phải kể đến sự cạnh tranh rất lớn đến từ 3 chân sút ngoại nơi đội bóng xứ Quảng.</p>\r\n<p>Phong độ chơi bóng mờ nhạt của Hà Minh Tuấn suốt thời gian qua chắc chắn sẽ là cơn đau đầu với HLV Đào Quang Hùng và còn cả HLV Park Hang-seo. Trong nỗ lực tìm chân sút khỏa lấp vị trí Anh Đức để lại ở ĐTQG, vị chiến lược gia người Hàn Quốc từng rất kỳ vọng vào Minh Tuấn.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Thậm chí, ông từng triệu tập cầu thủ sinh năm 1991 lên tuyển tham dự vòng loại World Cup 2022 khu vực châu Á vào năm ngoái. Có thể hình lý tưởng cùng với khả năng tì đè và dứt điểm tốt, Minh Tuấn được kỳ vọng sẽ tạo ra sự cạnh tranh với cặp đôi Đức Chinh - Tiến Linh trong vai trò tiền đạo mũi nhọn của <a class=\"TextlinkBaiviet\" title=\"đội tuyển Việt Nam\" href=\"https://www.24h.com.vn/doi-tuyen-viet-nam-c48e3410.html\">đội tuyển Việt Nam</a>.</p>\r\n<p>Trung phong của CLB Quảng Nam cần sớm cải thiện phong độ nếu muốn có cơ hội được gọi lại đội tuyển. Tuy vậy, mục tiêu này không hề dễ dàng nhất là khi Minh Tuấn đang chịu áp lực lớn sau quãng thời gian dài tịt ngòi.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(16, 'Vì sao thầy mới của Công Phượng không muốn hủy V-League 2020?', 'Chủ tịch kiêm HLV trưởng của CLB TP.HCM Nguyễn Hữu Thắng không muốn hủy V-League 2020.', '          <p>Trong một chia sẻ mới đây, Chủ tịch kiêm HLV trưởng CLB TP.HCM nêu quan điểm, thời điểm này là vội vàng nếu nói về việc hủy mùa giải 2020.</p>\r\n<p>“Mùa giải này quá nhiều sóng gió do dịch Covid-19 và mọi người cần đón nhận nó một cách bình thường. Giống như trong <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> có thắng, có thua nhưng quan trọng là cần hướng đến tương lai. Đội TP.HCM vẫn bảo lưu quan điểm mong muốn mùa giải này không nên dừng lại ở đây”, ông Thắng nói.</p>\r\n<p>“Covid-19 đã làm xáo trộn quá nhiều thứ, tác động đến chuyện tài chính, lịch tập luyện, thi đấu của đội nhưng lúc này chúng ta cần có niềm tin vào công tác chống dịch của Chính phủ. Hiện nay, chúng ta cũng không nóng vội và đặt niềm tin lớn vào công tác chống dịch. Chỉ khi Chính phủ yêu cầu dừng hẳn mùa giải thì lúc đó mới là quyết định cuối cùng”, tiếp lời ông Thắng.</p>\r\n<p>Trước đó, nhiều CLB như SLNA, Thanh Hóa, Quảng Nam, Bình Dương đã đề nghị VFF, VPF hủy mùa giải 2020 để đảm bảo an toàn. Cũng có ý kiến cho rằng nên trao chức vô địch cho Sài Gòn FC còn nhóm cuối không có ai xuống hạng.</p>\r\n<p>Việc ông Thắng phản đối việc hủy mùa giải hoàn toàn có thể hiểu được. TP.HCM mang tham vọng vô địch, họ cũng đang không bị tụt lại quá xa so với Sài Gòn FC nên không muốn \"buông súng\".</p>\r\n<p>Đó là chưa kể TP.HCM mới đầu tư gần 30 tỷ đồng để đưa về 2 tiền đạo Costa Rica nhằm phục vụ cho cuộc đua vô địch. Đương nhiên, đội chủ sân Thống Nhất chẳng muốn nhìn thấy kịch bản 2 tân binh đắt giá ngồi chơi tới mùa giải năm sau.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thonthon'),
(17, 'Cựu tuyển thủ Việt Nam lọt top 5 chân sút vĩ đại nhất Đông Nam Á', 'Cựu tiền đạo Lê Công Vinh là cầu thủ duy nhất của đội tuyển Việt Nam lọt top 5 chân sút ghi nhiều bàn thắng nhất cho ĐTQG khu vực Đông Nam Á.', '          <p>Trang <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> khá nổi tiếng của khu vực Đông Nam Á - Asean Fooball mới đây đã thống kê 5 cây làm bàn xuất sắc nhất lịch sử. Lê <a class=\"TextlinkBaiviet\" title=\"Công Vinh\" href=\"https://www.24h.com.vn/cong-vinh-c48e4392.html\">Công Vinh</a> là cầu thủ duy nhất của <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> vinh dự lọt vào bản danh sách trên.</p>\r\n<p>Cụ thể, cầu thủ gốc Nghệ An đứng vị trí thứ 5 khi có tới 51 pha lập công chỉ sau 83 lần ra sân trong màu áo tuyển Việt Nam.</p>\r\n<p>Người đứng đầu trong bản danh sách này là cựu tiền đạo người Malaysia - Zainal Abidin. Ông ghi được 78 bàn thắng sau 134 lần khoác trong giai đoạn từ năm 1984 -1996.</p>\r\n<p>Xếp thứ 2 không ai khác là Kiatisak khi \"Zico Thái\" có tới 71 pha lập công sau gần 15 năm chơi cho đội tuyển Thái Lan.</p>\r\n<p>Đứng thứ 3 là huyền thoại bóng đá Singapore Fandi Ahmad. Cựu tiền đạo này ghi được 55 bàn thắng trong giai đoạn 1979-1997.</p>\r\n<p>Vị trí thứ 4 thuộc về Phil Younghusband, cầu thủ sinh năm 1987 có 52 pha lập công, hơn chính Công Vinh người xếp ngay sau với khoảng cách đúng 1 bàn.</p>\r\n<p>Mặc dù đã giải nghệ thế nhưng Lê Công Vinh vẫn đang giữ kỷ lục là cây làm bàn tốt nhất lịch sử bóng đá Việt Nam. Hai người xếp sau gồm Lê Huỳnh Đức (28 bàn) và Nguyễn Hồng Sơn (16 bàn) cũng đã từ giã sân cỏ.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Trong khi đó, nếu tính các cầu thủ hiện đang còn thi đấu thì Văn Quyết mới là người có nhiều bàn thắng nhất. Tính ra, cầu thủ sinh năm 1991 ghi đc 14 bàn thắng sau 51 lần ra sân.</p>\r\n<p>Dẫu vậy, chân sút thuộc biên chế CLB Hà Nội lại đang bị thầy Park \"lãng quên\" ở các đợt tập trung trước của đội tuyển Việt Nam nên anh khó lòng gia tăng thành tích.</p>\r\n<p>Những chân sút được kỳ vọng khác đang thi đấu như Nguyễn Công Phượng hay Văn Toàn lại chưa ghi nổi 10 bàn thắng.</p>\r\n<p>Cụ thể, sau 35 lần khoác áo tuyển Việt Nam, Công Phượng ghi 8 bàn. Với Văn Toàn chỉ là 4 pha lập công khi đã ra sân 27 trận.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(18, 'Trưởng ban trọng tài Dương Văn Hiền tuyên bố sẽ từ chức nếu....', 'Trưởng Ban trọng tài Dương Văn Hiền của VFF chia sẻ về áp lực của mình khi một số đội bóng lên tiếng đòi sếp \"Vua áo đen\" Việt Nam từ chức, sau nhiều sai lầm của các trọng tài tại V-League 2020.', '          <p>V-League 2020 chính thức tạm hoãn từ vòng 12 do ảnh hưởng của dịch Covid-19, sau thông báo của Công ty cổ phần <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> chuyên nghiệp Việt Nam (VPF) hôm 26/7, Dù vậy, đến ngày 27/7 CLB TP.HCM vẫn có đơn gửi đến VFF và VPF để kiến nghị về công tác trọng tài ở trận đấu giữa đội Á quân <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> và Hà Nội ở vòng 11 trên sân vận động Thống Nhất.</p>\r\n<p>Đáng chú ý, trong công văn gửi đến VFF và VPF, CLB TP.HCM tiếp tục bày tỏ mong muốn Liên đoàn <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> xem lại năng lực và vai trò của Trưởng Ban trọng tài Dương Văn Hiền. Không chỉ đội bóng Công Phượng đang đầu quân lên tiếng chỉ trích Trưởng Ban trọng tài, Giám đốc kỹ thuật Nguyễn Văn Sỹ của CLB Nam Định còn thẳng thắn cho rằng “ông Dương Văn Hiền nên từ chức nếu còn liêm sỉ”.</p>\r\n<p>Sau rất nhiều chỉ trích từ người hâm mộ và các đội bóng tham dự V-League, ông Dương Văn Hiền đã lên tiếng nhận lỗi về sai sót của các trọng tài trong buổi gặp gỡ truyền thông tại trụ sở VFF hôm 28/7. Trưởng Ban trọng tài cho biết nhiều trọng tài đã bị kỷ luật sau những sai lầm mắc phải ở các trận đấu liên quan đến đội Nam Định, TP.HCM, SLNA…</p>\r\n<p>Nhưng theo lãnh đạo các CLB kể trên, họ muốn thấy được trách nhiệm cụ thể của Trưởng Ban trọng tài Dương Văn Hiền. Ông Dương Văn Hiền là người trực tiếp phân công trọng tài, người “đứng mũi chịu sào” của Ban trọng tài nhưng lại không bị kỷ luật và vẫn “bình an vô sự”.</p>\r\n<p>Khi được hỏi lại về chuyện từ chức trong một cuộc trò chuyện mới đây cùng Vlogger Minh Hải, Trưởng Ban trọng tài Dương Văn Hiền đã chia sẻ rằng: “Tôi nghĩ đơn giản thế này, trong lúc khó khăn nhất mà tôi từ chức thì dễ quá. Cái chức của tôi do Ban chấp hành VFF bầu, Ban chấp hành VFF quyết định việc đấy. Nếu có người làm tốt hơn tôi, có tâm hơn tôi, tôi sẵn sàng ra đi.</p>\r\n<p>Chuyện đó với tôi rất bình thường. Nhiều người không biết tôi làm giảng viên dạy ở trường đại học đã 33 năm và làm công tác trọng tài 28 năm. Cái chất sư phạm vẫn có trong tôi, nếu tôi từ chức cũng thanh thản thôi, miễn sao có người tốt hơn tôi vào làm vị trí này.</p>\r\n<p>Tôi không hoàn thành nhiệm vụ thì ra đi thôi. Tôi là người đứng mũi chịu sào ở Ban trọng tài. Tôi ngồi khán đài cũng bị chửi, trong khi tôi không cầm còi, không quyết định gì trên sân mà lại bị chửi”.</p>\r\n<p>Bên cạnh đó, ông Dương Văn Hiền một lần nữa nêu những lý do dẫn đến việc các trọng tài mắc nhiều sai sót nghiêm trọng ảnh hưởng đến một số đội bóng ở mùa giải năm nay. “Mùa giải này, V-League thi đấu theo thể thức mới căng thẳng, quyết liệt hơn nên các trọng tài phần nào bị tâm lý, dẫn đến việc bị ảnh hưởng đến chuyên môn.</p>\r\n<p>Ngoài ra, mỗi sai lầm của trọng tài đều bị người hâm mộ lẫn truyền thông “soi” rất kỹ. Từ những sức ép ấy, sai sót của các trọng tài năm nay nặng hơn năm trước và đáng báo động ở một số thời điểm”, ông Dương Văn Hiền nói.</p>\r\n<p>Tiếp đến, Trưởng Ban trọng tài của VFF cho biết ông rất buồn và “đau đáu” khi bị cổ động viên Nam Định giăng băng rôn chỉ trích ở trận đấu giữa đội bóng thành Nam và Bình Dương mới đây. Ông Hiền một lần nữa khẳng định các trọng tài chỉ đơn thuần mắc lỗi về chuyên môn và con người ở trong các trận đấu của CLB Nam Định, chứ không có vấn đề về tư tưởng.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'binbin');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(19, 'AFF Cup 2020 bị hoãn, báo chí Indonesia nói về viễn cảnh khó tin', 'Tờ Bola Sport cho rằng việc AFF Cup 2020 bị hoãn đến năm sau sẽ khiến cơ hội vô địch của Indonesia nhỏ lại.', '          <p>Như Báo Giao thông đưa tin, giải <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> lớn nhất khu vực Đông Nam Á - AFF Cup 2020 sẽ được dời sang tháng 4 năm 2021 thay vì tổ chức vào cuối năm nay.</p>\r\n<p>Đây là sự thay đổi cần thiết trong bối cảnh dịch bệnh vẫn đang diễn biến phức tạp ở nhiều quốc gia trong khu vực.</p>\r\n<p>Việc AFF Cup dời thời gian diễn ra khiến báo chí Indonesia tỏ ra không lấy gì vui vẻ. Tờ Bola Sport cho rằng, giải đấu này hoãn lại sẽ làm cho thầy trò HLV Shin Tae-yong khó lòng... lên ngôi.</p>\r\n<p>“Nếu Indonesia thi đấu tốt ở các trận còn lại của vòng loại World Cup 2022 thì việc AFF Cup 2020 hoãn làm cho các cầu thủ mất đi sự hưng phấn cần thiết. Do đó, mục tiêu lần đầu chinh phục giải đấu này càng khó khăn hơn.</p>\r\n<p>Tuyển Indonesia cũng sẽ phải chờ thêm vài tháng để trở lại sân cỏ. Song nó giúp \"Garuda\" có thêm thời gian nghỉ ngơi và tăng cường sức mạnh. Dẫu vậy, AFF Cup chuyển sang năm 2021 khiến mọi kế hoạch mà HLV Shin Tae-yong đã vạch ra phải thay đổi”.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\"><span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Thực tế, Indonesia chưa từng lên ngôi ở giải đấu số 1 khu vực dù từng 5 lần lọt vào chung kết. Ở kỳ AFF Cup gần nhất (2018), đoàn quân xứ vạn đảo thậm chí còn phải rời giải đấu ngay sau vòng đấu bảng.</p>\r\n<p>Cạnh đó, không nhiều người tin tuyển Indonesia đủ sức mạnh để lên ngôi tại đấu trường số 1 khu vực thời điểm hiện tại bởi thời gian qua đội bóng này chơi rất kém thuyết phục.</p>\r\n<p>Trước đó, Tổng thư ký LĐBĐ Indonesia (PSSI) Yunus Nusi cũng phản ứng rất gay gắt khi biết tin AFF Cup 2020 bị dời sang năm sau.</p>\r\n<p>“Đó là giai đoạn chúng tôi phải chuẩn bị cho vòng chung kết U20 World Cup trên sân nhà, giải đấu bắt đầu vào tháng 5.</p>\r\n<p>Hơn nữa đó thời điểm đó cận tháng Ramadan. Đó là hai lý do chúng tôi không đồng ý thời điểm tổ chức AFF Cup 2021”, lãnh đạo PSSI nêu quan điểm.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'hiuhiu'),
(20, 'Sau V-League, Cúp quốc gia phải hoãn vì dịch Covid-19', 'Chiều nay 31/7, công ty cổ phần bóng đá chuyên nghiệp Việt Nam VFF chính thức thông báo tạm dừng vòng bán kết Cúp Quốc gia 2020, do ảnh hưởng của dịch Covid-19.', '          <p>Theo lịch thi đấu mới được Công ty cổ phần <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> chuyên nghiệp Việt Nam (VPF) điều chỉnh hôm 27/7, các trận đấu ở vòng tứ kết Cúp Quốc gia 2020 sẽ diễn ra trong hai ngày, 4 và 5/8. Vòng bán kết dự kiến được tổ chức ngày 9/8 và trận chung kết diễn ra vào ngày 13/8.</p>\r\n<p>Vòng tứ kết của Cúp Quốc gia 2020 với 4 cặp đấu được đánh giá khá hấp dẫn gồm: Bà Rịa Vũng Tàu – TP.HCM, Viettel – Bình Dương, Hồng Lĩnh Hà Tĩnh – Than Quảng Ninh và Hà Nội – Cần Thơ. Dù vậy, do diễn biến phức tạp của dịch Covid-19 ở Việt Nam hiện tại, một số đội bóng đã lên tiếng đề nghị VPF dừng tổ chức Cúp Quốc gia 2020.</p>\r\n<p>Cụ thể, CLB Bình Dương và Cần Thơ cùng lúc gửi công văn đến VFF và VPF để xin hoãn các trận đấu ở vòng tứ kết của Cúp Quốc gia 2020.</p>\r\n<p>“Trong lúc này, chúng ta nên tập trung phòng, chống dịch Covid-19. Không chỉ Cup Quốc gia, chúng tôi còn kiến nghị dừng các giải chuyên nghiệp và các giải trẻ khác. Chúng ta nên chờ khi nào có thông báo chính thức từ Bộ Y tế, Chính phủ về việc kiểm soát và dập dịch trên toàn quốc, lúc đó các giải đấu mới nên trở lại”, ông Lê Hồng Cường – Tổng giám đốc CLB Bình Dương cho hay.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Được biết, CLB Bình Dương và Cần Thơ chủ động không đặt vé đi Hà Nội để chuẩn bị cho các trận đấu với Viettel và Hà Nội. Ngoài Bình Dương và Cần Thơ, đại diện CLB Bà Rịa Vũng Tàu cũng lên tiếng ủng hộ phương án tạm hoãn Cúp Quốc gia 2020.</p>\r\n<p>Theo thông tin chúng tôi có được, các thành viên Ban điều hành các giải bóng đá chuyên nghiệp Việt Nam đã họp và đưa ra quyết định tạm dừng Cúp Quốc gia 2020, để chờ đến khi dịch Covid-19 được kiểm soát. Thông báo chính thức về việc tạm hoãn Cúp Quốc gia 2020 đã được VPF gửi đến các đội bóng trong chiều nay (31/7).</p>\r\n<p>Một lãnh đạo VPF cho biết việc sau <a class=\"TextlinkBaiviet\" title=\"V-League\" href=\"https://www.24h.com.vn/v-league-cuoc-dua-nong-bong-c48e3408.html\">V-League</a> và giải hạng Nhất, việc Cúp Quốc gia cũng tạm dừng do ảnh hưởng của dịch Covid-19 là tình huống bất khả kháng. Trong thời gian tới, VPF sẽ tiếp tục cập nhật tình hình dịch bệnh ở các địa phương để có phương án điều chỉnh lịch thi đấu các giải bóng đá chuyên nghiệp Việt Nam.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 1, 'thunthun'),
(21, 'Sửng sốt với mức giá của Văn Hậu so với thần đồng bóng đá của Thailand', 'Hậu vệ Đoàn Văn Hậu có mức giá chuyển nhượng kém tới 12,5 lần so với thần đồng bóng đá ThaiLand Supanthat Muenta.', '          <p>Mới đây, báo chí Malaysia đã thống kê 8 cầu thủ 21 tuổi trở xuống ở khu vực Đông Nam Á có giá trị chuyển nhượng cao nhất. Khá bất ngờ khi Việt Nam không có bất cứ cầu thủ nào lọt vào danh sách này.</p>\r\n<p>Trong khi đó, người được định giá cao nhất chính là thần đồng bóng đá Thái Lan - Suphanat Mueanta. Tiền đạo thuộc biên chế CLB Buriram Utd có giá lên tới 2,2 triệu USD.</p>\r\n<p>Tính ra, mức giá ấy gấp 12,5 lần so với cầu thủ trẻ thuộc diện tài năng nhất <a class=\"TextlinkBaiviet\" title=\"bóng đá Việt Nam\" href=\"https://www.24h.com.vn/bong-da-viet-nam-c182.html\">bóng đá Việt Nam</a> - hậu vệ <a class=\"TextlinkBaiviet\" title=\"Đoàn Văn Hậu\" href=\"https://www.24h.com.vn/doan-van-hau-c48e5200.html\">Đoàn Văn Hậu</a>.</p>\r\n<p>Cầu thủ gốc Thái Bình hiện tại chỉ được định giá 175.500 USD dù anh đã có khoảng thời gian gần một năm sang Hà Lan thi đấu.</p>\r\n<p>Người xếp thứ 2 trong bản danh sách trên cũng là một cầu thủ trẻ xứ chùa Vàng - Ekanit Panya. Tiền vệ Chiangrai Utd có giá 1,6 triệu USD.</p>\r\n<p>Đứng thứ 3 là sự xuất hiện của 4 cầu thủ gồm Kritsada Kaman (Chonburi -Thái Lan), Korrawit Tasa (Muangthong United - Thái Lan), Asnawi Mangkualam Bahar (PSM Makassar - Indonesia) lẫn Saddil Ramdani (Bhayangkara - Indonesia) khi họ đều được định cùng mức giá 900.000 USD.</p>\r\n<p>Hai cái tên cuối cùng đến từ Indonesia là Firza Andika (AFC Tubize - Indonesia) và Syahrian Abimanyu (Madura United - Indonesia), bộ đôi này sở hữu chung mức giá 872.000 USD.</p>\r\n<p>Thực tế, giá trị chuyển nhượng các cầu thủ không phải lúc nào cũng phản ánh đúng về mặt chuyên môn. Tính ra, cầu thủ đắt giá nhất tuyển Việt Nam hiện tại là Tiến Linh lẫn Quế Ngọc Hải (357.318 USD) chỉ bằng gần 1 nửa giá trị của Abimanyu hay Andika.</p>\r\n<p>Ngoài ra, tổng giá trị 8 cầu thủ trên (9,4 triệu USD) còn cao gần gấp đôi tổng giá trị cả đội tuyển Việt Nam (23 cầu thủ - 4,9 triệu USD) cộng lại.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'hiuhiu'),
(22, 'Cực Sốc: Juventus muốn Zidane thay Sarri, nhưng chọn Pirlo vì lý do này', 'Tờ Daily Mail của Anh tiết lộ Juventus muốn Zidane hoặc Pochetino thay thế Sarri nhưng cuối cùng họ lại chọn Andrea Pirlo.', '          <p>Mới đây, <a class=\"TextlinkBaiviet\" title=\"Juventus\" href=\"https://www.24h.com.vn/juventus-fc-c48e4450.html\">Juventus</a> để thua Lyon tại vòng 1/8 Champions League 2019/20. Hệ quả là trung vệ De Ligt nghỉ 3 tháng tới vì phải phẫu thuật vai trong khi HLV Maurizio Sarri bị sa thải trước thời hạn tới 2 năm. Dù giành chức vô địch Serie A nhưng bị loại sớm tại Cúp <a class=\"TextlinkBaiviet\" title=\"C1\" href=\"https://www.24h.com.vn/cup-c1-champions-league-c153.html\">C1</a> là điều không thể chấp nhận được với chủ tịch Andrea Agnelli.</p>\r\n<p>Báo chí Italia tiết lộ chủ tịch của Juventus chưa bao giờ thích “Pep đệ nhị” và chỉ đồng ý để Sarri dẫn dắt đội bóng sau khi giám đốc kỹ thuật Fabio Paratici và phó chủ tịch, Pavel Nedved thuyết phục. Sa thải Sarri, báo chí thế giới hào hứng đưa nhiều ứng viên vào “chiếc ghế nóng” như Max Allegri, Mauricio Pochettino… Tuy nhiên, người cuối cùng được chọn lại là Andrea Pirlo khiến tất cả mọi người ngỡ ngàng.</p>\r\n<p>Cựu tiền vệ người Italia mới chỉ lấy bằng HLV chuyên nghiệp hồi cuối tháng Bảy và vừa được bổ nhiệm làm HLV đội U23 Juventus trước đó vài ngày. Bởi vậy, nói về kinh nghiệm cầm quân Pirlo thậm chí còn “như một tờ giấy trắng” chứ không phải như Zidane khi tiếp quản Real Madrid (đã có kinh nghiệm với đội trẻ Real).</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Vậy nguyên nhân gì khiến Juventus lại mạo hiểm đưa ra quyết định như vậy? Theo tờ Daily Mail của Anh, có hai nguyên nhân chính. Đầu tiên đó là câu chuyện tiền lương. Theo nguồn tin của tờ báo này, hai cái tên được nhắm tới để thay Sarri là Mauricio Pochettino và Zinedine Zidane.</p>\r\n<p>Juventus đã liên lạc với cả hai chiến lược gia này, nhưng đều vấp phải “cái đinh” mang tên tiền lương. Ông thầy người Argentina muốn nhận được khoảng 9 triệu euro/mùa trong khi con số Zidane đưa ra thậm chí con cao hơn (12 triệu euro).</p>\r\n<p>Nên nhớ rằng, Juventus còn phải đền bù cho Maurizio Sarri khoảng 24 triệu euro vì phá vỡ hợp đồng sớm 2 năm. Con số này thậm chí còn có thể tăng lên 30 triệu euro như ông thầy người Ý muốn “làm căng”. Điều này ảnh hưởng nghiêm trọng tới quỹ lương vốn đã rất “căng thẳng” của Juventus. Bởi vậy, lựa chọn Pirlo ít nhất cũng giúp đội bóng thành Turin giảm bớt một phần gánh nặng.</p>\r\n<p>Bên cạnh đó, huyền thoại người Italia rất được chủ tịch Andrea Agnelli tín nhiệm. Ông chủ của Juventus cho rằng Pirlo phù hợp với triết lý <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> của CLB. Giám đốc kỹ thuật Fabio Paratici và phó chủ tịch, Pavel Nedved có chung quan điểm.</p>\r\n<p>Ngoài ra, Pirlo có thể dễ dàng bình ổn phòng thay đồ với danh vọng của mình ít nhất là trong thời gian đầu. Nếu Juventus thi đấu tốt thì cựu tiền vệ người Italia hoàn toàn có thể biến thành \"Zidane đệ nhị\".</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(23, 'Đội hình hay nhất châu Âu: Dàn sao tỷ đô, Messi - Ronaldo cất cánh', 'Mùa giải 19/20 đã đi đến hồi kết, với những diễn biến sau cùng của 2 giải đấu châu Âu là Champion League và Europa League. Trong một mùa giải nhiều biến cố, các siêu sao vẫn cho thấy giá trị tuyệt vời của mình.', '          <p>Các giải đấu VĐQG hàng đầu châu Âu, từ Ngoại hạng Anh, La Liga, Serie A, Bundesliga - đều đã kết thúc. Nhìn lại hành trình 2019/20 - mùa giải nhiều biến cố với đại dịch Covid-19, những màn trình diễn chói sáng của các ngôi sao đều xứng đáng được ghi nhận.</p>\r\n<p>Vừa mới đây, trang thống kê Whoscored công bố đội hình hay nhất châu Âu mùa 2019/20. Đây là sự tổng hòa của các yếu tố mà Whoscored đã thống kê trong suốt hành trình từ ngày 9/8/2019 đến 2/8/2020. Và những cái tên được đứng trong hàng ngũ đội hình XI của Whoscored đều rất xứng đáng.</p>\r\n<p>Để có được đội hình nói trên, Whoscored chỉ lựa chọn các cầu thủ chơi bóng tại 5 giải VĐQG hàng đầu châu Âu có điểm số cao nhất ở từng vị trí. Kết quả là thủ môn Jesse Joronen của Brescia được chọn cho vị trí trong khung gỗ với số điểm không quá nổi trội là 7,01.</p>\r\n<p>Dù Liverpool có một mùa giải chói sáng để giành chức vô địch Ngoại hạng Anh lần đầu tiên sau 30 năm, song chỉ có duy nhất 1 cầu thủ của họ là trung vệ Virgil van Dijk có tên trong đội hình tiêu biểu của Whoscored. Trung vệ người Hà Lan được Whoscored chấm điểm 7,3 trong thang điểm 10 cho màn trình diễn ở mùa giải 2019/20. Đá cặp trung vệ với Van Dijk là chốt chặn James Tarkowski của Burnley.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Nếu như Liverpool chỉ đóng góp 1 cầu thủ vào đội hình tiêu biểu thì con số này với Bayern Munich là 2. Cụ thể, \"Hùm xám\" có 2 đại diện được vinh danh là hậu vệ trái Alphonso Davies và tiền đạo Robert Lewandowski. Thực tế thì Bayern chính là CLB duy nhất có hơn 1 đại diện góp mặt trong đội hình xuất sắc nhất châu Âu 2019/20.</p>\r\n<p>Khá đáng tiếc là dù vừa giành chiến thắng trước Lewandowski trong cuộc đua tới danh hiệu Chiếc giày vàng dành cho vua phá lưới châu Âu, tiền đạo Ciro Immobile của Lazio lại không góp mặt ở đội hình này. Thay vào đó, vị trí còn lại trên hàng công thuộc về Timo Werner, chân sút của của RB Leipzig mới được Chelsea mua về với giá 47,5 triệu bảng.</p>\r\n<p>Và dĩ nhiên rồi, không thể không nhắc đến hai siêu sao <a class=\"TextlinkBaiviet\" title=\"Lionel Messi\" href=\"https://www.24h.com.vn/lionel-messi-c48e4386.html\">Lionel Messi</a> và Cristiano Ronaldo. Ở mùa giải 2019/20, cả 2 siêu sao <a class=\"TextlinkBaiviet\" title=\"Messi\" href=\"https://www.24h.com.vn/lionel-messi-c48e4386.html\">Messi</a> lẫn Ronaldo đều chơi tốt như thường lệ. Cần phải nhấn mạnh rằng, đây đã là lần thứ 9 trong 11 mùa giải gần đây Messi có tên trong đội hình tiêu biểu của Whoscored.</p>\r\n<p>Messi là một trong số ít cầu thủ đều vượt cột mốc 20 ở mùa giải 2019/20. Thủ quân Barca có 25 bàn thắng và 21 đường kiến tạo ở La Liga. Anh được trang Whoscored chấm điểm 8,7 - là người được chấm cao nhất trong đội hình XI. Trong khi đó, con số bàn thắng của Ronaldo là 31 bàn, và nhận điểm 7,8.</p>\r\n<p>Cầu thủ trẻ Jadon Sancho - trong một mùa giải chói sáng, cũng có 17 bàn thắng và 16 đường kiến tạo cho Dortmund ở Bundesliga. Người còn lại góp mặt ở đội hình tiêu biểu của Whoscored, đó chính là Kevin de Bruyne cùng thành tích cân bằng kỷ lục 20 đường kiến tạo ở giải Ngoại hạng Anh với huyền thoại Henry.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'hiuhiu'),
(24, 'MU chọn thủ môn mới thế chỗ De Gea: Sao sáng 765 tỷ đồng đến từ Seria', 'Giữa lúc tương lai của David De Gea đang đặt dấu hỏi lớn, MU đã hướng tầm ngắm đến Seria, nơi \"Quỷ đỏ\" đã chọn được một mục tiêu khả dĩ để thay thế ngôi sao người Tây Ban Nha.', '          <p>David De Gea đã gắn bó 8 năm với <a class=\"TextlinkBaiviet\" title=\"MU\" href=\"https://www.24h.com.vn/manchester-united-c48e1521.html\">MU</a> và vẫn đang là thủ thành số 1 của \"Quỷ đỏ\". Nhưng kể từ tháng 7 năm ngoái, khi ký hợp đồng mới có thời hạn đến tận năm 2025 và được nâng lương lên mức 375.000 bảng/tuần, thủ thành ngôi sao người Tây Ban Nha đã sa sút đáng báo động.</p>\r\n<p>De Gea thường xuyên mắc sai lầm trong mùa giải 2019/20 và khiến cho HLV Ole Gunnar Solskjaer cùng BLĐ MU đã phải tính đến phương án thay thế anh trong kỳ chuyển nhượng mùa Hè này. </p>\r\n<p>Dean Henderson đã tỏa sáng trong thời gian được đội chủ sân <a class=\"TextlinkBaiviet\" title=\"Old Trafford\" href=\"https://www.24h.com.vn/manchester-united-c48e1521.html\">Old Trafford</a> cho Sheffield United mượn mùa này và thủ thành trẻ người Anh được cho là sẽ thay thế De Gea trở thành sự lựa chọn số một của Solsa trong khung thành của \"Quỷ đỏ\" trong tương lai không xa.</p>\r\n<p>Thế nhưng, chính Dean Henderson đã ra yêu sách rằng anh sẽ chỉ trở lại MU nếu được đảm bảo suất bắt chính tại \"Nhà hát của những giấc mơ\". Ngôi sao 23 tuổi này cũng đang được Chelsea để mắt khi \"The Blues\" cũng xem anh là sự lựa chọn lý tưởng thế chỗ Kepa Arrizabalaga, người có thể rời Stamford Bridge hè này do không còn nằm trong kế hoạch của HLV Frank Lampard.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>MU từng nhắm đến Jan Oblak nhưng Atletico Madrid đã \"hét giá\" đến 100 triệu euro khiến \"Quỷ đỏ\" quyết định từ bỏ nỗ lực chiêu mộ thủ thành người Slovenia. Theo nguồn tin uy tín từ ESPN, đội bóng của HLV Solskjaer đang nhắm đến Alex Meret (Napoli) như một sự lựa chọn khả dĩ nhất thay thế De Gea.</p>\r\n<p>Meret năm nay mới 23 tuổi. Kể từ khi gia nhập Napoli từ Udinese năm 2018, thủ thành trẻ này và cựu sao Arsenal - David Ospina vẫn thường xuyên chia sẻ vai trò bắt chính ở đội chủ sân San Paolo. Tháng 11 năm ngoái, Meret đã lần đầu tiên được khoác áo ĐTQG Italia.</p>\r\n<p>Đến tháng 6 năm nay, Meret lại cản phá thành công cú sút 11m của Paulo Dybala giúp đội bóng thành Naples thắng Juventus 4-2 trong loạt thi sút luân lưu để đăng quang cúp Quốc gia Ý.</p>\r\n<p>Theo trang chuyên định giá cầu thủ Transfermarkt, mức giá chuyển nhượng của Meret hiện chỉ là 28 triệu euro (hơn 765 tỷ đồng). Đây là con số mà MU hoàn toàn có thể đáp ứng ngay trong mùa Hè năm nay.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'thunthun'),
(25, 'Messi solo như trò chơi điện tử, biến 5 Sao Napoli trở thành \"kẻ học việc\"', 'Chiến thắng của Barcelona trước Napoli ở vòng 1/8 Champion League in đậm dấu giày của siêu sao Lionel Messi.', '          <p>Siêu sao Lionel Messi lại khiến người hâm mộ phải nhắc tới mình khi góp công lớn vào chiến thắng 3-1 của <a class=\"TextlinkBaiviet\" title=\"Barcelona\" href=\"https://www.24h.com.vn/barcelona-c48e1507.html\">Barcelona</a> trước Napoli trong trận lượt về vòng 1/8 <a class=\"TextlinkBaiviet\" title=\"Champions League\" href=\"https://www.24h.com.vn/cup-c1-champions-league-c153.html\">Champions League</a> mùa này. Ấn tượng nhất có lẽ là khoảnh khắc thiên tài mà Messi đã thể hiện ở phút thứ 23 của trận đấu này.</p>\r\n<p>Sau bàn mở tỷ số từ khá sớm do công của trung vệ Lenglet, Barca đã tiếp tục tạo ra thế trận lấn lướt trước Napoli. Sự hưng phấn giúp siêu sao số 1 của đội bóng này là Lionel Messi quyết tâm tạo nên một siêu phẩm solo tưởng chừng chỉ có ở trong trò chơi điện tử.</p>\r\n<p>Nhận bóng từ Luis Suarez, Messi phải đối mặt với 3 cầu thủ đối phương phía trước anh. Thế nhưng, thay vì phối hợp tiếp với các đồng đội, chân sút người Argentina đã quyết định thử sức bằng một pha đi bóng xộc thẳng vào vòng cấm.</p>\r\n<p>Hàng thủ của Napoli thừa hiểu sự nguy hiểm của Lionel Messi. Tuy nhiên, họ có lẽ không thể ngờ rằng Messi lại khéo léo và nỗ lực đến vậy trong pha bóng này. Cú ra chân của tiền đạo Barca sau đó được thực hiện một cách hoàn hảo đưa bóng về phía cột xa và không cho thủ môn Ospina một cơ hội nào để cản phá.</p>\r\n<p>Pha solo dắt bóng qua tới 5 ngôi sao của Napoli đã thêm một lần nữa chứng minh tài năng thiên bẩm mà Messi sở hữu. Bàn thắng nhân đôi cách biệt của Messi chính là bước ngoặt lớn nhất của trận đấu bởi từ đây, Napoli đã không thể kiểm soát được tình hình và tiếp tục nhận thêm bàn thua thứ 3 từ chấm 11m của Luis Suarez trước giờ nghỉ giữa hiệp.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'thonthon'),
(26, 'Pirlo mua \"tiểu Totti\" & \"tiểu Pirlo\" giúp Ronaldo - Juventus mơ giấc vô địch C1 ', 'Pirlo ngay khi lên dẫn dắt Juventus đã có kế hoạch cụ thể bổ sung cho Juventus.', '          <p>Juventus sau khi sa thải HLV Maurizio Sarri đã bất ngờ quyết định bổ nhiệm Andrea Pirlo vào vị trí HLV trưởng của CLB, mặc dù Pirlo mới có 9 ngày kinh nghiệm làm HLV ở đội U23. Tuy nhiên Pirlo đã có chứng chỉ huấn luyện từ năm 2018 và đã quen với cấu trúc thượng tầng tại <a class=\"TextlinkBaiviet\" title=\"Juventus\" href=\"https://www.24h.com.vn/juventus-fc-c48e4450.html\">Juventus</a>.</p>\r\n<p>Sự thay đổi vị trí cầm quân cũng đồng nghĩa thay đổi luôn cả về chính sách chuyển nhượng, Pirlo đã xác định sẵn những mục tiêu mình muốn có cho Juventus và giờ guồng máy tuyển quân của Juventus chỉ việc khởi động, còn những mục tiêu mà Juventus quan tâm theo yêu cầu của HLV Sarri như Jorginho sẽ bị gạt đi.</p>\r\n<p>Theo tờ Tuttosport tại Turin, 2 cầu thủ đầu tiên Juventus sẽ nhắm đến đều là những tài năng trẻ bản địa. Người thứ nhất, Sandro Tonali, đã nổi lên là một trong những tiền vệ sáng giá của <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> Ý gần đây với một phong cách chơi khiến anh được so sánh với chính Pirlo. Tonali thậm chí đang đầu quân cho Brescia, cũng là CLB mà Pirlo đã khởi nghiệp.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Trong những tuần qua Inter Milan đã cử người lảng vảng ở Brescia để thuyết phục CLB này bán Tonali, nhưng theo Tuttosport ngay sau khi Pirlo lên nắm quyền, Juventus đã khởi động việc liên lạc với người đại diện Beppe Bozzo của Tonali lẫn ông chủ Massimo Cellino của Brescia để hỏi mua cầu thủ 20 tuổi.</p>\r\n<p><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/sandrotonali-cropped_p6y5d11vf631u7qsy4qpznpl-660-1596990031-742-width660height371.jpg\" alt=\"Pirlo mua &#34;tiểu Totti&#34; & tiểu Pirlo&#34; giúp Ronaldo - Juventus mơ Cúp C1 - 2\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/sandrotonali-cropped_p6y5d11vf631u7qsy4qpznpl-660-1596990031-742-width660height371.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\">Sandro Tonali được so sánh như chính Pirlo về mặt lối chơi</p>\r\n<p>Nếu vụ Tonali thất bại, Juventus sẽ chuyển sang Manuel Locatelli của Sassuolo, một tiền vệ có lối chơi tương tự và cũng mới chỉ 22 tuổi. Dù được Tonali hay Locatelli thì Pirlo cũng sẽ \"quy hoạch\" hàng tiền vệ theo hướng người mới đá bên cạnh Arthur sắp chuyển sang từ Barcelona, bên cạnh một tiền vệ nữa có xu hướng tấn công.</p>\r\n<p>Tiền vệ đó là người thứ hai được Tuttosport đề cập trong bản tin nói trên: Nicolo Zaniolo của AS Roma. Cầu thủ đá ở vị trí \"số 10\" này được so sánh với tiền bối Francesco Totti vì đá ở Roma, nhưng khả năng của anh còn đa dạng hơn thế bởi có tốc độ tốt và đặc biệt là cao 1m90, khiến anh cũng là một mối đe dọa trên không hay ở cả ngoài biên, bên cạnh đó đá được ở vị trí mà thường Paulo Dybala chiếm giữ.</p>\r\n<p>Tuy nhiên Zaniolo rất khó mua bởi AS Roma vừa có chủ mới giàu có và họ quyết giữ anh bằng mọi giá. Thậm chí Tuttosport cho biết Mauricio Pochettino, người tưởng như sẽ dẫn dắt Juventus, đang được Roma mời và HLV người Argentina đòi CLB không được bán Zaniolo. Nếu vụ này thất bại, Juventus sẽ chuyển sang Houssem Aouar, tiền vệ công của chính CLB Lyon vừa loại Juventus.</p>\r\n<p><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/nicola-zaniolo_3omikycgjg7z16jvui9efu064-660-1596990053-912-width660height425.jpg\" alt=\"Pirlo mua &#34;tiểu Totti&#34; & tiểu Pirlo&#34; giúp Ronaldo - Juventus mơ Cúp C1 - 3\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/nicola-zaniolo_3omikycgjg7z16jvui9efu064-660-1596990053-912-width660height425.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\">Zaniolo không dễ rời AS Roma</p>\r\n<p>Hàng tiền vệ được xem là điểm kém của Juventus mùa qua nên Pirlo quyết bổ sung những cầu thủ mới để cải thiện, bên cạnh đó \"Bà đầm già\" cũng sẽ đẩy đi Sami Khedira, Blaise Matuidi và có thể cả Aaron Ramsey để dọn chỗ. Bentancur, Rabiot sẽ là 2 tiền vệ ít ỏi chắc suất ở lại nhưng cũng sẽ phải cạnh tranh với những người mới.</p>\r\n<p>Juventus với sự bổ sung Arthur (tiền vệ giữa), Dejan Kulusevski (chạy cánh phải) cùng với ít nhất 2 tiền vệ và một tiền đạo cắm sẽ có một bộ mặt mới cho mùa 2020/21 dưới trướng Pirlo. Bổ nhiệm cựu danh thủ 41 tuổi là một canh bạc mạo hiểm nhưng Juventus nếu cung cấp được hỏa lực dồi dào cho Cristiano <a class=\"TextlinkBaiviet\" title=\"Ronaldo\" href=\"https://www.24h.com.vn/cristiano-ronaldo-c48e4384.html\">Ronaldo</a> và Dybala, không chừng Pirlo sẽ có năm đầu làm HLV vượt mọi kỳ vọng.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'hiuhiu'),
(27, 'Cực sốc: Ronaldo dễ theo chân Sarri, \"siêu cò\" gặp gỡ PSG gây náo loạn', 'Tương lai của Christiano Ronaldo một lần nữa bị đặt trong tầm ngắm khi mới đây truyền thông Pháp đã tung một thông tin bất ngờ.', '          <p>Nguồn tin từ Le10 Sport cho hay, Jorge Mendes – người đại diện lâu năm của <a class=\"TextlinkBaiviet\" title=\"Ronaldo\" href=\"https://www.24h.com.vn/cristiano-ronaldo-c48e4384.html\">Ronaldo</a>, sắp có cuộc gặp mặt quan trọng với giám đốc thể thao của PSG, Leonardo để bàn bạc về khả năng tạo ra một thương vụ “siêu bom tấn” ngay trong mùa hè này.</p>\r\n<p>Dù bị ảnh hưởng bởi đại dịch Covid-19, song nhà vô địch nước Pháp được cho là sẵn sàng chi tiền để mang về ngôi sao có thể “thay đổi lịch sử của đội bóng”, bài viết trên Le10 Sport cho biết thêm. Được biết, Mendes cũng là người đại diện của James Rodriguez, người cũng đang đứng trước tương lai bất định tại Real Madrid.</p>\r\n<p>Tuy nhiên, bài viết trên Le10 Sport khẳng định cuộc gặp mặt giữa Mendes và Leonardo là về Ronaldo. Phía PSG dự định sẽ có một buổi gặp riêng Ronaldo tại Lisbon (Bồ Đào Nha), nơi tổ chức phần còn lại của Champions League mùa này, giải đấu mà PSG đã lọt vào tứ kết và sắp tới sẽ có cuộc đụng độ với Atalanta.</p>\r\n<p>Ronaldo gia nhập <a class=\"TextlinkBaiviet\" title=\"Juventus\" href=\"https://www.24h.com.vn/juventus-fc-c48e4450.html\">Juventus</a> hai năm trước và vẫn còn 2 năm trong hợp đồng hiện tại với “Bà đầm già”. Dù chơi không đến nỗi nào nhưng cách mà CR7 kết thúc mùa giải năm nay không thể đau đớn hơn, khi Juventus của anh bị Lyon đá bay khỏi Champions League dù giành chiến thắng 2-1 ở trận lượt về vòng 1/8 trên sân nhà, trong đó Ronaldo lập cú đúp.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/Cuc-soc-Ronaldo-de-theo-chan-Sarri-sieu-co-gap-go-PSG-gay-nao-loan-rryyu-1596977253-779-width660height533.jpg\" alt=\"Cực sốc: Ronaldo dễ theo chân Sarri, “siêu cò” gặp gỡ PSG gây náo loạn - 2\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/Cuc-soc-Ronaldo-de-theo-chan-Sarri-sieu-co-gap-go-PSG-gay-nao-loan-rryyu-1596977253-779-width660height533.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\">Ronaldo đang trong kỳ nghỉ ngắn</p>\r\n<p>Sau thất bại nói trên, HLV trưởng Maurizio Sarri đã bị sa thải, và đây lại càng là thời cơ để Ronaldo quyết định tương lai. Nếu cập bến PSG, Ronaldo chắc chắn sẽ được hậu đãi đặc biệt, bên cạnh đó việc sát cánh cùng những người đàn em xuất sắc như Neymar và Mbappe cũng sẽ khiến CR7 hứng thú.</p>\r\n<p>Ronaldo từng ám chỉ anh “không còn thấy vui” ở Juventus và có ý định chuyển đến PSG ở thời điểm trước khi <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> phải tạm dừng vì sự lây lan của đại dịch Covid-19. Paris cũng là thành phố mà Ronaldo có kỷ niệm đẹp khi đây là nơi anh từng nâng cao cúp vô địch EURO 2016 cùng ĐT Bồ Đào Nha.</p>\r\n<p>Hiện Ronaldo đang trong kỳ nghỉ cùng bạn gái Georgina Rodriguez. Trong bức ảnh đăng trên Instagram, CR7 viết: “Cuối cùng thì mùa giải 2019/20 đã kết thúc, lâu hơn dự kiến nhưng lại sớm hơn chúng tôi dự định. Bây giờ là lúc nghỉ ngơi để suy nghĩ thấu đáo hơn. Chuyến đi nghỉ ngắn này sẽ giúp chúng tôi đưa ra những quyết định sáng suốt hơn và trở lại mạnh mẽ hơn”.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binngu'),
(28, 'Tin Hot bóng đá tối 10/8: Ronaldo được Juventus bầu xuất sắc nhất mùa giải', 'Bản tin Hot bóng đá tối 10/8: Juventus trao giải cầu thủ xuất sắc nhất mùa của CLB cho Christiano Ronaldo. Ngoài ra còn nhiều thông tin đáng chú ý khác.', '          <p><strong>Ronaldo được Juventus bầu Cầu thủ xuất sắc nhất mùa.</strong> Trang chủ Juventus công bố Cristiano Ronaldo đã được trao giải thưởng Cầu thủ xuất sắc nhất mùa giải 2019/20 của CLB. Ronaldo kết thúc mùa với 31 bàn & 5 kiến tạo tại Serie A giúp Juventus bảo vệ scudetto, cũng như ghi 4 bàn tại <a class=\"TextlinkBaiviet\" title=\"Champions League\" href=\"https://www.24h.com.vn/cup-c1-champions-league-c153.html\">Champions League</a> và 2 bàn tại Coppa Italia.</p>\r\n<p><strong>Lộ diện 2 cầu thủ Atletico nhiễm Covid</strong>. Theo xác nhận từ CLB Atletico Madrid, hai cầu thủ đã nhiễm Covid-19 là tiền đạo Angel Correa và hậu vệ phải Sime Vrsaljko. Nếu như Correa được xem là một lựa chọn xoay vòng của HLV Diego Simeone, Vrsaljko mùa này ra sân ít ỏi chỉ 7 trận và chỉ là dự bị. Hiện ngoài 2 cầu thủ trên, các cầu thủ khác và ban huấn luyện của Atletico đã có kết quả âm tính.</p>\r\n<p><strong>De Rossi, Totti sắp nắm quyền ở AS Roma?</strong> Theo báo giới tại Rome, việc đổi chủ mới của AS Roma có thể kéo theo một sự thay đổi ở vị trí quyền lực của đội bóng. Theo đó cựu tiền vệ Daniele De Rossi đã được nhắc đến là ứng cử viên cho vị trí HLV trưởng, trong khi huyền thoại Francesco Totti sẽ nắm giữ vị trí giám đốc thể thao.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p><strong>Tây Ban Nha - Bồ Đào Nha hội ngộ giao hữu tháng 10</strong>. LĐBĐ của Tây Ban Nha và Bồ Đào Nha đã cùng công bố hai đội sẽ đấu giao hữu với nhau tại Lisbon, BĐN vào ngày 7/10 tới. Đây sẽ là trận đầu tiên cho đội tuyển quốc gia hai nước trong năm 2020, và cũng là lần đầu tiên họ gặp nhau kể từ sau trận hòa 3-3 ở vòng bảng World Cup 2018.</p>\r\n<p><strong>Sao trẻ MU tự nhận có \"ADN Quỷ đỏ\": </strong>Trả lời phỏng vấn báo chí trước thềm trận đấu giữa MU và Copenhagen thuộc tứ kết Europa League, hậu vệ trẻ Brandon Williams khẳng định lối chơi máu lửa, dũng mãnh của anh một phần xuất phát từ \"ADN MU\" chảy trong cơ thể:</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-10/Tin-HOT-bong-da-trua-10-8-SAO-MU-tu-nhan-co-gettyimages-1199747216--1--1597035413-984-width660height503.jpg\" alt=\"Tin HOT bóng đá tối 10/8: Ronaldo được Juventus bầu xuất sắc nhất mùa - 2\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-10/Tin-HOT-bong-da-trua-10-8-SAO-MU-tu-nhan-co-gettyimages-1199747216--1--1597035413-984-width660height503.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\"> Brandon Williams là phát hiện của MU ở vị trí hậu vệ trái</p>\r\n<p>\"Sự máu lửa đã có trong tôi từ khi còn là đứa trẻ 8 tuổi. Đó là một phần ADN trong bản tôi, là những gì mà các HLV ở MU đã truyền cho tôi, tất nhiên bản phải biết cách để kiểm soát chúng\", trích lời cầu thủ 19 tuổi.</p>\r\n<p><strong>Huyền thoại Italia sốc vì Pirlo dẫn dắt Juventus: </strong>Trả lời phỏng vấn Sky Sport Italia, huyền thoại <a class=\"TextlinkBaiviet\" title=\"bóng đá\" href=\"https://www.24h.com.vn/bong-da-c48.html\">bóng đá</a> Italia - Gianfranco Zola thừa nhận đã rấ ngạc nhiên khi biết Juventus lựa chọn Adrea Pirlo làm HLV trưởng thay Maurizio Sarri. Cựu tiền vệ 41 tuổi chỉ vừa lấy bằng HLV chuyên nghiệp được vài ngày, thậm chí chỉ vừa nhận nhiệm vụ dẫn dắt đội U23 Juventus.</p>\r\n<p><strong>SAO Chelsea muốn vươn tầm như Lewandowski. </strong>Tammy Abraham khẳng định Lewandowski là tiêu chuẩn mà các tiền đạo khác phải nhắm đến và cho biết mục tiêu của anh là đạt đến đẳng cấp của ngôi sao người Ba Lan. \"Tôi muốn được như Lewandowski. Đến Munich và đối đầu với anh ấy giúp tôi học được nhiều điều. Đây là một trải nghiệm tuyệt vời với tôi. Từ mùa giải tiếp theo và sau đó, tôi cần bồi dưỡng bản thân và vươn tới đẳng cấp như vậy\".</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/Tin-HOT-bong-da-sang-10-8-c88--2--1596990309-526-width660height384.jpg\" alt=\"Tin HOT bóng đá tối 10/8: Ronaldo được Juventus bầu xuất sắc nhất mùa - 3\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/Tin-HOT-bong-da-sang-10-8-c88--2--1596990309-526-width660height384.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\">Abraham ngưỡng mộ Lewandowski</p>\r\n<p><strong>Solskjaer báo tin vui về Pogba. </strong>Chia sẻ trước giờ cùng MU lên đường sang Đức dự Europa League, HLV Ole Solskjaer đã ngầm báo tin vui về tương lai của Paul Pogba. Ông khẳng định: “Mọi người sẽ thấy Paul Pogba hay nhất trong 2 mùa giải tới”. Theo Goal, điều này có nghĩa Paul Pogba đã sẵn sàng gia hạn hợp đồng với MU.</p>\r\n<p><strong>Roma nhắm giám đốc của Leeds. </strong>Theo Calciomercato, AS Roma đang nỗ lực tìm kiếm một giám đốc bóng đá mới thay thế Gianluca Petrachi, và mục tiêu mới nhất của họ là giám đốc của Leeds United, Victor Orta. Bên cạnh HLV Bielsa, Victor Orta là người có công lớn nhất giúp Leeds trở lại <a class=\"TextlinkBaiviet\" title=\"Premier League\" href=\"https://www.24h.com.vn/premier-league-2018-2019-c48e2367.html\">Premier League</a> sau 16 năm vắng bóng.</p>\r\n<p><strong>De Ligt nghỉ thi đấu 3 tháng. </strong>Theo Tuttosport, De Ligt sẽ phải nghỉ thi đấu khoảng 3 tháng sau ca phẫu thuật vai. Trung vệ này đã nén đau thi đấu từ cuối năm ngoái đến nay vì Juventus khủng hoảng lực lượng ở hàng phòng ngự.</p>\r\n<p><strong>HLV PSG dính chấn thương nặng. </strong>Trang chủ của đội bóng thành Paris vừa cho biết ông thầy người Đức dính chấn thương khá nặng trong một buổi tập gần đây. \"Thomas Tuchel vừa bị bong gân cổ chân trái và gãy xương bàn chân trong một buổi tập hôm thứ Năm của đội bóng\".</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'hiuhiu'),
(29, 'Tin nóng chuyển nhượng 10/8: MU từ bỏ Grealish, chiêu mộ Sao trẻ Hà Lan', 'Man United từng muốn có được chữ ký của tiền vệ Jack Grealish. Tuy nhiên, \"Quỷ đỏ\" đang cân nhắc lại thương vụ này khi Aston Villa đưa ra mức giá cao.', '          <p>Sau khi trụ hạng thành công, Aston Villa đã ra giá 80 triệu bảng cho tiền vệ đội trưởng Jack Grealish và đó là mức phí chuyển nhượng MU khó có thể đáp ứng, khi đội bóng này chỉ đơn thuần đang tìm mua một phương án B, đủ khả năng dự phòng cho bộ đôi Paul Pogba và Bruno Fernandes.</p>\r\n<p>Tờ Express khẳng định, MU đã nhanh chóng chuyển hướng sang trường hợp của Donny van de Beek. Tiền vệ trẻ của sẽ có mức giá rẻ hơn khá nhiều. Đồng thời, anh cũng là mẫu tiền vệ đa năng, một điều mà HLV Solskjaer rất ưa thích.</p>\r\n<p><strong>Arsenal hỏi mua tuyển thủ Colombia</strong></p>\r\n<p>Arsenal đã liên hệ với đội bóng Nga Zenit St Petersburg về trường hợp của tiền vệ Wilmar Barrios. Barrios ​​sẽ rời Zenit vào mùa hè này và tuyển thủ người Colombia đang là mục tiêu hàng đầu của Newcastle. Truyền thông Nga cho biết Arsenal sẵn sàng trả mức giá cao hơn các đối thủ cạnh tranh khác để giành được chữ ký của Barrios. Cụ thể, một lời đề nghị trị giá 30 triệu euro được cho là sẽ giúp \"Pháo thủ\" thuyết phục thành công Zenit trong thương vụ này.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p><strong>Kasper Schmeichel sẵn sàng rời Leicester City</strong></p>\r\n<p>Mục tiêu của MU ở vị trí thủ môn, Kasper Schmeichel được cho là đã sẵn sàng rời Leicester City ngay trong mùa hè này. Con trai của cựu huyền thoại \"Quỷ Đỏ\" Peter Schmeichel là cái tên được MU theo đuổi nếu Dean Henderson không đồng ý ở lại Old Trafford. Tờ The Times cho biết, thủ thành 33 tuổi đang rất muốn chuyển đến một CLB giàu tham vọng hơn ở đấu trường châu Âu.</p>\r\n<p><strong>Man City rao bán công thần sau khi chiêu mộ thành công Ake</strong></p>\r\n<p>Man City đang lên kế hoạch thanh lý trung vệ Nicolas Otamendi sau khi chiêu mộ thành công sao trẻ Nathan Ake từ Bournemouth. Tờ The Sun khẳng định đội chủ sân Etihad muốn bán Otamendi vì họ cần giảm một phần quỹ lương đội bóng. Tuyển thủ Argentina đã chơi 210 trận cho Man City, nhưng đang có dấu hiệu xuống sức vì tuổi tác ở mùa giải vừa qua.</p>\r\n<p><strong>Arsenal sẵn sàng đẩy Ozil ra đường ngay hè này</strong>: Theo tờ Mirror của Anh, <a class=\"TextlinkBaiviet\" title=\"Arsenal\" href=\"https://www.24h.com.vn/arsenal-fc-c48e3424.html\">Arsenal</a> sẵn sàng trả luôn phần tiền lương còn lại của Mesut Ozil trị giá 18 triệu bảng để giải thoát cho đôi bên ngay hè này. Tiền vệ người Đức hiện tại hưởng lương 350.00 bảng/tuần nhưng không có đóng góp gì cho đội.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-10/Tin-nong-chuyen-nhuong-10-8-Liverpool-sap-co-hau-ve-trai-nguoi-Hy-Lap-9-660-1597034030-846-width660height396.jpg\" alt=\"Tin nóng chuyển nhượng 10/8: ​​​​​​​MU từ bỏ Grealish, chiêu mộ SAO trẻ Hà Lan - 2\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-10/Tin-nong-chuyen-nhuong-10-8-Liverpool-sap-co-hau-ve-trai-nguoi-Hy-Lap-9-660-1597034030-846-width660height396.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\">Mesut Ozil</p>\r\n<p><strong>PSG muốn có Guendouzi.</strong> Theo Daily Star, PSG là CLB mới nhất muốn có Matteo Guendouzi, cầu thủ đang thuộc biên chế Arsenal. Tiền vệ người Pháp đang có bất đồng lớn với HLV Arteta và sẵn sàng ra đi ngay trong mùa hè này. Arsenal cũng chẳng mặn mà lắm trong việc giữ tiền vệ người Pháp. Trước đó, Juventus và Barcelona cũng quan tâm tới cầu thủ này.</p>\r\n<p><strong>Chelsea chính thức chia tay Willian</strong>. Tiền vệ người Brazil chính thức rời Chelsea sau 7 năm gắn bó. Theo tờ Daily Mail thì nguyên nhân là bởi đôi bên không tìm được sự nhất trí trong bản hợp đồng mới. Theo nhiều nguồn tin thì Willian sẽ gia nhập Arsenal trong vài ngày tới với mức lương 150.000 bảng/tuần. </p>\r\n<p><strong>Liverpool sắp có hậu vệ trái người Hy Lạp</strong>. Sau khi vụ mua Jamal Lewis của Norwich không đi tới đâu, mới đây các nguồn tin thân với Liverpool cho biết nhà vô địch <a class=\"TextlinkBaiviet\" title=\"Premier League\" href=\"https://www.24h.com.vn/premier-league-2018-2019-c48e2367.html\">Premier League</a> đã đồng ý mức phí 13 triệu euro (11,75 triệu bảng) cho Kostas Tsimikas của Olympiakos và hậu vệ này sẽ làm kiểm tra sức khỏe trong những ngày tới. Tsimikas sẽ là phương án B cho Andy Robertson, người đang đá chính ở vị trí hậu vệ trái.</p>\r\n<p><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/liverpool-plan-konstantinos-tsimikas-medical-for-next-week-with-1175m-660-1596991709-140-width660height392.jpg\" alt=\"Tin nóng chuyển nhượng 10/8: ​​​​​​​MU từ bỏ Grealish, chiêu mộ SAO trẻ Hà Lan - 3\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/liverpool-plan-konstantinos-tsimikas-medical-for-next-week-with-1175m-660-1596991709-140-width660height392.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\">Tsimikas sắp đầu quân cho HLV Jurgen Klopp sau khi gây ấn tượng tại <a class=\"TextlinkBaiviet\" title=\"Champions League\" href=\"https://www.24h.com.vn/cup-c1-champions-league-c153.html\">Champions League</a> mùa này</p>\r\n<p><strong>Lingard & 3 SAO được AC Milan quan tâm</strong>. Theo tin từ báo chí Italia, sau khi đã chốt các vị trí quản lý CLB AC Milan đã bắt đầu tìm kiếm một cầu thủ chạy cánh do HLV Stefano Pioli hiện chỉ có Samu Castillejo và Alexis Saelemaekers, người thứ hai ông định cho đá hậu vệ cánh. 4 cầu thủ được Milan đang để ý là Jesse Lingard (MU), Federico Chiesa ((Fiorentina), Gerard Deulofeu (Watford) và Milot Rashica (Werder Bremen).</p>\r\n<p><strong>Lucas Torreira có thể về Atletico?</strong> Theo tờ Mundo Deportivo, Atletico Madrid có thể sẽ yêu cầu Arsenal phải nhả cho họ tiền vệ Lucas Torreira nếu Arsenal muốn mua Thomas Partey, bởi HLV Diego Simeone được cho là khá thích khả năng đá bao sân của Torreira.</p>\r\n<p><strong>Angel Gomes gia nhập Lille.</strong> CLB Lille đã chính thức thông báo họ đã ký thành công hợp đồng 5 năm tới 2025 với tuyển thủ U20 Anh Angel Gomes, người vừa hết hợp đồng tại MU. Tiền vệ tấn công cao 1m61 này lập tức được cho Boavista ở giải VĐQG Bồ Đào Nha mượn.</p>\r\n<p><strong>\"Máy quét\" Brazil đắt hàng. </strong>Theo tin từ báo chí Brazil, những ngày qua người đại diện từ các CLB Inter Milan, Marseille và Atletico Madrid đều đã đến tiếp xúc với CLB Palmeiras để hỏi mua tiền vệ đánh chặn Patrick de Paula. Cầu thủ này đá mới chỉ 8 trận ở giải VĐQG Brazil cho Palmeiras nhưng đã khiến các tuyển trạch viên Nam Mỹ của một số đại gia châu Âu bị ấn tượng.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(30, 'Cựu Sao MU hãi hùng vì chọc giận Ibrahimmovic, cảm giác như sắp bị lấy mạng', 'Zalatan Ibrahimovic thời còn khoác áo MU từng khiến cho một đồng đội cũ khiếp vía chỉ vì một xích mích nhỏ.', '          <p>Sau khi giành vị trí thứ 3 giải Ngoại hạng Anh để giành quyền dự Champions League mùa sau, MU vẫn đang chinh chiến ở Europa League mùa này, nơi \"Quỷ đỏ\" sẽ gặp Copenhagen ở trận tứ kết trên sân trung lập RheinEnergieStadion (Cologne, Đức) lúc 2 giờ rạng sáng 11/8 (giờ Việt Nam).</p>\r\n<p>\"Quỷ đỏ\" cũng là đội bóng đặc biệt khi đã từng là bến đỗ của không ít ngôi sao lớn của bóng đá thế giới với cá tính vô cùng mạnh mẽ và đầy chất \"ngông\". Một trong số đó chính là tiền đạo lừng danh Zlatan Ibrahimovic.</p>\r\n<p>Thậm chí, thời còn khoác áo MU, ngôi sao người Thụy Điển đã suýt nữa hành hung, thậm chí ra tay sát hại trung vệ Marcos Rojo - một đồng đội của mình thời họ còn chơi cho đội bóng do HLV Jose Mourinho dẫn dắt. Rojo đã không chuyền bóng cho Ibra trong một trận đấu cúp trên sân nhà Old Trafford và tranh cãi đã xảy ra giữa 2 ngôi sao rất nóng tính này.</p>\r\n<div id=\"ADS_159_15s\" class=\"txtCent\">\r\n<div id=\"ADS_159_15stxtBnrHor\"></div>\r\n<div class=\"banner_inread\"> </div>\r\n<span id=\"ADS_159_15s_0\" class=\"m_banner_show\"></span></div>\r\n<p>Thậm chí, HLV Mourinho đã phải can ngăn Ibra và Rojo lao vào nhau ẩu đả trong phòng thay đồ của đội chủ sân <a class=\"TextlinkBaiviet\" title=\"Old Trafford\" href=\"https://www.24h.com.vn/manchester-united-c48e1521.html\">Old Trafford</a> ngay sau sự cố đó. Điều này đã được chính Rojo tiết lộ trên tờ Inforbae:</p>\r\n<p>\"Zlatan (Ibrahimovic) muốn đường chuyền của tôi hướng đến vị trí của anh ấy. Nhưng chỉ có một mình Pogba ở bên cánh trái nên tôi đã chuyền cho Pogba. Và Zlatan bắt đầu chửi rủa tôi. Tôi ngay lập tức đáp trả: \"Im miệng đi, đồ mũi to. Anh luôn muốn nhận tất cả bóng về mình mà thôi.\" Tranh cãi giữa chúng tôi đã nổ ra.</p>\r\n<p>Chúng tôi kết thúc hiệp 1 trận đấu đó và bước vào phòng thay đồ. Khi tôi đang ở tủ quần áo của mình và bắt đầu cởi giày thì anh ấy (Ibra) bước vào với vẻ đầy tức giận và bắt đầu đá vào một thứ gì đó. Rồi anh ta dừng lại, nhìn tôi gườm gườm và bắt đầu hỏi: \"Cậu vừa nói gì với tôi đấy nhỉ?\"</p>\r\n<p>Tôi cũng hỏi ngược lại đầy cứng cỏi: \"Thế anh cũng vừa nói gì với tôi?\" Mặc dù vậy, trong thâm tâm, tôi vẫn mong muốn có ai đó đứng ra can ngăn chúng tôi, vì tôi hiểu rằng lúc đó Zlatan có thể giết chết tôi.\"</p>\r\n<p>MU lúc đó đang dẫn trước một đối thủ của họ 1-0 trên sân nhà Old Trafford khi hiệp đầu tiên khép lại. Nhưng Ibra vẫn không hài lòng. Dù vậy, \"lão tướng\" người Thụy Điển và người đồng đội Rojo vẫn phải làm hòa với nhau khi HLV Mourinho bắt đầu bước vào phòng thay đồ của toàn đội ở thời gian nghỉ giữa trận.</p>\r\n<p>Rojo tiếp tục thổ lộ: \"Các cầu thủ trụ cột người Anh bắt đầu đứng ra can ngăn chúng tôi. Mourinho bước vào  và bắt đầu la hét, tranh giành chai lọ với chúng tôi. Một mớ hỗn độn đã diễn ra trong phòng thay đồ. Vậy là mọi chuyện đã xong, chúng tôi lại bước ra sân ở hiệp 2 và đã giành chiến thắng để đi tiếp vào vòng trong.</p>\r\n<p>Những cầu thủ như Zlatan có một cá tính khủng khiếp. Anh ấy là người chiến thắng. Anh ấy sẵn sàng đánh đồng đội không như ý mình vì anh ấy luôn muốn cả đội giành chiến thắng. Anh ấy làm cho bạn hiểu rằng bạn luôn có thể làm tốt hơn. Tôi đã học hỏi được rất nhiều điều từ Zlatan. Đó là một trong những cầu thủ giành được nhiều chiến thắng nhất mà tôi biết.\"</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/SAO-MU-choc-gian-Ibrahimovic-la-nintchdbpict000326609309-e1582047000349-1596962101-548-width660height509.jpg\" alt=\"Cựu SAO MU hãi hùng vì chọc giận Ibrahimovic, cảm giác như sắp bị lấy mạng - 2\" data-original=\"https://cdn.24h.com.vn/upload/3-2020/images/2020-08-09/SAO-MU-choc-gian-Ibrahimovic-la-nintchdbpict000326609309-e1582047000349-1596962101-548-width660height509.jpg\" data-was-processed=\"true\" /></p>\r\n<p class=\"img_chu_thich_0407\">Ibra và Rojo từng cùng nhau giúp MU vô địch Europa League mùa giải 2016/17</p>\r\n<p>Sáng sớm hôm sau, các cầu thủ MU đã chứng kiến Ibra và Rojo làm lành như giữa hai người không hề có xích mích nào xảy ra. Trung vệ người Argentina thổ lộ: \"Tôi cảm thấy mình bị tóm ở gáy và không thể quay lại. Và anh ấy (Zlatan) nói: \"Cậu đã nói gì với tôi ý nhỉ?\", rồi anh ấy bắt đầu nhoẻn miệng cười.</p>\r\n<p>Hiện cả hai ngôi sao này đều không còn ở Old Trafford. Ibrahimovic đã rời MU tháng 1/2018 để chuyển sang giải Nhà nghề Mỹ (MLS) khoác áo Los Angeles Galaxy và mới đây đã cập bến AC Milan hồi tháng Giêng năm nay. Tiền đạo 38 tuổi người Thụy Điển đã ghi được 10 bàn thắng sau 18 trận ở <a class=\"TextlinkBaiviet\" title=\"Serie A\" href=\"https://www.24h.com.vn/bong-da-y-c150.html\">Serie A</a> mùa vừa qua để giúp \"Rossoneri\" trở lại cúp châu Âu (Europa League) mùa tới.</p>\r\n<p>Trong khi đó, Rojo ở kỳ chuyển nhượng mùa Đông vừa qua cũng rời MU để trở lại Argentina khoác áo Estudiantes theo dạng cho mượn.  </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'thunthun'),
(31, 'Các bác sĩ từng nghi ngờ sức khỏe Nadal', 'Bác sĩ trị liệu Ben-IIIouz tiết lộ nhiều đồng nhiều của ông từng quả quyết Rafael Nadal sẽ sớm phải giải nghệ vì chấn thương', '          <p class=\"Normal\">\"Nhiều bác sĩ nói thẳng với Nadal rằng cậu ấy sẽ không có một sự nghiệp dài lâu trong tennis vì những chấn thương\", bác sĩ Pháp Ben-Illouz, người từng trị liệu cho Nadal, chia sẻ trên trang <em>Tennis World</em> hôm 4/8. \"Nhưng cuối cùng, cậu ấy đã chứng minh rằng tất cả đã sai\".</p>\r\n<p class=\"Normal\">Trong sự nghiệp, hầu như năm nào Nadal cũng chấn thương. Tháng trước, anh nói rằng bản thân nhiều khi<a href=\"https://vnexpress.net/nadal-toi-ghen-ti-voi-cac-dong-nghiep-4128494.html\" rel=\"dofollow\"> ghen tị với các đồng nghiệp</a>, vì họ mắc ít chấn thương hơn. Tay vợt Tây Ban Nha từng gặp vấn đề ở khuỷu tay, cổ tay, lưng, dây chằng và đầu gối. Trong số này, chấn thương đầu gối là dai dẳng nhất.</p>\r\n<p class=\"Normal\">Năm 2012, Nadal <a href=\"https://vnexpress.net/nadal-tiet-lo-chuyen-dieu-tri-chan-thuong-1954832.html\" rel=\"dofollow\">cố dùng thuốc chống viêm đầu gối</a> để thi đấu tại Wimbledon. Ở giải đó, anh bị loại sớm, còn vết đau trở nên nặng hơn. Năm 2019, Nadal cũng rách gân đầu gối và suýt phải nghỉ hết mùa. Nhưng anh kịp trở lại và <a href=\"https://vnexpress.net/nadal-vo-dich-my-mo-rong-2019-3979309.html\" rel=\"dofollow\">vô địch Grand Slam sân cứng</a> Mỹ Mở rộng vào cuối năm.</p>\r\n<p class=\"Normal\">\"Bất chấp những chấn thương Nadal phải đối mặt trong sự nghiệp, cậu ấy vẫn cống hiến thứ tennis ở đẳng cấp cao nhất\", ông Ben-Illouz nói thêm. \"Càng về cuối sự nghiệp, cậu ấy càng cẩn trọng hơn trong việc chọn lựa giải đấu để tham dự. Cậu ấy làm cho sự nghiệp của bản thân phát triển tự nhiên và đều đặn như không có vấn đề gì\".</p>\r\n<p class=\"Normal\">\"Nadal luôn tìm hiểu kỹ về cơ thể\", ông nói thêm. \"Mọi người thường muốn được thông cảm khi bị đau, nhưng khi nhìn vào Nadal, bạn chỉ thấy sự cố gắng. Cậu ấy kích hoạt nhiều năng lượng đến mức vượt quá giới hạn sức mạnh và cường độ của một người bình thường. Những người làm công tác trị liệu nhìn rõ nhất những khó khăn của cậu ấy. Đôi khi trong phòng họp báo sau trận, cậu ấy bị chuột rút và phải ngồi lỳ trên ghế mãi\".</p>\r\n<p class=\"Normal\">Nadal năm nay 34 tuổi, nhưng vẫn đứng số hai thế giới. Anh cùng Novak Djokovic và Roger Federer thống trị làng banh nỉ dù cả ba đã ở độ tuổi \"băm\". Hiện tại, Nadal sở hữu 19 Grand Slam và chỉ kém một danh hiệu so với kỷ lục của Federer.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(32, 'Nadal không dự Mỹ Mở rộng', 'Rafael Nadal hôm 4/8 thông báo sẽ không đến Mỹ bảo vệ danh hiệu tại Grand Slam sân cứng', '          <p>\"Sau khi suy nghĩ kỹ lưỡng, tôi quyết định không dự Mỹ Mở rộng năm nay\", Nadal viết trên <em>Twitter</em>. \"Tình hình thế giới hiện tại rất phức tạp. Số ca nhiễm nCoV vẫn gia tăng, và dường như chúng ta không kiểm soát được nó\".</p>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/chu-de/rafael-nadal-370\" rel=\"dofollow\">Nadal</a> từng bốn lần vô địch <a href=\"https://vnexpress.net/chu-de/my-mo-rong-656\" rel=\"dofollow\">Mỹ Mở rộng</a>, vào các năm 2010, 2013, 2017 và 2019. Năm ngoái, anh hạ Daniil Medvedev ở <a href=\"https://vnexpress.net/nadal-vo-dich-my-mo-rong-2019-3979309.html\" rel=\"dofollow\">chung kết</a> sau năm set. Nadal là tay vợt nam đầu tiên không đến bảo vệ danh hiệu tại Flushing Meadows, từ sau Pete Sampras năm 2002.</p>\r\n<p class=\"Normal\">\"Chúng ta đều biết lịch đấu cuối năm rất khắc nghiệt, sau khi tennis tạm ngưng nhiều tháng vì Covid-19\", Nadal viết thêm. \"Tôi hiểu và cảm ơn những nỗ lực của ban tổ chức các giải đấu. Tôi vừa thấy thông báo về việc hủy giải Madrid Mở rộng. Tôi rất tôn trọng Hiệp hội quần vợt Mỹ (USTA) vì đã cố gắng tổ chức Mỹ Mở rộng cho các tay vợt và người hâm mộ trên thế giới. Tôi không bao giờ muốn vắng mặt, nhưng tôi phải quyết định theo những gì trái tim mách bảo\".</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(33, 'Nadal khó lên số một thế giới năm 2020', 'Rafael Nadal hẹp cửa san lấp khoảng cách 370 điểm với Novak Djokovic, khi giải ATP 1000 Madrid Mở rộng bị hủy', '          <p class=\"Normal\">Thông tin Madrid Mở rộng được <em>Marca</em> tiết lộ hôm 3/8. Trong hôm nay, ban tổ chức sẽ xác nhận hủy giải vì diễn biến phức tạp của đại dịch. Ban đầu, Madrid Mở rộng dự kiến diễn ra từ 12/9 đến 20/9.</p>\r\n<p class=\"Normal\">Từ tháng trước, Nadal đã khẳng định sẽ góp mặt ở Madrid. Đây là giải duy nhất trong mùa đất nện châu Âu năm nay mà tay vợt Tây Ban Nha có thể kiếm thêm điểm số. Mùa trước, Nadal chỉ vào đến bán kết. Nếu vô địch năm nay, \"Vua đất nện\" có thể thu về thêm 640 điểm ATP. Ở Rome Masters và Roland Garros, Nadal đều đang là ĐKVĐ.</p>\r\n<p class=\"Normal\">Cơ hội sáng nhất để Nadal kiếm thêm điểm mùa này nằm ở Western & Southern Mở rộng. Nadal không dự giải ATP 1000 sân cứng này năm ngoái, và có cơ hội lấy trọn 1000 điểm nếu vô địch năm nay. <a href=\"https://vnexpress.net/chu-de/rafael-nadal-370\" rel=\"dofollow\">Nadal</a> và <a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\" rel=\"dofollow\">Djokovic</a> đều có tên trong danh sách dự giải mà ban tổ chức mới công bố cách đây ít hôm. Tại giải đấu ở Cincinnati năm ngoái, Djokovic vào đến bán kết và thua Daniil Medvedev - người sau đó vô địch.</p>\r\n<p class=\"Normal\">Western & Southern Mở rộng dự kiến diễn ra từ 22-28/8. Năm nay, do đại dịch, giải được tổ chức ở tổ hợp sân đấu Flushing Meadows tại New York. Đây cũng là nơi diễn ra Mỹ Mở rộng - giải Grand Slam khởi tranh ngày 31/8. Điều này giúp các tay vợt hạn chế di chuyển và tập trung cho cả hai giải sân cứng quan trọng.</p>\r\n<p class=\"Normal\">Do nhiều giải đấu năm 2020 bị hủy vì Covid-19, ATP thay đổi cách thức tính điểm xếp thứ bậc. Hệ thống này lấy thành tích trong 22 tháng của các tay vợt, từ tháng 3/2019 đến tháng 12/2020, nhưng điểm số của mỗi người được xét trên thành tích tốt nhất ở 18 giải đấu. Trong đó, nếu một giải đấu được tổ chức hai lần trong giai đoạn 22 tháng kể trên, tay vợt sẽ được lấy thành tích tốt hơn để tính vào bảng điểm.</p>\r\n<p class=\"Normal\">Cách tính điểm cũ, dựa trên thành tích của 52 tuần kế trước, sẽ được ATP áp dụng trở lại nếu tennis diễn ra bình thường, không gián đoạn trong năm 2021.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(34, 'Kyrgios lại chỉ trích đồng nghiệp tham tiền', 'Tay vợt người Australia Nick Kyrgios cho rằng các đồng nghiệp đang đánh biểu diễn ở châu Âu là tham tiền và vô trách nhiệm', '          <p>\"Các đồng nghiệp của tôi ơi, hãy hành động vì lợi ích chung\", Kyrgios nói trên truyền thông Australia hôm 2/8. \"Các anh không thể cứ nhảy múa trên bàn, kiếm tiền khắp châu Âu bằng việc tổ chức những giải đấu biểu diễn. Như thế là rất ích kỷ. Làm ơn nghĩ về người khác chỉ một lần thôi trong đại dịch này. Không quan trọng các anh có bao nhiêu tiền hay xếp thứ bậc bao nhiêu, hãy hành động có trách nhiệm\".</p>\r\n<p class=\"Normal\">Trong thời gian ATP và WTA tạm ngưng vì đại dịch, nhiều tay vợt tổ chức và tham dự các giải đấu biểu diễn tại châu Âu. Trong đó, <a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\" rel=\"dofollow\">Novak Djokovic</a> bị chỉ trích nhiều nhất vì tổ chức Adria Tour - cụm giải bị xem như ổ dịch nCoV. <a href=\"https://vnexpress.net/dimitrov-nhiem-ncov-4118808.html\" rel=\"dofollow\">Grigor Dimitrov</a>, <a href=\"https://vnexpress.net/borna-coric-nhiem-ncov-4119250.html\" rel=\"dofollow\">Borna Coric</a> và chính <a href=\"https://vnexpress.net/djokovic-nhiem-ncov-4119934.html\" rel=\"dofollow\">Djokovic nhiễm nCoV</a> khi dự giải đấu vùng Balkan này.</p>\r\n<p class=\"Normal\">Cùng với việc chỉ trích các đồng nghiệp, Kyrgios cũng khẳng định anh sẽ không dự Grand Slam sân cứng Mỹ Mở rộng vào cuối tháng 8. \"Tôi sẽ không dự Mỹ Mở rộng\", anh nói. \"Tôi rất buồn khi không thể góp mặt ở đó và chơi tại sân Arthur Ashe, một trong những sân đấu vĩ đại nhất của môn thể thao này. Nhưng tôi làm vậy vì muốn bảo vệ những người thân của tôi, những người đồng hương Australia và vì rất nhiều người Mỹ đã qua đời. Hãy nghỉ ngơi trong giai đoạn này, vì điều quan trọng nhất bây giờ là sức khỏe và sự an toàn của cộng đồng. Chúng ta có thể vực lại nền kinh tế cũng như môn thể thao này, nhưng chúng ta không bao giờ có thể hồi sinh những người đã mất\".</p>\r\n<p class=\"Normal\">Trái với hình ảnh ngỗ ngược trên sân, Kyrgios tỏ ra quan tâm đến cộng đồng và không ngại giúp đỡ những hoàn cảnh khó khăn. Anh là tay vợt đầu tiên quyên tiền hỗ trợ những người bị ảnh hưởng bởi vụ cháy rừng tại Australia hồi đầu năm. Tay vợt 25 tuổi cũng mang thức ăn đến cho những người hàng xóm nghèo tại Canberra, và ủng hộ việc giúp đỡ những đồng nghiệp thứ bậc thấp. Những tháng gần đây, Kyrgios chỉ trích thậm tệ ba tay vợt trong top 10 thế giới là Djokovic, Thiem và Zverev. Tay vợt số 40 thế giới không hài lòng với việc Thiem không quyên tiền cho các tay vợt thứ bậc thấp, và bực tức khi thấy Zverev \"ăn chơi\" ở nơi công cộng dù vừa trở về từ ổ dịch Adria Tour.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(35, 'Zverev: \'Bị điên mới cố dự Mỹ Mở rộng\'', 'Tay vợt Alexander Zverev cho rằng Grand Slam sân cứng không nên diễn ra tại Mỹ trong bối cảnh Covid-19 chưa được kiểm soát', '          <p>\"Sẽ là điên rồ nếu cố dự Mỹ Mở rộng\", Zverev nói khi tham dự giải đấu biểu diễn Ultimate Showdown ở Pháp hôm 1/8. \"Tôi mong giải đấu không diễn ra và chúng tôi sẽ chơi tennis trở lại tại châu Âu. Không hiểu sao Mỹ Mở rộng chưa bị hủy. Đây không phải lúc thích hợp để bay đến Mỹ\".</p>\r\n<p class=\"Normal\">Trước khi Zverev lên tiếng, một số tay vợt hàng đầu thế giới đã rút khỏi Mỹ Mở rộng, bất kể giải đấu có diễn ra hay không. Số này có tay vợt nữ số 1 thế giới Ashleigh Barty và \"ngựa chứng\" Nick Kyrgios. Kyrgios thậm chí chỉ trích ban tổ chức là \"tham tiền\" khi chưa dứt khoát hủy giải.</p>\r\n<p class=\"Normal\">Mỹ Mở rộng 2020 dự kiến khởi tranh ngày 31/8 tại New York. Trước đó chín ngày, giải ATP 1000 Western & Southern Mở rộng sẽ diễn ra cũng tại tổ hợp sân đấu Flushing Meadows. Nhưng hai giải đấu quan trọng này đang có nguy cơ bị hủy về những vấn đề liên quan đến sức khỏe cộng đồng. Trước đó, giải ATP 500 Citi Mở rộng đã bị dời sang năm 2021.</p>\r\n<p class=\"Normal\">Zverev thuộc số ít tay vợt không nhiễm nCoV khi dự Adria Tour do Djokovic tổ chức hồi tháng 6. Anh nhiều khả năng sẽ hướng đến mùa đất nện vào tháng 9 tới, với tâm điểm là Roland Garros khởi tranh ngày 20/9. Tay vợt số bảy thế giới vừa hợp tác với David Ferrer - cựu tay vợt có sở trường đánh đất nện. Ferrer, vừa giải nghệ năm ngoái, sẽ cùng cha của Zverev làm HLV cho tay vợt Đức.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(36, 'Federer muốn dự Olympic Tokyo', 'Tay vợt Roger Federer muốn lần thứ năm dự Olympic, sau khi lỡ hẹn với Olympic Rio 2016', '          <p>\"Olympic luôn rất đặc biệt. Tôi háo hức chờ giải đấu ở Tokyo và hy vọng nó sẽ diễn ra suôn sẻ trong năm 2021\", Roger Federer nói hôm 28/7 với <em>SRFSport</em>. \"Tôi muốn đoạt huy chương tại đó, bất kể đánh đơn, đôi nam hay đôi nam nữ\".</p>\r\n<div class=\"social_pin\">\r\n<ul class=\"social_left\">\r\n<li class=\"li_comment\"><a class=\"social_comment flexbox\" title=\"Bình luận\" href=\"https://vnexpress.net/federer-muon-du-olympic-tokyo-4138124.html#box_comment_vne\"><span class=\"number_cmt num_cmt_detail widget-comment-4138124-1\">193</span></a></li>\r\n<li> </li>\r\n</ul>\r\n</div>\r\n<div class=\"sidebar-1\">\r\n<div class=\"header-content width_common\">\r\n<ul class=\"breadcrumb\" data-campaign=\"Header\">\r\n<ul class=\"breadcrumb\" data-campaign=\"Header\">\r\n<li><a title=\"Thể thao\" href=\"https://vnexpress.net/the-thao\" data-medium=\"Menu-TheThao\" data-itm-source=\"#vn_source=Detail&vn_campaign=Header&vn_medium=Menu-TheThao&vn_term=Desktop\" data-itm-added=\"1\">Thể thao</a></li>\r\n<li><a title=\"Tennis\" href=\"https://vnexpress.net/the-thao/tennis\" data-medium=\"Menu-Tennis\" data-itm-source=\"#vn_source=Detail&vn_campaign=Header&vn_medium=Menu-Tennis&vn_term=Desktop\" data-itm-added=\"1\">Tennis</a></li>\r\n</ul>\r\n</ul>\r\n<span id=\"parentCateDetail\" data-cate=\"1002619\"></span><span id=\"site-sub-id\" data-cate=\"1002619\"></span><span class=\"date\">Thứ tư, 29/7/2020, 16:25 (GMT+7)</span></div>\r\n<h1 class=\"title-detail\">Federer muốn dự Olympic Tokyo</h1>\r\n<p class=\"description\">Tay vợt Roger Federer muốn lần thứ năm dự Olympic, sau khi lỡ hẹn với Olympic Rio 2016.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">\"Olympic luôn rất đặc biệt. Tôi háo hức chờ giải đấu ở Tokyo và hy vọng nó sẽ diễn ra suôn sẻ trong năm 2021\", Roger Federer nói hôm 28/7 với <em>SRFSport</em>. \"Tôi muốn đoạt huy chương tại đó, bất kể đánh đơn, đôi nam hay đôi nam nữ\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Ofk-vPGcEuv5Dhkgq1MYRg 1x, https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=1020&h=0&q=100&dpr=1&fit=crop&s=SE0e7heUYav2QEh3kd3_pQ 1.5x, https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=680&h=0&q=100&dpr=2&fit=crop&s=OSsXbuQ4UPqCYQOMyqcIBQ 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Ofk-vPGcEuv5Dhkgq1MYRg 1x, https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=1020&h=0&q=100&dpr=1&fit=crop&s=SE0e7heUYav2QEh3kd3_pQ 1.5x, https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=680&h=0&q=100&dpr=2&fit=crop&s=OSsXbuQ4UPqCYQOMyqcIBQ 2x\" /><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Ofk-vPGcEuv5Dhkgq1MYRg\" alt=\"Federer (trái) đoạt HC bạc ở Olympic London 2012. Ảnh: News Group.\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2020/07/29/8ec8ef3c-4ddb-44a3-a9e1-32d5b4-9125-1424-1596014187.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=Ofk-vPGcEuv5Dhkgq1MYRg\" data-ll-status=\"loaded\" /></picture>\r\n<div class=\"embed-container\">\r\n<div id=\"VN_PC_Detail_Thethao_II1_1x1\" data-google-query-id=\"CJmkiIPZkusCFQwHvAodnDcABA\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.tennis.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.tennis.detail_0\" title=\"3rd party ad content\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.tennis.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" data-google-container-id=\"4\" data-load-complete=\"true\" data-mce-fragment=\"1\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Federer (trái) đoạt HC bạc ở Olympic London 2012. Ảnh:<em> News Group.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Federer sẽ chạm ngưỡng 40 tuổi nếu dự Olympic Tokyo 2021. Anh vừa phẫu thuật đầu gối hồi tháng Hai năm nay và gần như không thể tham dự bất cứ giải đấu nào trong phần còn lại của năm 2020.</p>\r\n<p class=\"Normal\">Tại Olympic Bắc Kinh 2008, Federer giành HC vàng đôi nam cùng Stan Wawrinka. Bốn năm sau, anh lần đầu đoạt huy chương nội dung đơn nam khi vào chung kết và chỉ chịu thua tay vợt chủ nhà Andy Murray. Huyền thoại người Thụy Sỹ muốn một lần nữa bước lên bục chiến thắng tại Thế vận hội, dù không có thêm Grand Slam nào từ Australia Mở rộng 2018.</p>\r\n<p class=\"Normal\">\"Nếu đã thi đấu 20 năm liên tục, bạn sẽ vui vẻ tận hưởng những này tháng yên tĩnh để nghỉ ngơi. Tôi nghĩ, thời gian tốt nhất để chuẩn bị cho Olympic là ba tháng. Trước nữa, bạn không nên bận tâm quá nhiều\", Federer bày tỏ.</p>\r\n<p class=\"Normal\">Federer hiện vẫn là tay vợt giành nhiều Grand Slam nhất, với 20 lần đăng quang, chỉ hơn một so với người xếp sau Rafael Nadal. Nhưng anh không chú ý nhiều tới chuyện này. \"Rõ ràng tôi đang ở cuối sự nghiệp. Đó là lý do tại sao tôi chỉ lên kế hoạch năm một và hạnh phúc với lịch trình ấy. Tôi không chắc điều gì sẽ xảy ra hai năm nữa, nhưng chắc chắn vẫn giao lưu tennis với đồng nghiệp lúc về già\", Federer nói.</p>\r\n</article>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'hiuhiu'),
(37, 'Hủy giải ATP 500 Citi Mở rộng', 'Giải đấu khởi động cho Grand Slam Mỹ Mở rộng bị hủy bỏ vì ban tổ chức không thể kiểm soát những vẫn đề liên quan đến đại dịch', '          <p class=\"Normal\">Hôm 21/7, Giám đốc điều hành Citi Mở rộng, ông Mark Ein thông báo hủy giải đấu năm 2020. Kỳ giải lần thứ 52 tại Washington được dời sang hè 2021. Lý do là ban tổ chức không thể kiểm soát việc hạn chế di chuyển giữa các quốc gia cũng như lo ngại về tình trạng dịch bệnh chưa có tiến triển tốt tại Mỹ.</p>\r\n<p class=\"Normal\">\"Quyết định được đưa ra để đảm bảo công bằng cho các tay vợt cũng như các đối tác, giúp họ chủ động trong các kế hoạch khác\", ông Ein nói với truyền thông Mỹ hôm 21/7</p>\r\n<p class=\"Normal\">Ban đầu, Citi Mở rộng dự định khởi tranh ngày 14/8 và là giải đánh dấu sự trở lại của ATP sau khi hệ thống này tạm ngưng từ tháng 3 vì đại dịch. Nhưng tình hình Covid-19 không khả quan tại Mỹ khiến ban tổ chức buộc phải hủy giải. Số phận của các giải còn lại ở mùa sân cứng Bắc Mỹ như ATP Western & Southern Mở rộng hay Mỹ Mở rộng cũng đang là dấu hỏi lớn.</p>\r\n<p class=\"Normal\">Ban tổ chức Mỹ Mở rộng - Hiệp hội quần vợt Mỹ (USTA) - trấn an dư luận khi tuyên bố việc Citi Mở rộng bị hủy không liên quan đến tiến trình chuẩn bị cho Grand Slam sân cứng tại New York. \"USTA tạo ra môi trường an toàn, trong tầm kiểm soát và những người tham dự được giảm thiếu tối đa nguy cơ về sức khỏe\", thông báo của USTA có đoạn. \"Chúng tôi tự tin giải đấu vẫn sẽ được diễn ra dựa trên những nguyên tắc được tuân thủ\".</p>\r\n<p class=\"Normal\">Trong khi ATP lùi ngày trở lại, hệ thống giải chuyên nghiệp nữ WTA sẽ sôi động từ 3/8, thời điểm giải đất nện ở Palermo, Italy khởi tranh. Ở tuần kế tiếp, giải đấu sân cứng ở Kentucky, Mỹ được tổ chức với sự góp mặt của chủ nhân 23 Grand Slam - Serena Williams.</p>\r\n<p class=\"Normal\">Theo lịch dự kiến, giải ATP Western & Southern Mở rộng sẽ khởi tranh ngày 22/8 tại New York, thay vì Cincinnati như mọi năm. Cũng tại tổ hợp Flushing Meadows tại New York, <a href=\"https://vnexpress.net/chu-de/my-mo-rong-656\" rel=\"dofollow\">Mỹ Mở rộng </a>diễn ra từ 31/8 - 13/9. Một tuần sau khi Grand Slam sân cứng kết thúc, ngày 20/9, Grand Slam đất nện <a href=\"https://vnexpress.net/chu-de/roland-garros-659\" rel=\"dofollow\">Roland Garro</a>s khởi tranh tại Paris, Pháp.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(38, 'Djokovic tập luyện chuẩn bị cho Mỹ Mở rộng', 'Tay vợt Novak Djokovic không giấu ý định dự các giải saatn cứng Bắc Mỹ, khi tập luyện ở mặt sân tương tự tại quê nhà Belgrade, Serbia', '          <p>Kế hoạch của Djokovic thay đổi sau khi anh không còn nhiễm nCoV. Sau kỳ nghỉ tại Bosnia, tay vợt Serbia trở về Belgrade tập luyện tại học viện của bạn thân, cựu tay vợt đồng hương Janko Tipsarevic. Mặt sân tại học viện này giống với mặt sân tại Grand Slam sân cứng Mỹ Mở rộng và tương tự các giải đấu khác của mùa sân cứng Bắc Mỹ.</p>\r\n<p class=\"Normal\">Với động thái này, <a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\" rel=\"dofollow\">Djokovic </a>xem như sẽ chọn dự Mỹ Mở rộng thay vì tham gia các giải đất nện chuẩn bị cho Roland Garros - Grand Slam khởi tranh chỉ một tuần sau khi Mỹ Mở rộng kết thúc. Hiện tại, Djokovic vẫn chưa xác nhận dự giải đấu nào ở mùa sân cứng Bắc Mỹ.</p>\r\n<p class=\"Normal\">Tờ báo Tây Ban Nha, <em>AS</em> cho rằng Djokovic nhắm đến mùa sân cứng Bắc Mỹ bởi hai lý do. Tay vợt số một thế giới nhận thấy cơ hội giành thêm Grand Slam tại Mỹ Mở rộng tăng lên, sau khi <a href=\"https://vnexpress.net/chu-de/rafael-nadal-370\" rel=\"dofollow\">Rafael Nadal</a> nhiều khả năng không góp mặt tại New York. Tuần trước, Nadal xác nhận dự Madrid Masters - giải ATP 1000 đất nện khởi tranh chỉ một ngày sau khi Mỹ Mở rộng kết thúc.</p>\r\n<p class=\"Normal\">Lý do thứ hai là số điểm tối đa mà một tay vợt có thể thu về từ mùa sân cứng Bắc Mỹ là 4.640 điểm, cao hơn nhiều so với mùa đất nện diễn ra gần như cùng thời điểm (2.860).</p>\r\n<p class=\"Normal\">Trong khi Djokovic hướng đến mùa sân cứng, Nadal vẫn miệt mài tập luyện trên mặt sân đất nện. Hai tay vợt đang cạnh tranh quyết liệt trong cuộc đua về số danh hiệu Grand Slam với Roger Federer - người đang giữ kỷ lục 20 Grand Slam. Djokovic đang có 17 Grand Slam còn Nadal sở hữu 19 danh hiệu lớn.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'thunthun'),
(39, 'Francisco Roig: \'Nadal đánh sân cỏ hay bậc nhất\'', 'Thành viên ban huấn luyện của Rafael Nadal, ông Francisco Roig cho rằng tay vợt Tây Ban Nha là một trong những người đánh sân cỏ hay nhất thế giới', '          <p>\"Ngay từ những ngày đầu, tôi đã thấy Nadal có khả năng thích nghi tốt với sân cỏ\", HLV Roig nói về học trò trên trang chủ <em>ATP</em> hôm 13/7. \"Nhưng cũng phải mất vài năm để cậu ấy tự tin trên mặt sân này và biến nó thành sở trường. Nadal có một phẩm chất đáng kinh ngạc, làm cậu ấy khác biệt với phần còn lại. Đó là cậu ấy biết cách vượt qua nghịch cảnh. Tôi đánh giá Nadal thuộc diện hay bậc nhất ở Wimbledon. Không có gì phải bàn cãi, khi nói rằng cậu ấy là một trong những người hay nhất lịch sử, trên bất kỳ mặt sân nào\".</p>\r\n<p class=\"Normal\">Sân cỏ, ban đầu, được xem là sở đoản của Nadal bởi bóng đi nhanh và nảy thấp. Nhưng trong giai đoạn 2006-2011, Nadal năm lần vào tới chung kết Wimbledon và vô địch hai lần.</p>\r\n<p class=\"Normal\">Từ năm 2012-2017, Nadal liên tục bị loại sớm tại Wimbledon và không năm nào vào đến tứ kết. Tay vợt Tây Ban Nha cải thiện thành tích ở các năm 2018 và 2019, khi đều vào đến bán kết.</p>\r\n<p class=\"Normal\">\"Ở ba kỳ giải gần nhất, cậu ấy chơi thứ tennis tuyệt vời\", HLV Roig nói tiếp về Nadal. \"Năm 2017, Nadal thua ở vòng bốn trước Muller trong trận đấu mà mọi thứ đã có thể rất khác. Năm đó, tôi tin Nadal chơi đủ tốt để có thể làm nên điều gì đó ở Wimbledon. Hai năm sau, cậu ấy đã cải thiện đáng kể thành tích cũng như lối chơi\".</p>\r\n<p class=\"Normal\">Wimbledon 2020 bị hủy vì Covid-19, nhưng các tay vợt đủ điều kiện dự giải vẫn được hỗ trợ tài chính. Ban tổ chức giải trích ra 12,5 triệu USD từ tiền đền bù bảo hiểm, để chia cho 620 tay vợt. Căn cứ theo bảng thứ bậc ATP và WTA, các tay vợt đáng lẽ dự vòng loại sẽ được hỗ trợ 15.700 USD, còn các tay vợt được dự vòng đấu chính nhận 31.400 USD. Các tay vợt đánh đôi cũng nhận được 7.850 USD.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'binbin'),
(40, 'Chú Nadal lo lắng ngay từ lần đầu thấy Djokovic', 'Ông Toni Nadal nhìn ra tài năng của Novak Djokovic ngay từ lần đầu chứng kiến tay vợt Serbia thi đấu', '          <p class=\"Normal\">Djokovic lần đầu dự Wimbledon 2005, khi chưa lọt vào top 100 trên bảng thứ bậc ATP. Ở vòng một, tay vợt Serbia gặp Juan Monaco - người có mối quan hệ tốt với Nadal và đội ngũ huấn luyện của anh. Khi đó, ông Toni Nadal tiện đường tạt qua sân số 18 xem Monaco thi đấu, ấn tượng với đối thủ bên kia lưới chỉ sau vài điểm số.</p>\r\n<p class=\"Normal\">\"Cậu thanh niên kia là ai thế?\", ông Toni Nadal hỏi mọi người xung quanh. \"Cậu ta mới 18 tuổi, đang xếp ngay ngoài top 100 thế giới\", một người ngồi đó đáp.</p>\r\n<p class=\"Normal\">\"Cậu ta tên gì?\", chú ruột Nadal hỏi tiếp. \"Novak Djokovic\", người đó trả lời.</p>\r\n<p class=\"Normal\">Ông Toni Nadal ghi nhớ cái tên đó. Sau khi xem thêm vài phút, ông đi vào khu thay đồ của tổ hợp quần vợt All England Club - nơi Rafael Nadal chờ sẵn. Khi gặp người cháu ruột vừa vô địch Roland Garros 2005 tháng kế trước, ông nói: \"Rafael, chúng ta có một vấn đề. Chú vừa thấy một cậu nhóc cừ lắm\".</p>\r\n<p class=\"Normal\">Sau đó, cả hai hay tin Djokovic hạ Monaco chỉ sau ba set, với tỷ số 6-3, 7-6, 6-3. Đó mới là chiến thắng thứ hai của \"Nole\" tại Grand Slam. Ở vòng hai, tay vợt Serbia tạo ra màn ngược dòng khó tin. Djokovic thua 3-6, 3-6 trước Gullermo Garcia-Lopez trong hai set đầu, rồi bị dẫn 3-5 trong set ba. Garcia-Lopez cầm giao bóng khi dẫn 5-4 rồi ghi điểm winner lúc dẫn 40-30. Khi hai tay vợt tiến đến định bắt tay sau match-point, trọng tài đổi ý và cho rằng bóng ngoài.</p>\r\n<p class=\"Normal\">Djokovic thắng liền ba điểm để đoạt game đỡ bóng và gỡ hòa 5-5. Nhưng sau đó, Garcia-Lopez cũng thắng game đỡ bóng và dẫn 40-0 trong game giao. Đối mặt với ba match-point, Djokovic cứu cả ba rồi thắng ngược 7-6 trong set ba. Tay vợt Serbia thắng tiếp 7-6, 6-4 trong hai set cuối để hoàn tất màn ngược dòng sau năm set.</p>\r\n<p class=\"Normal\">\"Cậu ấy là tay vợt không bao giờ đầu hàng, khi đó tôi đã thấy cậu ấy có tiềm năng lớn\", Garcia-Lopez nói với <em>ATP</em>. \"Cậu ấy đánh cuối sân quá tốt. Nếu đối thủ là người khác, tôi đã thắng. Cậu ấy sinh ra đã là một chiến binh\".</p>\r\n<p class=\"Normal\">Toni Nadal và Garcia-Lopez đều đã dự đoán đúng về tài năng của Djokovic. Tay vợt Serbia, tính đến giờ, giành 17 Grand Slam và có 282 tuần đứng số một thế giới. Anh được xem như một trong ba người vĩ đại nhất lịch sử quần vợt nam, cùng Roger Federer và Rafael Nadal.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 4, 'hiuhiu'),
(41, 'Đà tăng giá cà phê tháng 7 có tiếp tục được duy trì?', 'Cục xuất nhập khẩu dự đoán trong thời gian tới, nguồn cung cà phê trong nước hạn chế cùng với tình trạng thiếu nước tại khu vực Tây Nguyên khiến sản lượng thấp sẽ thúc đẩy giá nội địa tăng lên.', '          <p>Cuối tháng 7, giá cà phê trên thị trường thế giới tăng mạnh do đồng USD suy yếu, các dự báo thị trường lạc quan hơn và thời tiết Brazil không thuận lợi ảnh hưởng đến sự phát triển của cây cà phê. </p>\r\n<p>Theo đó, nhu cầu cà phê robusta của thế giới tăng nhanh do đại dịch Covid-19 khiến xu hướng làm việc tại nhà tăng. Nhu cầu tiêu dùng cà phê được dự báo sẽ tăng khoảng 5,8% trong năm 2020.</p>\r\n<p>Brazil có thể sẽ xuất hiện sương giá đen, thường làm cây trồng bị chết.</p>\r\n<p>Bên cạnh đó, đồng Real hồi phục trong khi đồng USD suy yếu đã giúp cho giá cà phê phục hồi. </p>\r\n<p>Trên sàn giao dịch London, ngày 28/7 giá cà phê robusta giao kỳ hạn tháng 9 và tháng 11/2020 tăng 14,3% và 13,8% so với ngày 30/6, lên mức 1.346 USD/tấn và 1.361 USD/tấn.</p>\r\n<p>Trên sàn giao dịch New York, ngày 28/7 giá cà phê Arabica giao kỳ hạn tháng 9 và tháng 12 tăng lần lượt 10,2% và 10,5% so với ngày 30/6, lên mức 110,35 Uscent/lb và 113,25 Uscent/lb.</p>\r\n<p>Ở thị trường cà phê trong nước, theo Bộ NN&PTNT, giá cà phê biến động tăng cùng xu hướng thị trường thế giới. So với tháng 6, giá cà phê vối nhân xô các tỉnh Tây Nguyên tăng 1.400 – 1.600 đồng/kg lên mức 32.300 – 32.700 đồng/kg. </p>\r\n<p>Giá cà phê cao nhất ở khu vực tỉnh Đắk Lắk và thấp nhất tại khu vực tỉnh Lâm Đồng. Giá cà phê robusta giá FOB giao tại cảng thành phố Hồ Chí Minh tăng 270 USD/tấn lên 1.537 USD/tấn. </p>\r\n<p>Nhìn chung trong 2 quý đầu năm 2020, xu hướng giảm giá là chủ đạo. Tuy nhiên, so với thời điểm cuối năm 2019, giá cà phê trong nước đã tăng 500 – 700 đồng/kg sau chuỗi thời gian dài giá thấp ảm đạm. </p>\r\n<p>Cục Xuất nhập khẩu dự báo trong thời gian tới, nguồn cung cà phê trong nước hạn chế cùng với tình trạng thiếu nước tại khu vực Tây Nguyên khiến sản lượng thấp sẽ thúc đẩy giá nội địa tăng lên. </p>\r\n<p>Đối với thị trường thế giới, trong tháng 7, đồng Real vẫn đang có lợi cho người bán khiến cho người trồng cà phê đẩy mạnh hoạt động bán hàng. </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(42, 'Trung Quốc hủy mua đậu nành từ Brazil để mua từ Mỹ', 'Bloomberg dẫn nguồn tin thân cận cho biết Trung Quốc đang hủy bỏ các giao dịch mua đậu nành đắt tiền Brazil và thay thế bằng nguồn cung đậu nành của Mỹ', '          <p>Nguồn tin của <em>Bloomberg </em>cho hay các thương nhân đang hủy bỏ các giao dịch mua đậu nành Brazil thực hiện hồi đầu năm do chênh lệch giá giữa sản phẩm của Brazil so với Mỹ ngày càng nới rộng.</p>\r\n<p>Một số thương nhân thực hiện thỏa thuận để tự cung ứng cho nhà máy của riêng họ ở Trung Quốc, trong khi số khác kí thỏa thuận thay mặt cho khách hàng Trung Quốc.</p>\r\n<p>Thời gian quan, giá đậu nành Brazil tăng do nguồn cung cạn kiệt. Trong khi đó, vụ thu hoạch của nông dân Mỹ đang cận kề và các thương nhân dự đoán Mỹ sẽ có một vụ mùa bội thu.</p>\r\n<p>Bộ Nông nghiệp Mỹ (USDA) sẽ công bố báo cáo hàng tháng vào ngày 12/8 tới. Các nhà phân tích tham gia khảo sát của<em> Bloomberg</em> dự đoán sản lượng đậu nành Mỹ trong vụ mùa này sẽ đạt mức cao nhất kể từ năm 2016.</p>\r\n<p>Chênh lệch giá đậu nành giữa hai nước đã góp phần thúc đẩy các giao dịch mua đậu nành Mỹ. Hôm 10/8, USDA cho biết Mỹ đã bán gần 600.000 tấn đậu nành sang Trung Quốc trong các giao dịch diễn ra vào cuối tuần trước.</p>\r\n<p>Cũng ngày 10/8, nguồn tin thân cận của <em>Bloomberg</em> cho biết các công ty Mỹ đã bán thêm ít nhất 6 chuyến hàng khác đến Trung Quốc. Một chuyến hàng thông thường vận chuyển khoảng 55.000 - 60.000 tấn đậu nành.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(43, 'Giá lúa, gạo tại các tỉnh ĐBSCL tăng trong nửa đầu tháng 7', 'Các chuyên gia nhận định xuất khẩu gạo Việt Nam và thị trường lúa gạo hàng hóa ở ĐBSCL trong nửa cuối năm nay sẽ có những chuyển động, trước mắt là giá lúa vụ Hè Thu', '          <p>Báo cáo của Cục Chế biến và phát triển thị trường nông sản (Bộ NN&PTNT) cho biết giá lúa, gạo tại các tỉnh ĐBSCL diễn biến tăng trong nửa đầu tháng 7. Giá lúa tăng đầu tháng do nông dân sắp chuẩn bị thu hoạch vụ lúa Hè Thu, vào thời điểm đó nguồn cung từ vụ Đông Xuân không còn nhiều. Đồng thời, giá lúa, gạo xuất khẩu có bước phục hồi tốt. </p>\r\n<p>Cụ thể, tại An Giang, lúa IR50404 tăng 200 đồng/kg lên 5.000 đồng/kg, hiện ở mức 4.900 đồng/kg (22/7); lúa OM 5451 tăng 300 đồng/kg lên 5.200 đồng/kg; lúa OM 6976 giữ ở mức 5.400 đồng/kg; lúa gạo thường ổn định ở mức 11.000 đồng/kg; gạo thơm đặc sản jasmine ở mức 14.500 đồng/kg.</p>\r\n<p>Tại Kiên Giang, lúa IR50404 tăng 300 đồng/kg lên 5.700 – 5.900 đồng/kg; lúa OM 6976 tăng 100 đồng/kg lên mức 6.400 – 6.600 đồng/kg; lúa OM 5451 tăng 300 đồng/kg lên 6.300 – 6.700 đồng/kg. </p>\r\n<p>Tại thị trường xuất khẩu, giá gạo Thái Lan xuất khẩu trong tháng 7 dao động trong khoảng 440 – 515 USD/tấn.</p>\r\n<p>Giá gạo Việt Nam xuất khẩu trong tháng qua giảm nhẹ so với tháng trước đó do nhu cầu giảm cũng như nguồn cung từ vụ thu hoạch Hè Thu đang dần được đưa ra thị trường vào khoảng nửa cuối tháng 7/2020.</p>\r\n<p>Giá gạo Việt Nam xuất khẩu trong tháng dao động trong khoảng 415 – 457 USD/tấn. Trong khi đó, giá gạo Ấn Độ xuất khẩu có xu hướng tăng nhẹ, tuy nhiên các thương nhân nước này cho biết nhu cầu thị trường đối với gạo Ấn Độ chỉ đang ở mức trung bình. Giá gạo Ấn Độ xuất khẩu trong tháng dao động trong khoảng 373 – 382 USD/tấn.</p>\r\n<p>Các chuyên gia nhận định xuất khẩu gạo Việt Nam và thị trường lúa gạo hàng hóa ở ĐBSCL trong nửa cuối năm nay sẽ có những chuyển động, trước mắt là giá lúa vụ Hè Thu.</p>\r\n<p>Ngoài ra, theo thông tin dự báo cập nhật mới nhất trong tháng 7/2020 của Bộ Nông nghiệp Mỹ, sản lượng gạo của thế giới năm 2020 ước đạt 495,2 triệu tấn, giảm khoảng 0,3% so với năm 2019. Tiêu dùng gạo thế giới năm 2020 ước đạt 490,4 triệu tấn, tăng khoảng 1,3% so với năm 2019.</p>\r\n<p> </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binbin'),
(44, 'Giá cà phê hôm nay 11/8: Đồng loạt giảm 100 đồng/kg, giá tiêu giảm 500 đồng/kg', 'Trong sáng nay giá cà phê (11/8) trong đầu tuần tại khu vực Tây Nguyên giảm nhẹ 10 đồng/kg, giá dao động từ 32.200 - 32.700 đồng/kg', '          <h2>Giá cà phê trong nước</h2>\r\n<p>Theo thông tin từ trang<em> tintaynguyen.com</em> giá cà phê nhân xô tại các tỉnh Tây Nguyên đồng loạt giảm 100 đồng/kg, mức giá dao động từ 32.200 – 32.700 đồng/kg.</p>\r\n<p>Trong đó, giá cà phê tại Bảo Lộc, Lâm Hà (Lâm Đồng) giảm 100 đồng/kg xuống 32.200 đồng/kg, Di Linh xuống giá 32.100 đồng/kg.</p>\r\n<div class=\"google-auto-placed ap_container\"><ins class=\"adsbygoogle adsbygoogle-noablate\" data-ad-format=\"auto\" data-ad-client=\"ca-pub-4181597810933246\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_1_expand\"><ins id=\"aswift_1_anchor\"></ins></ins></ins></div>\r\n<p>Giá cà phê tại Ea H\'leo, Buôn Hồ (Đắk Lắk) giảm 100 đồng/kg xuống mức 32.500 và Cư M\'gar xuống mức 32.700 đồng/kg. Tương tự, giá cà phê tại Gia Lai (Pleiku và Ia Grai) giảm nhẹ 100 đồng/kg xuống 32.500 đồng/kg.</p>\r\n<p>Giá cà phê tại Đắk Nông cũng tương tự giảm 100 đồng/kg Gia Nghĩa xuống 32.400 đồng/kg và Đắk R\'lấp có giá 32.500 đồng/kg , Kon Tum giảm xuống 32.400 đồng/kg.</p>\r\n<p><a class=\"link-inline-content\" title=\"Giá cà phê\" href=\"https://vietnambiz.vn/gia-ca-phe.html\">Giá cà phê</a> R1 giao tại cảng TP HCM ở ngưỡng 34.000 đồng/kg.</p>\r\n<div class=\"VCSortableInPreviewMode\">\r\n<table class=\"VCCTable msword-table\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><colgroup><col /><col /></colgroup>\r\n<tbody>\r\n<tr>\r\n<td class=\"xl71\" rowspan=\"2\" height=\"40\"><strong>Tỉnh/huyện (khu vực khảo sát)</strong></td>\r\n<td class=\"xl71\" rowspan=\"2\"><strong>Giá thu mua VNĐ/kg</strong></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl65\" height=\"21\"><strong>LÂM ĐỒNG</strong></td>\r\n<td class=\"xl64\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Lâm Hà  ROBUSTA</td>\r\n<td class=\"xl68\">32.200</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Bảo Lộc  ROBUSTA</td>\r\n<td class=\"xl68\">32.200</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Di Linh  ROBUSTA</td>\r\n<td class=\"xl68\">32.100</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl65\" height=\"22\"><strong>ĐẮK LẮK</strong></td>\r\n<td class=\"xl69\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Cư M\'gar  ROBUSTA</td>\r\n<td class=\"xl68\">32.700</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Ea H\'leo  ROBUSTA</td>\r\n<td class=\"xl68\">32.500</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Buôn Hồ  ROBUSTA</td>\r\n<td class=\"xl68\">32.500</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl65\" height=\"22\"><strong>GIA LAI</strong></td>\r\n<td class=\"xl69\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Chư Prông  ROBUSTA</td>\r\n<td class=\"xl68\">32.400</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Ia Grai  ROBUSTA</td>\r\n<td class=\"xl68\">32.500</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Pleiku  ROBUSTA</td>\r\n<td class=\"xl68\">32.500</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl65\" height=\"22\"><strong>ĐẮK NÔNG</strong></td>\r\n<td class=\"xl69\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Đắk R\'lấp  ROBUSTA</td>\r\n<td class=\"xl68\">32.400</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Gia Nghĩa  ROBUSTA</td>\r\n<td class=\"xl68\">32.500</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl65\" height=\"22\"><strong>KON TUM</strong></td>\r\n<td class=\"xl69\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"22\">— Đắk Hà  ROBUSTA</td>\r\n<td class=\"xl68\">32.400</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl65\" height=\"22\"><strong>HỒ CHÍ MINH</strong></td>\r\n<td class=\"xl69\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\">— R1</td>\r\n<td class=\"xl68\">34.000</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p>Tại thị trường thế giới giảm trở lại, <a class=\"link-inline-content\" title=\"giá cà phê trực tuyến\" href=\"https://vietnambiz.vn/gia-ca-phe-truc-tuyen.html\">giá cà phê trực tuyến</a> robusta tại London giao tháng 9/2020 giảm 0,14 % giao dịch xuống mức 1.391 USD/tấn.</p>\r\n<p>Trong khi tại New York, giá cà phê arabica tháng 9/2020 giảm 2,47% giao dịch xuống mức 112,6 US cent/pound.</p>\r\n<p>Cuối tháng 7, giá cà phê trên thị trường thế giới tăng mạnh do đồng USD suy yếu, các dự báo thị trường lạc quan hơn và thời tiết Brazil không thuận lợi ảnh hưởng đến sự phát triển của cây cà phê.</p>\r\n<p>Theo đó, nhu cầu cà phê robusta của thế giới tăng nhanh do đại dịch Covid-19 khiến xu hướng làm việc tại nhà tăng. Nhu cầu tiêu dùng cà phê được dự báo sẽ tăng khoảng 5,8% trong năm 2020.</p>\r\n<p>Trên sàn giao dịch London, ngày 28/7 giá cà phê robusta giao kỳ hạn tháng 9 và tháng 11/2020 tăng 14,3% và 13,8% so với ngày 30/6, lên mức 1.346 USD/tấn và 1.361 USD/tấn.</p>\r\n<h2>Cập nhật giá hồ tiêu</h2>\r\n<p>Từ trang <em>tintaynguyen.com</em>, giá tiêu nguyên liệu thu mua tại Tây Nguyên và miền Nam quay đầu giảm khi giá ngày hôm trước tăng 2.000 đồng/kg, hôm nay giá tiêu giảm nhẹ trở lại 500 đồng/kg tại Đắk Lắk và Đắk Nông.</p>\r\n<p>Cụ thể, Các tỉnh Đắk Lắk, Đắk Nông giảm 500 đồng/kg đang báo giá 48.000 đồng/kg. Tại Gia Lai đứng yên giá 47.000 đồng/kg.</p>\r\n<p>Tại Bà Rịa Vũng Tàu không thay đổi so với cuối tuần trước có giá 49.500 đồng/kg.</p>\r\n<div class=\"google-auto-placed ap_container\"><ins class=\"adsbygoogle adsbygoogle-noablate\" data-ad-format=\"auto\" data-ad-client=\"ca-pub-4181597810933246\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_3_expand\"><ins id=\"aswift_3_anchor\"></ins></ins></ins></div>\r\n<p>Giá tiêu tại Đồng Nai ổn định có giá 47.000 đồng/kg, hiện 48.000 đồng/kg là giá tiêu tại Bình Phước.</p>\r\n<p><a class=\"link-inline-content\" title=\"Giá tiêu hôm nay\" href=\"https://vietnambiz.vn/gia-tieu-hom-nay.html\">Giá tiêu hôm nay</a> trung bình hiện lên tới 47.000 đồng/kg.</p>\r\n<div class=\"VCSortableInPreviewMode\">\r\n<table class=\"VCCTable msword-table\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><colgroup><col /><col /></colgroup>\r\n<tbody>\r\n<tr>\r\n<td class=\"xl72\" rowspan=\"2\" height=\"41\"><strong>Tỉnh/huyện (khu vực khảo sát)</strong></td>\r\n<td class=\"xl72\" rowspan=\"2\"><strong>Giá thu mua Đơn vị: VNĐ/kg</strong></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\"><strong>ĐẮK LẮK</strong></td>\r\n<td class=\"xl68\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl67\" height=\"22\">— Ea H\'leo</td>\r\n<td class=\"xl69\">48.000</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\"><strong>GIA LAI</strong></td>\r\n<td class=\"xl70\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl67\" height=\"22\">— Chư Sê</td>\r\n<td class=\"xl69\">47.000</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\"><strong>ĐẮK NÔNG</strong></td>\r\n<td class=\"xl70\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl67\" height=\"22\">— Gia Nghĩa</td>\r\n<td class=\"xl69\">48.000</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\"><strong>BÀ RỊA - VŨNG TÀU</strong></td>\r\n<td class=\"xl70\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl67\" height=\"22\">— Giá trung bình</td>\r\n<td class=\"xl69\">49.500</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\"><strong>BÌNH PHƯỚC</strong></td>\r\n<td class=\"xl70\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl67\" height=\"22\">— Giá trung bình</td>\r\n<td class=\"xl69\">48.000</td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl66\" height=\"21\"><strong>ĐỒNG NAI</strong></td>\r\n<td class=\"xl70\"> </td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl67\" height=\"21\">— Giá trung bình</td>\r\n<td class=\"xl69\">47.000</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p>Theo Cục Chế biến và Phát triển Thị trường Nông sản, khối lượng tiêu xuất khẩu tháng 7 năm 2020 ước đạt 20 nghìn tấn với giá trị đạt 49 triệu USD, đưa khối lượng và giá trị xuất khẩu tiêu 7 tháng đầu năm 2020 đạt 187 nghìn tấn và 405 triệu USD, giảm 6,5% về khối lượng và giảm 20,6% về giá trị so với cùng kỳ năm 2019.</p>\r\n<p>Giá tiêu xuất khẩu bình quân 6 tháng năm 2020 đạt 2.134 USD/tấn, giảm 16,4% so với cùng kỳ năm 2019.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binbin'),
(45, 'Dự báo giá heo hơi ngày mai 11/8: Tăng tốc tái đàn, giá heo hơi sẽ còn giảm sâu?', 'Giá heo hơi hôm nay (10/8) tiếp tục điều chỉnh tại cả 3 miền. Công tác tái đàn đang ghi nhận nhiều dấu hiệu tích cực. Trong thời gian tới, giá heo hơi sẽ còn tiếp tục giảm sâu?', '          <p>Tại miền Bắc, thị trường heo hơi hiện đang giao dịch trong khoảng 86.000 - 91.000 đồng/kg. Tại Yên Bái, giá giảm 1.000 đồng/kg xuống còn 89.000 đồng/kg. Một số địa phương vẫn neo cao quanh mốc 90.000 đồng/kg như Lào Cai là 91.000 đồng/kg, Bắc Giang, Nam Định đạt 90.000 đồng/kg trong ngày hôm nay. </p>\r\n<p>Miền Trung, Tây Nguyên điều chỉnh trái chiều trong ngày hôm nay. So với các địa phương khác trên cả nước, đây vẫn là khu vực có giá thu mua thấp nhất, hiện giao dịch trong khoảng 81.000 - 87.000 đồng/kg heo hơi. Tại Quảng Trị, giá tăng 1.000 đồng/kg lên mức 85.000 đồng/kg. Trong khi đó, Ninh Thuận giảm nhẹ 1.000 đồng/kg, thu mua ở mức 86.000 đồng/kg. </p>\r\n<div class=\"vnbcbc-sapo\" data-role=\"sapo\">Giá heo hơi hôm nay (10/8) tiếp tục điều chỉnh tại cả 3 miền. Công tác tái đàn đang ghi nhận nhiều dấu hiệu tích cực. Trong thời gian tới, giá heo hơi sẽ còn tiếp tục giảm sâu?</div>\r\n<div class=\"vnbbc-relate relationnews\">\r\n<ul class=\"vnbbcr-ul\">\r\n<li class=\"vnbbcru-li clearfix\"><span class=\"relate-time\">10-08-2020</span><a class=\"vnbbcrul-link show-popup visit-popup\" title=\"Giá heo hơi hôm nay 10/8: Miền Bắc đứng giá, heo hơi thu mua quanh ngưỡng 88.000 đồng/kg\" href=\"https://vietnambiz.vn/gia-heo-hoi-hom-nay-10-8-mien-bac-dung-gia-heo-hoi-thu-mua-quanh-nguong-88000-dong-kg-20200809233855159.htm\" data-popup-url=\"/gia-heo-hoi-hom-nay-10-8-mien-bac-dung-gia-heo-hoi-thu-mua-quanh-nguong-88000-dong-kg-20200809233855159rf20200810174700513.htm\" data-type=\"0\">Giá heo hơi hôm nay 10/8: Miền Bắc đứng giá, heo hơi thu mua quanh ngưỡng 88.000 đồng/kg </a></li>\r\n<li class=\"vnbbcru-li clearfix\"><span class=\"relate-time\">10-08-2020</span><a class=\"vnbbcrul-link show-popup visit-popup\" title=\"Giá cả thị trường hôm nay 10/8: Đầu tuần, siêu thị lên kệ thịt heo, thịt bò giá ưu đãi\" href=\"https://vietnambiz.vn/gia-ca-thi-truong-hom-nay-10-8-dau-tuan-sieu-thi-len-ke-thit-heo-thit-bo-gia-uu-dai-20200810071934284.htm\" data-popup-url=\"/gia-ca-thi-truong-hom-nay-10-8-dau-tuan-sieu-thi-len-ke-thit-heo-thit-bo-gia-uu-dai-20200810071934284rf20200810174700513.htm\" data-type=\"0\">Giá cả thị trường hôm nay 10/8: Đầu tuần, siêu thị lên kệ thịt heo, thịt bò giá ưu đãi </a></li>\r\n<li class=\"vnbbcru-li clearfix\"><span class=\"relate-time\">10-08-2020</span><a class=\"vnbbcrul-link show-popup visit-popup\" title=\"Giá thịt heo hôm nay 10/8: Lặng sóng đầu tuần\" href=\"https://vietnambiz.vn/gia-thit-heo-hom-nay-10-8-lang-song-dau-tuan-20200810082709423.htm\" data-popup-url=\"/gia-thit-heo-hom-nay-10-8-lang-song-dau-tuan-20200810082709423rf20200810174700513.htm\" data-type=\"0\">Giá thịt heo hôm nay 10/8: Lặng sóng đầu tuần </a></li>\r\n</ul>\r\n</div>\r\n<div data-check-position=\"vnb_detail_body_start\"> </div>\r\n<div id=\"vnb-detail-content\" class=\"vnbcbc-body vceditor-content\" data-role=\"content\">\r\n<h2>Giá heo hơi hôm nay giảm nhẹ ở cả 3 miền</h2>\r\n<p>Tại miền Bắc, thị trường heo hơi hiện đang giao dịch trong khoảng 86.000 - 91.000 đồng/kg. Tại Yên Bái, giá giảm 1.000 đồng/kg xuống còn 89.000 đồng/kg. Một số địa phương vẫn neo cao quanh mốc 90.000 đồng/kg như Lào Cai là 91.000 đồng/kg, Bắc Giang, Nam Định đạt 90.000 đồng/kg trong ngày hôm nay. </p>\r\n<p>Miền Trung, Tây Nguyên điều chỉnh trái chiều trong ngày hôm nay. So với các địa phương khác trên cả nước, đây vẫn là khu vực có giá thu mua thấp nhất, hiện giao dịch trong khoảng 81.000 - 87.000 đồng/kg heo hơi. Tại Quảng Trị, giá tăng 1.000 đồng/kg lên mức 85.000 đồng/kg. Trong khi đó, Ninh Thuận giảm nhẹ 1.000 đồng/kg, thu mua ở mức 86.000 đồng/kg. </p>\r\n<div class=\"google-auto-placed ap_container\"><ins class=\"adsbygoogle adsbygoogle-noablate\" data-ad-format=\"auto\" data-ad-client=\"ca-pub-4181597810933246\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_1_expand\"><ins id=\"aswift_1_anchor\"><iframe id=\"aswift_1\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-4181597810933246&output=html&h=280&adk=1289162305&adf=1620448602&w=610&fwrn=4&fwrnh=100&lmt=1597136248&num_ads=1&rafmt=1&armr=3&sem=mc&pwprc=9027973353&psa=1&guci=2.2.0.0.2.2.0.0&ad_type=text_image&format=610x280&url=https%3A%2F%2Fvietnambiz.vn%2Fdu-bao-gia-heo-hoi-ngay-mai-11-8-tang-toc-tai-dan-gia-heo-hoi-se-con-giam-sau-20200810174700513.htm&flash=0&fwr=0&pra=3&rh=153&rw=610&rpe=1&resp_fmts=3&wgl=1&fa=27&adsid=ChEI8IPJ-QUQxbaql6ia8rvLARIvAMLYKV_Q3NcCDHC-RQIK7j1ZXT6xUDqfGCWESPIqZLOVBZSTjo59sdS5ItKYjRM&dt=1597136248208&bpp=4&bdt=578&idt=-M&shv=r20200805&cbv=r20190131&ptt=9&saldr=aa&abxe=1&cookie=ID%3D9325470264497642%3AT%3D1597135183%3AS%3DALNI_MbMXcaKpYh7D3ErolELGgqEDHk5Gw&prev_fmts=0x0&nras=2&correlator=2050828801855&frm=20&pv=1&ga_vid=1874653616.1597135181&ga_sid=1597136248&ga_hid=1325208267&ga_fc=0&iag=0&icsg=688128&dssz=59&mdo=0&mso=0&u_tz=420&u_his=10&u_java=0&u_h=900&u_w=1600&u_ah=860&u_aw=1600&u_cd=24&u_nplug=3&u_nmime=4&adx=60&ady=878&biw=762&bih=731&scr_x=0&scr_y=0&eid=42530558%2C42530560%2C42530587%2C21066433%2C21066392&oid=3&pvsid=2866881084190829&pem=394&ref=https%3A%2F%2Fvietnambiz.vn%2Fhang-hoa%2Fnong-san.htm&rx=0&eae=0&fc=1408&brdim=775%2C0%2C775%2C0%2C1600%2C0%2C795%2C867%2C779%2C731&vis=1&rsz=%7C%7Cs%7C&abl=NS&fu=9344&bc=31&jar=2020-08-11-08&ifi=1&uci=a!1&btvi=1&fsb=1&xpc=FEwLHkYf8D&p=https%3A//vietnambiz.vn&dtd=17\" name=\"aswift_1\" width=\"610\" height=\"280\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" sandbox=\"allow-forms allow-pointer-lock allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts allow-top-navigation-by-user-activation\" allowfullscreen=\"allowfullscreen\" data-google-container-id=\"a!1\" data-google-query-id=\"CMyWoJbkkusCFQ2hlgodhOYPIQ\" data-load-complete=\"true\" data-mce-fragment=\"1\"></iframe></ins></ins></ins></div>\r\n<p><a class=\"link-inline-content\" title=\"Giá heo hơi\" href=\"https://vietnambiz.vn/gia-heo-hoi.html\"><strong><span style=\"color: #ff9900;\">Giá heo hơi</span></strong></a> hôm nay tại miền Nam giảm nhẹ 1.000 đồng/kg tại Long An và Bến Tre. Hai tỉnh hiện đang giao dịch quanh mức 85.000 - 87.000 đồng/kg. Riêng tại Bạc Liêu, giá giảm sâu xuống còn 81.000 đồng/kg, là một trong những địa phương có giá heo hơi thấp nhất cả nước.</p>\r\n<h2>Tăng tốc tái đàn, giá heo hơi sẽ còn giảm sâu?</h2>\r\n<p>Cục Chăn nuôi (Bộ NN&PTNT) cho biết, đàn heo nái của Việt Nam đạt đỉnh điểm cao nhất năm 2016 đạt xấp xỉ 5 triệu con, đến năm 2019 do ảnh hưởng của <a class=\"link-inline-content\" title=\"dịch tả heo châu Phi\" href=\"https://vietnambiz.vn/dich-ta-heo-chau-phi.html\"><strong><span style=\"color: #ff9900;\">dịch tả heo châu Phi</span></strong></a> nên đàn nái giảm mạnh xuống còn khoảng 2,7 triệu con.</p>\r\n<p>Tuy nhiên, đàn nái cụ kị, ông bà tương đối ổn định qua nhiều năm, năm 2019 mặc dù xảy ra dịch tả heo Châu Phi song đàn nái cụ kị, ông bà cả nước vẫn duy trì xung quanh 109.000 con (chỉ giảm 9%, tương đương 11.000 con). </p>\r\n<p>Dự kiến, đàn nái cụ kị, ông bà năm 2020 sẽ tăng trung bình 0,5%/tháng, tương đương khoảng 6%/năm.</p>\r\n<p>Đến hết tháng 7/2020, theo báo cáo của các địa phương tổng đàn nái cả nước đã đạt xấp xỉ 3 triệu con, tăng khoảng 7% so với ngày 31/12/2019, đạt trên 99% so với kế hoạch của quí II/2020.</p>\r\n<p>Theo ông Nguyễn Văn Trọng, Phó Cục trưởng Cục Chăn nuôi, cùng với đàn nái, đến nay cả nước có trên 65.000 con heo đực giống, đủ để sản xuất tinh và phối giống cho đàn nái gần 3 triệu con.</p>\r\n<p>Mặc dù đàn nái cụ kị, ông bà cơ bản giữ được số lượng xung quanh 109.000 con, song theo ông Trọng đến phải cuối quí III, đầu quí IV/2020 mới bắt đầu cơ bản đủ con giống cho sản xuất, báo <em>Nông nghiệp Việt Nam</em> đưa tin. </p>\r\n<p>Công tác tái đàn đang ghi nhận nhiều dấu hiệu tích cực. Trong thời gian tới, giá heo hơi sẽ còn tiếp tục giảm sâu? </p>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(46, 'Xuất khẩu gạo 7 tháng đầu năm tăng dần 11%', 'Khối lượng xuất khẩu gạo 7 tháng đầu năm 2020 đạt gần 3,9 triệu tấn, trị giá 1,9 tỉ USD, giảm 1,4% về khối lượng nhưng tăng gần 11% về giá trị so với cùng kì năm 2019', '          <p>Cục Chế biến và phát triển thị trường (Bộ NN&PTNT) cho biết tính đến ngày 15/7, khối lượng gạo xuất khẩu đạt 3,7 triệu tấn, trị giá 1,8 tỉ USD tăng 0,6% về lượng và 13,5% về giá trị so với cùng kì năm 2019. </p>\r\n<p>Khối lượng gạo xuất khẩu tháng 7 năm 2020 ước đạt 400.000 tấn với giá trị đạt 194 triệu USD, đưa tổng khối lượng và giá trị xuất khẩu gạo 7 tháng đầu năm 2020 đạt gần 3,9 triệu tấn và 1,9 tỉ USD, giảm 1,4% về khối lượng nhưng tăng gần 11% về giá trị so với cùng kì năm 2019. </p>\r\n<p>Trong 6 tháng đầu năm, Philippineses đứng vị trí thứ nhất về thị trường xuất khẩu gạo của Việt Nam trong 6 tháng đầu năm 2020 với 36,9% thị phần. Xuất khẩu gạo sang thị trường này đạt 1,4 triệu tấn và 634,3 triệu USD, tăng 13,3% về khối lượng và tăng 30,5% về giá trị so với cùng kì năm 2019. </p>\r\n<p>Cũng trong 6 tháng đầu năm 2020, các thị trường có giá trị xuất khẩu gạo tăng mạnh nhất là: Senegal gấp 19,6 lần đạt 41.100 tấn, Indonesia gấp 2,8 lần đạt 45.200 tấn và Trung Quốc tăng gần 89% đạt 457.600 tấn.Ngược lại, thị trường có giá trị xuất khẩu gạo giảm mạnh nhất là Iraq giảm 47,6%. </p>\r\n<p>Giá gạo xuất khẩu bình quân 6 tháng đầu năm 2020 đạt 487,6 USD/tấn, tăng 13% so với cùng kì năm 2019. </p>\r\n<p>Về chủng loại xuất khẩu, trong 6 tháng đầu năm 2020, giá trị xuất khẩu gạo trắng chiếm 39,1% tổng kim ngạch; gạo jasmine và gạo thơm chiếm 37,6%; gạo nếp chiếm 18,7%; gạo japonica và gạo giống Nhật chiếm 4,4%. </p>\r\n<p>Các thị trường xuất khẩu gạo trắng lớn nhất của Việt Nam là Philippines với 423,2 triệu USD, chiếm 57,7%, Malaysia với 95,3 triệu USD, chiếm 13% và Cuba với 42,2 triệu USD, chiếm 5,7%. </p>\r\n<p>Với gạo jasmine và gạo thơm, thị trường xuất khẩu lớn nhất là Philippines với 246,9 triệu USD, chiếm 35%, Ghana với 107,3 triệu USD, chiếm 15,2% và Bờ Biển Ngà với 73,9 triệu USD, chiếm 10,5%. </p>\r\n<p>Với gạo nếp, thị trường xuất khẩu lớn là Trung Quốc với 259,2 triệu USD, chiếm 73,7%, Philippines với 25,5 triệu USD, chiếm 7,2% và Malaysia với 23,6 triệu USD, chiếm 6,7%. </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binngu');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(47, '7,5 tấn nhãn Việt Nam bán tại Úc, giá gần 200.000 đồng/kg', 'Ngày 6/8, 7,5 tấn nhàn từ phía Nam Việt Nam đã được thông qua tại bang Nam Úc.', '          <p>Năm 2019 là thời điểm đánh dấu lô nhãn tươi đầu tiên do doanh nghiệp Việt Nam xuất khẩu thành công khi được Úc mở cửa thị trường. </p>\r\n<p>Tiếp nối thành công này, năm nay, Thương vụ Việt Nam tại Úc tiếp tục tổ chức chương trình \"Nhãn Việt Nam mình!\", như một lời giới thiệu, tâm tình về hương vị Việt Nam với bạn bè Úc và đồng bào ở xa Tổ quốc.</p>\r\n<p>Theo Bộ Công Thương, ngày 6/8, 7,5 tấn nhãn được xuất khẩu sang Úc bởi công ty Rồng Đỏ và nhà nhập khẩu 4wayfresh (Úc). </p>\r\n<p>Hiện nay, tại thị trường miền nam nước Úc, giá nhãn Việt Nam được bán với giá dao động 9 -11 AUD/kg (khoảng 150.000 - 184.000 đồng). Trong khi quả nhãn bán tại Việt Nam giá trung bình 25.000 - 35.000 đồng/kg.</p>\r\n<div class=\"w610 fr\">\r\n<div id=\"vnb-detail-content\" class=\"vnbcbc-body vceditor-content\" data-role=\"content\">\r\n<p>Đặc biệt, Thương vụ Việt Nam cũng đã có một số biện pháp tích cực nhằm tìm kiếm các giải pháp tiêu dùng mới. </p>\r\n<p>Theo đó, Thương vụ cũng đã đề xuất một số doanh nghiệp tại Úc nghiên cứu sử dụng, bổ sung nhãn sấy trong sản phẩm ngũ cốc ăn liền phục vụ điểm tâm hoặc làm bánh trong các tiệm cà phê… </p>\r\n<div class=\"google-auto-placed ap_container\"> </div>\r\n<p>Ngoài ra, Thương vụ vận động doanh nghiệp nhập khẩu nhãn sấy/long nhãn Việt Nam để đưa vào loạt các cửa hàng tại Úc; phối hợp xây dựng thương hiệu.</p>\r\n<p>Trong bối cảnh đại dịch Covid-19 bùng phát, việc xuất khẩu quả tươi gặp nhiều thách thức. Bên cạnh đó, cước vận chuyển hàng không ở mức cao làm nhiều nhà nhập phải cân nhắc.</p>\r\n<p>Một tin đáng mừng, sau chiến dịch xúc tiến sầu riêng đông lạnh vừa qua, một số chủ cửa hàng tại Úc đã phản hồi có sự cạnh tranh để mua được sầu riêng Việt Nam từ nhà nhập khẩu. </p>\r\n<p>Ngoài ra, cuối tháng 6 vừa qua, những lô vải thiều đầu tiên của nước ta xuất sang Nhật Bản đã chính thức được bày lên quầy kệ hơn 250 siêu thị nước này và bán với giá khoảng 500.000 đồng/kg. </p>\r\n<p>Đáng chú ý, chỉ sau vài giờ xuất hiện trên quầy kệ siêu thị, loại quả đặc sản này đã hết sạch. </p>\r\n<p>Trong ngày mở bán đầu tiên tại các hệ thống siêu thị của Nhật Bản (21/6), lô vải thiều tươi 2 tấn của Việt Nam đi bằng được hàng không đã bán hết sạch chỉ trong vòng vài tiếng.</p>\r\n<p>Ngoài ra, Thương vụ vận động doanh nghiệp nhập khẩu nhãn sấy/long nhãn Việt Nam để đưa vào loạt các cửa hàng tại Úc; phối hợp xây dựng thương hiệu.</p>\r\n<p>Trong bối cảnh đại dịch Covid-19 bùng phát, việc xuất khẩu quả tươi gặp nhiều thách thức. Bên cạnh đó, cước vận chuyển hàng không ở mức cao làm nhiều nhà nhập phải cân nhắc.</p>\r\n<p>Một tin đáng mừng, sau chiến dịch xúc tiến sầu riêng đông lạnh vừa qua, một số chủ cửa hàng tại Úc đã phản hồi có sự cạnh tranh để mua được sầu riêng Việt Nam từ nhà nhập khẩu. </p>\r\n<p>Ngoài ra, cuối tháng 6 vừa qua, những lô vải thiều đầu tiên của nước ta xuất sang Nhật Bản đã chính thức được bày lên quầy kệ hơn 250 siêu thị nước này và bán với giá khoảng 500.000 đồng/kg. </p>\r\n<p>Đáng chú ý, chỉ sau vài giờ xuất hiện trên quầy kệ siêu thị, loại quả đặc sản này đã hết sạch. </p>\r\n<p>Trong ngày mở bán đầu tiên tại các hệ thống siêu thị của Nhật Bản (21/6), lô vải thiều tươi 2 tấn của Việt Nam đi bằng được hàng không đã bán hết sạch chỉ trong vòng vài tiếng.</p>\r\n</div>\r\n<div class=\"clearfix\"> </div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(48, 'Thái Lan có nguy cơ rớt xuống vị trí thứ 5 về xuất khẩu gạo thế giới', 'Hiệp hội các nhà xuất khẩu gạo Thái Lan cảnh báo quốc gia Đông Nam Á có rủi ro rơi xuống vị trí thứ 5 về xuất khẩu gạo.', '          <p>Theo ông Charoen Laothammatas, Chủ tịch Hiệp hội các nhà <a class=\"seo-suggest-link\" title=\"xuất khẩu gạo\" href=\"https://vietnambiz.vn/xuat-khau-gao.html\" target=\"_blank\" rel=\"noopener\">xuất khẩu gạo</a>, xuất khẩu gạo Thái Lan được dự kiến sẽ tiếp tục giảm vì chính sách về gạo hiện tại của quốc gia này thiếu sự liên tục và kế hoạch phát triển dài hạn.</p>\r\n<p>Ngoài ra, Thái Lan cũng chỉ có ngân sách nghiên cứu và phát triển (R&D) hạn chế cho ngành gạo.</p>\r\n<p>\"Ưu tiên đầu tiên là ngân sách hạn chế cho R&D về phát triển giống gạo, đặc biệt đối với gạo trắng mềm, vốn đang có nhu cầu cao trên thị trường thế giới\", theo ông Charoen.</p>\r\n<p>Chi phí sản xuất gạo của Thái Lan cao hơn tương đối so với những đối thủ khác, trong khi chi phí hậu cần cao hơn và đồng baht mạnh khiến giá gạo Thái Lan đắt hơn so với những giống của đối thủ.</p>\r\n<p>Các thảm hoạ về hạn hán và lũ lụt luân phiên xuất hiện đã ảnh hưởng tới sản lượng gạo Thái Lan và thiếu dữ liệu chính xác về gạo khiến việc lên các kế hoạch thị trường hiệu quả khó khăn hơn.</p>\r\n<div class=\"w610 fr\">\r\n<div id=\"vnb-detail-content\" class=\"vnbcbc-body vceditor-content\" data-role=\"content\">\r\n<p>Ông Charoen cho hay đối với những chiến lượng dài hạn, các nhà chức trách có liên quan đang bị thúc giục để đẩy nhanh việc xem xét chuỗi cung ứng toàn diện trong ngành gạo, gồm cả phát triển giống gạo, các nhà xay xát gạo, thương thương buôn lúa, các nhà sản xuất và thương mại lúa, các nhà sản xuất gạo đóng gói hay nhà xuất khẩu gạo.</p>\r\n<p>Các nhà sản xuất gạo khác như Trung Quốc đã chi rất nhiều tiền vào R&D để tăng diện tích trồng lúa. Năng suất gạo của Trung Quốc đang ở khoảng 320 kg/ha, trong khi năng suất của Việt Nam là khoảng 153,6 kg/ha và Thái Lan là </p>\r\n<p>Trước đó, hiệp hội đã hạ dự báo xuất khẩu gạo trong năm 2020 xuống 6,5 triệu tấn, mức thấp nhất trong 20 năm, so với dự báo trước đó là 7,5 triệu tấn.</p>\r\n<p>Hiệp hội đưa ra một loạt các yếu tố bất lợi, gồm khủng hoảng COVID-19 - nguyên nhân khiến nhu cầu thế giới suy yếu, đồng baht mạnh khiến gạo Thái trở nên đắt đỏ hơn, và hạn hán kéo dài làm giảm sản lượng.</p>\r\n<p>Theo <em>Bangkok Post</em>, <a class=\"seo-suggest-link\" title=\"giá gạo xuất khẩu\" href=\"https://vietnambiz.vn/gia-gao-xuat-khau.html\" target=\"_blank\" rel=\"noopener\">giá gạo xuất khẩu</a> của Thái lan đang cao hơn so với các đối thủ khoảng 30 - 80 USD/tấn vì đồng baht mạnh, trong khi hạn hán trên diện rộng khiến nguồn cung gạo trong nước giảm 20%, tương đương 5 triệu tấn lúa trong năm nay.</p>\r\n<p>Theo ông Charoen, các nước nhập khẩu gạo quan trọng như Philippines, Malaysia và Trung Quốc ưa chuộng gạo mềm, nhưng Thái Lan vẫn chưa sản xuất các loại gạo này.</p>\r\n<p>Năm 2020, nhu cầu nhập khẩu của Trung Quốc dự kiến sẽ giảm do tồn kho gạo tương đối lớn của nhà tiêu thụ hàng đầu thế giới là 117 triệu tấn so với mức bình thường là 60 - 70 triệu tấn.</p>\r\n<p>Trung Quốc trở thành nhà xuất khẩu ròng trong năm nay, dự kiến sẽ đưa khoảng 3,5 - 4 triệu tấn gạo s</p>\r\n<p>\"Xuất khẩu gạo của Thái Lan trong nửa cuối năm nay dự kiến sẽ chậm lại, do sự bùng phát của đại dịch COVID-19 vào tháng 4 đã kích thích nhu cầu tăng cao và khiến một số quốc gia tăng dự trữ gạo trong nước trong nửa đầu năm\", vị Chủ tịch chia sẻ.</p>\r\n<p>\"Nhóm cố vấn kinh tế mới nên nghĩ đến việc đẩy nhanh tốc độ phát triển khả năng cạnh tranh gạo của Thái Lan và kiềm chế tỉ giá hối đoái ở mức 32,5 - 33 baht đổi một đô USD\".</p>\r\n<figure class=\"VCSortableInPreviewMode noCaption\">\r\n<div><a class=\"sp-img-zoom\" title=\"\" href=\"https://cdn.vietnambiz.vn/171464876016439296/2020/8/8/bang-1596890989601124684225.jpg\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216539293268221952\" class=\"lightbox-content\" title=\"Thái Lan có nguy cơ rợt xuống vị trí thứ 5 về xuất khẩu gạo thế giới  - Ảnh 2.\" src=\"https://cdn.vietnambiz.vn/171464876016439296/2020/8/8/bang-1596890989601124684225.jpg\" alt=\"Thái Lan có nguy cơ rợt xuống vị trí thứ 5 về xuất khẩu gạo thế giới  - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://cdn.vietnambiz.vn/171464876016439296/2020/8/8/bang-1596890989601124684225.jpg\" /></a></div>\r\n</figure>\r\n<p>Luỹ kế nửa đầu năm nay, Thái Lan đã xuất khẩu 3,14 triệu tấn gạo xay xát, giảm 32,7% so với cùng kì năm 2019, với giá trị xuất khẩu giảm 12% xuống 2,2 tỉ USD.</p>\r\n<p>Trong giai đoạn này, Thái Lan đã tụt xuống là nhà xuất khẩu gạo lớn thứ ba thế giới, sau Ấn Độ và Việt Nam, xuất khẩu lần lượt là 4,53 triệu tấn và 4,04 triệu.</p>\r\n</div>\r\n</div>\r\n<figure class=\"VCSortableInPreviewMode\">\r\n<div> </div>\r\n</figure>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binbin'),
(49, 'Cá rô phi có cơ hội hồi phục tại Mỹ sau khi miễn trừ thuế nhập khẩu', 'Trong bối cảnh số người thất nghiệp hiện tăng lên tới 27 triệu người, từ mức chỉ 3 triệu người trước đại dịch covid-19, thịt cá rô phi được xem cung cấp protein lí tưởng cho người dân vì giá rẻ', '          <p>Theo Undercurrentnews, việc giảm 25% thuế quan đối với cá rô phi philê đông lạnh cỡ nhỏ đã khiến cho thị trường nhập khẩu cá rô phi của Mỹ tiếp tục hồi phục trong bối cảnh dịch COVID-19 như một cú hích giúp người tiêu dùng có khả năng tiếp cận với các loại thực phẩm giàu protein với giá cả phải chăng.</p>\r\n<p>Điều đó sẽ xảy ra nếu như bãi trừ thuế được khôi phục sau khi hết hạn vào ngày 7/8.</p>\r\n<p>Nick Ovchinnikov, giám đốc điều hành của công ty nhập khẩu Lotus Seafood có trụ sở tại San Diego, trả lời phỏng vấn Undercurrentnews rằng “Trong bối cảnh số người thất nghiệp hiện tăng lên tới 27 triệu người, từ mức chỉ 3 triệu người trước đại dịch COVID-19, thịt cá rô phi được xem là nguồn cung cấp protein lí tưởng cho người dân vì giá rẻ”.</p>\r\n<p>Trong năm tháng đầu năm 2020, Mỹ đã nhập khẩu 43 nghìn tấn cá rô phi philê đông lạnh trị giá 124 triệu USD, tăng 14% so với cùng kì năm ngoái về mặt sản lượng.</p>\r\n<p>Theo dữ liệu từ Cơ quan Khí quyển và Đại dương Quốc gia (NOAA) do Undercurrentnews tổng hợp, đây là mức tăng sản lượng hàng năm đầu tiên trong khoảng thời gian năm tháng kể từ năm 2015.</p>\r\n<p>Cuối tháng 3, Đại diện Thương mại Mỹ đã công bố trong Công báo Liên bang các sản phẩm được miễn thuế.</p>\r\n<p>Theo đó, “cá rô phi đông lạnh với khối lượng không quá 115g/con” và “thịt cua đỏ tươi được đóng gói và tiệt trùng hoặc đông lạnh trong các container kín khí” là hai mặt hàng được miễn thuế từ tháng 3 cùng với 175 sản phẩm khác.</p>\r\n<p>Trước đó, hồi tháng 9/2018, Mỹ áp thuế 10% cho các mặt hàng của Trung Quốc trị giá 200 tỉ USD và có hiệu lực cho đến hết ngày 7/8/2020. Sau đó mức thuế đã tăng lên 25% vào ngày 9/5/2019.</p>\r\n<div class=\"w610 fr\">\r\n<div id=\"vnb-detail-content\" class=\"vnbcbc-body vceditor-content\" data-role=\"content\">\r\n<p>Ông Jason Carter, trợ lý giám đốc kinh doanh cho biết, tại một hội thảo của Liên minh Nuôi trồng Thủy sản Toàn cầu hồi đầu tháng 6, tập đoàn Baiyang đã phải tuyển thêm công nhân vào các dây chuyền đóng gói để đáp ứng \"nhu cầu khổng lồ\" đối với cá rô phi bán lẻ.</p>\r\n<p>“Dịch vụ thực phẩm đang đi xuống bởi những vấn đề trong cách đóng gói. Tuy nhiên hiện tại chúng tôi phải đóng gói cá rô phi philê vào các túi chân không và cho vào các túi có dán nhãn trọng lượng 1 hoặc 2 pound cho kịp thời gian sản xuất”, Carter cho hay.</p>\r\n<p>“Phần lớn những gì chúng tôi làm là tập trung vào bán lẻ, và chúng tôi đang dần lấy lại nó bằng việc cung cấp dịch vụ cho Trung Quốc”. </p>\r\n<p>Giá cá nguyên liệu dự kiến sẽ không dao động nhiều so với mức thấp hiện tại. “Trong khoảng 2-3 tuần trong cuối tháng 2 và đầu tháng 3, thời điểm các nhà máy Trung Quốc không có đủ công nhân vì dịch COVID-19, điều này đã ảnh hưởng đến giá sản phẩm. Tuy nhiên, trong 3 tháng gần đây giá đã rất ổn định”, ông Ovchinnikov cho bi</p>\r\n<p>“Điều đó có nghĩa là thị trường Mỹ Latin hướng tới cung cấp loại cá rô phi kích cỡ nhỏ và chất lượng thấp hơn trong khi ổn định về mặt sản lượng. Họ không đáp ứng được dịch vụ thực phẩm nhưng lại đáp ứng được về mặt bán lẻ”.</p>\r\n<p>Giá nguyên liệu thô dự kiến sẽ không dao động nhiều so với mức thấp hiện tại.</p>\r\n<figure class=\"VCSortableInPreviewMode\">\r\n<div><a class=\"sp-img-zoom\" title=\"Nguồn: Undercurrentnews\" href=\"https://cdn.vietnambiz.vn/171464876016439296/2020/8/8/unnamed-2-1596880894795665781285.png\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216496961133027328\" class=\"lightbox-content\" title=\"Cá rô phi có cơ hội hồi phục tại Mỹ sau khi miễn trừ thuế nhập khẩu - Ảnh 2.\" src=\"https://cdn.vietnambiz.vn/171464876016439296/2020/8/8/unnamed-2-1596880894795665781285.png\" alt=\"Cá rô phi có cơ hội hồi phục tại Mỹ sau khi miễn trừ thuế nhập khẩu - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://cdn.vietnambiz.vn/171464876016439296/2020/8/8/unnamed-2-1596880894795665781285.png\" /></a></div>\r\n</figure>\r\n</div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'thunthun'),
(50, 'Giá đường thế giới tháng 7 tăng đột biến về cuối tháng.', 'Giá đường thô giao ngay tăng nhẹ so với tháng 6 nhưng cao hơn đáng kể so với 10,96 cent/lb vào tháng 5 và 10,21 cent/lb vào tháng 4', '          <p>Hiệp hội mía đường Việt Nam (VSSA) cho biết theo ghi nhận của tổ chức ISO, giá thị trường thế giới tháng 7 có mức tăng đột biến về cuối tháng, nhưng hầu hết giao dịch ở mức thấp hơn trong giai đoạn giữa tháng. </p>\r\n<p>Đại dịch COVID-19 tiếp tục có tác động đến nhu cầu, mặc dù ở mức độ vừa phải hơn khi các biện pháp giãn cách xã hội được các quốc gia dần dỡ bỏ. Trong khi đó, nhu cầu nhập khẩu mạnh hơn dự kiến, dẫn đến các vấn đề logistic ở Brazil và phí bảo hiểm khu vực cao hơn. </p>\r\n<p>Giá đường thô trong tháng cũng được hỗ trợ bởi sự gia tăng của các vị thế mua ròng của thị trường giao dịch tương lai. Giá đường thô giao ngay (tính theo chỉ số giá ISA) đạt trung bình 12,27 USD cent/lb trong tháng 7, tăng nhẹ so với mức 12,1 cent/lb trong tháng 6 nhưng cao hơn đáng kể so với 10,96 cent/lb vào tháng 5 và 10,21 cent/lb vào tháng 4. </p>\r\n<p>Trong tháng 7, cấu trúc giữa các hợp đồng tương lai ít thay đổi do lượng hàng sẵn có ngay từ Brazil tăng lên đáp ứng kịp thời các nhu cầu mới từ các nhà máy tinh luyện đường. Chỉ số giá đường trắng ISO trung bình đạt 352,96 USD/tấn trong tháng 7, giảm so với mức 368,07/tấn trong tháng 6.</p>\r\n<p>Báo cáo thị trường 7/2020 tổ chức ISO cũng đã đưa thông tin về dự đoán sản lượng sụt giảm đáng kể của vụ mía sắp đến (2020/21) của Thái Lan rằng: “Dự báo của công ty Czarnikow cho vụ thu hoạch mía sắp tới (2020/21), sau đợt khảo sát thực địa, dự kiến chỉ đạt 65 triệu tấn mía, giảm hơn 12% so với mùa trước và bằng một nửa sản lượng vụ 2018/19. </p>\r\n<p>Sản lượng đường dự kiến đạt 7,4 triệu tấn, để lại số dư xuất khẩu dưới 5 triệu tấn, vì không có khả năng giảm thêm lượng dự trữ. Các đánh giá khác lưu ý đến tác động tích cực của những trận mưa gần đây tại các vùng trồng mía của Thái lan nên nâng cao dự đoán sản lượng vụ mía có thể cao hơn, từ 70 đến 75 triệu tấn.”</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(51, 'Giá gạo xuất khẩu Việt Nam vượt Thái Lan', 'Thông tin từ Thương vụ Việt Nam tại Thái Lan, sức cạnh tranh của gạo Thái Lan trên thị trường quốc tế giảm mạnh khiến quốc gia này cân nhắc thay đổi chính sách nhầm tăng cạnh tranh trở lại.', '          <div>Thời gian qua, đồng bath Thái tiếp tục tăng giá so với đồng USD và chi phí sản xuất tăng đã khiến giá gạo Thái Lan neo ở mức cao, dẫn đến sức cạnh tranh giảm. Theo Thương vụ Việt Nam tại Thái Lan ghi nhận, thời điểm đầu tháng 8 năm nay, giá FOB gạo trắng 5% tấm của Thái được giao dịch ở mức 460 USD/tấn, cao hơn 90 USD so với gạo đồng hạng của Ấn Độ trong khi gạo 5% tấm của Việt Nam xuất khẩu giá cao nhất 452 USD/tấn. Chỉ 1 tuần sau đó, cũng loại gạo này, Việt Nam xuất khẩu với giá 468 - 472 USD/tấn, gạo Thái lên 468 USD/tấn. Từ ngày 8.8 đến nay, trên thị trường thế giới, giá gạo Việt Nam dao động ở mức 478 - 482 USD/tấn, tăng 30 USD/tấn so với mức giá đầu tháng. Với mức này, gạo 5% tấm của Việt Nam đang cao hơn 15 USD/tấn so với gạo cùng loại của Thái Lan.</div>\r\n<div> </div>\r\n<div>Trước tình hình này, Thương vụ Việt Nam tại Thái Lan thông tin, Chính phủ Thái Lan đang cân nhắc thay đổi chính sách gạo, trong đó tập trung vào marketing, giảm chi phí sản xuất và nghiên cứu các giống gạo mới. Đây là nội dung trọng tâm của chiến lược sản xuất gạo giai đoạn 2020 - 2024 đã được Bộ Thương mại Thái Lan công bố vừa qua.</div>\r\n<div> </div>\r\n<div>Nhóm các loại gạo sẽ được tập trung phát triển chia làm 3 phân khúc bao gồm loại cao cấp như gạo hom mali, gạo hương; loại đại trà như gạo trắng mềm, gạo trắng cứng, gạo đồ; và loại đặc biệt như gạo nếp và gạo đặc biệt. Theo đó, Bộ Thương mại Thái Lan sẽ làm việc cùng Hiệp hội các nhà xuất khẩu gạo có cuộc họp sớm tìm kiếm các đối tác mới nhằm mở rộng thị trường do gạo Thái Lan hiện vẫn được nhiều đối tác quốc tế quan tâm nhờ chất lượng. Song song đó, trưởng đại diện các cơ quan Thương vụ của Thái Lan tại nước ngoài cũng được khuyến khích quảng bá, tìm kiếm thị trường tiềm năng.</div>\r\n<div> </div>\r\n<div>Trong khi giá gạo xuất khẩu vẫn giữ được “phong độ” trong tuần qua, ngày hôm nay (11.8), giá lúa và gạo trong nước có sự biến động trái chiều.</div>\r\n<div> </div>\r\n<div>Giá lúa các loại tăng nhẹ 100 đồng/kg. Cụ thể, giá lúa tươi IR 504 và OM 5451 tăng nhẹ 100 đồng lên mức 5.800 đồng/kg. Giá nhiều loại lúa cũng tiếp tục neo ở mức cao như lúa Jasmine 6.000 đồng/kg; OM 9577, OM 9582 ở mức 5.850 đồng/kg; đài thơm 8 có giá 6.200 đồng/kg; nàng hoa 9 giá 6.400 đồng/kg; OM 6976 giá 5.800 đồng/kg.</div>\r\n<div> </div>\r\n<div>Với mặt hàng gạo, hôm nay (11.8), giá các loại gạo NL IR 504 và OM 5451 quay đầu giảm nhẹ sau nhiều ngày tăng. Cụ thể, giá gạo NL IR 504 ở mức 9.000 đồng/kg, giảm 100 đồng; gạo TP IR 504 ở mức 10.700 đồng/kg, giảm 100 đồng. Dù vậy, các thương lái vẫn cho rằng, mức giá này hiện còn cao hơn 300 - 500 đồng/kg so với cách đây một tuần. </div>\r\n<div> </div>\r\n<div>Theo Cục Chế biến và Phát triển thị trường nông sản (Bộ NN-PTNT), 7 tháng đầu năm, khối lượng và giá trị xuất khẩu gạo đạt gần 3,9 triệu tấn và 1,9 tỉ USD, giảm 1,4% về lượng nhưng tăng 10,9% về giá trị so với cùng kỳ năm 2019. Hiện nay Philippines đứng thứ nhất về thị trường xuất khẩu gạo của Việt Nam với gần 37% thị phần. Ngoài ra, một số thị trường xuất khẩu gạo có giá trị tăng mạnh nhất là Senegal (gấp 19,6 lần đạt 41.100 tấn), Indonesia (gấp 2,8 lần đạt 45.200 tấn) và Trung Quốc (tăng 88,9% đạt 457.600 tấn). Hiện các doanh nghiệp xuất khẩu gạo Việt Nam tập trung hàng để hoàn tất các hợp đồng đã ký với những đối tác nước ngoài Malaysia, Philippines và Cuba.</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binbin'),
(52, 'Dự báo sâu bệnh tổng hợp trong tuần (10.08.2020)', 'Ở các tỉnh Đông Nam bộ và đồng bằng sông Cửu Long, rầy nâu tuổi 3 - 5, tiếp tục phát triển và gây hại phổ biển ở mức nhẹ - trung bình trên lúa giai đoạn đông - trỗ', '          <div>Sâu đục thân 2 chấm: Trưởng thành lứa 4 tiếp tục vũ hóa và đẻ trứng. Sâu non gây dảnh héo diện hẹp trên các trà lúa, mức độ hại phổ biến từ nhẹ - trung bình.</div>\r\n<div> </div>\r\n<div>+ Bệnh lùn sọc đen: Hiện nay với nguồn bệnh, rầy môi giới có sẵn trên đồng nên nguy cơ bệnh phát sinh, lây lan gây hại trên lúa Mùa rất lớn. Dự kiến trong kỳ tới bệnh phát sinh và gây hại nhẹ, diện hẹp trên lúa giai đoạn cuối đẻ nhánh - làm đòng tại các tỉnh Thái Bình, Ninh Bình, Nam Định, Hải Phòng, Thái Nguyên,... nơi có mật độ và tỷ lệ mẫu rầy lưng trắng dương tính với virus lùn sọc đen phương Nam cao.</div>\r\n<div> </div>\r\n<div>Ngoài ra, bệnh bạc lá- đốm sọc vi khuẩn, bệnh vàng lá di động (vàng lụi),... tiếp tục phát sinh gây hại tăng trên các giống nhiễm; chuột tiếp tục phát sinh gây hại diện hẹp trên lúa gieo thẳng, ruộng cạn nước, ven gò; sâu keo mùa thu tiếp tục hại trên những diện tích lúa chưa được phun trừ; bệnh đạo ôn tiếp tục phát sinh gây hại diện hẹp chủ yếu ở các tỉnh trung du miền núi.</div>\r\n<div> </div>\r\n<div>- Đối với các tỉnh Bắc Trung bộ: </div>\r\n<div> </div>\r\n<div>+ Rầy nâu, rầy lưng trắng: tiếp tục phát sinh và gây hại gia tăng trên các trà lúa. Mức độ hại phổ biến từ nhẹ - trung bình; hại nặng cục bộ trên lúa giai đoạn trỗ - chắc xanh, đỏ đuôi - chín tại các tỉnh như Nghệ An, Quảng Bình, Quảng Trị, Thừa Thiên Huế,...</div>\r\n<div> </div>\r\n<div>+ Chuột: tiếp tục gây hại trên các trà lúa tại các tỉnh trong vùng. Tập trung tại các địa phương có tập quán gieo thẳng như: Nghệ An, Hà Tĩnh, Quảng Bình, Quảng Trị, Thừa Thiên Huế. Hại tăng ở các tỉnh Thanh Hóa, Nghệ An, Hà Tĩnh, hại nặng ở các khu ruộng gần gò bãi, mương máng, trên trà lúa làm đòng - trỗ bông - chín, ở những vùng chưa thực hiện tốt công tác diệt chuột đầu vụ.</div>\r\n<div> </div>\r\n<div>Ngoài ra, Bệnh khô vằn, bạc lá - đốm sọc vi khuẩn,... tiếp tục phát sinh, gây hại tăng về diện tích và mức độ hại trên trên lúa Hè Thu - Mùa tại các tỉnh Nghệ An, Quảng Trị, Thừa Thiên Huế giai đoạn làm đòng - trỗ bông - chín sữa; bệnh lùn sọc đen có khả năng phát sinh gây hại tăng tại Nghệ An và Quảng Bình; bệnh vàng lá di động tiếp tục phát sinh gây hại trên lúa Hè Thu giai đoạn đòng - trỗ tại huyện Cẩm Xuyên, Hà Tĩnh; Vĩnh Phúc, Bắc Giang và Thái Nguyên các đối tượng khác như sâu cuốn lá nhỏ, nhện gié,.. phát sinh và gây hại nhẹ, hại nặng cục bộ.  </div>\r\n<div> </div>\r\n<div>b) Các tỉnh Duyên Hải Nam Trung bộ và Tây Nguyên</div>\r\n<div> </div>\r\n<div>Rầy nâu, rầy lưng trắng, bệnh khô vằn,... tiếp tục phát sinh và gây hại tăng trên lúa Hè Thu giai đoạn đòng trỗ - chắc xanh, mức độ hại phổ biến từ nhẹ - trung bình, hại nặng cục bộ trên lúa Hè Thu chính vụ - muộn tại các tỉnh đồng bằng duyên hải miền Trung như Quảng Nam, Quảng Ngãi, Bình Định, Phú Yên, ...; bệnh đạo ôn tiếp tục phát sinh gia tăng gây hại trên lúa Hè Thu giai đoạn đẻ nhánh - đòng trỗ ở các tỉnh Ninh Thuận, Bình Thuận và Tây Nguyên; sâu cuốn lá nhỏ, sâu đục thân hai chấm tiếp tục phát sinh gây hại tăng trên lúa Hè Thu muộn giai đoạn đẻ nhánh - đòng trỗ tại các tỉnh trong vùng; sâu keo mùa thu tiếp tục phát sinh gây hại với mức độ tự nhẹ - trung bình trên trà lúa đẻ nhánh - làm đòng trỗ tại Bình Thuận, ốc bươu vàng tiếp tục phát sinh gây hại nhẹ trên lúa vụ 3, lúa Mùa giai đoạn mạ - đẻ nhánh; chuột tiếp tục gây hại nhẹ, hại nặng cục bộ trên các trà lúa.</div>\r\n<div> </div>\r\n<div>c) Các tỉnh Đông Nam bộ và đồng bằng sông Cửu Long</div>\r\n<div> </div>\r\n<div>- Rầy nâu: Rầy nâu tuổi 3 -5, tiếp tục phát triển và gây hại phổ biến ở mức nhẹ - trung bình trên lúa giai đoạn đòng - trỗ.</div>\r\n<div> </div>\r\n<div>- Sâu cuốn lá nhỏ: gây hại phổ biến ở mức nhẹ - trung bình trên lúa giai đoạn đẻ nhánh - làm đòng tại các tỉnh trong vùng.</div>\r\n<div class=\"google-auto-placed ap_container\"><ins class=\"adsbygoogle adsbygoogle-noablate\" data-ad-format=\"auto\" data-ad-client=\"ca-pub-4271840947797499\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_9_expand\"><ins id=\"aswift_9_anchor\"><iframe id=\"aswift_9\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-4271840947797499&output=html&h=280&adk=263952776&adf=695548786&w=630&fwrn=4&fwrnh=100&lmt=1597135222&num_ads=1&rafmt=1&armr=3&sem=mc&pwprc=7741829637&psa=1&guci=2.2.0.0.2.2.0.0&ad_type=text_image&format=630x280&url=http%3A%2F%2Fwww.tintucnongnghiep.com%2F2020%2F08%2Fdu-bao-sau-benh-tong-hop-trong-tuan.html&flash=0&fwr=0&pra=3&rh=158&rw=630&rpe=1&resp_fmts=3&wgl=1&fa=27&adsid=ChEI8IPJ-QUQxbaql6ia8rvLARIvAMLYKV_26HVBLYbMYqQdL1v76rGWC38sLXOacp5NqFM1Xk5a1mW-_cYL45meVmA&dt=1597138032411&bpp=2&bdt=1408&idt=2&shv=r20200805&cbv=r20190131&ptt=9&saldr=aa&abxe=1&cookie=ID%3D10517da2dd9ebec1%3AT%3D1597137827%3AS%3DALNI_MbxuDJsrDbnO6ho2YifKvYMjixOWA&prev_fmts=670x280%2C0x0%2C630x280&nras=3&correlator=8131576680455&frm=20&pv=1&ga_vid=2081487366.1597137827&ga_sid=1597138032&ga_hid=1332255193&ga_fc=0&iag=0&icsg=2251808617249664&dssz=67&mdo=0&mso=0&u_tz=420&u_his=10&u_java=0&u_h=900&u_w=1600&u_ah=860&u_aw=1600&u_cd=24&u_nplug=3&u_nmime=4&adx=71&ady=2592&biw=771&bih=731&scr_x=0&scr_y=0&eid=42530557%2C42530559%2C44723322%2C21066898%2C21066392%2C21066807&oid=3&pvsid=1740696583044807&pem=944&wsm=1&ref=http%3A%2F%2Fwww.tintucnongnghiep.com%2F&rx=0&eae=0&fc=384&brdim=775%2C0%2C775%2C0%2C1600%2C0%2C795%2C867%2C779%2C731&vis=1&rsz=%7C%7Cs%7C&abl=NS&fu=8320&bc=23&jar=2020-08-11-09&ifi=9&uci=a!9&btvi=2&fsb=1&xpc=8HsoZMKs97&p=http%3A//www.tintucnongnghiep.com&dtd=50\" name=\"aswift_9\" width=\"630\" height=\"280\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" sandbox=\"allow-forms allow-pointer-lock allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts allow-top-navigation-by-user-activation\" allowfullscreen=\"allowfullscreen\" data-google-container-id=\"a!9\" data-google-query-id=\"CJSHgOnqkusCFUOylgod2tgNkQ\" data-load-complete=\"true\" data-mce-fragment=\"1\"></iframe></ins></ins></ins></div>\r\n<div> </div>\r\n<div>- Bệnh vàng lùn, lùn xoắn lá: Phát sinh gây hại nhẹ diện hẹp trên lúa giai đoạn đẻ nhánh - làm đòng tại tỉnh Hậu Giang và Đồng Tháp.</div>\r\n<div> </div>\r\n<div>Ngoài ra, do ảnh hưởng của thời tiết hiện nay mưa nhiều và thường xuất hiện vào buổi chiều tối, ẩm độ không khí cao kết hợp với gió mạnh tạo điều kiện thuận lợi cho bệnh đạo ôn, khô vằn, bạc lá - đốm sọc vi khuẩn, đen lép hạt... phát triển và gây hại mạnh trên lúa giai đoạn đẻ nhánh - đòng trỗ - chín. Các đối tượng khác xuất hiện và gây hại ở mức nhẹ.</div>\r\n<div> </div>\r\n<div><strong>2. Trên cây trồng khác</strong></div>\r\n<div> </div>\r\n<div>- Trên cây ngô: Sâu keo mùa thu, bệnh đốm lá, bệnh khô vằn tiếp tục phát sinh gây hại.</div>\r\n<div> </div>\r\n<div>- Trên cây rau, màu: sâu tơ, sâu xanh, bọ nhảy, sâu khoang, ruồi đục lá, bệnh phấn trắng, bọ trĩ, nhện đỏ… phát sinh gây hại nhẹ; bệnh xoăn lá virus tiếp tục gây hại trên cây cà chua.</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(53, 'Cà phê Việt Nam \'hút\' người tiêu EU', 'EU hiện là thị trường lớn thứ hai của cà phê của Việt Nam, chiếm trên 42% lượng cà phê Việt xuất khẩu', '          <div>Theo Cục Chế biến và Phát triển thị trường nông sản - Bộ Nông nghiệp và Phát triển nông thôn (NN&PTNT), xuất khẩu cà phê tháng 7 vừa qua ước đạt 120.000 tấn với giá trị đạt 213 triệu USD. Tính chung 7 tháng, giá trị xuất khẩu cà phê đạt 1,06 triệu tấn và 1,8 tỷ USD, giảm 0,1% về khối lượng và giảm 0,6% về giá trị so với cùng kỳ năm 2019.</div>\r\n<div> </div>\r\n<div>Đức, Hoa Kỳ và Italia tiếp tục là 3 thị trường tiêu thụ cà phê lớn nhất của Việt Nam với thị phần lần lượt là 14,3% (đạt 228,1 triệu USD), 9% (đạt 142,9 triệu USD) và 7,8% (đạt 124,5 triệu USD).</div>\r\n<div> </div>\r\n<div>Giá trị xuất khẩu cà phê cũng tăng mạnh tại các thị trường: Ba Lan, Bỉ và Nhật Bản.</div>\r\n<div>\r\n<div>Hiệp hội Cà phê - Ca cao Việt Nam (Vicofa) cho biết, EU hiện là thị trường lớn thứ hai của cà phê của Việt Nam, chiếm trên 42% lượng cà phê Việt xuất khẩu.</div>\r\n<div> </div>\r\n<div>Mặc dù đại dịch Covid-19 đang gây khó khăn cho nền kinh tế, nhưng bánh mì, sữa, cà phê... vẫn là những mặt hàng có mức tiêu thụ cao tại EU. Bên cạnh đó, cùng với “cú hích” của Hiệp định EVFTA, mặt hàng cà phê Việt Nam có thể gia tăng giá trị vào thị trường này trong thời gian tới.</div>\r\n<div> </div>\r\n<div>Cục Chế biến và Phát triển thị trường nông sản đánh giá, trong 2 quý đầu năm 2020, cà phê có xu hướng giảm giá. Thế nhưng, so với thời điểm cuối năm 2019, giá cà phê trong nước đã tăng 500 – 700 đồng/kg sau một thời gian dài rớt giá thê thảm. Dự báo, thời gian tới, nguồn cung cà phê trong nước hạn chế cùng với tình trạng thiếu nước tại khu vực Tây Nguyên khiến sản lượng thấp sẽ thúc đẩy giá cà phê trong nước tăng lên.</div>\r\n<div> </div>\r\n<div>Theo Bộ Nông nghiệp Hoa Kỳ (USDA), Liên minh châu Âu (EU) dự kiến sẽ tăng nhập khẩu thêm 2 triệu bao, đạt mức 49,5 triệu bao cà phê, chiếm gần 45% lượng nhập khẩu cà phê hạt của toàn thế giới; Mỹ - nhà nhập khẩu lớn thứ 2 của thế giới, cũng được dự báo tăng nhu cầu nhập khẩu thêm 2 triệu bao lên mức 27 triệu bao./.</div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(54, 'Bán sang Nhật, rau tía tô có giá 500 - 700 đồng/lá', 'Cuối tháng 6 vừa qua, lô hàng lá tía tô đầu tiên của Việt Nam được xuất khẩu sang Nhật Bản. Theo đơn vị trồng và xuất khẩu, giá bán mỗi chiếc lá tía tô vào nhà hàng Nhật Bản với lên tới 500 - 700 đồng/lá.', '          <p>Cuối tháng 6 vừa qua, lô hàng lá tía tô đầu tiên của Việt Nam được xuất khẩu sang Nhật Bản. Theo đơn vị trồng và xuất khẩu, giá bán mỗi chiếc lá tía tô vào nhà hàng Nhật Bản với giá lên tới 500-700 đồng/lá. Nếu áp dụng đúng theo quy trình sản xuất, 1 ha trồng tía tô sẽ cho thu hoạch khoảng 17-18 triệu lá, doanh thu khoảng 2,5 tỷ đồng.</p>\r\n<div>Để xuất khẩu được lá tía tô trên, công ty đã khảo sát địa điểm, nguồn nước, chất đất, xây nhà kính và áp dụng quy trình sản xuất nghiêm ngặt.</div>\r\n<div> </div>\r\n<div>Hiện nay, mỗi ngày công ty này xuất khẩu sang Nhật vài chục nghìn lá. Phản hồi của khách hàng về lá tía tô của Việt Nam rất tốt. Dự kiến, nhu cầu lá tía tô của Nhật Bản mỗi năm trên 5 tỷ đồng/năm.</div>\r\n<div> </div>\r\n<div>Bên cạnh yêu cầu khắt khe về kỹ thuật khi trồng, khi thu hoạch, các lá tía tô phải thu hoạch đúng ngày tuổi và có kích cỡ đều tăm tắp giống nhau, không được rách, nát.</div>\r\n<div> </div>\r\n<div>Nếu để quá lứa phải hái bỏ các lá đó đi. Yêu cầu về sự đồng nhất từ hình thức đến chất lượng cũng như sự ổn định là rất cao. Đơn vị xuất khẩu phải đặt riêng một chỗ với hãng hàng không để đảm bảo ngày nào cũng có sản phẩm xuất sang Nhật Bản.</div>\r\n<div> </div>\r\n<div>Trang trại có diện tích 11,3 ha, với tổng số vốn khoảng 150 tỷ đồng, triển khai từ tháng 7/2016. Trong đó 8,2 ha diện tích dành để xây dựng 12 nhà kính rộng từ 0,5-1,2 ha, còn lại là các công trình phụ trợ khác, như: nhà xưởng, ao hồ, đường đi, cây xanh…</div>\r\n<div> </div>\r\n<div>Câu chuyện lá tía tô mở đường vào Nhật Bản không còn là chuyện “con cá lá rau”. Nó cho thấy tiềm năng rất lớn cho nông sản Việt Nam vào thị trường Nhật Bản khi chuẩn hoá được theo đúng quy trình và yêu cầu khách hàng. Hiện nay, năng lực sản xuất của Việt Nam khá tốt với 11 nông sản (lúa gạo, cá tra, cà phê, tôm…) có năng suất dẫn đầu thế giới nhưng chất lượng và khâu chế biến, tiêu thụ còn rất yếu.</div>\r\n<div> </div>\r\n<div>Nói cách khác là chúng ta vẫn quen bán cái mình có mà chưa sản xuất được sản phẩm theo yêu cầu của thị trường. Tình trạng mặt hàng nông sản, thực phẩm “nghẽn” đầu ra khiến giá bán giảm mạnh và sau đó là các cuộc giải cứu nối tiếp nhau: Chuối (Đồng Nai), dưa hấu (Quảng Ngãi)… chủ yếu do thiếu công nghệ thu hoạch, bảo quản, chế biến, không liên kết theo chuỗi.</div>\r\n<div> </div>\r\n<div>Theo báo cáo của Bộ NN&PT NT, kim ngạch xuất khẩu sáu tháng đầu năm của ngành nông nghiệp ước đạt 17,1 tỉ đô la Mỹ, tăng hơn 13% so với cùng kỳ năm trước. Trong đó, giá trị xuất khẩu các mặt hàng nông sản chính ước đạt 9,1 tỉ đô la Mỹ, tăng hơn 15%. Những mặt hàng có kim ngạch xuất khẩu tăng mạnh là cà phê, hạt điều, rau quả.</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binbin'),
(55, 'Trồng khổ qua trong chai nhựa bán giá gấp đôi ', 'Với sáng kiến dùng chai nhựa bọc trái khổ qua, ông Nguyễn Văn Phát (huyện Nghĩa Hành, Quảng Ngãi) thu được quả sạch, bán giá gấp đôi thông thường.', '          <p>Với nhiều nông dân sân thượng, trồng rau sạch trong thùng xốp hay bồn nhựa cho năng suất cao khá dễ dàng. Tuy nhiên, gieo trồng các loại cây leo giàn như bầu bí, khổ qua, dưa chuột, mướp,…không mấy người thành công ngay lần đầu. Chị Bùi Thương (30 tuổi- TP.HCM) chia sẻ: “<em>Bí đao, khổ qua hay dưa chuột là loại cây ăn quả chứa nhiều chất dinh dưỡng tốt cho sức khỏe con người. Thường, thời gian trồng và đơm hoa kết trái của chúng dài ngày hơn rau xanh gấp nhiều lần. Vì vậy, người trồng  phải chú trọng tới cách gieo hạt và chăm sóc tỉ mỉ để có thể gặt hái được nhiều trái ngon”.</em></p>\r\n<p>Sau đây, chị Thương sẽ chia sẻ cách trồng và chăm sóc các loại cây leo giàn trên sân thượng bằng kỹ thuật đơn giản, đem lại năng suất cao.</p>\r\n<p><strong>Đất trồng</strong></p>\r\n<p>Chị Thương cho biết, khâu trộn đất rất quan trọng vì nó là “nền tảng” giúp cây sinh trưởng tốt sau này. Tùy vào độ mới cũ của đất, chị Thường có công thức khác nhau.</p>\r\n<p><em><strong>Đất cũ</strong></em></p>\r\n<p>Đây là loại đất đã gieo trồng rau quả trước đó. Thu hoạch xong, đất được trộn với vôi bột, phơi nắng 3 ngày. Cứ 1 lớp đất rải nấm trichoderma sẽ rải lớp rau rác sao cho đầy thùng và ủ 30 ngày. Khi trồng cây, pha thêm phân bò hoai hoặc xác phân cá vào đất.</p>\r\n<p><em><strong>Đất mới</strong></em></p>\r\n<p>Đất được trộn với hỗn hợp trấu hun, sơ dừa, phân bò và phân cá. Đặc biệt, cần phủ lớp đất mỏng chưa xử lý nên bề mặt tránh ruồi muỗi đậu.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(56, 'Làm trang trại trên đất đồi khô cằn', 'Sau nhiều năm ấp ủ, anh Phan Văn Hóa (37 tuổi, ở Xã Phước, H.Đồng Xuân, Phú Yên) đã biến vùng đất đồi khô cằn thành trang trại', '          <div>Thế mà anh Hóa đã biến cả khu đồi hơn 3 ha thành trang trại chăn nuôi kết hợp với trồng trọt. Riêng về chăn nuôi, anh Hóa nuôi vịt trời, gà thả vườn và gà H’Mông của người dân ở vùng Tây Bắc. Anh kể:<em> “Mình thấy mô hình nuôi khá thành công của một người dân ở Bắc Giang. Người này đã thuần hóa vịt trời để nuôi thành vịt thành phẩm, thu nhập bạc tỉ. Và cũng chính sự thú vị của mô hình chăn nuôi này đã thu hút mình”</em>. Tháng 7.2015, anh Hóa ra Đà Nẵng nhờ một người bạn thân, tìm mua 25 con vịt trời giống thuần chủng về nuôi. <em>“Mình vừa nuôi thử nghiệm để tìm hiểu kỹ thuật chăm sóc, quy trình ấp, úm vịt con. Khi đã hiểu rõ, mình quyết định nhân giống số lượng nhiều, mở rộng quy mô chăn nuôi thành trang trại”,</em> anh Hóa nói.</div>\r\n<div> </div>\r\n<div>Trên diện tích 3 ha đất, anh Hóa dành 700 m2 để xây chuồng trại, rào lưới thép B40 chia thành nhiều khu nuôi với chi phí ban đầu hơn 40 triệu đồng. Anh dành 2 khu để nuôi vịt trời. Chuồng vịt trời khá đơn sơ, mỗi khu nuôi anh xây bể xi măng để chứa nước cho vịt tắm rửa, bên trên phủ lưới để vịt khỏi bay ra ngoài. Anh Hóa tâm tình: <em>“Đặc tính của chúng là sống bầy đàn, nếu con nào bay ra ngoài thì nó cũng tìm cách bay vào ở cùng bầy. Chi phí nuôi rất ít so với vịt thường vì mức ăn của vịt trời bằng 1/3 vịt thường. Thức ăn chủ yếu là lúa, cây chuối... mà mình tận dụng xung quanh trang trại”</em>. Hiện trang trại của anh có khoảng 80 con vịt đẻ, hơn 500 vịt trời thương phẩm. Nhờ nắm rõ quy trình ấp nên tỷ lệ trứng nở thành công hơn 85% và hiện nay, mỗi tuần anh Hóa cho ra lò khoảng 50 - 70 vịt con.</div>\r\n<div>\r\n<div>Theo anh Hóa, vịt trời rất dễ nuôi, thích nghi với thời tiết, khí hậu ở miền núi Phú Yên. Do có sức đề kháng tốt, khả năng tăng trọng cao nên chỉ khoảng 4 tháng, vịt đạt trọng lượng bình quân 1,2 kg/con. Thịt vịt trời thơm, ngon nên giá bán hơn vịt thường. <em>“Giá hiện nay mình xuất bán từ 230.000 - 250.000 đồng/con tùy nhu cầu của khách hàng. Khách hàng ở Đà Nẵng họ yêu cầu cung cấp vịt thịt loại 1 kg/con”</em>, anh Hóa nói. Không chỉ cung cấp vịt thịt, anh còn cung cấp cả vịt giống, tư vấn kỹ thuật nuôi...</div>\r\n<div> </div>\r\n<div>Ngoài nuôi vịt trời, anh Hóa còn đầu tư nuôi 700 con gà ta ở địa phương và hơn 50 con gà H’Mông thuần chủng. Gà H’Mông có nguồn gốc từ các tỉnh miền núi vùng Tây Bắc. Đây là giống gà quý hiếm thuộc nhóm gà thịt đen, xương đen, với hàm lượng a xít amin cao, được sử dụng như là thuốc bồi bổ cơ thể, chữa bệnh suy nhược, tim mạch. Bước đầu, anh đã ấp thành công trứng gà giống H’Mông. Loại gà này cũng thích nghi với khí hậu miền núi Phú Yên, đang phát triển tốt, không bệnh tật.</div>\r\n<div> </div>\r\n<div>Không chỉ phát triển chăn nuôi, anh còn đa dạng trang trại bằng những cây trồng khác nhau. <em>“Diện tích còn lại, mình trồng chuối, cam, quýt, bưởi và bơ với mong muốn phủ xanh toàn bộ vùng đất đồi này”</em>, anh Hóa khoe.</div>\r\n<div> </div>\r\n<div>Ông Nguyễn Hồng Tâm, Chủ tịch Hội Nông dân xã Xuân Phước, cho biết: Anh Hóa là người tiên phong thử nghiệm mô hình trang trại chăn nuôi kết hợp với trồng trọt trên vùng đất đồi núi, đặc biệt là nuôi vịt trời. Trang trại của anh Hóa đã mang lại hiệu quả kinh tế và đang mở rộng quy mô lớn hơn.</div>\r\n</div>\r\n<div> </div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(57, 'Quá choáng với tiết lộ của một nông dân trồng dưa chuột', '\"Dưa chuột là loại rau quả mà người trồng phun nhiều thuốc trừ sâu nhất, nếu không phun thì có mất mùa\"', '          <div>Cùng với đậu đũa, dưa chuột là một trong những loại rau của được liệt vào danh sách tồn dư hóa chất độc hại rất lớn.</div>\r\n<div> </div>\r\n<div>Mặc cho các cơ quan chức năng, các phương tiện truyền thông đưa ra khuyến cáo về tính độc hại đối với sức khỏe của việc dùng hóa chất khi nuôi trồng các loại rau củ, quả,… nhưng vì lợi nhuận trước mắt đa số người trồng dưa chuột phớt lờ khuyến cáo này.</div>\r\n<div> </div>\r\n<div>Theo tiết lộ của bác Nhường, một người trồng dưa chuột ở Hải Dương, dưa chuộc là loại cây “ăn” nhiều thuốc trừ sâu và thuốc kích thích nhất. Khi cây bắt đầu đậu quả thì cứ 2-3 ngày lại phải phun thuốc 1 lần, càng gần đến ngày thu hoạch càng phun với mật độ dày đặc. Nước tưới thì có thể tuần tưới 1 lần, nhưng thuốc trừ sâu thì phải “dải” liên tục. Có khi hôm trước phun, hôm sau đã hái quả đem bán.</div>\r\n<div>\r\n<div>Ngoài thuốc trừ sâu thì loại thuốc kích thích tăng trưởng này hầu như người trồng dưa chuột nào cũng dùng. Loại thuốc kích thích này có thể dễ dàng mua được ở các cửa hàng chuyên bán thuốc bảo vệ thực vật. Giá khoảng 9000-1000 đồng/ gói. Người mua hầu hết ưa hình thức nên phải dùng loại này để bắt mắt”, bác Nhường tiết lộ thêm.</div>\r\n<div> </div>\r\n<div>Khi PV thắc mắc: Có những người bán dưa chuột vừa ngồi bán vừa cầm quả dưa không gọt vỏ “vô tư” ăn để chứng thực dưa của mình là dưa sạch, không phun thuốc trừ sâu, hành động của họ liệu có đáng tin, hay chỉ là một cách “câu khách”, và chẳng nhẽ để “câu khách” họ sẵn sàng “đùa” với sức khỏe của mình?. thì nhận được câu trả lời: “Đó là hành động “đánh lận con đen” đấy. </div>\r\n<div> </div>\r\n<div>Trong giỏ của người bán luôn có một gói dưa khác, là loại dưa chuyên trồng để nhà ăn hoặc để “làm hàng”. Loại dưa này họ không sử dụng một loại thuốc bảo vệ thực vật hay thuốc tăng trưởng nào. Là loại rau quả sạch. Nếu quả dưa này được để chung với loại dưa bày bán thì người bán sẽ “đánh dấu” để chọn đúng quả đó cầm ăn. Dưa bán ngoài chợ đa phần là dưa có phun thuốc”, bác Nhường cho biết.</div>\r\n<div> </div>\r\n<div><strong>Thuốc có nguồn gốc Trung Quốc?</strong></div>\r\n<div> </div>\r\n<div>PV đem gói thuốc mà bác Nhường cung cấp đến hỏi một cán bộ làm việc tại Viện Nghiên cứu Rau quả trực thuộc Đại học Nông Nghiệp I thì được vị này cho biết: Đây là thuốc GA3. Tên khoa học của nó là Giberellin. Từ trước đến nay, loại thuốc này có nhiều khả năng trong việc kích thích tăng trưởng cây, lá phát triển nhanh... .</div>\r\n<div> </div>\r\n<div>Hiện một số cửa hàng có bán thuốc GA3. Nhưng theo quy định, thuốc bán phải có thông tin bằng tiếng Việt. Việc bán thuốc toàn bằng tiếng Trung Quốc có thể là nhập lậu, theo đường tiểu ngạch nên khó kiểm soát độ an toàn.</div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'thonthon'),
(58, 'Liên kết trồng cà tím rồi...\"tím mặt\"', 'Nông dân ở Gia Lai thua lỗ nặng khi liên kết với doanh nghiệp trồng cà tím nhưng đơn vị này không làm đúng hợp đồng đã cam kết.', '          <div>Những ngày qua, nhiều nông dân tại huyện Phú Thiện, tỉnh Gia Lai phản ánh bị Công ty CP Thủy sản Bạc Liêu (trụ sở tại huyện Cam Lâm, tỉnh Khánh Hòa) nợ tiền nhân công, thu mua giá cà tím sai hợp đồng.</div>\r\n<div> </div>\r\n<div>Giữa năm 2019, công ty này về tận huyện Phú Thiện tổ chức hội thảo và mời hàng trăm nông dân tại các xã tham gia. Tại đây, công ty đã vẽ ra nhiều viễn cảnh tươi đẹp, giúp nông dân đổi đời khi liên kết với công ty để trồng cà tím các giống Senryo-02, Senryo-03, BL.</div>\r\n<div> </div>\r\n<div>Thấy hấp dẫn, gia đình ông Trần Nguyên Khang (xã Chư A Thai, huyện Phú Thiện) ký hợp đồng liên kết với công ty từ tháng 8-2019 để trồng 2 ha cà tím. Theo hợp đồng, ông Khang sẽ tham gia bằng diện tích đất trồng, quản lý bảo đảm sản xuất từ các khâu theo hướng dẫn kỹ thuật của công ty. Ngoài ra, ông phải mua giống cây từ công ty với giá 2.500 đồng/cây; phải bón phân mua của công ty, định kỳ theo quy định. Nếu bị phát hiện sử dụng thuốc bảo vệ thực vật ngoài danh mục, trái không đủ độ ngọt do không sử dụng phân bón thì công ty sẽ không thu mua, đơn phương chấm dứt hợp đồng.</div>\r\n<div> </div>\r\n<div>Trong hợp đồng, công ty sẽ đầu tư gieo, ghép và giao cây giống, thu mua sản phẩm; thu mua tất cả nguyên liệu đạt chuẩn với giá giống cà tím Senryo-02 và Senryo-03 là 6.000 đồng/kg và giống BL là 14.000 đồng/kg. Những quả không đạt tiêu chuẩn sẽ mua với giá 1.000 đồng (giống Senryo-02, Senryo-03) và 3.000 đồng (giống BL); cung cấp danh mục thuốc và cử nhân viên hướng dẫn, giám sát.</div>\r\n<div>\r\n<div><em>\"Bên A (công ty) đầu tư chi phí công chăm sóc và phân bón trong suốt thời gian trồng và thu hoạch. Sau khi trừ tất cả chi phí đầu tư, số tiền lợi nhuận sẽ được chia theo tỉ lệ mỗi bên 50%\"</em> - hợp đồng ghi rõ.</div>\r\n<div> </div>\r\n<div>Theo ông Khang, công ty kêu ông cứ trả tiền cho nhân công và sẽ thanh toán lại sau. Đến nay, tổng cộng ông đã thuê hơn 300 triệu đồng tiền nhân công nhưng công ty mới trả lại 200 triệu đồng, còn lại vẫn nợ.</div>\r\n<div> </div>\r\n<div>Bên cạnh đó, giá thu mua cũng không đúng theo hợp đồng khi chỉ từ 2.000 - 6.000 đồng/kg. <em>\"Chúng tôi cứ hái cà, công ty cho người vào cân rồi chở đi. Đến khi báo giá thì rất ít cà tím có giá 6.000 đồng/kg mà đa phần chỉ từ 3.000 - 4.000 đồng/kg. Thậm chí, nhiều tấn đã giao cho công ty nhưng không trả tiền\" </em>- ông Khang bức xúc nói và cho biết khi đến địa điểm đại diện của công ty hỏi lý do thì chỉ được nói do quả không đạt tiêu chuẩn.</div>\r\n<div> </div>\r\n<div>Hộ bà Trần Thị Thanh (xã Ya Reng, huyện Phú Thiện) cũng liên kết với công ty trồng 1,1 ha cà tím giống BL. Đến nay, công ty vẫn nợ gia đình bà hơn 50 triệu đồng tiền nhân công. Giá cà cũng không mua đúng cam kết là 14.000 đồng/kg mà chỉ 7.000 - 8.000 đồng/kg.</div>\r\n<div> </div>\r\n<div>Theo bà Thanh, các loại vật tư như phân bón, thuốc trừ sâu lại có giá cao hơn nhiều so với các sản phẩm cùng loại bán trên thị trường. Nếu mua ngoài thị trường bà chỉ tốn khoảng 2 triệu đồng, trong khi mua của công ty tốn 5 triệu đồng mà không hiệu quả, phải mua nhiều lần. Chính vì vậy, dù bà đã bán lượng lớn sản phẩm cho công ty nhưng vẫn chưa đủ tiền mua giống, thuốc trừ sâu và phân bón.</div>\r\n<div> </div>\r\n<div><em>\"Trước đây, tôi trồng lúa mỗi năm cũng thu hoạch được khoảng 100 triệu đồng. Nghe công ty nói trồng cà năng suất cao, thu nhập mỗi năm phải 400 - 500 triệu đồng nên tôi bỏ lúa trồng cà. Ai ngờ giờ không đủ tiền công mà còn lỗ nặng\" </em>- bà Thanh than thở.</div>\r\n<div> </div>\r\n<div>Trên địa bàn huyện Phú Thiện còn rất nhiều hộ dân khác phải ngậm đắng khi liên kết với công ty để trồng cà tím. Chúng tôi đã nhiều lần đến địa điểm liên lạc của công ty tại xã Ia Ke, huyện Phú Thiện để nắm thông tin về vụ việc nhưng được một phụ nữ ở đây cho biết quản lý đi vắng, gọi điện thoại thì không phản hồi.</div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(59, 'Mua phải giống chanh leo ra toàn lá, nhà nông méo mặt', 'Đầu tư vườn chanh leo từ vài chục đến hàng trăm triệu đồng, nhiều nông dân ở Gia Lai, Kon Tum đang khốn khổ, nguy cơ trắng tay vì mua giống kém chất lượng, không rõ nguồn gốc.', '          <p>Nhìn bề ngoài vườn chanh leo cành lá um tùm, nhiều người cứ tưởng vườn cây của gia đình chị Võ Thị Nhung (thôn Kon Gung, xã Đăk Mar, huyện Đăk Hà, Kon Tum) sắp trúng đậm. Nhưng thực tế khảo sát khu vườn thì hoàn toàn khác hẳn, cây chanh leo lá nhiều nhưng trái không bao nhiêu.</p>\r\n<div>Chia sẻ với phóng viên Báo NTNN, chị Nhung nghẹn ngào nói: <em>“Thông qua người quen giới thiệu, tôi mua 1.000 cây giống chanh leo, giá mỗi cây 32.000 đồng về trồng. Ban đầu họ giới thiệu giống này rất tốt, cây giống được trồng tại Việt Nam, còn mắt ghép được nhập từ Đài Loan và cam kết nếu trồng không hiệu quả sẽ bồi thường. Lúc cây bị bệnh, họ có đến vườn kiểm tra 1 lần và bán thùng thuốc giá 400.000 đồng, sau đó thì không liên lạc được nữa”.</em></div>\r\n<div> </div>\r\n<div>Theo chị Nhung, vườn có quả nhưng chỉ lèo tèo, quả nhỏ không đạt yêu cầu. Ngoài vườn chanh leo 1ha tại thôn Kon Gung, gia đình chị cũng vừa nhổ bỏ 8 sào (800 gốc) chanh leo khác. Ước tính chi phí đầu tư cho 2 vườn cây không dưới 300 triệu đồng.</div>\r\n<div class=\"google-auto-placed ap_container\"><ins class=\"adsbygoogle adsbygoogle-noablate\" data-ad-format=\"auto\" data-ad-client=\"ca-pub-4271840947797499\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_8_expand\"><ins id=\"aswift_8_anchor\"><iframe id=\"aswift_8\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-4271840947797499&output=html&h=280&adk=3822689152&adf=3525225461&w=630&fwrn=4&fwrnh=100&lmt=1597135222&num_ads=1&rafmt=1&armr=3&sem=mc&pwprc=7741829637&psa=1&guci=2.2.0.0.2.2.0.0&ad_type=text_image&format=630x280&url=http%3A%2F%2Fwww.tintucnongnghiep.com%2F2020%2F03%2Fmua-phai-giong-chanh-leo-ra-toan-la-nha.html&flash=0&fwr=0&pra=3&rh=158&rw=630&rpe=1&resp_fmts=3&wgl=1&fa=27&adsid=ChEI8IPJ-QUQxbaql6ia8rvLARIvAMLYKV-_PXIFYr_Mvo8c0fwZiyW9TNsN7AUaCKn66X5ZX7ck16edoqzC4sd9a9o&dt=1597139720370&bpp=3&bdt=1604&idt=3&shv=r20200805&cbv=r20190131&ptt=9&saldr=aa&abxe=1&cookie=ID%3D10517da2dd9ebec1%3AT%3D1597137827%3AS%3DALNI_MbxuDJsrDbnO6ho2YifKvYMjixOWA&prev_fmts=670x280%2C0x0&prev_slotnames=7447195561&nras=2&correlator=5301938557933&frm=20&pv=1&ga_vid=2081487366.1597137827&ga_sid=1597139719&ga_hid=1048912726&ga_fc=0&iag=0&icsg=2262803733527424&dssz=61&mdo=0&mso=0&u_tz=420&u_his=7&u_java=0&u_h=900&u_w=1600&u_ah=860&u_aw=1600&u_cd=24&u_nplug=3&u_nmime=4&adx=71&ady=1466&biw=771&bih=724&scr_x=0&scr_y=0&eid=44724796%2C42530557%2C42530559%2C44723322%2C21066897&oid=3&pvsid=3365465532967128&pem=944&ref=http%3A%2F%2Fwww.tintucnongnghiep.com%2F2020%2F08%2Fca-phe-viet-nam-hut-nguoi-tieu-dung-eu.html&rx=0&eae=0&fc=384&brdim=775%2C0%2C775%2C0%2C1600%2C0%2C795%2C860%2C779%2C724&vis=1&rsz=%7C%7Cs%7C&abl=NS&fu=8320&bc=23&jar=2020-08-11-09&ifi=8&uci=a!8&btvi=2&fsb=1&xpc=i7MHrWy3Wi&p=http%3A//www.tintucnongnghiep.com&dtd=55\" name=\"aswift_8\" width=\"630\" height=\"280\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" sandbox=\"allow-forms allow-pointer-lock allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts allow-top-navigation-by-user-activation\" allowfullscreen=\"allowfullscreen\" data-google-container-id=\"a!8\" data-google-query-id=\"CKK99Y3xkusCFUPElgodZ-cDiQ\" data-load-complete=\"true\" data-mce-fragment=\"1\"></iframe></ins></ins></ins></div>\r\n<div> </div>\r\n<div>Tương tự, anh Nguyễn Xuân Đại (tổ dân phố 7, thị trấn Đăk Tô, huyện Đăk Tô, Kon Tum) mua 1.500 cây giống (33.000/cây) của 1 công ty ở Gia Lai và kết quả chịu thiệt hại khá nặng nề. Trên diện tích 2ha, anh Đại đầu tư gần 500 triệu đồng nhưng thu hoạch chỉ được 1/3 so với số tiền bỏ ra.</div>\r\n<div> </div>\r\n<div><em>“Tôi làm vườn cũng bài bản lắm, lúc mới trồng được một thời gian thì cây có biểu hiện bệnh phấn trắng, hoa cười. Sợ cây bị bệnh lây cho những cây khác nên tôi nhổ bỏ 100 gốc mà tình hình vẫn không thay đổi. Do mua không có hợp đồng, cam kết gì nên giờ không biết bắt đền ai”</em>- anh Đại nói.</div>\r\n<div> </div>\r\n<div>Theo nhiều hộ dân, mỗi ha chanh leo phải bỏ ra chi phí rất lớn, từ vài chục đến hàng trăm triệu đồng, nếu không may mua giống dỏm sẽ lâm cảnh nợ nần.</div>\r\n<div> </div>\r\n<div><strong>Mua giống này ra quả giống kia</strong></div>\r\n<div> </div>\r\n<div>Chung tình cảnh với nhiều nông hộ ở Kon Tum, tai tỉnh Gia Lai cũng có nhiều gia đình trồng chanh lâm cảnh khốn khó, nợ nần do mua phải giống chanh leo dởm. Nhiều hộ dân cũng lâm cảnh dở khóc dở cười vì mua giống này nhưng ra quả lại là giống kia. Hệ quả là cây có trái nhưng không có người mua.</div>\r\n<div> </div>\r\n<div>Anh Lê Văn Hùng (thôn Dư Keo, xã Ia Hla, huyện Chư Pưh, Gia Lai) buồn bã nói: <em>“Tôi mua 612 cây chanh leo, giá mỗi cây 30.000 đồng. Họ nói đây là giống chanh leo quả tím, giống thực sinh (cây trồng bằng hạt) phát triển rất mạnh, cứ yên tâm trồng. Đến mùa thu hoạch mới phát hiện giống chanh leo mình đang trồng không phải chanh leo tím mà là chanh leo vàng. Quả này rất khó bán, bán không ai mua, nếu may mắn chỉ bán bằng nửa giá chanh leo tím”.</em></div>\r\n<div> </div>\r\n<div>Theo anh Hùng, anh vẫn còn lưu thùng giấy đã cũ ghi tên công ty cung cấp giống là “Nông Huy Phát”, chủng loại “chanh bông Thái” nhưng không có địa chỉ cụ thể, liên hệ không được.</div>\r\n<div> </div>\r\n<div>Anh Võ Văn Luân (xã Ia Hlốp, huyện Chư Sê) cho biết, thời gian qua giống chanh leo rất loạn. Chưa kể những công ty ma, chỉ cần lên mạng tìm kiếm là có rất nhiều người rao bán giống đủ loại. Vì tin tưởng người ta giới thiệu hay, anh Luân cũng mua thử 400 cây chanh leo về trồng, kết quả là chanh leo dỏm.</div>\r\n<div> </div>\r\n<div><em>“Để giảm thiểu thiệt hại cho dân, tôi nghĩ cơ quan chức năng cần tăng cường kiểm soát giống chặt chẽ, đồng thời khuyến cáo cho dân”</em>- anh Luân đề nghị.</div>\r\n<div> </div>\r\n<div>Tại Gia Lai, năm 2017 Báo NTNN cũng đã có loạt bài “Lừa bán chanh dây dỏm” phản ánh thực trạng trên tại 2 huyện Chư Sê và Chư Pứh gây thiệt hại cho hàng chục hộ dân. Đến nay việc bán giống chanh dây dỏm lại tái diễn, với cam kết bằng lời nói, dân lại tiếp tục mắc lừa.</div>\r\n<div> </div>\r\n<div>Từ đầu năm 2020, nhằm đảm bảo nguồn giống chanh leo an toàn cho người dân trong niên vụ mới, Cục Trồng trọt đã có văn bản đề nghị các địa phương tăng cường quản lý việc sản xuất, kinh doanh giống chanh leo. Và mới đây, ngày 23/3, Sở NNPTNT Gia Lai đã báo cáo về tình hình sản xuất, kinh doanh giống chanh leo trên địa bàn tỉnh.</div>\r\n<div> </div>\r\n<div>Theo đó diện tích trồng chanh leo tại Gia Lai là 2.390ha, hiện chỉ có 7 đơn vị tham gia nhập khẩu giống chanh leo và 2 đơn vị sản xuất, kinh doanh chanh leo (gồm Công ty CP Nafoods và Công ty CP thực phẩm xuất khẩu Đồng Giao). Hiện nay đã sắp bước vào vụ trồng mới (khoảng đầu tháng 4 - 5), Sở NNPTNT sẽ chỉ đạo Chi cục Trồng trọt và Bảo vệ thực vật phối hợp với các cơ quan chức năng, chính quyền địa phương tiến hành thanh kiểm tra theo đúng quy định.</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'binbin'),
(60, 'Lúa không phép, quảng cáo trên trời', 'Dù cơ quan chức năng xác định, giống lúa chưa được cấp phép, nhưng doanh nghiệp vẫn bán tràn lan. Thậm chí còn quảng bá giống lúa này như một thần dược có thể chữa được bệnh tật.', '          <div>Gần đây, trên Youtube và mạng xã hội Facebook, có một giống lúa tên “Thiên Đàng” được quảng bá rầm rộ. Theo nội dung clip, vào tháng 11.2018, ông Bùi Tấn Kiệt (Ba Lực) đã “thỉnh” 2kg lúa giống “Thiên Đàng” từ miền Đông Bắc Thái Lan về Việt Nam gieo trồng. Chỉ trong thời gian ngắn, giống lúa này đã phát triển nhanh đến chóng mặt tại nhiều tỉnh, thành vùng ĐBSCL, và cả miền Trung, miền Bắc với tổng lượng lúa giống 6.000 tấn.</div>\r\n<div> </div>\r\n<div>Theo tìm hiểu của chúng tôi, dù là giống lạ, nhưng lúa \"Thiên Đàng\" đang được nông dân trồng rất nhiều, và lan rộng ra nhiều tỉnh thành như: An Giang, Vĩnh Long, Cần Thơ, Đồng Tháp… Đơn vị bán lúa giống này là Công ty TNHH giống lúa Thiên Đàng được thành lập vào ngày 1.4.2019, có địa chỉ số 24, Quốc lộ 1A (ấp Phú Thạnh, xã Tân Phú Thạnh, huyện Châu Thành A, tỉnh Hậu Giang); do bà Bùi Thoại Anh làm giám đốc. Còn ông Bùi Tấn Kiệt (cha của bà Thoại Anh) là đại diện công ty.</div>\r\n<div> </div>\r\n<div>Vào tháng 6.2019, công ty hợp đồng với hợp tác xã nông nghiệp làng hữu cơ Hiếu Thuận (xã Hiếu Thuận, huyện Mang Thít, tỉnh Vĩnh Long), sản xuất lúa và bán lại cho công ty với thời gian 3 năm (từ 2019 - 2022). Diện tích sản xuất là 20ha. Giá lúa giống công ty bán cho hợp tác xã là 40 ngàn đồng/kg. Lúa thu hoạch sẽ được công ty thu mua lại với giá từ 10 - 12 ngàn đồng/kg.</div>\r\n<div> </div>\r\n<div>Sau khi ký hợp đồng, hợp tác xã Hiếu Thuận mới tá hỏa khi được cơ quan chức năng khuyến cáo ngưng sản xuất vì giống lúa chưa được cấp phép gieo trồng.</div>\r\n<div> </div>\r\n<div><strong>“Thần dược” chưa được cấp phép</strong></div>\r\n<div> </div>\r\n<div>Trước tình trạng lúa “Thiên Đàng” gây náo loạn, cơ quan chức năng tỉnh An Giang đã vào cuộc điều tra, và phát hiện hàng loạt sai phạm.</div>\r\n<div> </div>\r\n<div>Ông Trần Thanh Hiệp - Chánh thanh tra Sở NNPTNT tỉnh An Giang - cho biết: Qua thống kê, diện tích gieo trồng giống lúa “Thiên Đàng” (OM RUMA) trong vụ đông xuân 2019-2020 là 296,5ha. Tuy nhiên, giống lúa này chưa được cơ quan có thẩm quyền cấp phép và công ty đã tự ý thực hiện ký kết với nông dân trong tỉnh để sản xuất, sau đó thu mua lúa nguyên liệu về phơi sấy, đóng bao lúa giống “Thiên Đàng” để tiếp tục quảng bá, cung cấp cho hộ dân nhằm mở rộng diện tích ở các vụ tiếp theo.</div>\r\n<div> </div>\r\n<div>Làm việc với báo chí, ông Bùi Tấn Kiệt - đại diện Công ty Thiên Đàng nói: <em>“Khi có nhiều lúa, đi đâu tôi cũng nói với mọi người, cây lúa này ngày sau một hạt gạo thôi cũng cứu được một mạng người” (?).</em></div>\r\n<div> </div>\r\n<div>Ông Kiệt còn tuyên bố và khẳng định: Gạo này nếu ăn vô sẽ trị hết nhiều loại bệnh. Người nào bị bệnh ăn 1 hạt 5 phút sẽ hết sốt ngay. Còn bị bệnh nặng ăn 5 hạt sẽ khỏi.</div>\r\n<div> </div>\r\n<div>Tuy nhiên, khi được hỏi cơ sở nào chứng minh gạo có thể trị bệnh, lập tức ông Kiệt đập bàn, dùng nhiều lời lẽ hăm dọa và đuổi phóng viên ra ngoài.</div>\r\n<div> </div>\r\n<div>Qua làm việc với cơ quan chức năng tỉnh An Giang, công ty này thừa nhận giống lúa “Thiên Đàng” chưa được cấp phép. Ngoài ra, theo thông tin từ nông dân huyện Tri Tôn và Tịnh Biên cung cấp, trồng lúa giống “Thiên Đàng” bị nhiễm đạo ôn và một số đối tượng dịch hại khác như muỗi hành, sâu lá… không giống như quảng cáo của công ty. </div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 2, 'hiuhiu'),
(61, 'Hòa Bình: Tập huấn nuôi thủy sản an toàn thực phẩm', 'Trung tâm Khuyến nông tỉnh Hòa Bình vừa tổ chức lớp tập huấn \"Kỹ thuật nuôi thủy sản an toàn thực phẩm\" cho 30 học viên', '          <p>Đây là các cán bộ khuyến nông cấp tỉnh, huyện và khuyến nông viên xã từ 4 huyện, thị: Mai Châu, Lương Sơn, Tân Lạc và thành phố Hòa Bình.</p>\r\n<p>Tại lớp tập huấn, các học viên đã được chia sẻ, trao đổi kinh nghiệm về các nội dung: Nhận biết các loài cá; cách chọn vị trí đặt lồng bè; thiết kế và xây dựng lồng bè; cách chọn giống, vận chuyển và thả cá giống; chăm sóc và phòng trị bệnh cho cá; cuối cùng là các tiêu chuẩn nuôi trồng thủy sản an toàn thực phẩm.</p>\r\n<p>Bên cạnh trao đổi về lý thuyết, các học viên còn thảo luận nhóm sôi nổi về các chủ đề: Cách phòng trị bệnh hiệu quả cho cá, cách chăm sóc cá vào mùa mưa lũ, thực hiện an toàn thực phẩm trong nuôi trồng thủy sản và lợi ích của nó.</p>\r\n<p>Sau 1,5 ngày học lý thuyết, các học viên được tham quan mô hình nuôi cá lồng tại xã Thung Nai, huyện Cao Phong, với quy mô 20 lồng. Tại đây, các học viên được giới thiệu, chia sẻ kinh nghiệm trong nuôi trồng thủy sản về cách chọn cá giống, cách chăm sóc cá qua từng giai đoạn và cách phòng trị một số bệnh thường gặp ở cá nước ngọt.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binngu'),
(62, 'Tiền Giang: Giá nghêu thương phẩm tăng mạnh', 'Theo phòng NN-PTNT huyện Gò Công Đông, tỉnh Tiền Giang, những ngày qua, giá nghêu thương phẩm tại vùng chuyển canh đang tăng mạnh, người nuôi có lãi khá.', '          <p>Cụ thể, giá nghêu đang ở mức 25.000 - 27.000 đồng/kg, tùy theo kích cỡ thu hoạch, tăng hơn cùng kỳ năm trước khoảng 5.000 đồng/kg. Với giá này, mỗi ha, giá trị sản xuất đạt từ 370-400 triệu đồng.</p>\r\n<p>Cũng theo Phòng NN-PTNT huyện Gò Công Đông, tỉnh <a href=\"https://nongnghiep.vn/tien-giang-dang-lam-ro-vu-no-sung-lam-hai-thanh-nien-nhap-vien-d268472.html\" target=\"_blank\" rel=\"noopener\">Tiền Giang</a> giá nghêu thương phẩm tăng mạnh khả năng do nhu cầu thị trường lớn nhưng nguồn cung hạn chế. Là huyện nằm ven biển tỉnh Tiền Giang, huyện Gò Công Đông có vùng nuôi nghêu rộng 2.200 ha, tập trung xã Tân Thành án ngữ bờ Bắc vàm Cửa Tiểu trên sông Tiền (tỉnh Tiền Giang).</p>\r\n<p>Mùa khô năm 2020, thiên tai hạn, mặn đã ảnh hưởng nghiêm trọng đến sản xuất và đời sống của huyện biển. Trên con nghêu nuôi, tình hình dịch bệnh khiến một số diện tích nghêu nuôi bị thiệt hại lớn, do vậy, sản lượng thu hoạch không cao.</p>\r\n<p>Từ đầu năm đến nay, toàn vùng nuôi chỉ thu hoạch được khoảng 8.000 tấn <a href=\"https://nongnghiep.vn/nuoi-ngheu-thuong-pham-d130456.html\" target=\"_blank\" rel=\"noopener\">nghêu thương phẩm</a>, giảm hơn 3.000 tấn so với cùng kỳ năm trước. Huyện phấn đấu trong năm 2020 đạt sản lượng nghêu nuôi khoảng 20.000 tấn phục vụ tiêu dùng và chế biến xuất khẩu.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(63, 'Làm giàu nhờ nuôi cá chốt', 'Trong đợt đầu tiên năm 2020, sau tháng 5 nuôi cá chốt, ông thu hoạch được 1,3 tấn cá thương phẩm. Thương lái đến mua tại ao với giá 130.000/kg (nếu bán lẻ, giá 170.000 đ/kg)', '          <p>Ông Nguyễn Văn Hết, 52 tuổi, quê ở ấp Hòa Nhờ B, xã Hòa Tú 2, huyện Mỹ Xuyên, tỉnh <a class=\"autotag\" href=\"http://nongnghiep.vn/soc-trang-tag81561/\" target=\"_blank\" rel=\"follow noopener\">Sóc Trăng</a> là một nông dân sống bằng nghề nuôi tôm sú. Gần đây ông chuyển sang <a class=\"autotag\" href=\"http://nongnghiep.vn/nuoi-ca-chot-tag116045/\" target=\"_blank\" rel=\"follow noopener\">nuôi cá chốt</a>, góp phần đa dạng hóa đối tượng nuôi.</p>\r\n<p>Gia đình ông trước đây có 7 ao tôm nhưng do tiền đầu tư cho con tôm quá cao lại thường hay gặp rủi ro nên sau một thời gian tìm hiểu về nhu cầu của thị trường, ông đã quyết định chuyển sang nuôi cá chốt và ông là người đầu tiên ở Mỹ Xuyên có ý tưởng táo bạo nuôi loài thủy sản mới mẻ này. Nhờ có nhiều năm nuôi trồng thủy sản, ông đã mày mò nghiên cứu cách nuôi, môi trường thích nghi cho sinh sản. Cuối cùng ông tìm được bí quyết làm thế nào để thành công.</p>\r\n<p>Xưa nay, cá chốt là một loài cá quê mùa, thường chỉ dùng cho các bếp nghèo. Đa số người khai thác được loài cá này chỉ dành để làm mắm. Nhưng nay thì khác, cá chốt đã trở thành một loại đặc sản quý hiếm vì đây là một loài cá sống ngoài thiên nhiên, thịt ngon và lành, ai cũng ưa thích. Hiện nay, nhiều nhà hàng, quán ăn nổi tiếng đã chế biến con cá chốt thành những món ngon độc đáo như cá chốt kho tiêu, cá chốt nấu lá me non và cá chốt nấu canh chua cơm mẻ. Do cá ngoài thiên nhiên ngày càng cạn kiệt nên thị trường khan hiếm, giá cả luôn cao hơn một số loài cá khác.</p>\r\n<p>Cá chốt có nhiều loại: cá chốt giấy, các chốt đen, cá chốt vàng, cá chốt trắng…ngon nhất là cá chốt trắng (tên khoa học là mystus planiceps), thuộc họ cá lăng, con dài từ 14 – 30 cm, có 4 đôi râu màu đen, thịt rất ngon, không tanh. Nắm bắt được ưu thế đó, ông Hết đã bỏ ra nhiều thời gian và công sức để nuôi thử nghiệm con cá chốt trắng.</p>\r\n<p>Khởi đầu ông mua cá bố mẹ (đang ngậm trứng) về nuôi nhưng cá không đẻ hoặc đẻ rất ít vì không nắm được đặc tính sinh trưởng của loài cá này. Sau đó, ông nhờ một kỹ sư thủy sản nghiên cứu kích thích cho sinh sản nhân tạo để tạo nguồn cá giống và phục vụ cho người nuôi. Từ thành công đó, ông bắt đầu cải tạo ao nuôi, tiêu diệt các loài cá tạp trước khi thả con giống, tạo nguồn nước ra vào cho ao.  </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(64, 'Phát triển sản xuất cánh đồng lớn tôm-lúa tại Kiên Giang', 'Mục tiêu của dự án là xây dựng cánh đồng lớn đạt tiêu chuẩn về quy mô, ứng dụng kỹ thuật đồng bộ, đạt sản lượng lớn, chất lượng cao.', '          <p>Với mục đích sản xuất theo hướng tập trung, xây dựng vùng nguyên liệu, tạo ra sản lượng lớn sản phẩm có chất lượng cao, tiến tới ký kết các hợp đồng tiêu thụ sản phẩm, Trung tâm Khuyến nông tỉnh Kiên Giang phối hợp với Phòng Nông nghiệp và PTNT huyện Vĩnh Thuận, UBND xã Vĩnh Thuận triển khai Dự án phát triển sản xuất Cánh đồng tôm giảm chi phí theo hướng cánh đồng lớn năm 2019, tại ấp Vĩnh Trinh và ấp Kênh 14, xã Vĩnh Thuận, huyện Vĩnh Thuận.</p>\r\n<p>Có 42 hộ nông dân tham gia với diện tích 90ha, ngày thả giống từ 9/1 đến ngày 30/1/2020, mỗi hộ tham gia được hỗ trợ gần 5 triệu đồng.</p>\r\n<p>Theo đánh giá thì từ đầu vụ nuôi đến nay điều kiện môi trường tương đối thích hợp cho đối tượng tôm nuôi phát triển, nông dân tham gia phần lớn có nhiều kinh nghiệm trong sản xuất, tôm thả nuôi được chọn từ các thương hiệu có uy tín.</p>\r\n<p>Cỡ tôm thu hoạch trung bình trong mô hình đạt 34,5 con/kg, dao động từ 30 - 40 con/kg. Tỷ lệ sống trung bình đạt so với yêu cầu mô hình và cao hơn so với ngoài mô hình. Năng suất tôm nuôi trong mô hình đạt trung bình 476kg/ha, dao động từ 400 -520 kg/ha. Năng suất nuôi vượt so với yêu cầu mục tiêu của mô hình. Giá bán tôm sú trung bình là 139.000 đồng/kg.</p>\r\n<p>Tuy nhiên, năm 2020 do ảnh hưởng của dịch bệnh Covid-19 nên giá tôm thương phẩm thấp hơn so với những năm trước nên hiệu quả kinh tế chưa cao.</p>\r\n<p>Bên cạnh đó, tình hình thời tiết nắng nóng, làm môi trường ao nuôi tôm biến động, gây sốc cho tôm nuôi ảnh hưởng một số hộ trong mô hình.</p>\r\n<p>Tổng chi cho 1ha nuôi tôm trung bình là hơn 25 triệu đồng đồng. Tổng thu vụ nuôi tôm trung bình là hơn 66 triệu đồng/ha. Lợi nhuận mỗi ha vụ nuôi tôm đạt trung bình gần 41 triệu đồng.</p>\r\n<div class=\"adv\">\r\n<div class=\"e\"> </div>\r\n</div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(65, 'Thanh niên khởi nghiệp nuôi cá chạch lấu thành tỷ phú', 'Anh Trần Thanh Hùng (36 tuổi, quê xã Đông Phước, huyện Châu Thành, Hậu Giang) khởi nghiệp thành công với con cá cảnh và cá chạch lấu.', '          <p>Trần Thanh Hùng có bằng Kỹ sư Thủy sản. Lúc khởi nghiệp anh nuôi cá cảnh. Sau đó chuyển sang nuôi <a href=\"https://nongnghiep.vn/nuoi-ca-chach-lau-long-be-d102388.html\" target=\"_blank\" rel=\"noopener\">cá chạch lấu</a>, chủ yếu là sản xuất con giống. Ba năm nay, cơ sở anh chuyên ép, ươm và cung cấp cá bột và cá giống đủ kích cỡ cho người nuôi trên khắp cả nước.</p>\r\n<p>Với diện tích ban đầu là 1.000 m2 mặt nước, hiện nay tăng lên 2 ha gồm 40 ao (bể đất lót bạt) cá thịt và nhiều bể xi măng ươm cá giống. Anh đã xây dựng và hoàn chỉnh hệ thống bể nuôi theo đúng quy trình kỹ thuật. Anh chia sẻ, thời gian đầu gặp nhiều khó khăn nhất định vì thiếu vốn, thiếu kinh nghiệm, nhưng bằng ý chí quyết tâm, ham học hỏi, từng bước khắc phục vươn lên nên đã vượt qua những chặng đường gian nan, cuối cùng anh đã thành công mỹ mãn.</p>\r\n<p>Là một thanh niên có trình độ khoa học kỹ thuật lại có tầm nhìn xa nên trong quá trình lập nghiệp, anh luôn theo dõi thị trường để sản xuất kinh doanh theo nhu cầu của khách hàng. Theo anh, kỹ thuật nuôi cá chạch lấu cho đẻ rất khó, đòi hỏi nhiều công phu tỉ mỉ, nhất là giai đoạn thuần dưỡng cá bố mẹ thành thục trước khi sử dụng thuốc kích thích vuốt lấy trứng. Trứng cá được đặt trên một tấm vỉ lưới trong bể xi măng và sục khí liên tục 24/24 giờ. Sau  6 ngày trứng mới nở thành cá bột.</p>\r\n<p>Tuy vất vả nhưng với lòng tự tin, anh đã kiên trì học hỏi và thử nghiệm nhiều lần. Để đạt năng suất chất lượng cao, lúc đầu anh phải lên tận vùng biên giới Campuchia chọn cá bố mẹ về làm con giống. Qua nột thời gian trải nghiệm, hiện nay trong ao anh đã dự trữ gần 2 tấn cá bố mẹ, đủ cung cấp con giống cho khách hàng ở các tỉnh ĐBSCL, một số tỉnh miền Trung và miền Bắc. Cá chạch lấu hàng năm đẻ từ tháng 3 đến tháng 9 âm lịch. Vào thời điểm nầy, mỗi tháng bình quân anh sản xuất hàng trăm ngàn con cá bột và cá giống, giá bán mỗi con từ 7.000đ – 15.000đ/con tùy kích cỡ.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(66, 'Vĩnh Phúc: Đồng bộ nhiều giải pháp hỗ trợ cá gióng mới', 'Năm 2020, thực hiện chương trình hỗ trợ nuôi cá giống mới trên toàn tỉnh', '          <p>Thời gian qua, Trung tâm Giống nông nghiệp Vĩnh Phúc đã triển khai đồng bộ nhiều giải pháp hỗ trợ hiệu quả người dân tham gia chương trình. Cụ thể, Trung tâm đã tổ chức khảo sát và lựa chọn được 17 hộ gia đình trên địa bàn huyện Lập Thạch tham gia; số lượng giống hỗ trợ gần 530.000 con, tương đương 17.600 con/ha.</p>\r\n<p>Hộ gia đình được chọn đều có kinh nghiệm, nguồn vốn đối ứng mua giống, đầu tư thức ăn và các điều kiện khác. Diện tích ao nuôi đạt từ 0,5 ha trở lên, ao thoáng gió, độ sâu từ 1,5 - 2,5 m, chủ động về nguồn nước. Ao phải tiến hành cải tạo đúng quy trình kỹ thuật trước khi thả nuôi. Giống cá được Trung tâm lựa chọn từ các đơn vị cung ứng uy tín, bảo đảm về chất lượng và khả năng sinh trưởng, phát triển sau khi thả nuôi.</p>\r\n<p>Quá trình nuôi , các hộ thường xuyên được cán bộ Trung tâm hướng dẫn các biện pháp kỹ thuật chăm sóc, nuôi dưỡng, quản lý ao nuôi và phòng trừ dịch bệnh… Nhờ vậy, đàn cá trong mô hình sinh trưởng phát triển khỏe mạnh. Tỷ lệ cá sống đạt hơn 95%. Trọng lượng cá hiện đạt hơn 100 g/con, tăng từ 80 - 90 g/con sau hơn 1 tháng thả nuôi</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin'),
(67, '\"Bùng nổ\" nuôi tôm công nghệ cao', 'Những năm gần đây, nghề NTTS, nhất là nuôi TTCT đã mang lại nguồn thu nhập cao cho nhiều địa phương', '          <p>Bên cạnh những “vựa tôm” của cả nước như Bạc Liêu, Cà Mau…, nhiều địa phương trong cả nước đã mạnh dạn đầu tư công nghệ cao trong nuôi tôm.</p>\r\n<p>Điển hình như tại huyện Nghi Lộc, tỉnh Nghệ An; người nuôi nơi đây đã chú trọng áp dụng công nghệ vào sản xuất để tăng năng suất và giảm dịch bệnh; từng bước thích ứng với sự biến đổi của khí hậu, kiểm soát được dịch bệnh, tỷ lệ thành công cao, đạt lợi nhuận hàng trăm triệu đồng/ha/vụ. Nuôi tôm theo quy trình công nghệ cao, an toàn, tức là con giống phải sạch, quá trình nuôi sạch, lựa chọn thức ăn cho tôm không sử dụng kháng sinh, không sử dụng chất cấm, ứng dụng công nghệ hiện đại, thu hoạch và chế biến sản phẩm cũng phải bảo đảm tiêu chuẩn an toàn. Cùng với đó, tôm nuôi được quây kín trong các nhà bạt, đảm bảo an toàn về dịch bệnh, hạn chế tác động của thời tiết, nhất là những đợt rét đậm, rét hại và những đợt nắng nóng kéo dài. Và ưu điểm vượt trội so với nuôi tôm ở ngoài trời là khi thời tiết thay đổi bất lợi, mưa lớn, ao nuôi không bị phân tầng nước.</p>\r\n<p>Gia đình anh Nguyễn Viết Thắng ở xóm Bắc Sơn xã Nghi Yên đã đầu tư hơn 4 tỷ đồng cải tạo mặt bằng, đào ao, mua sắm các trang thiết bị thực hiện mô hình nuôi TTCT thâm canh trong bể tròn. Mỗi năm nuôi 2 vụ, bình quân thả trên 50 vạn con giống/vụ, tỷ lệ tôm sống 90 - 95%. Sau 4 tháng áp dụng kỹ thuật công nghệ cao, anh Thắng thu 12 - 15 tấn tôm, mang về gần 3 tỷ đồng/vụ. Anh Thắng cho biết: “Đây là mô hình mới được áp dụng ở miền Bắc, mặc dù địa hình ở đây tương đối khó khăn, nhưng trong quá trình cải tạo thì gia đình cũng đã nghiên cứu và đưa quy trình nuôi bể tròn áp dụng công nghệ cao vào nuôi để phù hợp với diện tích cũng như nâng cao chất lượng sản phẩm”.</p>\r\n<p> </p>\r\n<p><strong>… vào Nam</strong></p>\r\n<p>Đó là mô hình nuôi tôm bằng công nghệ tuần hoàn nước (RAS) lần đầu tiên được HTX Nông nghiệp Quyết Thắng (P. Long Hương, TP Bà Rịa, tỉnh Bà Rịa - Vũng Tàu) áp dụng tại địa phương. Đây là công nghệ được nghiên cứu và ứng dụng ở Na Uy, Hà Lan, Thái Lan, Trung Quốc và được triển khai thành công tại một số tỉnh Tây Nam bộ… để phục vụ các trại sản xuất giống và nuôi tôm thâm canh. Đáng lưu ý, nguồn nước thải từ nuôi tôm sẽ được chảy tuần hoàn qua các bể lắng, lọc để xử lý đạt chuẩn để đưa trở lại ao nuôi tái sử dụng. Vì chất lượng nguồn nước, các thông số kỹ thuật như độ mặn, tỷ lệ ôxy trong nước được kiểm soát nên công nghệ này giúp giảm thiểu hao hụt con giống, năng suất gấp nhiều lần nuôi bình thường, chất lượng bảo đảm và không gây ô nhiễm môi trường nguồn nước.</p>\r\n<p>Bên cạnh đó, việc nuôi tôm trong nhà màng cũng giảm nguy cơ dịch bệnh do ảnh hưởng từ điều kiện thời tiết bên ngoài. Tổng số vốn đầu tư để nuôi tôm bằng công nghệ RAS trên diện tích ao 2.000 m<sup>2</sup> khoảng 5 tỷ đồng. Đến nay, sau 1 năm thực hiện, mô hình đạt được những kết quả khả quan, năng suất đạt 15 tấn/vụ.</p>\r\n<p>Còn tại huyện Bình Đại, Bến Tre, đến cuối năm 2019, toàn huyện có 130 hộ nuôi với 400 ha nuôi TTCT hai giai đoạn, tập trung ở các xã Đại Hòa Lộc, Thạnh Phước, Bình Thắng, Thừa Đức, Thới Thuận.</p>\r\n<p>Anh Trần Văn Bắc, thành viên Hội quán nuôi tôm hai giai đoạn ở huyện Bình Đại tâm đắc mô hình nuôi tôm công nghệ cao. Anh cho rằng, trước tình hình xâm nhập mặn ngày càng diễn biến khó lường, thì nuôi tôm 2, 3 giai đoạn cho hiệu quả cao đến hơn 80%. Người nuôi không sợ bị ảnh hưởng của xâm nhập mặn và nắng gắt. “Khi tham gia Hội quán, chúng tôi thường xuyên cập nhật tình hình giá cả thị trường, chia sẻ kinh nghiệm xử lý khi tôm bệnh, thông tin thương lái có uy tín… Vì hiện đa số người nuôi ở huyện chỉ bán qua thương lái chứ chưa ký được hợp đồng ổn định với đơn vị nào”, anh Trần Văn Bắc cho biết.</p>\r\n<p>Anh Bắc cũng đang phát triển nuôi tôm công nghệ cao lên 3, 4 giai đoạn, do tôm size lớn 25 con/kg có giá 130.000 đồng/kg; năng suất cao, với 100.000 tôm giống khi nuôi 3, 4 giai đoạn, tôm đạt 20 con/kg, sản lượng đạt trên 4 tấn.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin'),
(68, 'Nuôi cua xanh ghép tôm sú', 'Nuôi cua kết hợp với tôm sú là mô hình hiệu quả và an toàn', '          <p><strong>Chọn ao nuôi</strong></p>\r\n<p><strong><em>Điều kiện</em></strong></p>\r\n<p>Ao nuôi thích hợp để nuôi ghép cua và tôm sú có diện tích từ 0,3 - 1 ha, đáy ao là bùn cát có độ lún 10 - 15 cm, độ mặn thích hợp từ 15 - 25‰. Ao nên có 1 - 2 cống cấp và thoát nước. Độ sâu nước thích hợp từ 0,8 - 1,5 m. Các chỉ tiêu thủy hóa: pH = 8,0 - 8,5, nhiệt độ nước 26 - 30<sup>o</sup>C, NH<sub>3</sub>-N, NO<sub>2</sub>, H<sub>2</sub>S < 0,02 mg/lít, ôxy hòa tan 6 mg/lít.</p>\r\n<p><strong><em>Vị trí</em></strong></p>\r\n<p>Chọn ao nuôi ở vùng trung triều hoặc hạ triều để thuận lợi cho việc cấp và thoát nước, giao thông đi lại, cung cấp thức ăn và tiêu thụ sản phẩm dễ dàng.</p>\r\n<p><em><strong>Chuẩn bị</strong></em></p>\r\n<p>Với những ao nuôi thuận lợi nên tháo cạn nước để phơi đáy ao, bón vôi với để diệt tạp chất còn tồn đọng của vụ trước, hạn chế mầm bệnh cho vụ tới. Tùy theo độ pH của đất và nước, nên bón vôi với liều lượng 1.000 - 1.500 kg/ha. Với ao nuôi thường khó tháo cạn nước để phơi đáy ao diệt tạp, cần tiến hành tẩy dọn ao bằng cách dùng saponin với liều lượng 10 - 15 g/m<sup>3</sup>, thời gian xử lý 24 - 36 giờ. Đồng thời, vẫn bón vôi để khử chua và diệt tạp trong đáy ao.</p>\r\n<p>Để cua không bò ra ngoài, dùng lưới có mắt lưới khoảng 1 cm với kích thước 0,5x0,7 m chắn quanh bờ ao, đặt lưới chắn nghiêng vào trong lòng ao góc 45<sup>0</sup> nhằm đảm bảo cua trong ao không thể bò qua được. Tại các cống cấp và thoát nước, dùng lưới có kích thước mắt 2 mm để chắn vào miệng cống nhằm bảo vệ không cho địch hại xâm nhập vào ao nuôi.</p>\r\n<p>Ao nuôi nên duy trì mực nước sâu 0,8 - 1 m trong 2 tháng đầu sau đó tăng dần nước đạt 1 - 1,4 m vào các tháng tiếp theo.</p>\r\n<p> </p>\r\n<p><strong>Thả giống</strong></p>\r\n<p>Trước đây, nguồn giống cua xanh được khai thác hoàn toàn tự nhiên, tuy nhiên, gần đây nhiều công ty đã sản xuất thành công cua xanh giống để phân phối trên thị trường. So với nguồn cua giống tự nhiên, nguồn giống nhân tạo có nhiều ưu điểm vượt trội, kích cỡ đồng đều, sạch bệnh, sức đề kháng cao và tốc độ tăng trưởng nhanh.  Nên chọn mua giống tôm, cua từ các trại sản xuất giống có uy tín.</p>\r\n<p>Cỡ con giống: Cua giống có độ rộng vỏ đầu ngực (mai cua) đạt 17 - 20 mm, trọng lượng 0,8 - 1 g/con; tôm sú giống nên chọn cỡ PL15 trở lên. Mật độ thả nuôi phù hợp: Thả cua với mật độ 1 con/2 m<sup>2</sup> và mật độ tôm sú là 10 con/m<sup>2</sup>. Thời điểm thả giống: Thả cua giống trước 45 ngày, sau đó mới thả tôm sú giống.</p>\r\n<p> </p>\r\n<p><strong>Quá trình chăm sóc</strong></p>\r\n<p><strong><em>Cho ăn</em></strong></p>\r\n<p>Thức ăn phù hợp cho cả cua và tôm sú là cá tạp, nhuyễn thể, giáp xác kích thước nhỏ và thức ăn tổng hợp dạng viên. Tỷ lệ trộn thức ăn: cá tạp 50 - 60%, nhuyển thể 30 - 40%, giáp xác 10%. Để đảm bảo tôm, cua phát triển tốt cần bổ sung thức ăn tổng hợp dạng viên. Lượng thức ăn cho ăn hàng ngày phụ thuộc vào kích cỡ của cua và tôm. Lượng tăng dần trong khi nuôi nhưng tỷ lệ cho ăn so với trọng lượng của cua giảm dần, thường cho ăn 3 - 10% trọng lượng cua.</p>\r\n<p>Thời gian cho ăn: Dựa vào tập tính của cua và tôm hoạt động tìm mồi vào sáng sớm và chiều tối nên cho cua và tôm ăn 2 lần/ngày vào lúc 7 - 9 giờ và 17 - 18 giờ. Nếu thức ăn dư thừa cần vớt khỏi ao nuôi sau 10 giờ tính từ lúc cho ăn. Nên cho cua ăn trên sàng, được bố trí đều trong ao nuôi, khoảng cách giữa các sàng là 4 - 7 m</p>\r\n<p><strong><em>Chế độ nước</em></strong></p>\r\n<p>Hàng ngày, kiểm tra các yếu tố môi trường như pH, độ sâu, độ mặn… Trong mỗi kỳ con nước, liên tục khoảng 3 - 5 ngày, tiến hành thay 1/3 - 2/3 nước cũ và cấp nước mới vào ao nuôi.</p>\r\n<p> </p>\r\n<p><strong>Thu hoạch</strong></p>\r\n<p>Đối với cua: Sau 4 tháng nuôi, tiến hành thu hoạch cua đực đạt cỡ thương phẩm khoảng 250 g/con trở lên, cua chắc thịt hoặc đã đầy gạch (cua cái) để giảm dần mật độ. Thu bằng cách cho thức ăn vào sàng để cua vào ăn, sau đó kéo sàng lên để bắt những con đạt tiêu chuẩn. Sau khi thu hoạch, buộc cua bằng dây đay hoặc dây chuối…; tùy theo thời gian bảo quản mà có thể buộc tất cả các chân bò (đôi càng) và chân bơi hoặc cũng có thể chỉ trói đôi chân bò; để cua trong bóng mát, giữ độ ẩm.</p>\r\n<p>Đối với tôm: Sau 2,5 - 3 tháng nuôi, dùng đăng kéo lưới thu hoạch tôm đạt cỡ thương phẩm. Có thể dẫn dụ tôm vào bằng cách đặt một cây đèn dầu trong đó, hom bắt tôm để dẫn dụ tôm vào. Sau khi thu hoạch, nên bảo quản sống bằng cách sục ôxy để tôm bán có giá trị cao hơn.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(69, 'Giải pháp phòng bệnh cho cá rô phi mùa nắng nóng', 'Rô phi là loài cá ăn tạp, dễ nuôi, thịt thơm ngon, có giá trị kinh tế thị trường ưa chuộng', '          <p><strong>Mầm bệnh</strong></p>\r\n<p>Tác nhân gây bệnh cho cá là liên cầu khuẩn gram dương, gồm 2 loài vi khuẩn gây bệnh chính là Streptococcus iniae và Streptococcus agalactiae. Bệnh đã và đang là mối đe dọa cho nhiều loài cá ở các môi trường sống khác nhau như cá biển, cá nước lợ và cá nước ngọt. Tuy nhiên, mối đe dọa chính của bệnh này trong những năm gần đây là cá rô phi, điêu hồng nuôi mật độ cao trong ao/lồng.</p>\r\n<p> </p>\r\n<p><strong>Dấu hiệu lâm sàng</strong></p>\r\n<p>Cá bị bệnh do Streptococcus sp. thể hiện một số triệu chứng điển hình: Ban đầu có hiện tượng cá kém ăn, bỏ ăn, bơi lờ đờ ở tầng mặt hoặc sát thành lồng. Những cá bệnh nặng có hiện tượng bơi xoay vòng (bơi không định hướng do vi khuẩn tấn công vào não bộ), mắt lồi và mờ đục một hoặc hai bên (bệnh nặng có thể dẫn đến hiện tượng vỡ mắt), đen thân, có hiện tượng xuất huyết trên da, xương nắp mang và gốc vây (hình 1). Mặc dù triệu chứng điển hình nhất của bệnh là hiện tượng mắt lồi và xuất huyết, trong một số trường hợp cá nhiễm bệnh do Streptococcus sp nhưng không biểu hiện rõ triệu chứng trước khi chết. Cá bệnh chỉ xuất hiện hiện tượng đuôi bị ăn mòn, mang nhợt nhạt có hiện tượng xuất huyết dạng điểm, trong trường hợp này cá thường chết với tỷ lệ cao trong thời gian ngắn là do cá nhiễm trùng máu nặng kèm theo tổn thương não và hệ thống thần kinh </p>\r\n<p>Giải phẫu xoang bụng cho thấy gan, lá lách, thận, ruột là các cơ quan có nhiều tổn thương và chiếm tỷ lệ cao. Trong đó, gan và thận thể hiện sự tổn thương ở nhiều mức độ khác nhau như sưng, xung huyết, xuất huyết và tụ huyết, mẫu cá có biểu hiện lồi mắt và kéo màng. Một số con bị bệnh nặng gan thường bị sưng rất to, rìa tù và nhũn (hình 3). Lách cá bệnh thường bị sưng, tụ huyết và thay đổi hình dạng bình thường. Lách của cá rô phi khỏe mạnh thường mỏng dẹt, có hình lá liễu, tuy nhiên khi mắc bệnh lá lách tụ máu, sưng to, những con bị nặng lá lách căng phồng lên và chuyển sang dạng hình chuông. Ruột cá bệnh thường xung huyết hoặc xuất huyết, ruột chứa nhiều chất nhầy, niêm mạc ruột xuất huyết, một số vùng của niêm mạc ruột còn bị thoái hóa rõ rệt (Trương Đình Hoài và cs. 2014).</p>\r\n<p> </p>\r\n<p><strong>Yếu tố rủi ro xuất hiện bệnh</strong></p>\r\n<p>Một số nguyên nhân gây stress trong ao/lồng nuôi của cá bị bệnh như nhiệt độ cao, pH>8, ôxy hòa tan thấp, nước có hàm lượng amonia, nitrite cao, nuôi với mật độ dày, quá trình vận chuyển và đánh bắt cá là những yếu tố quan trọng làm cho dịch bệnh thêm trầm trọng. Nhiệt độ cao trên 31ºC là một yếu tố quan trọng làm bùng phát dịch bệnh này. Đối với khu vực phía Bắc, bệnh thường xảy ra vào tháng 6 đến tháng 9, đây là thời điểm có nền nhiệt độ cao và đi kèm với những trận mưa rào là điệu kiện thuận lợi cho việc bùng phát bệnh này.</p>\r\n<p><strong> </strong></p>\r\n<p><strong>Giải pháp phòng bệnh</strong></p>\r\n<p>Để phòng bệnh cho cá, người nuôi nên thực hiện biện pháp phòng bệnh tổng hợp. Đặc biệt, cần thực hiện triệt để một số công việc sau:</p>\r\n<p>- Duy trì độ sâu mực nước ao luôn lớn hơn 2 m.</p>\r\n<p>- Tránh gây sốc cho cá trong vận chuyển, đánh bắt, cho ăn (giảm lượng cho ăn trên ngày, cắt bữa ăn trưa khi nhiệt độ nước trên 35oC).</p>\r\n<p>- Tăng cường sức đề kháng cho cá: Bổ sung Vitamin C + Vitamin tổng hợp và khoáng.</p>\r\n<p>- Quản lý chênh lệch nhiệt độ giữa tầng mặt và tầng đáy: Bật máy quạt nước từ 12h - 18h hàng ngày và từ 22h đêm đến 5h sáng; Dùng lưới đen che mặt lồng.</p>\r\n<p>- Sử dụng C tạt trực tiếp xuống ao nuôi vào lúc 14 - 16h trong những ngày thời tiết nắng gắt.</p>\r\n<p>- Định kỳ sử dụng chế phẩm Probiotics và men xử lý đáy để tạo hệ vi sinh có lợi và hấp thu khí độc ở tầng đáy.</p>\r\n<p>- Dự trữ ôxy cấp cứu trong kho để sử dụng trong các trường hợp cần thiết.</p>\r\n<p> </p>\r\n<p><strong>Xử lý ao/lồng bị bệnh</strong></p>\r\n<p>Đối với ao/lồng bị bệnh, cần áp dụng tổng hợp các biện pháp sau:</p>\r\n<p>- Loại bỏ ngay những con chết và yếu.</p>\r\n<p>- Xử lý môi trường nước bằng phương pháp phun hóa chất xuống ao nuôi hoặc treo túi hóa chất đối với khu lồng - bè.</p>\r\n<p>- Dừng cho cá ăn 1 ngày.</p>\r\n<p>- Gửi mẫu xét nghiệm và làm kháng sinh đồ.</p>\r\n<p>- Sử dụng kháng sinh (dựa trên kết quả kháng sinh đồ), Vitamin C/chất tăng cường hệ miễn dịch (Beta - glucan) cho ăn liên tục 7 - 10 ngày.</p>\r\n<p>- Sau khi kết thúc dung kháng sinh, tiếp tục cho ăn Vitamin C và sử dụng them men tiêu hóa và giải độc gan trong vòng 10 ngày.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin'),
(70, 'Enzyme đối với dinh dưỡng thủy sản', 'Nhờ cải thiện việc hấp thụ năng lượng và các acid amin cũng như loại bỏ tác động của các yếu tố kháng dinh dưỡng', '          <p><strong>Khái niệm</strong></p>\r\n<p>Enzyme (hay men) là chất xúc tác sinh học có thành phần cơ bản là protein. Nó chính là các protein xúc tác các phản ứng hóa học, chúng xúc tác tốc độ phản ứng chứ không phải biến đổi bởi phản ứng đó. Chúng cung cấp các công cụ bổ sung mạnh mẽ có thể làm bất hoạt các chất kháng dinh dưỡng và tăng giá trị của protein thực vật có trong thức ăn. Cung cấp một cách tự nhiên để chuyển đổi các thành phần thức ăn phức tạp thành các chất dinh dưỡng đơn giản dễ hấp thụ. Việc bổ sung enzyme vào trong thức ăn có thể cải thiện việc sử dụng chất dinh dưỡng, giảm chi phí thức ăn và sự thải ra chất dinh dưỡng vào môi trường.</p>\r\n<p>Enzyme có một nhược điểm là không chịu được nhiệt và áp suất cao, trong quá trình chế biến thức ăn, nguyên liệu được đưa vào nồi hấp áp suất cao, nhiệt độ 90 - 110ºC, sau đó ép thành viên dưới áp suất cao một lần nữa, quá trình này sẽ phá hủy enzyme nếu pha trộn nó từ phía nhà máy thức ăn chăn nuôi. Giải pháp duy nhất là làm thức ăn bổ sung chứa enzyme, trộn vào thức ăn trước khi cho ăn. Hiện nay, với ứng dụng công nghệ sinh học truyền vận thuốc (drug delivery), enzyme được vi bọc (micro-encapsulation) bằng gelatin, PVA hay sodium alginate (muối natri của rong câu). Các phân tử enzyme được gói trong 1 lớp bao, sẽ chịu được nhiệt độ cao, áp suất cao, hay điều kiện bất lợi và vẫn giữ nguyên được hoạt tính.</p>\r\n<p> </p>\r\n<p><strong>Xu hướng tất yếu</strong></p>\r\n<p>Mặc dù tôm có cơ quan để tiết ra các enzyme tiêu hóa hầu hết các thức ăn mà chúng ăn vào, tuy nhiên đường ruột của tôm lại ngắn nên chúng cần ăn loại thức ăn tiêu hóa nhanh như đạm trong môi trường được tăng cường acid hữu cơ và enzyme protease ưa acid. Hơn nữa, khả năng tiêu hóa tảo của tôm cũng rất hạn chế, nếu không có những enzyme tăng cường trong thức ăn. Khi cho tôm ăn đạm có nguồn gốc thực vật như bã dầu nành, bã dầu phộng… tôm sẽ thải hết thức ăn nguyên vẹn theo phân ra ngoài do không thể tiêu hóa được. Nguyên nhân là do các chất kháng dinh dưỡng có trong các thực vật này. Đây là một chất rất giàu phosphate bổ dưỡng cho tôm, nhưng thiếu enzyme phytase, nó sẽ không được tiêu hóa, thải ra môi trường ao, chất phosphate này là nguyên nhân chính làm bùng phát tảo lam và tảo dị dưỡng ở tháng cuối vụ. Nếu thêm vào thức ăn, enzyme phytase một số lượng hợp lý, người nuôi có thể yên tâm cho tôm ăn đạm nguồn gốc thực vật. Hơn nữa, hiện bột cá thường ít được sử dụng trong thức ăn thủy sản do sự khan hiếm nguyên liệu và chi phí cao; điều này đã khuyến khích các nhà sản xuất tìm cách nâng cao giá trị dinh dưỡng của thức ăn bằng cách bổ sung enzyme, tìm ra các chất thay thế phù hợp cho bột cá. Các yếu tố quyết định việc sử dụng enzyme cho thủy sản nuôi là do sự gia tăng nhu cầu về chất lượng thực phẩm hạt cho tôm và cá; tìm kiếm các nguồn thực phẩm thay thế với giá trị dinh dưỡng tốt hơn. Từ đó giúp giảm chi phí, tăng lợi nhuận và đặc biệt là vẫn đảm bảo các yếu tố bền vững đối với môi trường nuôi.</p>\r\n<p>Mục đích chính của việc sử dụng enzyme trong thức ăn là để cải thiện tiêu hóa. Các quá trình tiêu hóa sẽ hoạt động tốt hơn và kết quả thể hiện ở hiệu suất sử dụng thức ăn cải thiện nhờ cung cấp thêm các enzyme. Hơn nữa, động vật thủy sinh thiếu enzyme tiêu hóa nhất định trong giai đoạn mới phát triển hoặc trong suốt đời sống của chúng.</p>\r\n<p> </p>\r\n<p><strong>Ứng dụng</strong></p>\r\n<p>Bổ sung enzyme trong thức ăn sẽ giúp: Tăng cường tiêu hóa và hấp thu các chất dinh dưỡng đặc biệt là chất béo và đạm; Cải thiện giá trị năng lượng trao đổi có hiệu chỉnh Nitơ của chế độ ăn; Giảm độ nhớt trong tiêu hóa; Tăng lượng thức ăn ăn vào, hệ số thức ăn và tăng trưởng; Giảm thải ammoniac; Cải thiện khả năng tiêu hóa dinh dưỡng; Các enzyme nội sinh được tìm thấy trong hệ tiêu hóa của tôm và cá giúp phá vỡ các phân tử hữu cơ lớn như tinh bột, cellulose và protein thành những chất đơn giản hơn. Quá trình tiêu hóa carbohydrate cải thiện bằng cách sử dụng enzyme từ vi khuẩn. Bổ sung các enzyme carbohydrate ngoại sinh vào thức ăn làm tăng việc sử dụng các carbohydrate trong chế độ ăn không có sẵn. Một lượng lớn các polysaccharide không phải tinh bột (NSP) như cellulose, xylan và mannan làm giảm giá trị dinh dưỡng của nhiều loại thành phần thực vật. Enzyme trong đường ruột để tiêu hóa các loại carbohydrate này không được sản sinh bởi hầu hết các động vật.</p>\r\n<p>Về cơ bản, enzyme nên được bổ sung hàng ngày, ít nhất một cữ/ngày trong quá trình nuôi cá tôm để hỗ trợ cho quá trình tiêu hóa, ngăn ngừa các bệnh về đường tiêu hóa như phân trắng, đường ruột đứt khúc, sình bụng ở cá… Ngoài ra, với các trường hợp dưới đây có thể bổ sung tăng liều lượng và liên tục: Giai đoạn còn nhỏ. Khi đường ruột đứt khúc, lỏng lẻo; Kém ăn, giảm ăn, tiêu hóa kém; Giai đoạn sử dụng kháng sinh trong chu trình nuôi; Giai đoạn sau khi hết bệnh; Chậm lớn; Điều kiện môi trường bất lợi; Nuôi mật độ cao (hỗ trợ giảm thiểu ô nhiễm môi trường); Vùng xung quanh bị dịch bệnh.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(71, 'Quảng Ngãi: Thu nhập khá nhờ nuôi cá kết hợp du lịch', 'Nhờ kinh hoạt mở dịch vụ du lịch giải trí câu cá ở lòng hồ Núi Ngang mà người dân xã Ba Liên(Ba Tơ) đã có nguồn thu nhập khá', '          <p>Thời gian gần đây, người dân xã Ba Liên bước vào mùa thu hoạch cá. Không như mọi năm, trước khi khai thác, 46 hộ dân tham gia nuôi cá ở lòng hồ đều thống nhất mở dịch vụ cho du khách các nơi đến câu cá, tạo thú vui và tăng thêm nguồn thu nhập.</p>\r\n<p>Theo đó, các hộ thống nhất phân thành nhiều nhóm, mỗi nhóm có 5 hộ luân phiên nhau chở khách đến nơi câu cá. Mỗi khách được quy định trả tiền theo dụng cụ câu cá. Nếu lưỡi câu lớn thì trả cho nhóm 100.000 đồng/ngày, lưỡi câu nhỏ thì 50.000 đồng/ngày, tổng chi cho ngày câu trên hồ từ 130.000 - 150.000 đồng/ngày.</p>\r\n<p>Theo nhiều hộ dân, từ khi mở dịch vụ câu cá trên hồ, lượng khách đến đây rất đông, có ngày lên đến 30 - 40 người. Du khách chỉ được đem cần câu, không được sử dụng bất kỳ phương tiện nào khác. Vì vậy, đa số du khách sử dụng dịch vụ thuê ghe, thuê người đưa đến nơi câu cá. Nhờ đó, mỗi nhóm thu được từ 3 - 4 triệu đồng/ngày. Số tiền này, được trích một phần gây quỹ, còn lại chia cho các hộ trong nhóm. Du khách cũng rất hưởng ứng với hoạt động này, vừa giải trí vừa có sản phẩm mang về.</p>\r\n<p>Năm 2001, Trạm Khuyến nông huyện Ba Tơ phối hợp với Sở Thủy sản (nay là Sở NN&PTNT) triển khai cho các hộ dân ở các thôn: Đá Chát, Hương Chiêng và Núi Ngang tập huấn nuôi cá cải thiện cuộc sống. Nhờ biết áp dụng kỹ thuật nuôi cá trong lòng hồ, nên đã nhanh chóng đem lại hiệu quả kinh tế cao. Đến nay, nhiều hộ đã có nguồn thu nhập khá từ nuôi cá trong lòng hồ, bình quân mỗi hộ thu được 20 - 30 triệu đồng/năm.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(72, 'Sinh ra trên đất Việt, đạt chuẩn mực quốc tế', 'Thủy sản là một trong những ngành kinh tế mũi nhọn của nước ta', '          <p><strong>Sinh ra trên đất Việt</strong></p>\r\n<p>Nuôi trồng thủy sản ở nước ta có truyền thống lâu đời, bắt đầu chỉ là những hệ thống trang trại nhỏ lẻ. Ngày nay, ngành phát triển đa dạng và chứng kiến tốc độ tăng trưởng cao đạt kim ngạch xuất khẩu trên 1 tỷ USD (nguồn mard.gov.vn - Cổng thông tin điện tử Bộ Nông nghiệp và Phát triển nông thôn).</p>\r\n<p>Việt Nam vốn có lợi thế về đường bờ biển dài, mật độ sông ngòi, kênh rạch chằng chịt tạo môi trường thủy sinh lý tưởng. Với tiềm năng đó, ngành chuyển mình từ khai thác thiên nhiên sang tăng tỷ trọng nuôi trồng trên mặt nước, mở rộng sản xuất nhưng vẫn bảo tồn tự nhiên.</p>\r\n<p>Chung tầm nhìn trên, với sứ mệnh hỗ trợ các nhà sản xuất thủy sản tiếp cận toàn diện với giải pháp dinh dưỡng (thức ăn, dịch vụ, dưỡng chất) nhằm tối ưu hóa hiệu quả kỹ thuật và kinh tế cũng như đảm bảo sự tăng trưởng và bền vững, nhãn hàng Ocialis đã liên tục nghiên cứu và cải tiến sản phẩm, nhằm mang lại những sản phẩm tối ưu cho quá trình nuôi tại Việt Nam và nhắm tới đáp ứng nhu cầu trên nhiều nước tại châu Á - Thái Bình Dương và châu Mỹ.</p>\r\n<p>Đội ngũ Ocialis là những cá nhân tâm huyết, giàu kinh nghiệm thực tiễn, bao gồm các nhân viên kỹ thuật và các chuyên gia trong và ngoài nước đã không ngừng sáng tạo, nghiên cứu, phát triển các dòng sản phẩm đa dạng, phù hợp với nhu cầu của địa phương, nhằm mang lại hiệu quả kinh tế, kỹ thuật và sự phát triển bền vững.</p>\r\n<p><strong>Đạt chuẩn mực quốc tế</strong></p>\r\n<p>Ocialis đang chứng minh vị thế của mình khi vươn ra quốc tế. Sau gần 20 năm, Ocialis là nhãn hàng thức ăn thủy sản được tiêu thụ khắp châu Á - Thái Bình Dương và châu Mỹ.</p>\r\n<p>Ocialis được bà con tin tưởng khi giúp người nuôi trồng tối đa tiềm năng, lợi nhuận. Nguồn nguyên liệu được tuyển chọn nghiêm ngặt, truy xuất được nguồn gốc. Công thức đáng tin cậy, đảm bảo giá trị dinh dưỡng ổn định với quá trình ép đùn ép viên được kiểm soát tốt. Ocialis quyết tâm đảm bảo chất lượng sản phẩm đạt tiêu chuẩn cao, đạt các chứng nhận quốc tế, được kiểm định hàng năm theo tiêu chuẩn ISO 9001, chứng nhận GlobalGAP và chứng chỉ “Thực hành Nuôi trồng thủy sản tốt nhất”.</p>\r\n<p>Ocialis luôn cải tiến chất lượng, đảm bảo mang lại giải pháp toàn diện dựa vào 3 giá trị cốt lõi của nhãn hàng. Đây là giải pháp nuôi trồng toàn diện, giúp thủy sản nước ta đáp ứng tốt nhu cầu nội địa và đủ điều kiện xuất khẩu năm châu.</p>\r\n<p>+ <strong>Chuẩn Dinh dưỡng</strong>: Gần 20 năm đồng hành cùng các hộ nuôi, Ocialis thấu hiểu rằng thức ăn là yếu tố quan trọng hàng đầu. Do đó, các chuyên gia của Ocialis đã liên tục nghiên cứu, cải tiến công thức, mang đến những chuẩn mực dinh dưỡng đạt chất lượng cao cho khách hàng tại Việt Nam và nhiều nước khác ở châu Á - Thái Bình Dương và châu Mỹ.</p>\r\n<p>+ <strong>Chuẩn Mô hình</strong>: Sức mạnh của Ocialis đến từ giải pháp toàn diện để giúp khách hàng cải thiện điều kiện trang trại, áp dụng các phương pháp kỹ thuật, nâng cao hiệu quả và tối ưu hiệu suất nuôi. Đội ngũ kỹ thuật của Ocialis đã dành nhiều tâm huyết phát triển và nghiên cứu thực nghiệm, tự hào mang đến nhiều mô hình phù hợp cho quý khách hàng.</p>\r\n<p>+<strong> Chuẩn Tương lai</strong>: Ocialis là nhãn hàng được xây dựng trên nền tảng vì một tương lai tốt đẹp thông qua sự gắn kết cùng phát triển kinh tế, bảo vệ tài nguyên và môi trường, nâng cao các chuẩn mực sống vì một hệ sinh thái bền vững.</p>\r\n<p>Đồng hành cùng bà con nuôi trồng Việt, Ocialis tin rằng thủy sản Việt Nam sẽ còn vươn xa hơn nữa trong quá trình hội nhập. Và có thể tự tin khẳng định, thủy sản nước ta “<strong>Sinh Ra Trên Đất Việt, Đạt Chuẩn Mực Quốc Tế</strong>”.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin'),
(73, 'Thái Bình: Bệnh đốm trắng vẫn xuất hiện tại một số vùng nuôi', 'Đây là thông tin từ Chi cục Chăn nuôi và Thú y Thái Bình.', '          <p>Tại huyện Tiền Hải, xã Đông Minh là một trong những địa phương có diện tích nuôi tôm bị thiệt hại lớn nhất với 150 ao nuôi bị nhiễm bệnh, tương đương hơn 105.000 m<sup>2</sup> (chiếm 10,68% diện tích nuôi tôm toàn xã).</p>\r\n<p>Ông Đoàn Văn Hiền ở thôn Minh Châu, xã Đông Minh nuôi tôm với tổng diện tích 3.000 m<sup>2</sup>; vụ vừa qua có hai ao nuôi bị nhiễm bệnh đốm trắng gây thiệt hại khoảng 80 triệu đồng. Ông Hiền cho biết vẫn chưa hiểu rõ được nguyên nhân khiến tôm nhiễm bệnh là gì và lúng túng trong việc xử lý khi dịch bệnh xảy ra.</p>\r\n<p>Chi cục Chăn nuôi và Thú y Thái Bình khuyến cáo: Để nuôi tôm hiệu quả, trước mỗi vụ cần thực hiện cải tạo ao đầm theo hướng dẫn của cơ quan chuyên môn, bảo đảm đúng yêu cầu kỹ thuật khi nuôi. Trong quá trình nuôi, cần thường xuyên kiểm soát, loại bỏ vật chủ trung gian truyền bệnh. Kiểm tra sức khỏe tôm hàng ngày; điều chỉnh lượng thức ăn phù hợp, tránh để dư thừa gây ô nhiễm ao nuôi. Định kỳ kiểm tra màu nước, các chỉ tiêu về môi trường, sử dụng chế phẩm vi sinh để xử lý nước ao nuôi, giữ ổn định các yếu tố môi trường, tránh gây sốc cho tôm nuôi. Bổ sung Vitamin C, khoáng chất, men tiêu hóa để nâng cao sức đề kháng cho tôm nuôi. Khi phát hiện tôm bị bệnh hoặc nghi mắc bệnh, cần báo ngay cho cơ quan thú y cơ sở hoặc chính quyền địa phương để xử lý kịp thời.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(74, 'Hà Tĩnh: HIệu quả cao nhờ nuôi cá chình hoa', 'Mô hình nuôi thi điểm cá chình hoa thương phầm đầu tiên ở thị xã Kỳ Anh chuẩn bị vào thời kỳ mới', '          <p>Tham gia mô hình, bà Lê Thị Xuân (tổ dân phố Tân Long, phường Kỳ Long) cho hay, được sự hỗ trợ của Trung tâm, gia đình bà đầu tư khoảng hơn 100 triệu đồng xây dựng 2 ao nuôi có tổng diện tích gần 700 m<sup>2</sup>, thả 700 con giống. Sau gần 9 tháng, cá đã bắt đầu có thể tỉa bán.</p>\r\n<p>Theo Trung tâm Ứng dụng Khoa học kỹ thuật và Bảo vệ cây trồng vật nuôi thị xã Kỳ Anh: Đây là mô hình nuôi cá chình đầu tiên của thị xã, nằm trong dự án “Hỗ trợ nuôi cá chình hoa trong ao đất vùng núi tại hộ gia đình”. Trung tâm hỗ trợ 60% tiền con giống, 40% chi phí thức ăn và kỹ thuật nuôi cho bà con.</p>\r\n<p>Bà Xuân chia sẻ: “Cá chình có đặc tính sợ ánh sáng nên nó sẽ ăn vào thời gian cố định lúc chập tối. Trong ao nuôi, độ pH phải luôn đạt từ 7 - 8,5; từ tháng thứ 2 trở đi phải thay nước 2 - 3 lần/tuần, mỗi lần thay 20 - 30% lượng nước; 1 tháng/lần tách con lớn, con nhỏ nuôi riêng để cá đồng đều và chóng lớn…”. Trong tháng đầu, thức ăn chính của cá là bột công nghiệp, khoảng hơn một tháng sau có thể cho ăn cá tạp, tôm tép cắt nhỏ. Sau gần 9 tháng nuôi, đàn cá chình có trọng lượng trung bình 6,3 lạng/con, một số có trọng lượng vượt đàn đã có thể tỉa bán. Với mức giá từ 550.000 - 600.000 đồng/kg, bà Xuân tin chắc vụ nuôi này cho lợi nhuận hơn 500 triệu đồng.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(75, 'Kỹ thuật nuôi sò huyết trong áo cho lãi cao', 'Do tận dụng được các nguồn thúc ăn tự nhiên, vốn đầu tư ít và dễ quản lý nên mô hình nuôi sò huyết trong ao', '          <p><strong>Thiết kế ao nuôi</strong></p>\r\n<p>Khi nuôi sò trong các đầm phải xây dựng một số hạng mục công trình như sau: Bãi nuôi nên có thiết kế hình chữ nhật và xây dựng kèm theo các công trình. Bờ ao xung quanh bãi phải đắp chắc chắn, mặt bờ rộng khoảng 2 - 2,5 m, đáy bờ 3 - 3,5 m, chiều cao của bờ 1,2 - 1,5 m. Xây dựng thêm mương bao xung quanh phía trong bờ ao. Diện tích mương bằng 15 - 20% diện tích bãi nuôi. Thủy triều trước khi vào bãi qua mương được lọc lại bùn, cát và các tạp vật khác làm cho nước vào bãi trong sạch. Mương ngoài tác dụng là rào chắn không cho sinh vật có hại xâm nhập vào bãi còn có nhiệm vụ điều chỉnh nhiệt độ trên bãi nuôi.</p>\r\n<p>Phía trước bãi nuôi và đối diện với cửa cống cấp nước xây một bờ ngăn cao khoảng 0,6 m, bề mặt 0,6 m, cách bãi nuôi chừng 1,5 m và cách cửa cống 1,5 m, mục đích làm phân tán dòng chảy, giảm lưu tốc chảy của nước từ cống cấp vào bãi để đảm bảo cho bãi nuôi không bị xói mòn.</p>\r\n<p> </p>\r\n<p><strong>Xử lý ao nuôi</strong></p>\r\n<p>Sau khi cải tạo, sên vét ao nuôi, mương bao, tiến hành lấy nước vào ao nuôi thông qua túi lọc để hạn chế trứng các sinh vật gây hại sò huyết xâm nhập vào ao nuôi. Nên theo dõi chất lượng nước ngoài kênh rạch trước khi cấp để đảm bảo các điều kiện tốt nhất cho sò huyết phát triển. Tiến hành gây màu cho ao nuôi tạo nguồn thức ăn tự nhiên (tảo) giai đoạn đầu, sau 2 - 3 ngày tiến hành diệt tạp, diệt khuẩn cho sò khi mới thả giống. Một số hóa chất thông thường có thể sử dụng để diệt tạp như dây thuốc cá (10 - 15 kg/1.000 m<sup>3</sup>), Saponin (10 - 15 kg/1.000 m<sup>3</sup>); gây màu dùng phân vô cơ DAP, Ure (3 - 5 kg/1.000 m<sup>3</sup>) nhằm tạo môi trường tốt cho thủy sản nuôi phát triển. Kiểm tra môi trường ao nuôi (pH, độ mặn, độ, độ kiềm, độ trong...) trước khi thả nhằm đối chiếu các thông số môi trường trong ao nuôi với giới hạn cho phép xem có nằm trong ngưỡng thích hợp hay không để kịp thời điều chỉnh.</p>\r\n<p> </p>\r\n<p><strong>Nguồn giống</strong></p>\r\n<p>Sò huyết giống hiện nay chủ yếu là lấy từ giống tự nhiên, cho nên trước khi tiến hành lấy giống cần phải điều tra, dự báo diện tích bãi giống, trữ lượng giống để có thể chủ động trong sản xuất. Xác định diện tích qua điều tra vùng phân bố của sò giống và xác định trữ lượng giống bằng cách lấy mẫu sinh lượng, dựa trên diện tích bãi giống và sinh lượng để tính ra trữ lượng giống. Thời điểm lấy giống nên tiến hành khi phát hiện giống khoảng 10 - 15 ngày (giống cỡ 25.000 - 30.000 con/kg). Sò tốt thường có màu sắc trắng hồng không lẫn tạp vật và các sinh vật hại sò.</p>\r\n<p> </p>\r\n<p><strong>Vận chuyển giống</strong></p>\r\n<p> Sau khi lấy giống, có thể vận chuyển giống bằng phương pháp giữ ẩm. Trong quá trình vận chuyển, tránh để sò huyết tiếp xúc với nước ngọt đặc biệt là nước mưa. Thời gian vận chuyển con giống không quá 6 giờ. Sò huyết giống được dựng trong cập đệm hoặc bao bố, để nơi thoáng mát, vận chuyển bằng xe hoặc tàu thuyền, thường xuyên tưới nước biển lên các bao đựng để sò huyết giống dễ hô hấp. Ở nhiệt độ thấp thì thời gian cho phép để vận chuyển lâu hơn và tỷ lệ sống cao hơn. Thời điểm thả giống có thể vào buổi sáng hoặc buổi chiều, trời mát, không mưa bão.</p>\r\n<p> </p>\r\n<p><strong>Mật độ thả</strong></p>\r\n<p>Trung bình nên thả sò với số lượng như sau: Cỡ 400 con/kg thả mật độ 850 - 900 con/1.000 m<sup>2</sup>, cỡ dưới 400 - 350 con/kg thả mật độ 950 - 1.000 con/1.000 m<sup>2</sup>, cỡ dưới 350 - 300 con/kg thả 950 - 1.000 con/1.000 m<sup>2</sup>, cỡ dưới 300 - 250 con/kg thì thả 1.000 con/1.100 m<sup>2</sup>.</p>\r\n<p> </p>\r\n<p><strong>Chăm sóc, quản lý</strong></p>\r\n<p>Sau khi đã thả nuôi phải thường xuyên có người quản lý, kịp thời tu sửa đê bao của đầm nuôi, tránh bị rò nước ra ngoài, chú ý điều tiết lượng nước trong đầm. Cứ 15 ngày tháo nước một lần, kiểm tra sự sinh trưởng và điều kiện sống của sò, làm sạch đầm, loại bỏ sinh vật gây hại. Tiêu diệt các loài <em>Muschlus senhousei</em> và rong bún <em>Enteromorpha spp… </em>Nếu phát hiện mật độ sò quá cao, sò sinh trưởng chậm, thì chuyển bớt một bộ phận tới nuôi ở đầm khác. Khi nuôi sò huyết kết hợp, cần san thưa định kỳ 2 - 3 tháng/lần để tạo môi trường cho sò huyết sinh trưởng và phát triển tốt. Trong quá trình nuôi, hạn chế việc sử dụng hóa chất. Tuy nhiên, vào những ngày mưa lớn kéo dài, nên bón vôi CaCO<sub>3</sub> liều lượng 10 - 15 kg/1.000 m<sup>3</sup>. Kiểm tra các yếu tố môi trường ngoài kênh cấp trước khi cấp và thay nước để ổn định chất lượng nước ao nuôi. Định kỳ kiểm tra mức tăng trưởng của sò huyết, các đối tượng nuôi trong ao và các yếu tố môi trường pH, độ mặn, độ kiềm, độ trong... để có hướng xử lý kịp thời.</p>\r\n<p> </p>\r\n<p><strong>Thu hoạch sò huyết</strong></p>\r\n<p>Cỡ sò huyết giống thả 500 - 800 con/kg, sau thời gian 7 - 8 tháng nuôi sẽ cho thu hoạch, sò đạt cỡ thương phẩm 60 - 70 con/kg. Cỡ sò huyết giống thả 1.000 - 1.200 con/kg thời gian nuôi 12 - 18 tháng thu hoạch, sò đạt 60 - 70 con/kg. Thông thường người dân áp dụng hai hình thức thu hoạch là thu tỉa và thu dứt điểm, áp dụng phương pháp thủ công khi thu hoạch sò huyết nuôi, rút nước trong ao còn khoảng 1/3, sau đó mò bắt.</p>\r\n<p> </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(76, 'Tiêu thụ tôm đối với sự thay đổi vĩnh viễn', 'Đại dịch virus corona trên toàn cầu có thể thay đổi ngành tôm vĩnh viễn ', '          <p>Hơn rất nhiều loài hải sản, tôm tạo doanh thu hàng năm ổn định cho ngành dịch vụ thực phẩm. Tuy nhiên, nhu cầu tôm đã giảm mạnh chỉ sau một đêm do dịch COVID-19, nhất là tại Mỹ và EU. Điều này theo Jim Gulkin, Tổng Giám đốc Công ty Siam Canadian, Thái Lan là: “Đã tạo ra một khoảng trống lớn trong doanh số mà sẽ mất rất nhiều thời gian để có thể hồi phục. Không chỉ 6 tháng hay 1 năm, chúng ta có thể phải mất đến vài năm để khôi phục lại trạng thái như trước dịch bệnh”.</p>\r\n<p>Một thách thức quan trọng khác mà ngành tôm phải giải quyết là lấp đầy khoảng trống trong nhu cầu do sự suy yếu của ngành dịch vụ thực phẩm cùng với việc cung cấp thực ăn cho một nền kinh tế suy thoái nghiêm trọng.</p>\r\n<p>Theo Jim Gulkin, người tiêu dùng có xu hướng chuyển dần sang mua hàng qua các kênh bán lẻ, đặc biệt là sử dụng dịch vụ thương mại điện tử. Sự chuyển dịch này có thể tồn tại mãi mãi và đặt ra nhiều vấn đề mới cần giải quyết.</p>\r\n<p>Mặc dù thức ăn đông lạnh khá phổ biến tại các nước phương Tây, ông Gulkin tin rằng, xu hướng chuyển dịch sang thương mại điện tử cũng sẽ rất mạnh tại các nước Đông Á, nơi mà người dân thường quen mua sắm tại chợ truyền thống.</p>\r\n<p>Theo nguồn tin của bà Fatima Ferdouse, cố vấn thủy, hải sản của Liên Hiệp quốc, các nước Đông Nam Á có rất ít tôm trong thị trường mua sắm trực tuyến. Bà nói thêm: “Người dân Đông Nam Á vẫn ưa chuộng các loại cá hơn là sản phẩm tôm không đầu. Hơn nữa, các sản phẩm tôm đông lạnh là rất hiếm tại khu vực này, nên người tiêu dùng ngày càng mua ít tôm và mua nhiều cá hơn bất kỳ loại hải sản nào”.</p>\r\n<p>Tuy nhiên, phát triển thị trường nội địa là một phương án tuyệt vời để giúp nông dân thoát khỏi tình trạng khó khăn như hiện nay. Bà Ferdouse nhận định: “Chúng ta cần thúc đẩy tiêu thụ nội địa một cách tốt nhất có thể. Tại Đông Nam Á, mọi chuyện phụ thuộc vào chi tiêu mà người tiêu dùng sẵn sàng bỏ ra để mua tôm”.</p>\r\n<p>Ví dụ, Ấn Độ là một thị trường tiềm năng bất tận cho tiêu thụ tôm. Ngay cả khi chỉ 30% dân số ăn 2 kg tôm mỗi năm, họ vẫn sẽ tiêu thụ hết lượng tôm mà quốc gia này sản xuất.</p>\r\n<p>Trong khi, tại Mỹ, Antonio Camposano, Chủ tịch Phòng NTTS Ecuador nhận định, sự khác biệt về văn hóa cũng là một rào cản cho tôm xuất khẩu của các quốc gia khác. Vì vậy, phải điều chỉnh sản phẩm dựa trên nền tảng đó và sau đó phải điều chỉnh nó phù hợp với nhu cầu của người tiêu dùng cụ thể.</p>\r\n<p>Ông Antonio Camposano nói thêm: “Dù là giải pháp nào thì trong một năm hoặc lâu hơn, thị trường tôm sẽ có một diện mạo hoàn toàn khác biệt. Dịch COVID-19 đã cho chúng ta thấy sự mong manh của thị trường, mọi thứ đang dần thay đổi trong bối cảnh phong tỏa và cách ly xã hội. Và cuối cùng, chúng ta nhận thấy, thị trường đã mãi thay đổi, thay đổi về khách hàng, thay đổi về cách mua hàng và thay đổi cả về cách ăn thức ăn”.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin'),
(77, 'Hướng đi mới trong sản xuất thức ăn thủy sản', 'Trong nuôi trồng thủy sản, thức ăn thường chiếm khoảng 50-80%', '          <p><strong>Ngành sản xuất tiềm năng</strong></p>\r\n<p>Hiện nay, cả nước có khoảng 404 cơ sở sản xuất, nhập khẩu thức ăn thủy sản (thức ăn hỗn hợp, thức ăn bổ sung và nguyên liệu sản xuất thức ăn), tập trung tại các tỉnh, thành phố như: TP Hồ Chí Minh, Đồng Tháp, Cần Thơ, Bình Dương, Khánh Hòa, Hà Nội, Đồng Nai, Long An. Số lượng sản phẩm thức ăn đa dạng, có khoảng 8.000 sản phẩm đang được lưu hành (khoảng 3.000 sản phẩm thức ăn hỗn hợp, 5.000 sản phẩm thức ăn bổ sung và nguyên liệu thức ăn). Nguyên liệu cho chế biến thức ăn tôm phần lớn được nhập khẩu. Thức ăn nuôi tôm chủ yếu thông qua hệ thống đại lý phân phối kinh doanh để cung ứng đến ao nuôi.</p>\r\n<p>Thức ăn cho nuôi biển cung cấp bởi hai nguồn chính là sản xuất trong nước và nhập ngoại. Thức ăn hỗn hợp hoàn chỉnh phục vụ nuôi cá biển phần lớn được nhập khẩu. Thức ăn tự chế từ tận dụng các loại phế, phụ phẩm trong nông nghiệp, các loài cá tạp được sử dụng khá phổ biến trong nuôi biển, đặc biệt nuôi cá và tôm hùm.</p>\r\n<p>Việc sản xuất và cung cấp thức ăn chuyên cho nghề nuôi cá biển vẫn chưa phát triển mạnh. Phần lớn lượng thức ăn công nghiệp cho nuôi biển do các doanh nghiệp đầu tư nước ngoài sản xuất hoặc nhập ngoại nên khó kiểm soát được giá thành, chất lượng, nguồn gốc thức ăn cũng như khả năng và các phương thức cung cấp. Đây là một trong những yếu tố tác động đến phát triển bền vững của nuôi biển hiện nay, dẫn đến chậm phát triển. Hiện nay, Viện Nghiên cứu NTTS I đã chủ động nghiên cứu công thức thức ăn riêng cho cá biển và đặt hàng doanh nghiệp sản xuất gia công, cung ứng cho nuôi cá biển tại vịnh Vân Phong (Khánh Hòa), bước đầu có hiệu quả tốt.</p>\r\n<p><strong> </strong></p>\r\n<p><strong>Chờ sản phẩm giá rẻ</strong></p>\r\n<p>Xu hướng sử dụng thức ăn công nghiệp trong nuôi trồng thủy sản gia tăng do tính tiện lợi, các yêu cầu của môi trường và những đòi hỏi về hàm lượng dinh dưỡng của các đối tượng nuôi. Trong tương lai, nhu cầu về số lượng thức ăn công nghiệp sẽ tăng tỷ lệ thuận với sản lượng và cũng đòi hỏi một lượng nguyên liệu tương đương để sản xuất. Thành phần chính để sản xuất thức ăn cho nuôi trồng thủy sản là bột cá, nguồn nguyên liệu này được cung cấp chủ yếu từ khai thác. Trong khi đó, theo dự báo của FAO thì nghề khai thác thủy sản sẽ không tăng lên về sản lượng, đây sẽ là một khó khăn rất lớn cho ngành công nghiệp sản xuất bột cá của các nước trên thế giới.</p>\r\n<p>Thức ăn có nhiều chủng loại đa dạng và phong phú được sản xuất trong nước và nhập khẩu từ các nước khác nhau; tuy nhiên, phần lớn các loại thức ăn có hệ số chuyển đổi thức ăn thấp (1,5 - 2,2). Trong thời gian tới, sẽ có những loại thức ăn được sản xuất với giá thành rẻ do áp dụng các quy trình sản xuất tiên tiến, hệ số chuyển đổi thức ăn thấp, sẽ rút ngắn được thời gian nuôi và giảm nguy cơ ô nhiễm môi trường.</p>\r\n<p> </p>\r\n<p><strong>Từng bước hiện đại hóa</strong></p>\r\n<p>Trong bối cảnh hội nhập, tự do hóa thương mại, kinh tế thị trường và cạnh tranh khốc liệt hiện nay, việc thu hút vốn đầu tư trong nước và nước ngoài, mở rộng liên doanh liên kết là cần thiết. Trên cơ sở đó, tranh thủ các kinh nghiệm về kỹ thuật và quản lý để chủ động xây dựng cơ sở sản xuất thức ăn nuôi trồng thủy sản gắn với vùng nguyên liệu bột tôm, bột cá để hoàn thiện hệ thống liên hoàn nhằm tối ưu hóa sử dụng tài nguyên và tối đa hóa lợi nhuận trong chuỗi giá trị.</p>\r\n<p>Bên cạnh đó, từng bước hiện đại hóa nghề sản xuất thức ăn cho nuôi trồng, xây dựng và hoàn thiện công nghệ sản xuất thức ăn công nghiệp hệ số thấp cho tất cả những đối tượng nuôi xuất khẩu chủ lực trên cơ sở sử dụng nguyên liệu sẵn có của địa phương.</p>\r\n<p>Kết hợp với các viện, trường, công ty nghiên cứu và sản xuất các loại thức ăn viên nổi cho các đối tượng như cá lóc, cá rô phi, tôm càng xanh với giá thành phù hợp, đảm bảo nuôi trồng có hiệu quả; đồng thời đẩy mạnh xây dựng nhà máy thức ăn công nghiệp phục vụ nuôi tôm nước lợ.</p>\r\n<p>Hiện nay, đang có nhiều hướng nghiên cứu sử dụng nguyên liệu khác để thay thế cho bột cá trong sản xuất thức ăn và đã có những thành công bước đầu. Như vậy, sẽ giảm sự lệ thuộc và bị động dẫn đến thiếu tính ổn định trong sản xuất.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'thunthun'),
(78, 'Xuất khẩu nhuyễn thể thân mềm phục hồi', 'Mặc dù chịu sự tác động của dịch Covid-19, thế nhưng xuất khẩu mực, bạch tuộc trong 6 tháng đầu năm đã có sự phục hồi nhẹ', '          <p><strong>Tăng trưởng đáng kể</strong></p>\r\n<p>Theo VASEP,  trong tháng 6, xuất khẩu mực, bạch tuộc của Việt Nam đã tăng tăng 6,5% đạt trên 48 triệu USD sau khi giảm liên tục trong 3 tháng trước đó. Lũy kế 6 tháng đầu năm, kim ngạch xuất khẩu mực, bạch tuộc đạt gần 240 triệu USD, giảm 16,9% so cùng kỳ năm 2019. Trong tổng cơ cấu mực, bạch tuộc xuất khẩu của Việt Nam, mực chiếm 55,1%, bạch tuộc chiếm 44,9%. Với các thị trường nhập khẩu mực truyền thống của Việt Nam thì ghi nhận mức tăng trưởng dương ở nhiều thị trường như Hàn Quốc, ASEAN, Trung Quốc, Mỹ, Đài Loan, Israel trong tháng 6. Tuy  nhiên, xuất khẩu sang Nhật Bản và EU vẫn chưa thể tăng.</p>\r\n<p>Đứng ở vị trí hàng đầu là Hàn Quốc, với trị giá tăng 23,5% trong tháng 6, tuy nhiên do giảm trong các tháng trước đó nên xuất khẩu mực, bạch tuộc sang thị trường này vẫn giảm 15% trong 6 tháng đầu năm nay, đạt trên 102 triệu USD. Nhật Bản là thị trường lớn thứ 2 nhập khẩu sản phẩm mực, bạch tuộc của Việt Nam, chiếm tỷ trọng 24,4%. Kim ngạch xuất khẩu nhóm hàng này sang Nhật Bản trong 6 tháng đầu năm 2020 đạt 58,6 triệu USD, giảm 21% so cùng kỳ năm trước. Việt Nam chủ yếu xuất khẩu sang Nhật Bản các sản phẩm như mực nang đông lạnh, mực ống cắt khoanh đông lạnh, mực ống nguyên con làm sạch sushi ăn liền đông lạnh, mực ống đông lạnh Geso, mực ống đông lạnh Sugata…</p>\r\n<p>Được biết, Nhật Bản nhập khẩu mực, bạch tuộc từ 11 nguồn cung. Các quốc gia cung cấp mực, bạch tuộc vào thị trường Nhật Bản lớn nhất lần lượt là Trung Quốc, Việt Nam, Thái Lan, Indonesia và Philippines, chiếm 81%, 9,5%, 4,8%, 1,3% và 0,6% tổng kim ngạch nhập khẩu mực, bạch tuộc của Nhật Bản. Nhật Bản nhập khẩu chủ yếu các sản phẩm như mực chế biến, bạch tuộc chế biến (trừ xông CO), mực tươi tươi sống, bạch tuộc hun khói/đông lạnh/sấy khô/ngâm nước muối. Mực chế biến là sản phẩm chiếm tỷ trọng nhiều nhất trong tổng cơ cấu sản phẩm mực, bạch tuộc nhập khẩu vào Nhật Bản.</p>\r\n<p> Do rào cản “thẻ vàng” cũng với chịu tác động từ đại dịch COVID-19 khiến các nhà hàng đóng cửa, nhu cầu tiêu thụ mực, bạch tuộc tại lĩnh vực dịch vụ thực phẩm giảm; kim ngạch xuất khẩu nhóm hàng này tại EU ghi nhận mức giảm 45,7% so cùng kỳ năm trước, chỉ đạt 17,5 triệu USD. Trong khối EU, các thị trường tiêu thụ mực, bạch tuộc Việt Nam lớn là Italy, Đức và Hà Lan cũng bị giảm lần lượt 51%, 28% và 37%. Tuy nhiên, từ 1/8/2020, khi EVFTA có hiệu lực, kỳ vọng hoạt động xuất khẩu mực, bạch tuộc sang EU trong những tháng cuối năm sẽ được cải thiện khi mực, bạch tuộc chế biến được hưởng thuế suất 0%.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'hiuhiu'),
(79, 'Giải pháp căn cơ phát triển thủy sản', 'Ngành thủy sản đang phải đối diện với rất nhiều khó khăn nội tại lẫn khách quan', '          <p><strong>Nhận định xác thực</strong></p>\r\n<p>Ông Phạm Anh Tuấn, Phó Chủ tịch Hội nghề cá Việt Nam Nam, cho biết các hiệp định thương mại được phê duyệt là “tấm hộ chiếu” cho sản phẩm thủy sản Việt Nam xâm nhập vào nhiều thị trường trên thế giới. Nhu cầu thủy sản tăng lên và thủy sản không chỉ là thực phẩm mà là nguồn nguyên liệu đầu vào cho các ngành khác. Tuy nhiên, phát triển thủy sản đang gặp nhiều thách thức, trong đó phải kể đến là dịch bệnh, gây rủi ro lớn (nguyên nhân là do hạ tầng đầu tư xuống cấp, chất lượng giống, sử dụng nhiều hóa chất, kháng sinh), các tác nhân mới gây dịch bệnh ngày một phát triển. Việt Nam có tiềm năng về nuôi biển, nhưng cần nhận định thách thức, khó khăn nào để có giải pháp thích hợp, tập trung về giống, những đối tượng có thị trường (ngoài cá song, cá giò, mở thêm các đối tượng khác), ứng dụng công nghệ cao. Mặt khác, nuôi trồng hiện đã vượt khai thác, trở thành nguồn cung thực phẩm chủ yếu cho hiện tại và tương lai. Nhưng thực tế, nhiều nước trong tương lai có phát triển nuôi biển với một số đối tượng nuôi cùng với Việt Nam, tốc độ 5 - 6%/năm; giá vật tư đầu vào tăng, giá bán sản phẩm có thể ổn định hoặc giảm; theo đó, sự cạnh tranh lớn trong thời gian tới cao, đòi  hỏi phải sản xuất hiệu quả hơn, giảm giá thành, truy xuất nguồn gốc, trách nhiệm xã hội cần đề cao hơn.</p>\r\n<p>Về lĩnh vực khai thác thủy sản, một số chuyên gia thủy sản cho rằng, lĩnh vực  này đang không bền vững do khai thác quá mức, cần phải giảm sản lượng nhất là giảm đội tàu khai thác, ngăn không đóng mới những nhóm tàu cần giảm và hỗ trợ chuyển đổi nghề như thế nào cho phù hợp, cần xây dựng đề án về giảm đội tàu khai thác. Về thị trường, theo các chuyên gia, công tác dự báo thị trường rất quan trọng cần thực hiện thường xuyên và cụ thể, chính xác với một số đối tượng cụ thể có sự kết hợp quảng bá thương hiệu sản phẩm đa dạng phong phú hơn; đồng thời đào tạo nguồn nhân lực; ứng dụng công nghệ thông tin trong quản lý tổng thể cũng là hướng đi cần chú trọng trong thời gian tới.</p>\r\n<p> </p>\r\n<p><strong>Thay đổi là cần thiết</strong></p>\r\n<p>Phát biểu tại Hội thảo: Xây dựng Dự thảo chiến lược ngành thủy sản đến năm 2030, định hướng đến năm 2045 của Tổng cục Thủy sản; Thứ trưởng Bộ NN&PTNT Phùng Đức Tiến cho biết, khai thác thủy sản hiện nay tuy có sự gia tăng về sản lượng nhưng chưa mang tính bền vững, bởi tổn thất sau thu hoạch vẫn còn cao 10 - 20%; NTTS có nhiều dư địa phát triển nhưng còn rào cản về chất lượng vật tư đầu vào, an toàn sinh học, dịch bệnh. Theo đó, khai thác thủy sản cần phải đi kèm với bảo vệ nguồn lợi, giảm tổn thất thông qua việc nâng cao hệ thống bảo quản sản phẩm, sản lượng có thể giảm nhưng giá trị phải tăng thông qua chế biến sâu. Trên cơ sở thực trạng này, đánh giá một cách hợp lý, đầy đủ, xác định nguyên nhân, nhìn nhận chính xác để xây dựng chiến lược hợp lý, hiệu quả. Chiến lược của một ngành rất giàu tiềm năng thế mạnh, có vai trò rất quan trọng, là định hướng cho sự phát triển bền vững, nâng cao giá trị; đảm bảo chất lượng sản phẩm và thương hiệu xuất khẩu khi nhu cầu thị trường cho thủy sản còn rất tiềm năng.</p>\r\n<p>Dự thảo chiến lược ngành thủy sản đến năm 2030, định hướng đến năm 2045 tập trung vào một số giải pháp thực hiện, là: Khoa học công nghệ, hợp tác quốc tế, cơ sở hạ tầng, nhân lực, thị trường, xúc tiến thương mại, bảo vệ và tái tạo nguồn lợi thủy sản. Trong đó, tập trung vào các giải pháp đột phá là ứng dụng khoa học công nghệ tiên tiến hiện đại; hoàn thiện cơ sở hạ tầng, đầu tư đồng bộ hệ thống thông tin liên lạc quản lý tàu thuyền, cảng cá, khu neo đậu; đào tạo bồi dưỡng nguồn lực thủy sản; hoàn thiện các chính sách về tài chính (thuế, phí, đầu tư, tín dụng, bảo hiểm)…</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'thunthun'),
(80, 'Lợi ích của krill trong nuôi cá hồi', 'Trong nghiên cứu gần đây, krill được bổ sung vào thức ăn của cá hội không chỉ thúc đẩy phúc lợi động vật mà còn cải thiện đáng kể chất lượng thịt cá', '          <p>Đây cũng là phát hiện mới trong một nghiên cứu hợp tác giữa Công ty khai thác krill Nam Cực Aker BioMarine và Viện Thực phẩm, khai thác và NTTS Na Uy (Nofima). Aker BioMarine và Nofima đã tuyên bố các kết quả nghiên cứu đánh giá sức khỏe và chất lượng thịt cá hồi Atlantic sau một khoảng thời gian cho ăn bổ sung krill.</p>\r\n<p>TS Turid Morkore tại Nofima và tác giả của nghiên cứu cho biết, bột krill Nam Cực có sức hút lớn với cá chuyên gia nghiên cứu biển do thành phần dinh dưỡng giá trị. Các nghiên cứu trước đây đều chứng tỏ các tác dụng của krill, còn trong nghiên cứu này, chúng tôi muốn làm sáng tỏ cơ chế cơ bản phía sau những tác dụng đã được chứng minh của krill lên cá hồi nuôi Atlantic.</p>\r\n<p>Nghiên cứu được thực hiện trên 800 con cá hồi Atlantic trọng lượng 2,3 kg/con và thả ngẫu nhiên vào 8 lồng trên biển tại cơ sở thử nghiệm thức ăn LetSea ở Na Uy. Khẩu phần ăn của cá trong nghiên cứu này được bổ sung 12% bột krill và chứa ít bột cá (5%). Toàn bột thức ăn được thiết kế nhằm đáp ứng nhu cầu dinh dưỡng của cá hồi với thành phần EPA và DHA cân bằng. Sau 10 tuần, toàn bộ số cá trên được cân trọng lượng. Sau đó chọn ngẫu nhiên 20 con trong lồng để phân tích.</p>\r\n<p>Bột krill Nam Cực được ví như một nguồn đạm động vật biển bền vững cho ngành thức ăn thủy sản; với thành phần dinh dưỡng gồm protein, axit amin và tro, krill được coi là nguồn thay thế bột cá tiềm năng. Do đó, krill luôn được các chuyên gia dinh dưỡng đánh giá cao và là chủ đề nghiên cứu suốt nhiều năm qua nhằm đánh giá hàm lượng cao các chất EPA, DHA, phospholipids, vitamins, nucleotides và astaxanthin tự nhiên trong krill tác động thế nào lên sức khỏe của cá, phúc lợi động vật khi bổ sung vào thức ăn thủy sản.</p>\r\n<p>Những phát hiện đáng chú ý từ nghiên cứu này cụ thể như sau:</p>\r\n<p>Hình dáng thân: To hơn và hệ số điều kiện (CF) cao hơn</p>\r\n<p>Sức khỏe tim: Giảm tích tụ chất béo xung quanh tim</p>\r\n<p>Chất lượng thịt: Thớ thịt đều, cấu trúc collagen ổn định hơn và tính toàn vẹn của phần cơ tốt hơn</p>\r\n<p>Gen miễn dịch: Biểu hiện của gen miễn dịch trong gan được tăng cường.</p>\r\n<p>Ruột cuối: Giảm nhiễm khuẩn ở ruột cuối và không còn dịch nhày xuất hiện.</p>\r\n<p>Nuôi dưỡng cá hồi bằng thức ăn bổ sung krill giúp cá phát triển tốt, khỏe và đạt chất lượng thịt cao. TS Tibiabin Benitez Santana cho biết thêm, krill có sinh khối động vật biển lớn nhất hành tinh. Cùng với những giá trị dinh dưỡng quyền năng như trong nghiên cứu chỉ ra, cũng như phương pháp khai thác bền vững, bột krill Nam Cực xứng đáng là đối thủ tiềm năng để thay thế bột cá.</p>\r\n<p>Tình hình hiện nay đang đặt ra nhu cầu tìm kiếm cấp bách nguồn thức ăn có thể thay thế bột cá mà không ảnh hưởng tới sức khỏe và chất lượng cá hồi, đặc biệt giai đoạn nuôi tăng trưởng khi lượng tiêu thụ thức ăn cao nhất và tạo ra những ảnh hưởng lớn nhất lên chất lượng thịt.</p>\r\n<p>Aker Biomarine ra đời với niềm tin mạnh mẽ vào hiệu quả tích cực của krill. Đại diện Công ty cho biết, hơn một thập kỷ qua, công việc kinh doanh không ngừng lớn mạnh vì luôn kết hợp chăm sóc hệ sinh thái với khai thác. Đảm bảo sự phát triển bền vững của sinh khối krill và góp phần vào việc duy trì hệ sinh thái Nam Cực thịnh vượng là những mục tiêu hàng đầu của Aker Biomarine. Aker BioMarine cũng là hãng khai thác krill đầu tiên trên thế giới nhận chứng nhận sinh thái MSC.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 5, 'binbin'),
(81, 'Top 3 game offline hay nhất dưới 100MB cho Android', 'Game di động trực tuyến rất hay, nhưng không phải lúc nào cũng Wifi hoặc 4F để chơi được', '          <p>Các trò chơi trực tuyến, hàng đầu trên di động như PUBG Mobile đang ngày càng trở nên phổ biến hơn với mọi người… Tuy nhiên, để có thể chơi các loại trò chơi này, bạn phải có một thiết bị tốt và kết nối internet tốt, vì chúng đòi hỏi cả hiệu năng và dữ liệu. Không phải ai cũng có đủ khả năng - trong bài viết này, tôi sẽ liệt kê ra top 10 game offline dưới 100 MB hay nhất dành cho Android.</p>\r\n<p><strong>1 – Street Racing 3D (83 MB)</strong></p>\r\n<div class=\"VCSortableInPreviewMode active\">\r\n<div><a class=\"detail-img-lightbox\" title=\"Trò chơi ngoại tuyến hay nhất dưới 100 MB\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-1-15971355409781841367017.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_217565010424668160\" class=\"lightbox-content\" title=\"Top 10 game offline hay nhất dưới 100 MB cho Android, xóa tan nỗi lo ngốn dung lượng - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-1-15971355409781841367017.jpg\" alt=\"Top 10 game offline hay nhất dưới 100 MB cho Android, xóa tan nỗi lo ngốn dung lượng - Ảnh 1.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-1-15971355409781841367017.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">Trò chơi ngoại tuyến hay nhất dưới 100 MB</p>\r\n</div>\r\n</div>\r\n<p>Nếu trò chơi đua xe là sở thích của bạn, Street Racing 3D sẽ là trò chơi hay nhất trong danh sách này. Đây là một trò chơi đua xe với hơn 30 xe cực xịn sò từ tất cả các thương hiệu. Trò chơi không đòi hỏi nhiều từ hệ thống của bạn - điều khiển siêu mượt mà. Nó cũng có thể chạy trên mọi phiên bản Android. Phần tốt nhất là bạn có thể mời bạn bè của mình đua với bạn.</p>\r\n<p><strong>2 – Nova Legacy (45 MB)</strong></p>\r\n<div class=\"VCSortableInPreviewMode active\">\r\n<div><a class=\"detail-img-lightbox\" title=\"Trò chơi sinh tồn ngoại tuyến hay nhất cho Android dưới 100 MB\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-2-15971355411131423365078.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_217565011229970432\" class=\"lightbox-content\" title=\"Top 10 game offline hay nhất dưới 100 MB cho Android, xóa tan nỗi lo ngốn dung lượng - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/11/photo-2-15971355411131423365078.jpg\" alt=\"Top 10 game offline hay nhất dưới 100 MB cho Android, xóa tan nỗi lo ngốn dung lượng - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-2-15971355411131423365078.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">Trò chơi sinh tồn ngoại tuyến hay nhất cho Android dưới 100 MB</p>\r\n</div>\r\n</div>\r\n<p>Nova Legacy là một trong những game bắn súng <span id=\"IMSAlertPersonCheckItem1597135788844\" class=\"IMSAlertPersonCheckItem ixlxexvxexl2\">chế độ</span> Story hay nhất hiện có trên thị trường. Trong khi <span id=\"IMSAlertPersonCheckItem1597135788845\" class=\"IMSAlertPersonCheckItem ixlxexvxexl2\">chế độ</span> chính của Nova Legacy là nhiều người chơi, có một chiến dịch mạnh mẽ với 19 cấp độ hành động. Bên cạnh <span id=\"IMSAlertPersonCheckItem1597135788846\" class=\"IMSAlertPersonCheckItem ixlxexvxexl2\">chế độ</span> cốt truyện, Nova Legacy cũng bao gồm một số <span id=\"IMSAlertPersonCheckItem1597135788847\" class=\"IMSAlertPersonCheckItem ixlxexvxexl2\">chế độ</span> bổ sung tốt nhất như nhiệm vụ thử thách trong thời gian giới hạn, tùy chỉnh nhiệm vụ và hơn thế nữa.</p>\r\n<p><strong>3 – World Cricket Championship 2 (68 MB)</strong></p>\r\n<div class=\"VCSortableInPreviewMode active\">\r\n<div><a class=\"detail-img-lightbox\" title=\"10 trò chơi ngoại tuyến hàng đầu dưới 100 MB\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-3-1597135541464586335774.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_217565013641695232\" class=\"lightbox-content\" title=\"Top 10 game offline hay nhất dưới 100 MB cho Android, xóa tan nỗi lo ngốn dung lượng - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/11/photo-3-1597135541464586335774.jpg\" alt=\"Top 10 game offline hay nhất dưới 100 MB cho Android, xóa tan nỗi lo ngốn dung lượng - Ảnh 3.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/photo-3-1597135541464586335774.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">10 trò chơi ngoại tuyến hàng đầu dưới 100 MB</p>\r\n</div>\r\n</div>\r\n<p>Nếu bạn thích xem hoặc chơi cricket, đây có lẽ là trò chơi hay nhất trong danh sách này. Trò chơi có sự lựa chọn gồm 28 đội, 42 sân vận động và nhiều hơn nữa cùng với các tính năng khác nhau như hiệu ứng thời tiết. Lối chơi của game khá cuốn hút - bạn có thể thực hiện tất cả các kiểu bắn, từ một cú đánh từ trên xuống cho đến trực thăng bắn súng và hơn thế nữa.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(82, '\"Gấu mẹ vĩ đại\" KkOma báo tin vui, sắp đón con đầu lòng cuối năm nay', 'Vị HLV huyền thoại KkOma được cổ động viên T1 gọi trìu mến là \"gấu mẹ\" cuối cùng cũng lên chức cha sau gần 1 năm lập gia đình.', '          <p>Trả lời phỏng vấn sau trận đấu, HLV trưởng của đội - <a class=\"seo-suggest-link\" title=\"kkoma\" href=\"https://gamek.vn/kkoma.htm\" target=\"_blank\" rel=\"noopener\">kkOma</a> đã bày tỏ sự tiếc nuối khi không thể giúp VG đạt được mục tiêu đã đề ra, đồng thời, anh cũng đưa ra lời khen ngợi về sự tiến bộ của các học trò trong giai đoạn mùa hè.</p>\r\n<p>Kết quả tại LPL năm nay khó có thể coi là một thành công của cựu thuyền trưởng SKT. Dẫu vậy, rõ ràng là VG dưới thời vị HLV này đã có những bước tiến nhất định, đặc biệt là về lối chơi và chiến thuật.</p>\r\n<p>Ngoài ra, vị chiến lược gia từng được coi là \"người trong mộng\" của không biết bao game thủ nữ trong quá khứ, còn thông báo thêm một tin vô cùng đáng mừng với các fan hâm mộ, đó là việc vợ chồng anh chuẩn bị đón em bé đầu lòng vào cuối năm nay.</p>\r\n<p>Cụ thể, khi Phóng viên đặt câu hỏi rằng: <em>\"Anh có dự định gì trong giai đoạn off-season sắp tới không?\"</em></p>\r\n<p>kkOma đã trả lời rằng: <em>\"Nếu không có vấn đề gì phát sinh, tôi muốn được quay về Hàn Quốc càng sớm càng tốt để đoàn tụ cùng gia đình. Con của tôi sắp chào đời và tôi hi vọng rằng sẽ trở về kịp vào ngày sinh của bé.\"</em></p>\r\n<p>Cựu HLV <a class=\"seo-suggest-link\" title=\"t1\" href=\"https://gamek.vn/t1.htm\" target=\"_blank\" rel=\"noopener\">T1</a> - kkOma chính thức lập gia đình vào cuối tháng 12 năm ngoái, trong một đám cưới bí mật với sự tham dự của người thân và một số bạn bè thân thiết. Được biết sau khi kết hôn, anh khăn gói sang Trung Quốc đảm nhiệm vị trí HLV trưởng của Vici Gaming, trong khi cô vợ Ji-soo vẫn ở lại Hàn Quốc cùng gia đình.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'thonthon'),
(83, 'Yone - Yasuo Đường dưới: Song sát hay bị tàn sát?', 'Mặc kệ thắng thua, bộ đôi kiếm sĩ Ionia đang được vô số người chơi đem xuống thử nghiệm tại đường dưới.', '          <p>Có thể nói kể từ ngày xuất hiện thì <a class=\"seo-suggest-link\" title=\"yone\" href=\"https://gamek.vn/yone.htm\" target=\"_blank\" rel=\"noopener\">Yone</a> không chỉ được đem ra đường giữa mà còn được đem xuống đường dưới rất nhiều. Và một trong những trợ thủ đang được thử nghiệm nhiều nhất với Yone tại đường dưới là \"đấng\" <a class=\"seo-suggest-link\" title=\"yasuo\" href=\"https://gamek.vn/yasuo.htm\" target=\"_blank\" rel=\"noopener\">Yasuo</a>. Nhưng vấn đề đang được nhiều người quan tâm là anh em nhà Yone và Yasuo \"song kiếm hợp bích\" thì có thực sự mạnh mẽ hay chỉ \"tấu hài\" tại đường dưới.</p>\r\n<p>Đầu tiên, đánh giá về điểm mạnh thì <a class=\"seo-suggest-link\" title=\"Yone - Yasuo Đường dưới \" href=\"https://gamek.vn/yone-yasuo-duong-duoi.htm\" target=\"_blank\" rel=\"noopener\">Yone - Yasuo</a> cũng như nhiều bộ đôi đấu sĩ đường dưới khác – có sát thương rất lớn, khả năng tiếp cận đối thủ tốt và khống chế cứng diện rộng ở tầm tương đối xa. Ngoài ra, bộ đôi này cũng không quá sợ những tướng tay dài cấu rỉa mạnh như Varus, vì Yasuo có Tường Gió (W) và nếu Varus giữ Q quá lâu cũng sẽ bị Yasuo và Yone tiếp cận và tấn công thẳng mặt.</p>\r\n<p>Đến cấp độ 6, combo kỹ năng của 2 đấu sĩ này là cực kỳ mạnh nếu kết hợp nhuần nhuyễn đúng người và đúng lúc. Khả năng băng trụ là có với Xuất Hồn (E) của Yone, sẵn sàng lao vào chống chịu sát thương từ trụ trước và rút về khi cảm thấy nguy hiểm. Tới giai đoạn late game thì sẽ không còn hỗ trợ tại đường dưới nữa mà là 2 đấu sĩ với lượng sát thương theo thời gian khổng lồ, rất mạnh dù là tham gia giao tranh tổng hay đẩy lẻ.</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/photo-1-159694578417422597786.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216769049133752320\" class=\"lightbox-content\" title=\"Yone -Yasuo Đường dưới: Song sát hay bị tàn sát? - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/9/photo-1-159694578417422597786.jpg\" alt=\"Yone -Yasuo Đường dưới: Song sát hay bị tàn sát? - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/photo-1-159694578417422597786.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Còn về nhược điểm, vì đều là những đấu sĩ tay ngắn nên nếu không giữ thế lính và farm hợp lí thì sẽ bị đối thủ đóng băng lính cũng như đè đường tới mức \"không thở được\". Bộ đôi này cũng có quá nhiều kỹ năng định hướng, thế nên lượng sát thương gây ra cũng tỉ lệ thuận với số kỹ năng dùng trúng đối thủ - rất thiếu ổn định. Đặc biệt, vì các kỹ năng tiếp cận đối thủ đều có thể bị ngăn chặn, vậy nên Yone và Yasuo có thể sẽ rất e ngại những tướng hỗ trợ cứng cáp nhiều khống chế không thể chặn bằng Tường Gió như Alistar, Galio, Leona, Poppy,...</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/photo-2-1596945783637384362198.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216769047233728512\" class=\"lightbox-content\" title=\"Yone -Yasuo Đường dưới: Song sát hay bị tàn sát? - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/9/photo-2-1596945783637384362198.jpg\" alt=\"Yone -Yasuo Đường dưới: Song sát hay bị tàn sát? - Ảnh 3.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/photo-2-1596945783637384362198.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Yone và Yasuo cũng không hề có kỹ năng tẩu thoát, nếu bị kẻ địch gank từ sớm thì tỉ lệ sống sót là không hề cao. Nếu bị những con bài như Rammus, Zac, Trundle,... tiếp cận thì gần như không có lối thoát. Kỹ năng khống chế cũng không phải lúc nào cũng có vì cả Yasuo và Yone đều cần tích Q để hất tung. Bên cạnh đó, việc mua 2 đồ hỗ trợ để lấy thêm tiền cũng không còn hiệu quả, vì nếu mùa đồ hỗ trợ mà farm quá nhiều thì lượng vàng có được khi farm lính sẽ bị giảm xuống rất nhiều. Một điểm yếu khác nữa là bộ đôi này chỉ có sát thương vật lý mà thôi, nếu hỗ trợ đối thủ lên full giáp thì Yone và Yasuo sẽ thiếu hụt sát thương trầm trọng.</p>\r\n<p>Với những lý do bên trên, nếu bạn chỉ chơi với mục đích đơn thuần là cho vui thì bộ đôi này rất tuyệt vời. Còn nếu dùng Yasuo và Yone đường dưới để leo rank hay \"tryhard\" thì cần có kỹ năng vượt trội đối thủ, cũng cần có màn cấm chọn hợp lý thì mới không bị hủy diệt tại đường dưới.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'binbin'),
(84, 'Trận đấu của Sunning và Funplus Phoenix bị lùi lịch vì tuyển thủ bất ngờ sốt cao', 'Rất may là trường hợp của xạ thử LWX bên phía FPX đã được xác nhận tạm thời không liên quan đến Covid-19.', '          <p>Theo dự kiện, trận đấu giữa <a class=\"seo-suggest-link\" title=\"Suning \" href=\"https://gamek.vn/suning.htm\" target=\"_blank\" rel=\"noopener\">Suning</a> và <a class=\"seo-suggest-link\" title=\"FunPlus Phoenix \" href=\"https://gamek.vn/funplus-phoenix.htm\" target=\"_blank\" rel=\"noopener\">FunPlus Phoenix</a> trong khuôn khổ lượt trận cuối cùng vòng đấu bảng <a class=\"seo-suggest-link\" title=\"LPL Mùa Hè 2020 \" href=\"https://gamek.vn/lpl-mua-he-2020.htm\" target=\"_blank\" rel=\"noopener\">LPL Mùa Hè 2020</a> sẽ diễn ra vào 17h ngày hôm nay 9/8 theo giờ Việt Nam. Tuy nhiên, cách đây ít giờ, Ban tổ chức giải đấu cũng như lãnh đạo đội tuyển FPX đã bất ngờ đưa ra thông báo về việc cuộc đối đầu này có nguy cơ bị hoãn do một tuyển thủ có dấu hiệu sốt cao.</p>\r\n<p>Cụ thể, Xạ thủ LWX của FPX được thông báo rằng có dấu hiệu mệt mỏi, sốt cao kể từ tối ngày hôm qua. FPX đã gửi thông báo lên phía Ban tổ chức LPL và đưa tuyển thủ của mình đi xét nghiệm do lo ngại dính đến dịch bệnh Covid-19.</p>\r\n<p>Kết quả kiểm tra ban đầu cho thấy LWX bị sốt cao, nhưng nguyên nhân là do viêm amidan cấp tính. Tuy vậy, Ban tổ chức giải vẫn yêu cầu theo dõi thêm đối với tuyển thủ này để cập nhật những diễn biến mới về tình hình sức khỏe của anh.</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/photo-1-1596955218039645029838.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216808626340831232\" class=\"lightbox-content\" title=\"Trận đấu của Suning và FunPlus Phoenix bị lùi lịch vì tuyển thủ bất ngờ sốt cao - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/9/photo-1-1596955218039645029838.jpg\" alt=\"Trận đấu của Suning và FunPlus Phoenix bị lùi lịch vì tuyển thủ bất ngờ sốt cao - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/photo-1-1596955218039645029838.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Chính vì lẽ đó, gặp trận giữa FPX và SN sẽ bị lùi từ 17h00 chiều hôm nay xuống 20h00 (giờ Việt Nam) và đánh sau 2 cặp trận của Rogue Warriors gặp Royal Never Give Up và Invictus Gaming gặp Vici Gaming.</p>\r\n<p>Với việc cả FunPlus Phoenix lẫn Suning đều đã chắc suất vào vòng Playoffs, trận đấu này chỉ còn mang ý nghĩa quyết định thứ bậc của họ trên BXH, qua đó xác định các cặp đấu sẽ diễn ra trong vòng Playoffs, vậy nên nhiều khả nằng FPX sẽ cho phép Xạ thủ chủ lực của họ nghỉ ngơi, và đôn tuyển thủ trẻ lên để thay thế.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'thunthun'),
(85, '\"One tap Machine\" chính thức giải nghệ CS:GO, chuyển sang thi đấu Valorant', 'Vào ngày hôm qua ScreaM đã chính thức thông báo từ giã CS:GO để tập trung thi đấu cho team Liquid ở bộ môn Valorant', '          <p>Tuy không có nền CS phát triển như một số quốc gia khác cũng thuộc châu Âu nhưng Vương quốc Bỉ cũng đã ghi dấu mình trên <span id=\"IMSAlertPersonCheckItem1597120148659\" class=\"IMSAlertPersonCheckItem ixlxexvxexl2\">bản đồ</span> <a class=\"seo-suggest-link\" title=\"cs:go\" href=\"https://gamek.vn/csgo.htm\" target=\"_blank\" rel=\"noopener\">CS:GO</a> thế giới với cái tên Adil \"<a class=\"seo-suggest-link\" title=\"Adil \" href=\"https://gamek.vn/adil-scream-benrlito.htm\" target=\"_blank\" rel=\"noopener\">ScreaM</a>\" Benrlitom. Player sinh năm 1994 này được nhiều người biết đến với biệt danh \"Headshot Machine\" khi anh nổi tiếng với những pha one tap cực kỳ chính xác và hoa mỹ.</p>\r\n<p>Xuyên suốt 10 năm thi đấu chuyên nghiệp của mình, ScreaM đã từng khoác áo nhiều đội tuyển nổi tiếng như Titan, G2 Esports, Envyus, Fnatic,... và đã cùng những người đồng đội vô địch nhiều giải đấu danh giá. Về mặt thành tích cá nhân, anh được vinh danh là MVP tại ECS Season 1 Finals và 2 lần xuất hiện trong danh sách top 20 player xuất sắc nhất năm do hltv bình chọn: #9 vào năm 2016 và #7 vào năm 2013.</p>\r\n<p>Tài năng là vậy nhưng sự nghiệp CS:GO của ScreaM khá là lận đận khi anh bị một số người đồng đội cũ nhận xét là \"thiếu nhiều yếu tố mà bạn cần ở một player chuyên nghiệp\". Sau khi bị kick khỏi G2 Esports vào đầu năm 2017 để nhường chỗ cho đội hình Super Team của những người Pháp thì anh đã gần như vắng bóng trên đấu trường chuyên nghiệp đỉnh cao. Anh có thời gian stand-in ngắn cho Fnatic nhưng không thành công.Sau đó anh thi đấu cho những đội thuộc tier 2 như NoChance, GamerLegion vài tập trung vào việc stream cá nhân.</p>\r\n<p>Vào ngày hôm qua, sau hơn 11 tháng ngồi trên ghế dự bị của GamerLegion thì ScreaM đã thông báo việc giải nghệ CS:GO sau hơn 10 năm thi đấu chuyên nghiệp. Thông báo này được đưa ra sau khi Team Liquid chính thức công bố đội hình team <a class=\"seo-suggest-link\" title=\"valorant\" href=\"https://gamek.vn/valorant.htm\" target=\"_blank\" rel=\"noopener\">Valorant</a> của mình, trong đó có sự góp mặt của ScreaM và các cựu thành viên của fish123.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(86, 'Những kỹ năng bá đạo mà Riot Games có thể đem vào Đấu Trường Chân Lý mùa 4', 'Mùa 3 cũng chỉ còn vài bản cập nhật nữa là sẽ kết thúc, vậy nên ngoài cộng đồng có thể \"mơ mộng\" về một mùa 4 đầy thú vị của Đấu Trường Chân Lý', '          Đối với người chơi LMHT thì việc trải nghiệm những vị tướng mới là điều cực kỳ thú vị, giống như Yone đang cùng \"đấng\" Yasuo khuấy đảo khắp các bậc rank Việt. Trong Đấu Trường Chân Lý cũng vậy, nhiều kỳ thủ rất thích chơi những \"quân cờ\" mới mỗi khi mùa giải mới bắt đầu.\r\n\r\nVì mùa 4 cũng không còn xa xôi nữa, chúng ta có thể dần mường tượng ra những kỹ năng cũng như những vị tướng thú vị sẽ xuất hiện trên bàn cờ trong thời gian tới:\r\nTrong mùa trước thì Riot Games đã giữ lại một khả năng trong game đó là miễn nhiễm khống chế, chuyển từ Singed sang Aurelion Sol. Vậy nên mùa này chúng ta cũng có thể hi vọng Riot Games sẽ giữ lại kỹ năng độc đáo của Thresh là đem các quân cờ ở hàng ghế dự bị xuống, Kalista với Định Mệnh Vẫy Gọi sẽ là cái tên cực kỳ phù hợp cho việc đó. Không những vậy, Kalista cũng là một bóng ma có trang phục Huyết Nguyệt – nằm trong chủ đề mà Riot đã giới thiệu về mùa 4 trong những ngày gần đây.\r\nMặc dù mùa 3 không có vị tướng nào sở hữu kỹ năng bất tử, thế nhưng mùa 2 thì có Taric và mùa 1 thì có Kayle. Vậy nên rất có thể mùa 4 Riot Games sẽ đem kỹ năng bất tử lại thông qua chiêu cuối của Tryndamere là Từ Chối Tử Thần. Trùng hợp là Tryndamere cũng là một con người và sở hữu trang phục Huyết Nguyệt cùng với Quan Vũ – các trang phục đều nằm trong chủ đề của mùa 4.\r\n\r\nRiven – Tam Bộ Kiếm\r\n\r\nNhững kỹ năng bá đạo mà Riot Games có thể đem vào Đấu Trường Chân Lý mùa 4 - Ảnh 3.\r\nMặc dù phiên bản Tam Bộ Kiếm của Riven hiện tại cũng ấn tượng, tuy nhiên nó lại thiếu đi cú hất tung chất lượng mà Riven nên có. Vậy nên trong mùa 4 nếu xuất hiện tại thì người chơi chắc chắn sẽ yêu thích một Riven có hất tung hoặc làm choáng hơn. Đặc biệt, Riven là vị tướng hiếm hoi có trang phục ở toàn bộ chủ đề mà Riot Games \"nhá hàng\" mùa 4 – trang phục cơ bản (người thường), Riven Tàn Kiếm U Hồn (Hoa Linh Lục Địa), Riven Ngạo Kiếm (chủ đề Tiên Hiệp cùng với Janna Tiên Kiếm), Riven Long Kiếm (chủ đề Warring Kingdoms cùng với Azir Đinh Dậu).\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'thonthon'),
(87, 'Chân dung Tianie - Nữ Streamer làm dậy sóng kênh thầy Ba vì giọng hát ngọt ngào', 'Thầy giáo Ba có vẻ rất có duyên với những nữ Streamer vừa xinh đẹp lại vừa tài năng thì phải.', '          <p>Lần đầu tiên trong sự nghiệp hành nghề của <a class=\"seo-suggest-link\" title=\"Thầy giáo Ba \" href=\"https://gamek.vn/thay-giao-ba.htm\" target=\"_blank\" rel=\"noopener\">Thầy Giáo Ba</a>, kênh trực tiếp của Thầy bị dính bản quyền mà các học trò vẫn không một lời oán thán, thậm chí còn cười như được mùa, bởi lẽ sự việc xảy đến một cách khá éo le, khi trong những buổi stream gần đây, Thầy Giáo Ba cùng duo với một nữ <a class=\"seo-suggest-link\" title=\"streamer\" href=\"https://gamek.vn/streamer.htm\" target=\"_blank\" rel=\"noopener\">streamer</a> có nghệ danh <a class=\"seo-suggest-link\" title=\"Tianie \" href=\"https://gamek.vn/tianie.htm\" target=\"_blank\" rel=\"noopener\">Tianie</a>.</p>\r\n<p>Với biệt tài ca hát, Tianie đã dành tặng cho các khán giả của kênh Thầy Ba những món quà đặc biệt, chính là những màn cover các ca khúc âm nhạc đang hot hiện nay. Và có lẽ vì cover quá hay, quá giống bản gốc, nên màn khoe giọng của Tianie lại vô tình khiến YouTube nhận diện nhầm và cảnh báo bản quyền với kênh Thầy Ba.</p>\r\n<p>Lý do này thì rõ là chẳng ai ngờ tới, nhưng điều đó cũng chẳng còn quan trọng nữa, khi mà giọng ca ngọt ngào của Tianie đã hớp hồn hoàn toàn các fan hâm mộ của SBTC, và ngay lập tức, danh tính của cô nàng nữ streamer có biệt tài \"nuốt đĩa\" này trở thành đề tài được cộng đồng game thủ vô cùng quan tâm.</p>\r\n<p>Tianie tên thật là Nguyễn Ngọc Trâm Anh, sinh ngày 11/05/1997. Dù là người Việt Nam chính gốc, nhưng Tianie lại sinh ra và lớn lên ở Moscow, Liên bang Nga. Có lẽ vì sinh sống và học tập từ bé ở nước ngoài, nên dù mang trong mình dòng máu Việt nhưng Trâm Anh vẫn khiến người đối diện mê mẩn bởi nét đẹp nhẹ nhàng pha lẫn chút \"Tây Tây\".</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/10/photo-1-1597068499351887127823.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_217283753314689024\" class=\"lightbox-content\" title=\"Chân dung Tianie - Nữ streamer làm dậy sóng kênh Thầy Ba vì giọng hát ngọt ngào: Du học sinh, hát hay học giỏi, Đại Cao Thủ LMHT - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/690/133514250583805952/2020/8/10/photo-1-1597068499351887127823.jpg\" alt=\"Chân dung Tianie - Nữ streamer làm dậy sóng kênh Thầy Ba vì giọng hát ngọt ngào: Du học sinh, hát hay học giỏi, Đại Cao Thủ LMHT - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/10/photo-1-1597068499351887127823.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Thế nhưng, điểm đặc biệt nhất của cô nàng streamer này lại là niềm đam mê đặc biệt với LMHT và một khả năng \"try-hard\" cực khủng, khiến cho ngay cả những game thủ nam cũng phải kiêng nể. Tianie sở hữu bậc Xếp hạng Đại Cao Thủ 350 điểm LMHT, ngoài ra, cô cũng chứng tỏ khả năng không phải dạng vừa với bộ môn cờ nhân phẩm Đấu Trường Chân Lý.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(88, 'Bạn biết gì về cặp đôi đường dưới Sona - Lux, sản phẩm siêu lỗi mà Riot vô tình tạo ra', 'Sự quá tay trong việc buff Lưỡi Gươm Đoạt thuật của Riot đã vô tình tạo ra bộ đôi đường dưới \"siêu lầy\" là Sona - Lux', '          <p>Những đội hình không có Xạ Thủ thuần túy giờ đây đã nên quen thuộc hơn đối với người chơi <a class=\"seo-suggest-link\" title=\"lmht\" href=\"https://gamek.vn/lmht.htm\" target=\"_blank\" rel=\"noopener\">LMHT</a> và chiến thuật <a class=\"seo-suggest-link\" title=\"Sona - Lux \" href=\"https://gamek.vn/sona-lux.htm\" target=\"_blank\" rel=\"noopener\">Sona - Lux</a> ở vị trí đường dưới là lối chơi mới nhất đi theo hướng này. Thậm chí nó đã được áp dụng vào các giải đấu chuyên nghiệp và được sử dụng bởi những tuyển thủ hàng đầu, điều này cho thấy rằng cặp đôi Sona - Lux hoàn toàn đủ mạnh để tồn tại trong game.</p>\r\n<p>Vậy chiến thuật này sinh ra từ đâu? Về cơ bản thì Sona là một tướng cực kì mạnh ở giai đoạn cuối trận, những đội tuyển chuyên nghiệp đã nhận ra điều này và họ thường xuyên sử dụng Taric hoặc Tahm Kench để bảo vệ. Tuy nhiên chúng lại khiến cho đường dưới của bạn chơi quá thụ động và gần như sẽ thua đường hoàn toàn ở đầu trận, dẫn tới thế trận bất lợi sau này.</p>\r\n<p>Vì thế khi mà dòng trang bị Lưỡi Gươm Đoạt Thuật được <a class=\"seo-suggest-link\" title=\"riot games\" href=\"https://gamek.vn/riot-games.htm\" target=\"_blank\" rel=\"noopener\">Riot Games</a> buff mạnh ở bản 10.15, game thủ đã tìm ra cách để Sona đường dưới trở lại và đối tác lần này là Lux, một pháp sư có khả năng kiểm soát đường cực kì tốt. Điểm đặc biệt của lối chơi này đó là cả 2 tướng Sona - Lux đều sử dụng Lưỡi Gươm Đoạt Thuật, cố gắng nâng cấp nó lên nhanh nhất có thể để tận dụng lượng chỉ số mạnh mà nó đêm lại.</p>\r\n<div class=\"VCSortableInPreviewMode\">\r\n<div><a class=\"detail-img-lightbox\" title=\"Việc Lưỡi Gươm Đoạt Thuật được buff quá mạnh khiến cho những Hỗ Trợ dạng Pháp Sư như Lux mạnh hơn rất nhiều\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/8/lux-15968602559881180827453.png\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216410387562868736\" class=\"lightbox-content\" title=\"Bạn biết gì về cặp đôi đường dưới Sona - Lux, sản phẩm siêu lỗi mà Riot vô tình tạo ra - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/8/lux-15968602559881180827453.png\" alt=\"Bạn biết gì về cặp đôi đường dưới Sona - Lux, sản phẩm siêu lỗi mà Riot vô tình tạo ra - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/8/lux-15968602559881180827453.png\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">Việc Lưỡi Gươm Đoạt Thuật được buff quá mạnh khiến cho những Hỗ Trợ dạng Pháp Sư như Lux mạnh hơn rất nhiều</p>\r\n</div>\r\n</div>\r\n<p>Điểm đặc biệt nhất của chiến thuật này là đội hình của bạn sẽ cực kì mạnh ở giữa trận nhờ sự đa dụng lớn từ cặp đôi Sona - Lux. Thông thường một vị tướng Xạ Thủ muốn đủ mạnh thì phải đáp ứng yêu cầu về mặt trang bị (từ 2-3 món đồ), đó còn chưa kể tới những thứ khác như giữ vị trí hay thời điểm giao tranh.</p>\r\n<p>Trái lại thì bộ đôi Sona - Lux có thể mạnh ngay từ ngưỡng 1 trang bị và hoàn thành việc lên Nanh Băng. Họ có thể gây ra một lượng sát thương đủ lớn, hồi máu tốt, khống chế diện rộng và giúp đội hình của bạn kiểm soát giao tranh cực kì tốt. Đó còn chưa kể tới việc buff cho những chủ lực khác và họ sẽ mạnh lên một cách đột biến trong những cuộc giao tranh đầu trận.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'binngu');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(89, 'Cầm Lee Sin đá bay nhà vô địch thế giới, Sofm cùng Suning tiến vào tứ kết LPL', 'Một cái kết đẹp cho sofM và các đồng đội tại vòng bảng LPL Mùa Hè 2020', '          <p>Cuộc đối đầu giữa <a class=\"seo-suggest-link\" title=\"Suning \" href=\"https://gamek.vn/suning.htm\" target=\"_blank\" rel=\"noopener\">Suning</a> và <a class=\"seo-suggest-link\" title=\"FunPlus Phoenix \" href=\"https://gamek.vn/funplus-phoenix.htm\" target=\"_blank\" rel=\"noopener\">FunPlus Phoenix</a> cũng chính là trận đấu hạ màn cho giai đoạn vòng bảng của LPL Mùa Hè 2020, và cả 2 đội đều bước vào ngày thi đấu hôm nay với quyết tâm giành chiến thắng rất cao để làm một cú hích cho giai đoạn playoffs sắp tới.</p>\r\n<p>Bước vào trận đấu này, FPX gặp phải bất lợi lớn khi Xạ thủ của họ LWX gặp vấn đề sức khỏe và phải nghỉ thi đấu, trong khi danh sách đăng ký LPL của đội tuyển này không bao gồm bất kỳ tuyển thủ thay thế nào.</p>\r\n<p>Cực chẳng đã, nhà ĐKVĐ thế giới đã tung ra một đội hình hết sức \"dị\", với Khan Đường trên, Gimgoon Đường giữa và Doinb sử dụng các vị tướng pháp sư Đường dưới. Chính vì không có một xạ thủ xịn trong đội hình, nên giai đoạn ban/pick của FPX cũng bị ảnh hưởng, bởi họ luôn phải ưu tiên cho quân bài Jayce để cân bằng giữa lượng sát thương vật lý và sát thương phép.</p>\r\n<p>Dẫu vậy, chất lượng của trận đấu cũng không vì thế mà giảm sút, cả hai đội đều thi triển một lối chơi tương đối cởi mở, và ở 2 ván đấu đầu tiên, những lợi thế liên tục được \"quăng quật\" sang hai phía sau hàng loạt pha giao tranh dài hơi.</p>\r\n<p>SN là đội vươn lên dẫn trước ở ván 1 với màn trình diễn hoàn hảo của Angel, ván 2 dù đã lựa chọn phương án counter pick mang tên Olaf, nhưng <a class=\"seo-suggest-link\" title=\"SN SofM \" href=\"https://gamek.vn/sn-sofm.htm\" target=\"_blank\" rel=\"noopener\">SofM</a> vẫn không thể giải mã thành công hiện tượng Lillia Đi rừng.</p>\r\n<p>Thế nhưng sang đến ván 3, sự áp đảo cuối cùng đã nghiêng về một phía, đó chính là Suning, với bộ đôi Renekton - Lee Sin, Suning triển khai thế trận tấn công vũ bão, tận dụng chất tướng giao tranh cực tốt đầu game, sau đó dễ dàng lăn cầu tuyết ván đấu khi cả Bin lẫn SofM đều quá xanh (nhưng thần rừng người Việt Nam vẫn quyết tâm lên đồ tank cho Lee Sin), kết liễu nhà Đương kim vô địch CKTG với tỉ số 2-1.</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/suning-15969923446721917921034.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216964341808402432\" class=\"lightbox-content\" title=\"Cầm Lee Sin đá bay nhà vô địch thế giới, SofM cùng Suning tiến vào Tứ kết LPL - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/690/133514250583805952/2020/8/9/suning-15969923446721917921034.jpg\" alt=\"Cầm Lee Sin đá bay nhà vô địch thế giới, SofM cùng Suning tiến vào Tứ kết LPL - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/9/suning-15969923446721917921034.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Với vị trí thứ 4 chung cuộc sau vòng bảng, Suning sẽ tiến thẳng vào vòng Tứ kết playoffs LPL Mùa Hè 2020. Đối thủ của họ sẽ là đội chiến thắng trong cặp trận giữa Victory Five và FunPlus Phoenix thuộc khuôn khổ Round 1.</p>\r\n<p>Nếu giành chiến thắng trong cặp đấu Tứ kết này, SN sẽ chính thức đủ điều kiện để tham dự Vòng loại khu vực Chung kết thế giới 2020 của Trung Quốc, cùng với đó, họ sẽ bước vào Bán kết để đối đầu với cái tên sừng sỏ Top Esports.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(90, 'Nội tại Pelu cũng không thể ngăn cản GAM Esports phục hận thành công Team Flash', 'Sự tỏa sáng của Dia1 đã giúp GAM trả lại Team Flash cả vốn lẫn lãi sau trận thua ở lượt đi.', '          <p>Trước khi cuộc thư hùng giữa <a class=\"seo-suggest-link\" title=\"GAM Esports \" href=\"https://gamek.vn/gam-esports.htm\" target=\"_blank\" rel=\"noopener\">GAM Esports</a> và <a class=\"seo-suggest-link\" title=\"team flash\" href=\"https://gamek.vn/team-flash.htm\" target=\"_blank\" rel=\"noopener\">Team Flash</a> diễn ra, cộng đồng game thủ đã có một chút sự lo lắng cho số phận của Levi và các đồng đội, không phải vì phong độ của họ kém hơn đối thủ, càng không phải do chất lượng đội hình, mà chỉ là bởi Thánh dự Pelu a.k.a BLV Hoàng Luân lại trổ tài dự đoán.</p>\r\n<p>Tuy nhiên, rốt cuộc thì lần này những người phải nhận trái đắng lại là Team Flash, bởi Hoàng Luân đã đưa ra một dự định chính xác về diễn biến cũng như kết quả trận đấu.</p>\r\n<p>Dù mang tính chất là một phiên bản \"El Clasico\" của VCS, nhưng với việc cả GAM lẫn FL đều gần như nắm chắc 2 vị trí đầu bảng, thì trận đấu này không còn nhiều ý nghĩa về mặt điểm số. Tuy vậy, đây lại là một sự may mắn với các fan hâm mộ, bởi nó đã dẫn đến một thế trận vô cùng cởi mở và giao tranh cực kỳ mãn nhãn giữa 2 đội.</p>\r\n<p>Trong cả 3 ván đấu, GAM hay FL đều không có bất kỳ lợi thế quá rõ ràng nào, khi giao tranh thì liên tục nổ ra và những mục tiêu lớn cũng được trao đổi qua lại, dẫu vậy, \"lời đồn\" về cái tên \"Dia1 Esports\" cũng hoàn toàn có cơ sở, bởi trong một thế trận giằng co, thì sự tỏa sáng của một cá nhân chính là chìa khóa dẫn đến chiến thắng.</p>\r\n<p> </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'binngu'),
(91, 'Những vị tướng sở hữu \"IQ 200\" trong vũ trụ LMHT, đi thi tốt nghiệp thừa sức đạt thử khoa', 'Không cần đến màn thể hiện của người chơi, một số vị tướng trong LMHT đã mặc định sở hữu một trí tuệ đỉnh cao, thể hiện qua tạo hình, bộ kỹ năng hay cốt truyện.', '          <p>Được mệnh danh là thần đồng xứ Zaun, <a class=\"seo-suggest-link\" title=\"ekko\" href=\"https://gamek.vn/ekko.htm\" target=\"_blank\" rel=\"noopener\">Ekko</a> thể hiện tài năng sáng chế vượt ngoài tầm hiểu biết của loài người khi đang còn ở độ tuổi thiếu niên. Động Cơ Z - Thiết bị có khả năng thao túng cả thời gian chính là sản phẩm tâm đắc nhất của vị tướng này, và cũng là minh chứng cho bộ óc thiên tài của cậu ta.</p>\r\n<p>Tên thật là Cecil B. Heimerdinger, được mệnh danh là nhà sáng chế vĩ đại nhất Piltover, từng đó thôi là đủ để hình dung về việc vị tướng này sở hữu trí thông minh vô tận như thế nào. Những cỗ máy chết chóc mà Heimerdinger sử dụng trong trò chơi chính là kết tinh của cả đời nghiên cứu miệt mài về khoa ma kỹ của ông.</p>\r\n<div class=\"VCSortableInPreviewMode noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/8/photo-1-1596905386475734852534.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216599614221619200\" class=\"lightbox-content\" title=\"Những vị tướng sở hữu IQ 200 trong vũ trụ LMHT, đi thi tốt nghiệp thừa sức đạt thủ khoa - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/690/133514250583805952/2020/8/8/photo-1-1596905386475734852534.jpg\" alt=\"Những vị tướng sở hữu IQ 200 trong vũ trụ LMHT, đi thi tốt nghiệp thừa sức đạt thủ khoa - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/8/photo-1-1596905386475734852534.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Thậm chí, chỉ cần nhìn tạo hình với cái đầu to quá khổ (ngay cả nếu so sánh với những Yordle khác), cùng cái tên Heimerdinger, vốn được ghép từ tên của 2 nhà khoa học lỗi lạc Robert Oppenheimer và Erwin Schrödinger, là đủ biết vị tướng này \"bác học\" rồi.</p>\r\n<p><strong>Jayce</strong></p>\r\n<p>Là một công dân của thiên đường công nghệ Piltover, chàng trai trẻ <a class=\"seo-suggest-link\" title=\"jayce \" href=\"https://gamek.vn/jayce.htm\" target=\"_blank\" rel=\"noopener\">Jayce</a> thậm chí đã sớm thành danh với những phát minh của mình, bên cạnh tinh thần nghĩa hiệp. Cây búa biến hình chế tạo từ công nghệ Hextech là một sản phẩm mang tính biểu tượng của Piltover, với tất cả những nét tinh hoa nhất của công nghệ sáng chế được thể hiện qua nó.</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/8/photo-1-15969056556151657270174.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_216600745487872000\" class=\"lightbox-content\" title=\"Những vị tướng sở hữu IQ 200 trong vũ trụ LMHT, đi thi tốt nghiệp thừa sức đạt thủ khoa - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/690/133514250583805952/2020/8/8/photo-1-15969056556151657270174.jpg\" alt=\"Những vị tướng sở hữu IQ 200 trong vũ trụ LMHT, đi thi tốt nghiệp thừa sức đạt thủ khoa - Ảnh 3.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/8/photo-1-15969056556151657270174.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Tuy nhiên, bởi nhận thức được những hiểm họa đang đe dọa thành phố, Jayce đã tạm gác niềm đam mê sáng chế lại để tập trung vào một công việc cần ít chất xám hơn, nhưng lại đòi hỏi tinh thần cao thượng và nhiệt huyết bất tận - Trở thành người bảo hộ của Piltover.</p>\r\n<p><strong>Viktor</strong></p>\r\n<p>So với 3 cái tên kể trên, Viktor thậm chí có thể coi là bộ óc thiên tài hơn tất thảy. Hắn ta là người đã phát minh ra Blitzcrank - Một cỗ máy có nhận thức như sinh vật sống, và thậm chí, đã tiệm cận tới trình độ khoa học có thể khiến nhân loại tiến hóa lên tầm cao mới (đó là hắn nghĩ thế).</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(92, '\"Dạy hư\" game thủ vì lối lên đồ dị, Cowsep lên tiếng: \"Xin lỗi Việt Nam\"', 'Mặc đù chỉ là một vài lời \"đổ thừa\" một cách bông đùa của fan, nhưng Cowsep thì vẫn tỏ ra rất chiều chuộng game thủ Việt và từ bỏ lối chơi này.', '          <p><a class=\"seo-suggest-link\" title=\"master yi\" href=\"https://gamek.vn/master-yi.htm\" target=\"_blank\" rel=\"noopener\">Master Yi</a> lên trang bị Thần Kiếm Manamune là một lối chơi rất được ưa chuộng bởi các <a class=\"seo-suggest-link\" title=\"streamer\" href=\"https://gamek.vn/streamer.htm\" target=\"_blank\" rel=\"noopener\">streamer</a> và boy-one-champ Yi. Và <a class=\"seo-suggest-link\" title=\"cowsep\" href=\"https://gamek.vn/cowsep.htm\" target=\"_blank\" rel=\"noopener\">Cowsep</a> cũng không phải ngoại lệ. Tuy nhiên, với tư cách là một người chơi sở hữu kỹ năng cao hơn mặt bằng chung, anh muốn chơi game kiểu lên đồ dị thì không sao, nhưng để các fan học theo thì lại cả một vấn đề.</p>\r\n<p>Không phải game thủ nào cũng sở hữu trình độ cá nhân cao, và việc lên đồ theo thần tượng một cách máy móc sẽ dễ dẫn đến những câu chuyện dở khóc dở cười. Bởi lẽ vấn đề lên trang bị thế nào chỉ là một vấn đề, còn kỹ năng và tư duy người chơi như thế nào mới là yếu tố quyết định trận đấu.</p>\r\n<p>Nhiều người chơi Yi lên Thần Kiếm Manamune nhưng lại không tận dụng được hiệu quả của trang bị này sẽ dẫn đến việc thời gian tăng tiến sức mạnh của vị tướng này bị trì trệ đáng kể, và kết quả là những màn feed xuyên màn đêm như thế này đây:</p>\r\n<p>Chứng kiến cộng đồng game thủ than thở và trêu chọc rằng \"chỉ vì Cowsep mà rank Việt đang nát vì trào lưu Master Yi Thần Kiếm\", anh chàng đã lên tiếng nhận lỗi vì... truyền bá trào lưu dị khiến cộng đồng game thủ điêu đứng.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'binbin'),
(93, 'Những vị tướng \"về từ cõi chết\", tưởng đã mồ yên mả đẹp nhưng lại bất ngờ sống dậy trong LMHT', 'Riot Games dường như đang hơi lạm dụng yếu tố \"chết đi sống lại\" với những vị tướng trong game, khiến LMHT dần trở thành Liên Minh Hồi Sinh thì phải.', '          <p>Màn \"hồi sinh\" hoành tráng và tốn kém chi phí làm kỹ xảo nhất có lẽ phải kể đến pha comeback ngoạn mục của <a class=\"seo-suggest-link\" title=\"azir\" href=\"https://gamek.vn/azir.htm\" target=\"_blank\" rel=\"noopener\">Azir</a> - Hoàng đế Shurima cổ đại. Như một sự sắp đặt của định mệnh (thực ra là của ông viết kịch bản), Sivir - Một tay trộm mộ, lại trộm đúng khu lăng tẩm của cụ cố mười mấy đời nhà mình, rồi sau đó thì vô tình bị ám hại, và máu của cô - Dòng máu của hậu duệ Hoàng gia Shurima, lại bất ngờ trở thành chất xúc tác giúp thân xác Azir hoàn thiện quá trình thăng hoa còn dang dở và hồi sinh từ cõi chết.</p>\r\n<p>Cùng với Azir thì Xerath và Renekton cũng được giải thoát trong sự kiện này, tuy nhiên 2 vị tướng này thực chất không hề chết, mà chỉ bị phong ấn hàng ngàn năm trong lăng mô cổ cùng với Azir mà thôi.</p>\r\n<p>Dù là Sion mới hay cũ, thì cốt truyện của vị tướng này vẫn không thay đổi bản chất của hắn - Một đại tướng chiến công lẫy lừng của Noxus không may chiến tử sa trường, giờ đây được hồi sinh bằng thứ ma thuật hắc ám và phục vụ dưới trướng quân đội Noxus với tư cách một biểu tượng, một chiến binh bất tử, nhưng thực chất chẳng khác gì thây ma.</p>\r\n<p>So với những cái tên khác trong danh sách này, thân thế của Sion có vẻ \"tầm thường\" hơn nhiều, và gã ta cũng chẳng sở hữu quyền năng đặc biệt gì ngoài việc \"đánh mãi không chết\" (vì đã chết một lần rồi). Nhưng đối với Đế chế Noxus - Những kẻ giờ đây đang lợi dụng người anh hùng của họ như một cỗ máy chiến tranh, thì chỉ cần thế thôi là đủ.</p>\r\n<p><strong>Senna</strong></p>\r\n<p>Là cái tên đánh dấu kỷ niệm sinh nhật 10 tuổi của LMHT, <a class=\"seo-suggest-link\" title=\"senna\" href=\"https://gamek.vn/senna.htm\" target=\"_blank\" rel=\"noopener\">Senna</a> trở nên đặc biệt bởi cô là trường hợp đầu tiên về một vị tướng có xuất thân từ cốt truyện game được ra mắt trên Đấu Trường Chân Lý. Như chúng ta đã biết, Senna vốn được cho rằng đã hi sinh trong cuộc chiến chống lại thế lực hắc ám đến từ Quần Đảo Bóng Đêm, mà \"chủ mưu\" là Thresh.</p>\r\n<p>Nhưng trong cốt truyện mới, linh hồn của cô bị giam giữ trong lồng đèn của Thresh cuối cùng đã tìm được cách thoát ra, và thậm chí còn sở hữu luôn quyền năng hấp thụ sức mạnh và thao túng linh hồn khác.</p>\r\n<div class=\"VCSortableInPreviewMode active noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/5/photo-1-15966463095171672520719.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_215512986384240640\" class=\"lightbox-content\" title=\"Những vị tướng về từ cõi chết, tưởng đã mồ yên mả đẹp nhưng lại bất ngờ sống dậy trong LMHT - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/5/photo-1-15966463095171672520719.jpg\" alt=\"Những vị tướng về từ cõi chết, tưởng đã mồ yên mả đẹp nhưng lại bất ngờ sống dậy trong LMHT - Ảnh 3.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/5/photo-1-15966463095171672520719.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Sự trở lại của Senna rõ ràng khiến Lucian vô cùng hạnh phúc, nhưng ẩn sau cuộc đoàn tụ đó vẫn là vô vàn điều bí ẩn xoay quanh những nghi vấn như: Ánh mắt của Senna sau khi gặp gỡ Lucian có ý nghĩa như thế nào, và liệu sức mạnh mà cô sở hữu có khiến bản chất của cô trở nên tha hóa, như rất nhiều vị tướng của Quần Đảo Bóng Đêm khác hay không?</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, ''),
(94, 'Game thử bàn luận xôn xao về skin PSyOps Ezreal - \"Cloud chuyển sinh sang LMHT rồi à?\"', 'Thậm chí hoạt ảnh biến về của PsyOps Ezreal cũng rất giống hành động của nhân vật Yondu trong MCU', '          <p>Vào tối ngày hôm qua theo giờ Việt Nam, <a class=\"seo-suggest-link\" title=\"riot games\" href=\"https://gamek.vn/riot-games.htm\" target=\"_blank\" rel=\"noopener\">Riot Games</a> đã tung hàng loạt video nhá hàng về dòng skin mới mang tên PsyOps với những vị tướng gồm Master Yi, Shen, Sona, Vi và <a class=\"seo-suggest-link\" title=\"ezreal\" href=\"https://gamek.vn/ezreal.htm\" target=\"_blank\" rel=\"noopener\">Ezreal</a>. Thậm chí trên máy chủ PBE thì những biểu cảm mới của nhóm trang phục này đã được tung ra và một trong số đó khiến người ta nghi ngờ rằng đó là tướng mới Samira. Sau đó thì Riot Squad5 (người thiết kế tướng Xạ Thủ mới) đã xác nhận rằng đó không phải là Samira.</p>\r\n<p>Tạm gác lại câu chuyện về Samira, skin được nhiều người bàn tán nhất đó là trang phục PsyOps Ezreal. Một phần là bởi người ta phàn nàn về chuyện Riot quá ưu ái cho anh chàng tóc vàng này, một phần là do skin mới của Ezreal có tạo hình quá giống với nhân vật Cloud Strife của dòng game Final Fantasy. Khác biệt lớn nhất là tướng của <a class=\"seo-suggest-link\" title=\"lmht\" href=\"https://gamek.vn/lmht.htm\" target=\"_blank\" rel=\"noopener\">LMHT</a> không có một thành kiếm khổng lồ mà thôi.</p>\r\n<p>Chuyện Riot Games lấy ý tưởng trang phục từ những dòng game khác hay văn hóa đại chúng có lẽ không còn là điều gì đó quá xa lạ nữa. Ngay skin Riven Tàn Kiếm U Hồn chuẩn bị ra mắt cũng được game thủ soi ra là rất giống với nhân vật Uchiha Sasuke của anime/manga Naruto, có lẽ người thiết kế skin PsyOps Ezreal cũng là một fan của Cloud Strife chăng?</p>\r\n<p>Bên cạnh đó thì hiệu ứng biến về của skin này cũng khiến người ta liên tưởng tới nhân vật Yondu với những màn huýt sáo điều khiển mũi tên đặc biệt của mình để tiêu diệt kẻ địch.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(95, 'Riot đã chỉ cho gảm thủ ngọc mạnh nhất của Yone', 'Không phải Chỉnh Phục như nhiều người nghĩ, Mưa kiếm mới là thử phù hợp nhất của mà Riot dành cho Yone', '          <p>Ngày hôm nay, 6/8, là ngày mà bản 10.16 chính thức cập nhật vào máy chủ Việt Nam, với 10 tướng bị nerf và 17 tướng được buff thì đây là phiên bản cực kì lớn mà game thủ cần phải tìm hiểu kỹ. Dù vậy sự chú ý của game thủ <a class=\"seo-suggest-link\" title=\"lmht\" href=\"https://gamek.vn/lmht.htm\" target=\"_blank\" rel=\"noopener\">LMHT</a> lại đang đổ dồn vào <a class=\"seo-suggest-link\" title=\"yone\" href=\"https://gamek.vn/yone.htm\" target=\"_blank\" rel=\"noopener\">Yone</a> - Kẻ Trở Về Từ Cõi Chết khi vị tướng này được giới thiệu vô cùng tốt, ngoại hình cực ngầu và hứa hẹn soán ngôi \"tướng quốc dân\" của chính người em Yasuo.</p>\r\n<p>Vậy thì câu hỏi đặt ra ở đây đó là làm thế nào để sử dụng Yone tốt nhất? Điểm ngọc nào mới phù hợp với vị tướng này? Nhiều người sẽ trả lời là ngọc Chinh Phục, đơn giản là vì Yone có lối chơi quá giống Yasuo và chuyện ông em dùng điểm ngọc này cũng khiến cho người ta nghĩ rằng người anh phù hợp nhất với nó.</p>\r\n<p>Tuy nhiên khi săm soi lại cinematic \"Lãng Khách và Kẻ Săn\" giới thiệu Yone và hành trình của Yasuo, chúng ta phải giật mình khi nhận ra sự hiện diện của hình ảnh ngọc Mưa Kiếm trong đó. Cụ thể thì khi Yone tung đòn chí mạng giữ con quỷ lại để cho Yasuo dùng tuyệt chiêu Trăn Trối, chúng ta thấy những dòng kiếm khí của Yone cuộn lại thành hình ảnh rất giống với ngọc Mưa Kiếm. Phải chăng đây là ý đồ của <a class=\"seo-suggest-link\" title=\"riot games\" href=\"https://gamek.vn/riot-games.htm\" target=\"_blank\" rel=\"noopener\">Riot Games</a>?</p>\r\n<div class=\"VCSortableInPreviewMode\">\r\n<div><a class=\"detail-img-lightbox\" title=\"Phân cảnh Yone tung đòn chí mạng vào con quỷ trong cinematic\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/6/yone-1596680175516619118221.png\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_215655072861425664\" class=\"lightbox-content\" title=\"Riot đã chỉ cho game thủ ngọc mạnh nhất của Yone là Mưa Kiếm ngay từ cinematic giới thiệu tướng? - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/6/yone-1596680175516619118221.png\" alt=\"Riot đã chỉ cho game thủ ngọc mạnh nhất của Yone là Mưa Kiếm ngay từ cinematic giới thiệu tướng? - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/6/yone-1596680175516619118221.png\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">Phân cảnh Yone tung đòn chí mạng vào con quỷ trong cinematic</p>\r\n</div>\r\n</div>\r\n<div class=\"VCSortableInPreviewMode active\">\r\n<div><a class=\"detail-img-lightbox\" title=\"Hình ảnh trên khiến người ta liên tưởng ngay tới ngọc Mưa Kiếm\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/6/archive-cover-hailofblades-15966804874411407519986.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_215656404299096064\" class=\"lightbox-content\" title=\"Riot đã chỉ cho game thủ ngọc mạnh nhất của Yone là Mưa Kiếm ngay từ cinematic giới thiệu tướng? - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/6/archive-cover-hailofblades-15966804874411407519986.jpg\" alt=\"Riot đã chỉ cho game thủ ngọc mạnh nhất của Yone là Mưa Kiếm ngay từ cinematic giới thiệu tướng? - Ảnh 3.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/6/archive-cover-hailofblades-15966804874411407519986.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">Hình ảnh trên khiến người ta liên tưởng ngay tới ngọc Mưa Kiếm</p>\r\n</div>\r\n</div>\r\n<p>Nếu bạn còn nhớ thì vào giai đoạn CKTG 2019, Mưa Kiếm từng được game thủ chuyên nghiệp tin dùng cho Yasuo và nó đẩy ngưỡng sức mạnh đầu trận và khả năng giao tranh đơn lẻ của \"đấng\" lên cực kì lớn. Lý do là bởi Mưa Kiếm tăng cho tướng rất nhiều tốc độ đánh, giảm rất nhiều hồi chiêu của kỹ năng Q - Bão Kiếm và Yasuo có thể mạnh từ sớm mà không cần quá nhiều trang bị.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(96, 'Vượt hàng loạt siêu sao, 2 tuyển thử VCS LL và Levi sở hữu KDA cao nhất thế giới tuần vừa rồi', 'LL và Levi vượt qua 2 cái tên đình đám khác là Showmaker và Cayon của Damwon trong danh sách này.', '          <p>Cách đây ít ngày, nhà sáng lập ra chuyên trang số liệu các giải đấu <a class=\"seo-suggest-link\" title=\"lmht\" href=\"https://gamek.vn/lmht.htm\" target=\"_blank\" rel=\"noopener\">LMHT</a> - Gol.gg đã công bố những thống kê vô cùng đáng chú ý như top KDA của tuần hay số lượng tướng được sử dụng trong tuần vừa rồi. Điều đặc biệt là ở danh sách những người chơi sở hữu KDA một trận cao nhất, có tới 2 cái tên của VCS được góp mặt trong danh sách này đó là <a class=\"seo-suggest-link\" title=\"LL \" href=\"https://gamek.vn/ll.htm\" target=\"_blank\" rel=\"noopener\">LL</a> của Cerberus Esports và <a class=\"seo-suggest-link\" title=\"levi\" href=\"https://gamek.vn/levi.htm\" target=\"_blank\" rel=\"noopener\">Levi</a> của GAM Esports.</p>\r\n<p>Dù cùng sở hữu chỉ số KDA là 21.0, tuy nhiên LL lại được xếp trên Levi khi anh có được tới 11 mạng hạ gục. Hơn nữa trận đấu mà LL có được con số KDA khủng khiếp kể trên lại là trận đấu vô cùng khó khăn khi phải đối đầu với EVOS Esports và đối mặt với Zeros. Dù không có được chiến thắng cuối cùng nhưng LL đã chứng minh rằng mình không hề thua thiệt về trình độ với đối thủ, dù đó có là Zeros đi nữa.</p>\r\n<p>Về phần Levi thì trận đấu của anh có phần dễ dàng hơn so với LL khi đối mặt với anh chỉ là một Meliodas không quá tinh quái và Saigon Buffalo trong giai đoạn trẻ hóa. Dù vậy thì KDA quá kinh khủng là 9/0/12 với Karthus cũng giúp cho Levi vượt qua những \"tay to\" của LCK là ShowMaker và Canyon ở hạng mục này. Điều này cho thấy khả năng giao tranh và đánh hơi điểm hạ gục của game thủ Việt chẳng thua kém gì so với những tuyển thủ khu vực khác.</p>\r\n<div class=\"VCSortableInPreviewMode\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/6/28/lmht-gam-levi-159331743609150911801.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_201550728876695552\" class=\"lightbox-content\" title=\"Vượt hàng loạt siêu sao, 2 tuyển thủ VCS - LL và Levi sở hữu KDA cao nhất thế giới tuần vừa rồi - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/6/28/lmht-gam-levi-159331743609150911801.jpg\" alt=\"Vượt hàng loạt siêu sao, 2 tuyển thủ VCS - LL và Levi sở hữu KDA cao nhất thế giới tuần vừa rồi - Ảnh 3.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/6/28/lmht-gam-levi-159331743609150911801.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">\"Khánh Cầu Hồn\" có một trận đấu quá xuất sắc trước Saigon Buffalo</p>\r\n</div>\r\n</div>\r\n<p>Mặc dù trong vài mùa trở lại đây, các đội VCS đang tìm cách có nhiều hơn những chiến thuật kiểm soát theo kiểu Hàn Quốc như Karma đường giữa nhưng hiệu quả không hề tốt chút nào. Có lẽ thay vì học hỏi sự kiểm soát tuyệt đối của người Hàn, chúng ta nên noi theo mô hình \"giao tranh hỗn loạn nhưng đầy tính toán\" của những đội tuyển LPL để có được thành công. Kỹ năng chúng ta không hề yếu, cái cần là sự tỉnh táo mà thôi.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'binngu'),
(97, 'Tổng hợp game miễn phí ngày 10/8', 'Hello anh em, chắc hẳn hôm nay anh em mới lại được thả mình vào thế giới ảo sau một tuần thật dài phải không nào.', '          <p>Những tựa game miễn phí vẫn đang chờ đón anh em vào cuối tuần này, và nếu anh em đã quá bận rộn mà không thể cập nhật tin tức kịp thời, thì mình xin tổng hợp lại những con game đang miễn phí ngay bây giờ để anh em tiện theo dõi nhé.</p>\r\n<p>Mặc dù tựa game F1 đã có phiên bản năm 2020 rồi, thế nhưng bản 2018 vẫn cứ là chất lượng không kém đâu nhé anh em. Tựa game đưa anh em vào giải Grand Prix F1 lớn nhất hành tinh, được điều khiển những con xe đỉnh cao được sinh ra để phá vỡ mọi giới hạn, và tất nhiên là có sự góp mặt của nhiều tay đua nổi tiếng thế giới, trong này làm anh Halminton đẹp trai lắm các bác ạ. </p>\r\n<p>Nếu anh em đang tìm một tựa game có bối cảnh lạ lùng, thiết kế đồ họa khác biệt, và đặc biệt là có sự châm biếm và hài hước mang đậm chất sitcom, thì Welcome to Shovelworks là một cái tên hoàn toàn. Nó xoay quay một hãng game \"cùi bắp\" với đội ngũ phát triển \"dở hơi\" và anh em sẽ là một phần trong đội ngũ này. Xử lý những tình huống oái ăm hàng ngày và tham gia phát triển những con game dở tệ đến nỗi bị troll, còn chờ gì nữa mà không đi làm ở một công ty thú vị như thế này nào anh em.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(98, 'Game thử AoE Hồng Anh chia sẻ\" \" Chơi AoE là đam mê chứ không đơn thuần chỉ là công việc của một steamer\"', 'Đó là câu nói của Hồng Anh trong buổi chia sẻ với nhóm phóng viên của chúng tôi về công việc một streamer', '          <p>Hồng Anh, tên thật là Đinh Đăng Hiếu, được biết đến như là game thủ xuất sắc nhất của AoE Thái Bình thời điểm hiện tại. Hồng Anh còn là một game thủ rất nổi tiếng trong cộng đồng AoE Trung Quốc với lối chơi tấn công vô cùng cảm xúc ở thể loại cung R. Cùng với <a class=\"link-inline-content\" title=\"Chim Sẻ Đi Nắng\" href=\"https://gamek.vn/tim-kiem.chn?keyword=aoe%20\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/tim-kiem.chn?keyword=aoe%20\">Chim Sẻ Đi Nắng</a>, cả 2 tạo thành cặp đôi \"độc cô cầu bại\" trong thể loại thuần tiễn.</p>\r\n<p>Năm 2020, đánh dấu một năm thành công của cá nhân game thủ Hồng Anh và team Thái Bình khi game thủ này đã dành danh hiệu vận động viên xuất sắc nhất của Bé Yêu Cup 2020. Chiếc cup vô địch ở thể loại solo Random và 44 Random đã phần nào thể hiện được sự trưởng thành của game thủ đế từ Phú Thọ.</p>\r\n<p>Là một Streamer nổi tiếng trong cộng đồng AoE nói riêng và cộng đồng game Việt nói chung, Hồng Anh chính là game thủ có nhiều người xem thứ 2 của cộng đồng AoE Việt chỉ sau Chim Sẻ Đi Nắng. Chia sẻ với nhóm phóng viên của chúng tôi về con đường đến với AoE và nghề streamer, game thủ này cho biết: <em>\"Ban đầu, mình đến với AoE là để vui, đi theo anh trai chơi vài kèo ở quê, thấy trò này hay nên em quyết tâm theo đuổi nó. Việc lên Hà Nội và gia nhập vào Thái Bình là bước ngoặt của em, từ một game thủ tự do em trở thành một game thủ chuyên nghiệp được nhiều người biết đến\"</em></p>\r\n<div class=\"VCSortableInPreviewMode\">\r\n<div><a class=\"detail-img-lightbox\" title=\"Hồng Anh chia sẻ việc đến với AoE Thái Bình là một lựa chọn đúng đắn\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/ha2-1597142571758964572950.jpg\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_217594501578076160\" class=\"lightbox-content\" title=\"Game thủ AoE Hồng Anh chia sẻ: Chơi AoE là đam mê chứ không đơn thuần chỉ là công việc của một streamer - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/8/11/ha2-1597142571758964572950.jpg\" alt=\"Game thủ AoE Hồng Anh chia sẻ: Chơi AoE là đam mê chứ không đơn thuần chỉ là công việc của một streamer - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/8/11/ha2-1597142571758964572950.jpg\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\">\r\n<p class=\"\" data-placeholder=\"[nhập chú thích]\">Hồng Anh chia sẻ việc đến với AoE Thái Bình là một lựa chọn đúng đắn</p>\r\n</div>\r\n</div>\r\n<p>Trước câu hỏi rằng Facebook Gaming và các buổi livestream trên nền tảng này ảnh hưởng như thế nào đến cuộc sống của bản thân, Hồng Anh thẳng thẳn trả lời: <em>\"Ban đầu, khi chưa có Facebook Gaming, việc thi đấu off khiến em cảm thấy thoải mái hơn nhưng nhiều khi không có động lực. Từ ngày Facebook Gaming vào Việt Nam và ký hợp đồng với các game thủ AoE, cơ hội để hình ảnh của mình đến với khán giả nhiều hơn khiến em cảm thấy hào hứng. Tuy nhiên như mọi người đã biết, để đảm bảo hợp đồng với Facebook Gaming, đòi hỏi việc game thủ phải đảm bảo số lượng view lúc thi đấu và số giờ chơi hàng tháng khiến bản thân mình khá mệt trong suốt một thời gian. Sức khỏe của mình không được tốt (cười), cho nên ban đầu livestream và công việc của một streamer khiến mình khá mệt. Dường như nó chiếm trọn thời gian của mình\"</em>.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'thunthun'),
(99, 'Chia sẻ cay đắng của game thử Singapore và Việt Nam ', 'Thật khó mà tin được rằng LMHT ở một vài quốc gia DNA lại có tình trạng bi đát tới như vậy.', '          <p><a class=\"seo-suggest-link\" title=\"lmht\" href=\"https://gamek.vn/lmht.htm\" target=\"_blank\" rel=\"noopener\">LMHT</a> kể từ khi được mang về Việt Nam vào năm 2012 đã và đang trở thành trò chơi vô cùng nổi tiếng với lượng người chơi khổng lồ. Tuy nhiên ở những quốc gia Đông Nam Á khác, câu chuyện của LMHT lại không hề đẹp đẽ như vậy. Thậm chí ở Singapore hay Philippines thì người chơi ở đây còn gọi nó với cái tên đầy cay đắng là \"dead game\" và còn không có lượng người chơi bằng những trò chơi di động.</p>\r\n<p><em><a class=\"seo-suggest-link\" title=\"riot games\" href=\"https://gamek.vn/riot-games.htm\" target=\"_blank\" rel=\"noopener\">Riot Games</a> đã thông báo rằng họ sẽ tự phát hành những game mới của mình, không phải bởi Garena. Chơi LMHT ở Đông Nam Á là trải nghiệm tệ hại, bọn tôi còn không có op.gg như game thủ Châu Âu/Bắc Mỹ. Hơn nữa, khi chơi ở Đông Nam Á thì chỉ gặp game thủ trong cùng 1 đất nước mà thôi. Garena cũng không thèm quan tâm tới mấy người chơi toxic, nút tố cáo chỉ như một thiết kế vớ vẩn với họ vậy. Tôi thực sự nghĩ rằng Garena không quan tâm tới game thủ Đông Nam Á luôn.</em></p>\r\n<p><em>Chúng ta đều biết LMHT chạy được trên hệ điều hành Mac OS đúng không? Nhưng mà Garena thì nói không nhé vì client của họ không chạy trên Mac, thật là bực mình. Bạn không thể chơi game mình thích kể cả khi nó tương thích với thiết bị của bạn chỉ bởi cái màn hình đăng nhập của Garena không chạy trên Mac.</em></p>\r\n<p><em>Tôi khá lo lắng về việc phát hành Liên Minh Huyền Thoại: Tốc Chiến ở Đông Nam Á bởi Riot nói rằng họ sẽ có quá cho người chơi LMHT PC khi game ra mắt. Tuy nhiên bọn tôi không có tài khoản Riot, chúng tôi chỉ có tài khoản Garena mà thôi, đó là 2 hệ thống khác biệt hoàn toàn.</em></p>\r\n<p><em>LMHT đang chết dần ở Phlippines, tôi không biết về những quốc gia Đông Nam Á khác nhưng đó là sự thật ở đây. Tôi buộc phải đổ lỗi cho Garena bởi game lúc nào cũng có bug, không có biện pháp ngăn chặn phá game và họ là nhà phát hành LMHT quá tệ. Tôi thực sự muốn Riot dừng hợp tác với Garena và tiếp quản khu vực Đông Nam Á.</em></p>\r\n<p><em>Tôi không muốn ghét bỏ gì công ty đó đâu nhưng cứ hỏi bất kì người sử dụng Garena nào đi, họ sẽ hiểu LMHT ở khu vực Đông Nam Á tệ hại như nào.</em></p>\r\n<p><em>Thế mới thấy LMHT không phải lúc nào cũng là trò chơi được yêu thích nhất dù tựa game này được quảng bá rộng rãi và nổi tiếng toàn cầu. Với những trường hợp đặc thù như tại Singapore và Philippines, các game thủ tại quốc gia này đã phát ngán với <a class=\"seo-suggest-link\" title=\"garena\" href=\"https://gamek.vn/garena.htm\" target=\"_blank\" rel=\"noopener\">Garena</a> và dần dần từ bỏ LMHT. Vì thế mà LMHT mới đang chết dần ở những quốc gia này.</em></p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'hiuhiu'),
(100, 'Top game Moba Mobile hay nhất tại Việt Nam, Liên Quân bất ngờ không phải là cái tên đầu tiên', 'Đây đều là những tựa game Moba được đánh giá cao bởi trang phê bình nổi tiếng trên thế giới và cả tại thị trường DNA, trong đố có Việt Nam', '          <p>Trên nền tảng PC thì DOTA 2 và Liên Minh Huyền Thoại chắc chắn là <a class=\"link-inline-content\" title=\"hai ông lớn bậc nhất\" href=\"https://gamek.vn/nhin-nhung-hinh-anh-nay-game-thu-co-ly-do-de-lo-lang-pubg-mobile-se-di-vao-vet-xe-do-cua-chinh-pubg-20200809111259794.chn\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/nhin-nhung-hinh-anh-nay-game-thu-co-ly-do-de-lo-lang-pubg-mobile-se-di-vao-vet-xe-do-cua-chinh-pubg-20200809111259794.chn\">hai ông lớn bậc nhất</a> của làng game MOBA thế giới. Thế nhưng, không phải người chơi nào cũng đủ kiên nhẫn để ngồi giải quyết một trận đấu kéo dài trung bình từ 30 – 40 phút. Đây cũng chính là lý do mà những tựa game MOBA trên nền tảng Mobile đã bắt đầu phát triển một cách mạnh mẽ. Và thú vị thay, cuộc chiến trên nền tảng di động lại trở nên khốc liệt hơn nhiều so với trên PC.</p>\r\n<p>Nếu như bạn vẫn không biết những tựa game MOBA Mobile nào từng được đánh giá cao nhất tại thị trường Việt Nam thì hãy cùng tìm hiểu danh sách dưới đây. Điểm đặc biệt là <a class=\"link-inline-content\" title=\"vị trí đầu tiên\" href=\"https://gamek.vn/nhanh-tay-tai-sieu-pham-game-mobile-cua-nam-dung-top-1-app-store-o-hon-100-quoc-gia-dang-mien-phi-20200808233033716.chn\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/nhanh-tay-tai-sieu-pham-game-mobile-cua-nam-dung-top-1-app-store-o-hon-100-quoc-gia-dang-mien-phi-20200808233033716.chn\">vị trí đầu tiên</a> bất ngờ không phải là Liên Quân, tựa game đang được xem là có lượng người chơi lớn bậc nhất Việt Nam hiện nay.</p>\r\n<p>Vainglory được xem như một trong những tựa game tiên phong cho thể loại MOBA trên nền tảng di động. Rất nhiều trang phê bình trên thế giới đánh giá Vainglory là một trong những sản phẩm MOBA <a class=\"link-inline-content\" title=\"đáng chơi bậc nhất \" href=\"https://gamek.vn/tung-la-niem-tu-hao-nuoc-nha-tai-sao-free-fire-lai-bi-chinh-nguoi-viet-cham-choc-cong-kich-va-ghet-bo-20200808192956153.chn\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/tung-la-niem-tu-hao-nuoc-nha-tai-sao-free-fire-lai-bi-chinh-nguoi-viet-cham-choc-cong-kich-va-ghet-bo-20200808192956153.chn\">đáng chơi bậc nhất </a>trên nền tảng Mobile. Tựa game này nhận số điểm 86/100 trên Metacritic, IGN thì cho số điểm 8.1/10 đối với tựa game của Super Evil Megacorp.</p>\r\n<p>Được đánh giá cao cả về đồ họa lẫn cơ chế gameplay khác biệt với phần lớn các tựa game MOBA Mobile khác. Vậy nhưng, tuy đã được <a class=\"link-inline-content\" title=\"phát hành chính thức tại Việt Nam\" href=\"https://gamek.vn/neu-khong-bi-pha-nat-boi-hack-lieu-day-co-phai-la-tuong-dai-game-mobile-hay-nhat-ma-nguoi-choi-tung-biet-20200808125241966.chn\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/neu-khong-bi-pha-nat-boi-hack-lieu-day-co-phai-la-tuong-dai-game-mobile-hay-nhat-ma-nguoi-choi-tung-biet-20200808125241966.chn\">phát hành chính thức tại Việt Nam</a>, song số lượng game thủ hiện tại còn chơi Vainglory là không nhiều. Có vẻ như, Vainglory chỉ được ưu ái đối với các trang phê bình bởi đồ họa và lối chơi, một điều khá đáng tiếc cho tựa game MOBA này.</p>\r\n<p>Liên Quân Mobile là tên gọi chính thức của Arena of Valor<strong> </strong>tại thị trường Việt Nam. Khỏi phải nói, tính đến thời điểm hiện tại, đây vẫn là một trong những sản phẩm MOBA <a class=\"link-inline-content\" title=\"ăn khách bậc nhất\" href=\"https://gamek.vn/game-thu-viet-van-tim-duoc-cach-tai-ve-lmht-toc-chien-va-cai-dat-thanh-cong-nhung-roi-phai-khoc-han-20200807170810919.chn\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/game-thu-viet-van-tim-duoc-cach-tai-ve-lmht-toc-chien-va-cai-dat-thanh-cong-nhung-roi-phai-khoc-han-20200807170810919.chn\">\"ăn khách\" bậc nhất</a> trên nền tảng Mobile của làng game Việt. Bước sang năm tuổi thứ 4, Liên Quân vẫn cho thấy đây là một tựa game được xây dựng chỉn chu, một nỗ lực rất lớn của Tencent khi cố gắng mô phỏng một Liên Minh Huyền Thoại trên Mobile.</p>\r\n<div class=\"VCSortableInPreviewMode noCaption\">\r\n<div><a class=\"detail-img-lightbox\" title=\"\" href=\"https://gamek.mediacdn.vn/133514250583805952/2020/1/12/44-1576752345956652753344-1578847032244256434504.png\" target=\"_blank\" rel=\"noopener\" data-fancybox-group=\"img-lightbox\"><img id=\"img_140856583318933504\" class=\"lightbox-content\" title=\"Top game MOBA Mobile hay nhất tại Việt Nam, Liên Quân bất ngờ không phải là cái tên đầu tiên - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/1/12/44-1576752345956652753344-1578847032244256434504.png\" alt=\"Top game MOBA Mobile hay nhất tại Việt Nam, Liên Quân bất ngờ không phải là cái tên đầu tiên - Ảnh 2.\" width=\"\" height=\"\" data-original=\"https://gamek.mediacdn.vn/133514250583805952/2020/1/12/44-1576752345956652753344-1578847032244256434504.png\" /></a></div>\r\n<div class=\"PhotoCMS_Caption\"> </div>\r\n</div>\r\n<p>Đáng tiếc rằng, tựa game này đang gặp phải ít nhiều những vấn đề trong việc cố gắng duy trì sự \"trị vì\" của mình tại Việt Nam như phải đối diện với vấn nạn hack hoành hành, tình trạng lag gia tăng nhiều trong một số phiên bản gần đây. Và hơn hết, khi <a class=\"link-inline-content\" title=\"Liên Minh Huyền Thoại: Tốc Chiến\" href=\"https://gamek.vn/game-thu-viet-van-tim-duoc-cach-tai-ve-lmht-toc-chien-va-cai-dat-thanh-cong-nhung-roi-phai-khoc-han-20200807170810919.chn\" target=\"_blank\" rel=\"nofollow noopener\" data-cke-saved-href=\"https://gamek.vn/game-thu-viet-van-tim-duoc-cach-tai-ve-lmht-toc-chien-va-cai-dat-thanh-cong-nhung-roi-phai-khoc-han-20200807170810919.chn\">Liên Minh Huyền Thoại: Tốc Chiến</a> hoặc thậm chí là cả Marvel Super War được phát hành thì liệu Liên Quân Mobile có duy trì được sự thành công như những năm vừa qua hay không?</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 3, 'thonthon'),
(101, 'Xiaomi Mi 10 Ultra xuất hiện với cụm camera khủng, sắc xảo từng chi tiết, camera ẩn dưới màn hình', 'Vào ngày mai (11/8), Xiaomi nhiều khả năng sẽ ra mắt phiên bản đặc biệt của Mi 10 và Redmi K30 với tên gọi là Mi 10 Ultra và Redmi K30 Ultra.', '          <p class=\"p1\">Theo tiết lộ từ chuỗi cung ứng màn hình, Mi 10 Ultra sẽ là chiếc smartphone đầu tiên sở hữu camera selfie được ẩn dưới màn hình, nhưng nhiều khả năng Mi 10 Ultra chỉ là bản demo và sẽ không bán ra thị trường thế giới.</p>\r\n<p class=\"p1\"> </p>\r\n<p class=\"p1\"><img src=\"https://media.techz.vn/media2019/source/NguyenKhoa/mi-10-ultra-3_800x565.jpg\" alt=\"mi-10-ultra-3_800x565\" /></p>\r\n<p class=\"p1\"> </p>\r\n<p class=\"p1\">Điều đặc biệt chính là hệ thống camera Mi 10 Ultra với 4 ống kính, ngoài 3 ống kính quen thuộc, đi kèm trong đó là ống kính tiềm vọng 120x, với khả năng zoom khung hình 120x.<span class=\"Apple-converted-space\"> </span></p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-7662\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-7662-1\"></ins></ins></p>\r\n<p class=\"p1\"> </p>\r\n<p class=\"p1\">Về cấu hình, tất nhiên, Mi 10 Ultra sẽ trang bị những gì tối tân nhất, với màn hình AMOLED 120Hz, Snapdragon 865, RAM LPDDR5, bộ nhớ UFS 3.1, pin dung lượng 4.500mAh, sạc nhanh 100 W, sạc không dây 50 W, cảm biến vân tay trong màn hình, động cơ rung tuyến tính trục X, loa stereo kép.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(102, 'OPPO Reno4 Series chính thức trở thành smartphone được đặt mua nhanh nhất Việt Nam 2020', 'OPPO chính thức mở bán Reno4 Series và OPPO Watch tại thị trường Việt Nam.', '          <p>Vượt xa mọi dự đoán, chỉ trong vỏn vẹn 6 ngày, lượng đơn đặt hàng Reno4 & Reno4 Pro liên tục gây sốc. Ngay trong ngày đầu trải nghiệm, dù chưa biết giá, Reno4 đã ghi dấu ấn với 2,5 ngàn đơn hàng có cọc. Chỉ 5 ngày sau đó, số đơn cọc đã cán mốc 15 ngàn, trước khi kết thúc vào ngày 07/08 với tổng cộng 23 ngàn đơn. Tính trung bình, mỗi ngày có hơn 3 ngàn người dùng quyết xuống tiền để sở hữu smartphone mới ra mắt của OPPO. Thành công này thực sự gây bất ngờ và rất đáng ghi nhận, nhất là trong bối cảnh kinh tế khó khăn cùng sự biến động phức tạp của thị trường. </p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/OPPO/Reno4%20mo%20ban/OPPOReno4%20(12%20of%2023).jpg\" alt=\"OPPOReno4 (12 of 23)\" /></p>\r\n<p>Riêng tại hệ thống Thế Giới Di Động, tính đến hết ngày 07/08, chỉ trong 6 ngày sau khi ra mắt, Reno4 & Reno4 Pro đã nhận gần 14,5 ngàn đơn hàng trong đó có gần 11 ngàn đơn có cọc chắc chắn “rước ngay” bộ đôi smartphone. Đặt cọc thậm chí không cần biết giá, đồng thời tỷ lệ cọc/tổng đơn hàng luôn duy trì ở mức rất cao hơn 80% trong các ngày sau đó. Điều này chứng minh OPPO đã có chỗ đứng vững trong tâm trí và được đánh giá cao từ phía người tiêu dùng. Ông Phùng Ngọc Tuyên, Giám đốc ngành hàng Viễn thông Di động - Tập đoàn Thế giới Di động nhận xét: “Từ sau F9, F11 series, OPPO mới có 1 sản phẩm có gần 11 ngàn đơn cọc chỉ trong 6 ngày sau khi ra mắt như vậy, đặc biệt là ở phân khúc trên 8 triệu đồng cạnh tranh quyết liệt. Với tình hình phức tạp như hiện tại, nếu Reno4 & Reno4 Pro đạt được 6 đến 7 ngàn đơn hàng có cọc thì cũng đạt yêu cầu rồi. Tuy nhiên, kết quả ghi nhận sau cùng lên đến gần 11 ngàn đơn hàng. Thật sự rất sốc!”.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/OPPO/Reno4%20mo%20ban/OPPOReno4%20(4%20of%2023).jpg\" alt=\"OPPOReno4 (4 of 23)\" /></p>\r\n<p>Đặc biệt, trong lần ra mắt này không chỉ Reno4 gây sức hút mạnh mẽ mà Reno4 Pro cũng bất ngờ vượt xa kỳ vọng của hãng. Điều này cho thấy người dùng ngày càng tin tưởng vào các sản phẩm ở phân khúc giá cao cấp hơn của OPPO. “Ban đầu, tôi nghĩ thời điểm hiện tại chỉ có thể bán tốt Reno4. Theo dự đoán ban đầu, Reno4 Pro chỉ có thể chiếm tối đa 10-15% tổng số đơn cọc, Nhưng hiện tại Reno4 Pro đang chiếm đến hơn 20-25% tổng số”. Ông Phùng Ngọc Tuyên chia sẻ.</p>\r\n<p>Reno4 & Reno4 Pro được trang bị hệ thống 4 camera, bao gồm: camera chính 48MP, camera siêu góc rộng 8MP, camera Macro 2MP và ống kính đơn sắc 2MP. Trong đó, tính năng Chân dung màu AI có thể giữ màu chủ thể trên hậu cảnh đơn sắc làm nổi bật phong cách nhân vật mà không cần thông qua ứng dụng thứ ba. Đây là “cứu cánh” cho những bức ảnh chân dung đẹp dù bối cảnh phức tạp, lộn xộn. Ngoài ra, Reno4 Pro còn là smartphone thương mại có tốc độ sạc nhanh nhất thị trường hiện nay khi trang bị SuperVOOC 2.0 công suất 65W, sạc đầy viên pin 4000mAh chỉ trong 36 phút. Bộ vi xử lý mạnh mẽ Qualcomm Snapdragon 720G, RAM 8GB cùng bộ nhớ lên đến 128GB và 256GB tuỳ phiên bản, cũng là yếu tố thuyết phục sau cùng cho những ai còn lăn tăn về hiệu năng của chiếc smartphone phong cách Reno4 & Reno4 Pro.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-408\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-408-1\"></ins></ins></p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/OPPO/Reno4%20mo%20ban/OPPOReno4%20(7%20of%2023).jpg\" alt=\"OPPOReno4 (7 of 23)\" /></p>\r\n<p>Bên cạnh đó, lần đầu tiên ra mắt tại thị trường Việt Nam, OPPO Watch cũng đã thể hiện “sức nóng” của mình khi nhận được sự quan tâm và ủng hộ nồng nhiệt từ giới truyền thông cũng như khách hàng nhờ độ hoàn thiện tốt và những tính năng ấn tượng của sản phẩm. Chỉ trong vỏn vẹn 6 ngày đặt hàng, OPPO đã ghi nhận tổng cộng 1,5 ngàn đơn đặt mua và hơn 800 đơn đặt cọc tại cả hai điểm bán độc quyền Thế Giới Di Động và OPPO Experience Store. Đặc biệt hơn, tại Thế Giới Di Động, số lượng đặt mua chiếc smartwatch đầu tiên của OPPO đạt 1,5 ngàn với 671 cọc tính đến 10h tối ngày 07/08. Tính trung bình, mỗi ngày có gần 100 chiếc OPPO Watch được đặt mua và chờ đến tay người dùng chính thức vào ngày mở bán hôm nay. Cùng với Reno4 Series, đây được xem một kết quả khả quan và đáng ghi nhận cho sản phẩm smartwatch “chào sân” từ OPPO, đặc biệt trong giai đoạn cực kì khó khăn và biến động của thị trường. </p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/OPPO/Reno4%20mo%20ban/OPPOReno4%20(2%20of%2023).jpg\" alt=\"OPPOReno4 (2 of 23)\" /></p>\r\n<p>Một điểm đặc biệt khác cũng có ảnh hưởng đến số lượng đơn hàng đặt trước chính là cú bắt tay giữa OPPO và Thế Giới Di Động. Theo đó, chuỗi bán lẻ lớn nhất Việt Nam sẽ kinh doanh độc quyền OPPO Watch (cùng với OPPO Experience Store), đồng thời kì vọng bán được 20,000 đơn vị trong 3 tháng tới, gấp đôi doanh số của những smartwatch sừng sỏ khác hiện có trên thị trường. Việc kí kết độc quyền cho thấy sự tin tưởng của OPPO và đối tác, đồng thời cũng thể hiện chất lượng sản phẩm tuyệt vời, rất hấp dẫn và giá thành phù hợp nhu cầu người dùng.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/OPPO/Reno4%20mo%20ban/OPPOReno4%20(17%20of%2023).jpg\" alt=\"OPPOReno4 (17 of 23)\" /></p>\r\n<p>OPPO Watch là sản phẩm đồng hồ thông minh đầu tiên của OPPO. Sở hữu 2 phiên bản 41 mm và 46 mm, OPPO Watch được trang bị hệ điều hành WearOS, phù hợp với các ứng dụng và dịch vụ cốt lõi của Google đối với thiết bị đeo tay. Bên cạnh khả năng sạc 100% pin trong 75 phút nhờ công nghệ Watch VOOC, OPPO Watch còn có các tính năng chăm sóc sức khỏe và thể thao. Với người dùng, OPPO Watch là thiết bị công nghệ góp phần cải thiện chất lượng cuộc sống. Còn với OPPO, đây được xem là thiết bị chiến lược trong quá trình xây dựng hệ sinh thái IoT độc đáo của thương hiệu. Từ các thế hệ smartphone phổ thông đến cao cấp, tai nghe không dây, loa Bluetooth, hệ điều hành ColorOS và giờ đây là smartwatch, tất cả chứng minh OPPO thật sự sẵn sàng cho hành trình tạo ra hệ sinh thái mang tên mình.</p>\r\n<div> </div>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'hiuhiu');
INSERT INTO `bao` (`idbao`, `tieude`, `noidung`, `noidungchinh`, `ngaydang`, `slxem`, `slbinhluan`, `loaibao`, `idchuyenmuc`, `tacgia`) VALUES
(103, 'Lệnh cấm WeChat có thể giảm doanh số iPhone', 'Doanh số iPhone tại Trung Quốc có thể giảm mạnh thời gian tới nếu người dùng không thể tải WeChat từ kho ứng dụng của Apple.', '          <p>Lệnh cấm của Tổng thống Donald Trump không cho phép các công ty Mỹ được làm ăn với Tencent, chủ sở hữu WeChat. Điều này có thể dẫn đến việc Apple không được đưa ứng dụng WeChat lên App Store.</p>\r\n<article>\r\n<p>Ở Trung Quốc, \"siêu ứng dụng\" WeChat có mặt trong hầu hết ngóc ngách của đời sống, từ nhắn tin, gọi điện, thanh toán, tìm kiếm, trao đổi thông tin cho đến hẹn hò. Có hơn một tỷ người dùng Trung Quốc đang sử dụng WeChat thường xuyên. Du khách đến đây thường phải cài ứng dụng nếu muốn thanh toán cho các giao dịch từ lớn đến nhỏ.</p>\r\n<figure data-size=\"true\">\r\n<div><img src=\"https://media.techz.vn/media2019/source/Vu_Ve/iphone/111.jpg\" alt=\"\" /></div>\r\n<figcaption>\r\n<p><em>Ứng dụng WeChat có thể bị gỡ trên kho ứng dụng của Apple nếu lệnh cấm của Trump được thực thi. Ảnh: Bloomberg.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p>Nếu không thể truy cập vào WeChat, người dùng khó có thể mua iPhone ở Trung Quốc. Nếu mua iPhone mà không có WeChat, thì sẽ không ai dùng iPhone. Một cuộc thảo luận diễn ra trên diễn đàn trực tuyến của các nhà đầu tư chứng khoán về việc \"Bạn sẽ bỏ iPhone hay WeChat nếu Apple xoá ứng dụng này khỏi App Store?\". Kết quả phần lớn người dùng đều chọn ứng dụng thay vì điện thoại.</p>\r\n<div>Anand Srinivasan, nhà phân tích của Bloomberg, cho biết thị trường Trung Quốc chiếm khoảng 20% doanh số bán iPhone của Apple, vì vậy việc xoá WeChat trên App Store \"sẽ là một trở ngại nghiêm trọng\". Thông tin này đã khiến cổ phiếu của Apple giảm 2,5% vào ngày 7/8.</div>\r\n<div>\r\n<p>Mặc dù cửa hàng ứng dụng của Google bị cấm ở Trung Quốc, có nhiều cách để cài ứng dụng này lên điện thoại Android. Tuy nhiên, việc cài một phần mềm ngoài App Store lên iPhone lại không phải là điều đơn giản. Apple không bình luận gì về điều này.</p>\r\n<ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-212\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-212-1\"></ins></ins>\r\n<p>Lệnh cấm của Trump cũng có thể khiến Trung Quốc trả đũa, làm Apple bị tổn hại. Srinivasan cho biết, phần lớn hoạt động sản xuất của Apple diễn ra tại Trung Quốc. Nếu chính quyền Bắc Kinh \"phản đòn\", hoạt động kinh doanh của Apple sẽ bị ảnh hưởng nghiêm trọng. Trung Quốc cũng có thể hạn chế nguồn cung cấp vật liệu, chẳng hạn những thành phần kim loại hiếm trong cấu tạo của iPhone. \"Đây là cuộc chiến trên nhiều mặt trận và nó đã leo thang lên cả lĩnh vực phần mềm\", nhà phân tích của Bloomberg đánh giá.</p>\r\n<p>Sắc lệnh của Trump sẽ có hiệu lực sau 45 ngày, kể từ ngày 6/8. Tuy nhiên, nhiều nội dung vẫn chưa được làm rõ và vẫn còn nhiều điều bất ngờ có thể xảy ra. Một vài nguồn tin cho biết ông Trump sẽ tiếp tục thảo luận về lệnh cấm này và có thể đưa ra một số điều kiện ngoại lệ. Apple cũng có thể mở hệ điều hành của mình, cho phép người dùng tải ứng dụng mà không cần qua App Store. Tuy nhiên, đây sẽ là bước ngoặt lớn vì hãng vẫn cố gắng bảo vệ quyền truy cập vào hệ sinh thái và thu phí 30% từ nhiều ứng dụng.</p>\r\n<p>Nếu lệnh cấm được tiến hành và Apple không có cách giải quyết, người dùng Trung Quốc có thể sẽ chuyển sang các thương hiệu nội địa như Huawei. Điều này lại vô tình có ích cho công ty mà Trump đang cố gắng làm suy giảm sức mạnh trong nhiều năm qua.</p>\r\n</div>\r\n</article>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(104, 'Bộ đôi điện thoại hội tụ đầy đủ những tiên phong công nghệ bậc nhất mà ai cũng nên lựa chọn!', 'Samsung Galaxy A51 và Galaxy A71 xứng đáng là người bạn đồng hành trong những cuộc vui, cháy hết mình của giới trẻ. ', '          <p style=\"font-weight: 400;\">Samsung vẫn thống trị phân khúc điện thoại tầm trung với dòng Galaxy A ngày càng trẻ trung, nhiều công nghệ tiên tiến và liên tục cập nhập những xu hướng được ưa chuộng. Sau khi Galaxy S20 thành công với tính năng Chụp Một Chạm \"đỉnh cao\" dòng Galaxy A 2020 vốn được nhiều bạn trẻ yêu thích cũng tiếp bước để mang tới trải nghiệm hoàn hảo về camera.</p>\r\n<p style=\"font-weight: 400;\">Giới trẻ lựa chọn Galaxy A51 và Galaxy A71 nhờ thiết kế đẹp mắt, trẻ trung nhưng không kém phần sang trọng, màn hình kích thước lớn, pin cả ngày dài và cụm camera \"cực xịn\" vừa được bổ sung thêm vũ khí. Những cuộc vui không thể thiếu những chiếc điện thoại, ghi lại khoảnh khắc, đoạn video hài hước, chẳng giống ai hay chỉ đơn giản là phát những bản nhạc yêu thích.</p>\r\n<p style=\"font-weight: 400;\"><strong>Bộ 4 camera sau tối tân - \"càng nhiều càng vui\"</strong></p>\r\n<p style=\"font-weight: 400;\">Cả Galaxy A71 và Galaxy A51 đều sở hữu 4 camera sau với cảm biến chính độ phân giải cao, camera góc rộng, camera tele và cảm biến macro cho ảnh chụp cực ấn tượng. Ảnh chụp đa dạng, AI kết hợp thuật toán ấn tượng giúp Galaxy A51 và Galaxy A71 trở thành người bạn đồng hành không thể thiếu với các bạn trẻ. Việc trang bị thêm nhiều cảm biến giúp camera smartphone tiệm cận hơn với máy ảnh, đồng thời mang tới các góc chụp đa dạng và độc đáo phù hợp với giới trẻ. Cũng giống như những cuộc vui của giới trẻ, cảm biến camera càng nhiều, càng thêm vui.</p>\r\n<p style=\"font-weight: 400;\"><strong>Đỉnh cao Chụp Một Chạm</strong></p>\r\n<p style=\"font-weight: 400;\">Chụp Một Chạm là tính năng cao cấp nhất của Samsung nếu chỉ tính riêng ở camera. Với một lần chạm, bạn trẻ đã có được nhiều video, hình ảnh được ghi lại ở nhiều góc khác nhau, mới lạ mà thậm chí đưa máy cho người \"vô tâm\" vẫn thu được những bức hình ưng ý. Chỉ với một tính năng, Samsung đã nâng tầm cả một dòng điện thoại ở tầm giá hấp dẫn mà ai cung có thể lựa chọn.</p>\r\n<p style=\"font-weight: 400;\">Ngoài những ấn tượng về camera, bộ đôi Galaxy A51 và Galaxy A71 khiến thị trường dậy sóng với màu Bạc Trendy lôi cuốn, thời lượng sử dụng dài hơi với viên pin lên tới 4500mAh và một màn hình đảm bảo giải trí ở mức cao nhất. Bội đôi sản phẩm của Samsung là sự lựa chọn hợp lý dành cho các bạn trẻ, cuộc vui thêm dài, thêm vui và thêm nhiều khoảnh khắc thú vị.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(105, 'OnePlus chính thức gia nhập thị trường Việt Nam', 'Cuối cùng, OnePlus đã xác nhận gia nhập thị trường Việt Nam thông qua fanpage và group Facebook, trang Instagram chính thức của hãng sau nhiều dấu hiệu gần như rõ ràng.', '          <p dir=\"ltr\">Trước đó, hình ảnh cũng như thông tin OnePlus gia nhập thị trường Việt Nam đã được đăng tải trên rất nhiều kênh thông tin khác nhau. Từ fanpage chính thức, website bổ sung thêm thị trường Việt Nam cho đến hình ảnh Tổng Giám Đốc Thế Giới Di Động cầm chiếc OnePlus 8 Pro đều cho thấy ngày OnePlus gia nhập đã đến gần.</p>\r\n<p dir=\"ltr\"><img src=\"https://media.techz.vn/media2019/source/conghau/Oneplus/Oneplus%20viet%20nam/OnePlus-gia-nhap-Viet-Nam-1.png\" alt=\"OnePlus-gia-nhap-Viet-Nam-1\" /></p>\r\n<p dir=\"ltr\">Những dấu hiệu này mang đến niềm vui không nhỏ dành cho cộng đồng người dùng OnePlus khi họ sẽ được sử dụng những mẫu smartphone được cung cấp chính hãng với chính sách bảo hành tin cậy. OnePlus là thương hiệu dẫn đầu về việc mang tới trải nghiệm tốt nhất, công nghệ cao nhất dành cho người dùng với mức giá vừa phải. Chính vì vậy, rất nhiều người Việt chấp nhận bất lợi khi tìm đến con đường xách tay để sở hữu sản phẩm của OnePlus. Các mẫu smartphone cao cấp của hãng có thể cạnh tranh sòng phẳng tại phân khúc điện thoại Android cao cấp với mức giá hấp dẫn hơn rất nhiều.</p>\r\n<p dir=\"ltr\"><img src=\"https://media.techz.vn/media2019/source/conghau/Oneplus/Oneplus%20viet%20nam/OnePlus-gia-nhap-Viet-Nam-2.png\" alt=\"OnePlus-gia-nhap-Viet-Nam-2\" /></p>\r\n<p dir=\"ltr\">Hơn ai hết, người dùng Việt mong chờ OnePlus chính thức tham gia thị trường để có thể sở hữu những sản phẩm tin cậy, chất lượng cao. Cuối cùng, sự chờ đợi đã được đền đáp, OnePlus chính thức tiến vào Việt Nam với sản phẩm đầu tay là mẫu smartphone cao cấp nhất OnePlus 8 Pro.</p>\r\n<p dir=\"ltr\"><strong>OnePlus sẽ mang tới điều gì cho thị trường Việt Nam?</strong></p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-8382\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-8382-1\"></ins></ins></p>\r\n<p dir=\"ltr\">OnePlus gia nhập thị trường Việt Nam theo một cách nghiêm túc từ việc triển khai website chính thức, fanpage chính thức và kết hợp cùng Chuỗi bán lẻ thiết bị di động lớn nhất để cung cấp mẫu smartphone đầu tiên tới tay người dùng. Cách làm này đảm bảo cho tiến độ tiếp cận người dùng Việt một cách nhanh nhất. Ngay lập tức, những tín hiệu tích cực từ cộng đồng người dùng OnePlus nói riêng và người tiêu dùng Việt Nam nói chung.</p>\r\n<p dir=\"ltr\"><img src=\"https://media.techz.vn/media2019/source/conghau/Oneplus/Oneplus%20viet%20nam/OnePlus-gia-nhap-Viet-Nam-4.png\" alt=\"OnePlus-gia-nhap-Viet-Nam-4\" /></p>\r\n<p dir=\"ltr\">Rất nhiều người đã dự đoán về giá bán của OnePlus 8 Pro sẽ rất tốt, rẻ hơn phần lớn các mẫu flagship đang lên kệ tại Việt Nam. Giá thành cao hơn so với thị trường xách tay nhưng đổi lại sự tin cậy, nhiều chương trình khuyến mãi, hậu mãi hấp dẫn.</p>\r\n<p dir=\"ltr\">Việc OnePlus gia nhập Việt Nam, sức cạnh tranh của toàn thị trường điện thoại được tăng cao, đặc biệt ở phân khúc cao cấp. Giá thành hấp dẫn của OnePlus sẽ khiến các ông lớn phải dè chừng và thế thống thị phần nào bị ảnh hưởng. OnePlus cũng hứa hẹn mang lại những mẫu smartphone tầm trung giá tốt sau khi khởi động chiến dịch với OnePlus 8 Pro. Rõ ràng, người được hưởng lợi cuối cùng là người dùng Việt khi chúng ta có thêm nhiều lựa chọn tốt trong tương lai gần.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(106, '11 Pro Max cùng loạt iPhone cũ lao dốc không phanh, dọn đường cho iPhone 12 ra mắt', 'Rất nhiều dòng iPhone cũ giảm giá mạnh khi ngày iPhone 12 ra mắt đã cận kề. Thông tin này khiến nhiều khách hàng háo hức hơn cả.', '          <p style=\"font-weight: 400;\">Apple sắp sửa cho ra mắt iPhone 12, chiếc điện thoại được trông chờ nhất năm nay trong vòng khoảng 1 tháng nữa. Thời điểm này cũng là lúc những mẫu iPhone cũ lần lượt giảm giá từ 1 – 5 triệu đồng. Rất nhiều nhà bán lẻ đã bắt đầu hành trình “dọn đường” cho iPhone 12.</p>\r\n<h3>iPhone 11 Pro Max (giảm 3 triệu đồng)</h3>\r\n<p style=\"font-weight: 400;\">iPhone 11 Pro Max đang là chiếc điện thoại cao cấp nhất của Apple. Sản phẩm này được bán với giá khoảng 33,99 triệu đồng. Dù đã giảm đến 3 triệu đồng cùng một số chương trình khuyến mại khác nhưng mức tiền vẫn khá cao so với mặt bằng chung. Vì thế mà cũng hiếm người dùng có thể đủ kinh phí để mua iPhone 11 Pro Max.</p>\r\n<h3>iPhone 11 Pro (giảm 2,5 triệu đồng)</h3>\r\n<p style=\"font-weight: 400;\">iPhone 11 Pro được xem như bản nâng cấp chip của XS. Nó có kích thước tương ứng, song bổ sung thêm cụm camera góc cực rộng 12MP và dung lượng pin được nâng cấp. Hiện tại, iPhone 11 Pro đã giảm xuống còn 30,99 triệu đồng.</p>\r\n<h3>iPhone XS Max (giảm 3 triệu đồng)</h3>\r\n<p style=\"font-weight: 400;\">iPhone XS Max cũng là dòng cao cấp của Apple hiện nay. Trong thời điểm iPhone 12 cận kề ra mắt, nó bắt đầu giảm 3 triệu đồng và còn khoảng 25,99 triệu đồng. Trên thực tế, XS Max chỉ đứng sau 11 Pro Max, những tính năng chụp ảnh hay bề ngoài của nó cũng sang chảnh không kém người anh em của mình.</p>\r\n<h3>iPhone XS (giảm 2 triệu đồng)</h3>\r\n<p style=\"font-weight: 400;\">XS dù đã giảm khá nhiều song nhìn chung giá vẫn chát khi có mức 19,99 triệu đồng. Mẫu iPhone này có màn hình nhỏ gọn và khả năng chụp ảnh khá ổn nên cũng được ưa chuộng. Cùng với đó, mức giá của nó cũng dễ chấp nhận hơn những dòng XS Max hay 11 Pro Max.</p>\r\n<h3>iPhone 11 (giảm 1 triệu đồng)</h3>\r\n<p style=\"font-weight: 400;\">iPhone 11 là chiếc điện thoại hot nhất nhì ở thị trường Việt Nam. Đây đang là thời điểm tốt để sắm cho mình một chiếc iPhone 11 khi giá đã giảm xuống từ 21,99 triệu đồng. Trong tầm giá của những chiếc iPhone 2019, iPhone 11 có mức giá phải chăng nhất song sức mạnh thì cũng không phải dạng vừa.</p>\r\n<h3>iPhone SE 2020  (giảm 1,5 triệu đồng)</h3>\r\n<p style=\"font-weight: 400;\">Cùng với iPhone 11, iPhone SE 2020 cũng đang được giảm nhẹ. Chỉ từ 17,99 triệu đồng là bạn đã có thể sở hữu chiếc điện thoại này.</p>\r\n<p style=\"font-weight: 400;\"><em>Lưu ý: Một số dòng máy chỉ áp dụng mức giá trên đến hết 13/8.</em></p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(107, 'Đây là những gì được giới thiệu trong Galaxy Unpacked 2020!', 'Samsung vừa tổ chức sự kiện Unpacked mới với rất nhiều sản phẩm thú vị. Đó là những sản phẩm gì?!!', '          <p>Samsung vừa tổ chức sự kiện Unpacked mới với rất nhiều sản phẩm thú vị. Sản phẩm được mong chờ nhất không gì khác đó chính là Samsung Galaxy Note 20 và Samsung Note 20 Ultra. Bộ đôi sản phẩm này màn hình 120Hz, màu đồng đặc trưng và mới nhất của Samsung năm nay. Tiếp theo là sản phẩm Samsung Galaxy Tab S7/Samsung Galaxy Tab S7+ cũng được trang bị công nghệ mới, tính năng mới, có thể kể ra đó chính là các tác vụ đa nhiệm. Điểm nhấn ngoài sản phẩm đó còn có Samsung Galaxy Z Fold 2 thừa hưởng ưu điểm từ Samsung Galaxy Z Flip và Galaxy Fold. Các sản phẩm khác được giới thiệu trong sự kiện lần này bao gồm Samsung Galaxy Buds Live, Galaxy Watch 3 và hệ sinh thái cực kỳ khủng của Samsung.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5135\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5135-1\"></ins></ins></p>\r\n<p><iframe src=\"https://www.youtube.com/embed/omncgLKRMYE\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\" data-mce-fragment=\"1\"></iframe></p>\r\n<p> </p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(108, 'Cơ hội vàng mua Galaxy Note 20 với ưu đãi tốt nhất thị trường từ Viettel trị giá đến 19,6 triệu đồng', 'Thị trường smartphone lại một lần nữa sôi động khi Samsung trình làng bộ ba sản phẩm Galaxy Note 20. Bắt đầu từ ngày 06/08, các nhà bán lẻ và các đơn vị hợp tác với Samsung đã cho đặt hàng trước Galaxy Note 20 với ưu đãi cực kỳ hấp dẫn.', '          <p><span lang=\"EN-US\">Tối qua 05/08, Samsung chính thức trình làng siêu phẩm Galaxy Note 20 đáp ứng kì vọng của người dùng: Màn hình QHD 6.9’’, tần số 120Hz, chip Snapdragon 865+, hỗ trợ 5G,...</span><span lang=\"EN-US\"> </span></p>\r\n<p><span lang=\"EN-US\">Mặc dù 21/8 mới có hàng tại Việt Nam nhưng các đại lý, nhà bán lẻ đã cho khách hàng đặt trước model này cùng nhiều ưu đãi hấp dẫn. Đây là cơ hội vàng dành cho Sam Fan khi mua Galaxy Note mới.</span></p>\r\n<p><span lang=\"EN-US\">Theo đó, đáng chú ý nhất là thông tin Viettel Telecom bắt đầu hợp tác với các thương hiệu điện thoại cao cấp tung ra ưu đãi độc quyền tốt nhất trên thị trường, nhằm đem đến trải nghiệm tốt nhất cho khách hàng. </span><span lang=\"EN-US\">Đợt đặt hàng đầu tiên 25/07 đến 05/08/2020 đã nhận được sự quan tâm </span><span lang=\"EN-US\">lớn của khách hàng Viettel với tỉ lệ truy cập tích cực đến trang đặt hàng </span><span lang=\"EN-US\"><a href=\"http://viettel.vn/note2020\" target=\"_blank\" rel=\"noopener\">http://viettel.vn/note2020</a>.</span></p>\r\n<p><span lang=\"EN-US\">Chính vì vậy, Samsung đã công bố bổ sung quyền lợi cho chương trình </span><span lang=\"EN-US\">ưu đãi đặc biệt cho CBNV Viettel với tổng giá trị nay lên</span><span lang=\"EN-US\"> đến 19.600.000đ.</span><span lang=\"EN-US\"> </span><span lang=\"EN-US\"> </span></p>\r\n<p><span lang=\"EN-US\">Sự kiện khá bất ngờ của Viettel đã tạo nên cơn sốt khiến khách hàng “đứng ngồi không yên”. Khi đặt mua Galaxy Note mới tại Viettel.vn/note2020</span> <span lang=\"EN-US\">từ ngày 25/7 đến ngày 20/8</span><span lang=\"EN-US\">, khách hàng có cơ nhận ưu đãi lên đến 19,6 triệu đồng, đây là gói ưu đãi dành cho khách hàng tốt nhất thị trường hiện nay. </span><span lang=\"EN-US\"> </span></p>\r\n<p><span lang=\"EN-US\">Chi tiết gói ưu đãi độc quyền của Viettel dành tặng khách hàng như sau: </span></p>\r\n<p><strong><span lang=\"EN-US\">1. Thời gian:</span></strong></p>\r\n<p><span lang=\"EN-US\">Từ ngày 06/08 đến 20/08/2020</span></p>\r\n<p><strong><span lang=\"EN-US\">2. Thông tin sản phẩm:</span></strong></p>\r\n<p><span lang=\"EN-US\">Samsung ra mắt 3 phiên bản với giá và màu sắc như sau: </span></p>\r\n<p><strong>Samsung Galaxy Note20</strong> có giá bán 23.990.000 VND với 3 màu Đồng Ánh Kim Huyền Bí, Xanh Huyền Bí, Xám Huyền Bí.</p>\r\n<p><strong>Samsung Galaxy Note20 Ultra</strong> có giá bán 29.990.000 VND với 3 màu Đồng Ánh Kim Huyền Bí, Trắng Huyền Bí, Đen Huyền Bí.</p>\r\n<p><strong>Samsung Galaxy Note20 Ultra 5G</strong> có giá bán 32.990.000 VND hiện có một màu duy nhất Đồng Ánh Kim Huyền Bí</p>\r\n<p><span lang=\"EN-US\"><strong>3. Quà tặng:</strong></span></p>\r\n<p><span lang=\"EN-US\">Chương trình Đặt gạch từ </span><span lang=\"EN-US\">06/08-20/08: </span><span lang=\"EN-US\">Khách hàng đặt gạch Samsung Galaxy Note Mới tại </span><span lang=\"EN-US\"><a href=\"http://viettel.vn/note2020\" target=\"_blank\" rel=\"noopener\">http://viettel.vn/note2020</a></span><span lang=\"EN-US\"> sẽ nhận được các khuyến mại như sau, tổng giá trị lên đến 19,6 triệu đồng:</span></p>\r\n<p><strong><span lang=\"EN-US\">* Quà độc quyền từ Viettel trị giá 6.400.000đ:</span></strong></p>\r\n<p><span lang=\"EN-US\">Giảm 3 triệu trực tiếp trên giá máy khi đăng kí gói cước V500S (miễn phí gọi nội mạng với cuộc gọi dưới 20 phút, miễn phí 250 phút ngoại mạng/tháng, 5GB/ngày)</span></p>\r\n<p><span lang=\"EN-US\">Mi</span><span lang=\"EN-US\">ễn phí 240 GB trong 12 tháng, trị giá 2.400.000đ</span></p>\r\n<p><span lang=\"EN-US\">Tặng thêm 1 triệu điểm Viettel++, tương đương 1.000.000đ</span><span lang=\"EN-US\"> </span></p>\r\n<p><strong><span lang=\"EN-US\">* Bộ quà tặng sành điệu từ Samsung</span></strong></p>\r\n<p><span lang=\"EN-US\"><img src=\"https://media.techz.vn/media2019/source/NguyenKhoa/Screen%20Shot%202020-08-07%20at%2012-41-22%20PM.png\" alt=\"Screen Shot 2020-08-07 at 12-41-22 PM\" /></span></p>\r\n<p><strong><span lang=\"EN-US\">5. Đặt cọc trước: 1.000.000VNĐ</span></strong></p>\r\n<p><span lang=\"EN-US\">Chi tiết chương trình truy cập trang </span><span lang=\"EN-US\"><a href=\"http://viettel.vn/note2020\" target=\"_blank\" rel=\"noopener\">http://viettel.vn/note2020</a></span><span lang=\"EN-US\"> hoặc HOTLINE 18008098.</span></p>\r\n<p><span lang=\"EN-US\">Như vậy, khách hàng đặt gạch Galaxy Note sớm tại </span><span lang=\"EN-US\"><a href=\"http://viettel.vn/note2020\" target=\"_blank\" rel=\"noopener\">http://viettel.vn/note2020</a></span><span lang=\"EN-US\"> sẽ tiết kiệm được 19,6 triệu đồng. Còn chần chừ gì nữa, đặt hàng càng sớm nhận quà càng to, đừng bỏ lỡ cơ hội vàng đến từ Viettel. Hãy truy cập ngay </span><span lang=\"EN-US\"><a href=\"http://viettel.vn/note2020\" target=\"_blank\" rel=\"noopener\">http://viettel.vn/note2020</a></span><span lang=\"EN-US\"> hoặc HOTLINE 18008098 (miễn phí) để đặt mua Galaxy Note mới với giá ưu đãi tốt nhất thị trường. </span></p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(109, 'Trên tay nhanh bộ đôi Galaxy Note 20/Note20 Ultra: Giá từ 24 triệu', 'Galaxy Note 20 và Note 20 Ultra đều sở hữu chung nhiều ưu điểm hiếm mà không phải bất kì dòng smartphone cao cấp nào cũng sở hữu ở thời điểm hiện tại.', '          <p>Galaxy Note 20 Ultra vẫn kế thừa những đường nét vuông vức nam tính vốn có của dòng Note-Series cùng điểm nổi bật là cụm camera sau hình chữ nhật khá lớn, nhưng không quá thô như các sản phẩm trước. Trong khi đó, bản tiêu chuẩn Galaxy Note 20 lại có nhiều góc bo tròn nhiều hơn và nắp lưng được hoàn thiện từ nhựa sơn mờ chống bám bụi bẩn rất tốt. sở hữu các cạnh được bo cong, vỏ được phủ một lớp sơn mờ chống bám dấu vân tay. Galaxy Note 20 có 3 màu: Xanh huyền bí, Xám huyền bí và Đồng huyền bí, còn phiên bản Ultra sẽ Màu Đồng Ánh Kim Huyền Bí, Đen Huyền Bí, Trắng Huyền Bí.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02906.jpg\" alt=\"MEL02906\" /></p>\r\n<p>Galaxy Note 20 Ultra được trang bị màn hình Dynamic AMOLED kích thước 6.9 inch, hỗ trợ tốc độ làm mới khung hình 120 Hz, độ sáng tăng 25% so với bản tiền nhiệm. Một điều khó hiểu là Samsung chỉ trang bị cho Galaxy Note 20 màn hình tần số quét 60Hz đi kèm kích thước 6.7-inch sử dụng công nghệ Super AMOLED Plus. Đây thực sự là cắt giảm rất mạnh tay của Samsung nếu so với dòng S20-Series ra mắt hồi đầu năm.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02842.jpg\" alt=\"MEL02842\" /></p>\r\n<p>Bên cạnh đó, Galaxy Note 20 Ultra cũng chỉ sở hữu viên pin 4.500mAh còn Note 20 sẽ là 4.300mAh và hỗ trợ sạc nhanh 25W và đi kèm vi xử lý Exynos 990, RAM 8GB cùng bộ nhớ trong 256GB. Phiên bản đặc biệt Note 20 Ultra 5G sẽ có RAM 12GB và tích hợp thêm module mạng 5G.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02852.jpg\" alt=\"MEL02852\" /></p>\r\n<p>Galaxy Note 20 Ultra, có 4 cảm biến bao gồm camera chính có độ phân giải siêu khủng 108 MP, cảm biến 12 MP và 2 cảm biến phụ khác. Máy có khả năng quay video 8K ở 2 tỉ lệ 16:9 hoăc 21:9 không giới hạn dung lượng cùng nhiều tính năng tuỳ chỉnh chuyên nghiệp. Đối với Note 20, máy sẽ bao gồm 4 ống kính với camera chính 64MP cùng cảm biến phụ 12MP và 2 cảm biến nhỏ khác. Dự kiến bộ đôi Note 20 sẽ lên kệ vào ngày 21/8 tới.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-9196\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-9196-1\"></ins></ins></p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02917.jpg\" alt=\"MEL02917\" /></p>\r\n<p><strong>Một số hình ảnh khác về Galaxy Note 20 I Note 20 Ultra:</strong></p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02815.jpg\" alt=\"MEL02815\" /></p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02821.jpg\" alt=\"MEL02821\" /></p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Tr%C3%AAn%20tay%20Galaxy%20Note%2020%20Note%2020%20Ultra/MEL02848.jpg\" alt=\"MEL02848\" /></p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(110, 'Những điểm ấn tượng nhất trên Samsung Galaxy Note 20 và Note 20 Ultra', 'Sau nhiều tháng chờ đợi, Samsung Galaxy Note 20 đã chính thức được giới thiệu với khá nhiều bất ngờ thú vị, nhiều nâng cấp công nghệ sáng giá và đặc biệt là thêm những màu sắc mới, sang trọng nhưng không kém phần trẻ trung', '          <p style=\"font-weight: 400;\">Sự kiện Samsung Unpacked diễn ra tối qua đã để lại rất nhiều ý kiến trái chiều và phần lớn trong đó đều ủng hộ những thay đổi trên dòng Note mới. Thêm một dòng smartphone cao cấp đạt ngưỡng hoàn hảo, khẳng định vị thế dẫn đầu thế giới của ông lớn xứ Hàn. Đây là những điểm nổi bật của Galaxy Note 20 sẽ khiến bạn \"cuốn hút\" ngay khi nghe thấy.</p>\r\n<p style=\"font-weight: 400;\"><strong>Thiết kế thanh lịch, thêm nhiều màu sắc mới</strong></p>\r\n<p style=\"font-weight: 400;\">So với Galaxy S, dòng Galaxy Note luôn có thiết kế khác biệt rõ ràng, các góc được làm vuông vức hơn để phục vụ cho triết lý sản phẩm. Galaxy Note 20 và Note 20 Ultra đều mang trong mình kiểu dáng hiện đại, cao cấp. Với phiên bản lớn hơn là mặt kính với khung kim loại đẹp mắt, sang trọng trong khi Note 20 sử dụng chất liệu polycarbonate với mặt lưng được làm nhám với nhiều màu sắc trẻ trung. Cả hai đều sở hữu màn hình với viền siêu mỏng, chiếm gần như tối đa ở mặt trước.</p>\r\n<p style=\"font-weight: 400;\">Thay vì sử dụng màu Aurora của thế hệ Galaxy Note trong năm 2019, Samsung sử dụng màu sắc phần nào đó tinh tế hơn, diu mắt hơn. Note 20 Ultra có màu đen, trắng và màu đồng mới được hứa hẹn sẽ là sự lựa chọn hàng đầu trong thời gian tới. Trong khi đó, Note 20 cũng có màu đồng, màu xanh lục cực kỳ đẹp và màu xám gần giống với Galaxy A51 và A71 ra mắt gần đây. Thực tế, Samsung muốn dòng Galaxy Note trở nên phổ biến hơn, hướng tới nhiều đối tượng hơn, một sản phẩm phục vụ cho công việc và sáng tạo.</p>\r\n<p style=\"font-weight: 400;\"><strong>Màn hình vẫn ở \"đỉnh\" thế giới</strong></p>\r\n<p style=\"font-weight: 400;\">Samsung vẫn đang thành công với màn hình Dynamic AMOLED cho màu sắc rực rỡ, độ sáng cao làm nên thương hiệu cho các dòng smartphone của hãng. Galaxy Note 20 Ultra sử dụng màn hình cong độ phân giải WQHD+ 3200x1440 pixels, trong khi Note 20 chỉ được trang bị màn hình phẳng FHD+ 2400x1080 pixels. Mật độ điểm ảnh của hai sản phẩm lần lượt là 393 ppi trên Note 20 và 511 ppi trên Note 20 Ultra.</p>\r\n<p style=\"font-weight: 400;\">Độ phân giải không phải vấn đề quá lớn trên Galaxy Note 20, máy vẫn đảm bảo được khả năng sử dụng, trải nghiệm tốt nhưng chỉ có Galaxy Note 20 Ultra mới có tốc độ làm mới 120Hz (Full HD+). Đây là điều khá đáng tiếc nhưng xét về giá thành thì lại cho thấy nước cơ hợp lý của Samsung. Cả hai đều sử dụng công nghệ Infinity-O độc đáo với lỗ tròn camera selfie ở mặt trước, sát với cạnh trên.</p>\r\n<p style=\"font-weight: 400;\"><strong>Cấu hình mạnh mẽ</strong></p>\r\n<p style=\"font-weight: 400;\">Cả Galaxy Note 20 và Note 20 Ultra đều hỗ trợ kết nối 5G nhưng sẽ phụ thuộc vào từng phiên bản, thị trường khác nhau. Nhiều khả năng thị trường Việt Nam sẽ bán ra Note 20 Ultra 5G là phiên bản cao nhất.  Note 20 Ultra bản thường sẽ có 8GB RAM và 3 lựa chọn bộ nhớ trong khác nhau, trong khi Note 20 chỉ có 8GB và 256GB bộ nhớ trong.</p>\r\n<p style=\"font-weight: 400;\">Galaxy Note 20 Ultra là thiết bị mạnh mẽ nhất với vi xử lý 8 nhân, xây dựng trên tiến trình 7nm với xung nhịp 3.0GHz mỗi nhân, trong khi Note 20 tương tự nhưng xung nhịp hạ xuống còn 2.7GHz. Bộ nhớ trong được sử dụng cũng khác biệt, người dùng có lựa chọn UFS 3.0 và UFS 3.1</p>\r\n<p style=\"font-weight: 400;\"><strong>Camera - mọi thứ vẫn ở điểm cao nhất</strong></p>\r\n<p style=\"font-weight: 400;\">Samsung trang bị cho Galaxy Note 20 Ultra tất cả những gì được coi là tốt nhất với cảm biến chính 108MP, camera siêu động 12MP và camera tele 12MP. Nhờ hệ thống quang học phức tạp, máy cho phép zoom quang đến 5x và khi kết hợp digital sẽ cho ra mức cao nhất là 50x. Trong khi đó, Galaxy Note 20 sở hữu camera chính 12MP, cảm biến siêu rộng 12MP và camera tele 64MP. Phiên bản nhỏ hơn sẽ không có zoom quang học mà sẽ là zoom lai 3x và zoom digital cao nhất 30x.</p>\r\n<p style=\"font-weight: 400;\">Cả hai thiết bị đều sử dụng công nghệ chống rung quang học, cải thiện độ ổn định của video, tăng cường ảnh chụp thiệu sáng. Cả hai đều có thể quay video 8K ở mức 24 khung hình/giây. Đây cũng là độ phân giải cao nhất quay được trên một thiết bị di động.</p>\r\n<p style=\"font-weight: 400;\"><strong>Bút S Pen được cải tiến, trải nghiệm tự nhiên</strong></p>\r\n<p style=\"font-weight: 400;\">Những gì được coi là tiêu biểu nhất khi nhắc đến Galaxy Note đó là bút S Pen. Bút mới được cung cấp bới Wacom và gắn vào vị trí phía dưới bên trái màn hình (khác với bên phải truyền thống). Ngoài những tính năng vốn có, bút S Pen mới được tăng độ hiệu quả với con quay hồi chuyển và cảm biến gia tốc với thời gian đáp ứng 8ms, nhạy cảm hơn với các chuyển động và nghiên khác nhau. Điều này khiến trải nghiệm viết trên màn hình Galaxy Note mới tự nhiên hơn rất nhiều.</p>\r\n<p style=\"font-weight: 400;\">Samsung Galaxy Note 20 và Note 20 Ultra sẽ được bán ra sớm tại thị trường Việt Nam. Cũng giống mọi năm, hai sản phẩm mới của Samsung được đánh giá là smartphone Android đáng sở hữu nhất thế giới.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'hiuhiu'),
(111, 'Samsung giới thiệu Galaxy Note 20-Series cùng loạt thiết bị thông minh mới', 'Samsung vừa chính thức giới thiệu bộ đôi Galaxy Note 20 và Note 20 Ultra trong buổi ra mắt trực tuyến.', '          <p>Galaxy Note 20 Ultra vẫn kế thừa những đường nét vuông vức nam tính vốn có của dòng Note-Series cùng điểm nổi bật là cụm camera sau hình chữ nhật khá lớn, nhưng không quá thô như các sản phẩm trước. Trong khi đó, bản tiêu chuẩn Galaxy Note 20 lại có nhiều góc bo tròn nhiều hơn và nắp lưng được hoàn thiện từ nhựa sơn mờ chống bám bụi bẩn rất tốt. sở hữu các cạnh được bo cong, vỏ được phủ một lớp sơn mờ chống bám dấu vân tay. Galaxy Note 20 có 3 màu: Xanh huyền bí, Xám huyền bí và Đồng huyền bí, còn phiên bản Ultra sẽ Màu Đồng Ánh Kim Huyền Bí, Đen Huyền Bí, Trắng Huyền Bí.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Note%2020%20Unpacked/Galaxy-Ecosystem-Image_1-1.jpg\" alt=\"Galaxy-Ecosystem-Image_1-1\" /></p>\r\n<p>Galaxy Note 20 Ultra được trang bị màn hình Dynamic AMOLED kích thước 6.9 inch, hỗ trợ tốc độ làm mới khung hình 120 Hz, độ sáng tăng 25% so với bản tiền nhiệm. Một điều khó hiểu là Samsung chỉ trang bị cho Galaxy Note 20 màn hình tần số quét 60Hz đi kèm kích thước 6.7-inch sử dụng công nghệ Super AMOLED Plus. Đây thực sự là cắt giảm rất mạnh tay của Samsung nếu so với dòng S20-Series ra mắt hồi đầu năm.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5799\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5799-1\"></ins></ins></p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Note%2020%20Unpacked/Galaxy-Note20-Series-1.jpg\" alt=\"Galaxy-Note20-Series-1\" /></p>\r\n<p>Bên cạnh đó, Galaxy Note 20 Ultra cũng chỉ sở hữu viên pin 4.500mAh còn Note 20 sẽ là 4.300mAh và hỗ trợ sạc nhanh 25W và đi kèm vi xử lý Exynos 990, RAM 8GB cùng bộ nhớ trong 256GB. Phiên bản đặc biệt Note 20 Ultra 5G sẽ có RAM 12GB và tích hợp thêm module mạng 5G.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Note%2020%20Unpacked/Galaxy-Note20-Ultra_Back_Mystic-Bronze-1.jpg\" alt=\"Galaxy-Note20-Ultra_Back_Mystic-Bronze-1\" /></p>\r\n<p>Galaxy Note 20 Ultra, có 4 cảm biến bao gồm camera chính có độ phân giải siêu khủng 108 MP, cảm biến 12 MP và 2 cảm biến phụ khác. Máy có khả năng quay video 8K ở 2 tỉ lệ 16:9 hoăc 21:9 không giới hạn dung lượng cùng nhiều tính năng tuỳ chỉnh chuyên nghiệp. Đối với Note 20, máy sẽ bao gồm 4 ống kính với camera chính 64MP cùng cảm biến phụ 12MP và 2 cảm biến nhỏ khác. Dự kiến bộ đôi Note 20 sẽ lên kệ vào ngày 21/8 tới.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/Samsung/Note%2020%20Unpacked/Press-Release_Canvas-Image-1%20(1).jpg\" alt=\"Press-Release_Canvas-Image-1 (1)\" /></p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(112, 'OnePlus sắp được bán chính hãng qua kênh bán lẻ smartphone lớn nhất Việt Nam?', 'CEO kênh bán lẻ smartphone lớn nhất Việt Nam vừa đăng tải trên kênh facebook cá nhân hình ảnh cầm chiếc smartphone cao cấp nhất của OnePlus.', '          <p dir=\"ltr\">Kể từ thế hệ smartphone đầu tiên ra mắt và ngay lập tức mang danh \"flagship killer\" nhờ cấu hình mạnh mẽ nhưng lại được niêm yết ở tầm giá rất hấp dẫn, OnePlus đã hoàn thiện, phát triển mạnh mẽ để lọt top đầu thương hiệu được yêu thích. Các sản phẩm của OnePlus được nhiều quốc gia ưa chuộng nhờ sử dụng nền tảng Hydrogen và Oxygen gần với trải nghiệm tương tự như Android gốc cùng cấu hình, công nghệ hàng đầu thế giới. </p>\r\n<p dir=\"ltr\">Không phải đơn giản mà bất kỳ mẫu smartphone cao cấp nào của OnePlus đều được các thị trường lớn như Châu Âu, Châu Á hay Châu Mỹ đặc biệt quan tâm dù không có các chiến dịch truyền thông mạnh mẽ. Sản phẩm tốt, giá thành dễ tiếp cận là yếu tố đem tới thành công cho OnePlus trong những năm qua. Cộng đồng người dùng OnePlus ở nước ta cũng không hề nhỏ nhưng đa phần phải lựa chọn con đường xách tay nhiều rủi ro.</p>\r\n<p dir=\"ltr\"><img src=\"https://media.techz.vn/media2019/source/conghau/Oneplus/117200485_2874249902679512_2611889973398192722_n.jpg\" alt=\"117200485_2874249902679512_2611889973398192722_n\" /></p>\r\n<p dir=\"ltr\"><strong>Liệu OnePlus có tham gia thị trường Việt Nam? </strong></p>\r\n<p dir=\"ltr\">Có những dấu hiệu cho thấy OnePlus đang tiến vào Việt Nam dù vẫn chưa có gì khẳng định một cách chắc chắn. Khi truy cập website chính thức của OnePlus đã có thêm thị trường Việt Nam dù giao diện chưa được Việt hoá hoàn toàn đi kèm với đó là sự xuất hiện của fanpage OnePlus Việt Nam.</p>\r\n<p dir=\"ltr\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5448\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5448-1\"></ins></ins></p>\r\n<p dir=\"ltr\"><img src=\"https://media.techz.vn/media2019/source/conghau/Oneplus/116918462_2874249856012850_6126705201393762330_n%20(1).jpg\" alt=\"116918462_2874249856012850_6126705201393762330_n (1)\" /></p>\r\n<p dir=\"ltr\">Thậm chí, vào tối ngày 05/08/2020, Tổng Giám Đốc Thế Giới Di Động (MWG), ông Đoàn Văn Hiểu Em đã bất ngờ đăng tải hình ảnh mới cùng sự xuất hiện của OnePlus 8 Pro. \"Đoạn caption\". Để ý kỹ thì đây chính là chiếc OnePlus 8 Pro cao cấp nhất của hãng ra mắt nửa đầu năm 2020.</p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(113, 'Tại sao người dùng iPhone không muốn dùng máy khác?!!', 'Dường như những người dùng phổ thông thường chọn iPhone và những người dùng iPhone thường ít đổi sang dùng thứ khác. Vì sao lại vậy?', '          <p>Dường như những người dùng phổ thông thường chọn iPhone và những người dùng iPhone thường ít đổi sang dùng thứ khác. Vì sao lại vậy? Apple có phải là đang khóa chặt người dùng? Tất cả nhờ vào việc Apple có một hệ sinh thái cực kỳ tốt. Nếu như những ai đang dùng các sản phẩm đến từ Apple như iPhone, iPad, Macbook thì có thể nhận ra hệ sinh thái Apple thật sự hiệu quả, thật sự dễ dùng và hiểu người dùng. Trái tim của hệ sinh thái này nằm ở iCloud và iCloud lại khá là khó đồng bộ lại với các sản phẩm và các hệ sinh thái khác. Đó là các lý do vì sao đã dùng Apple là không bỏ được.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-4042\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-4042-1\"></ins></ins></p>\r\n<p><iframe src=\"https://www.youtube.com/embed/033Awr6G9eQ\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\" data-mce-fragment=\"1\"></iframe></p>\r\n        ', '2020-08-11', 0, 0, 'Thường', 6, 'binbin'),
(114, 'Samsung sẽ ra mắt phiên bản 5G của Galaxy A51', 'Samsung cho biết trong tuần này Galaxy A51 sẽ ra mắt phiên bản 5G.', '          <p>Điện thoại tầm trung tiếp theo của Samsung sẽ có kết nối 5G là Galaxy A51. Được ra mắt lần đầu vào tháng 4, A51 có giá 400$ (Hơn 9 triệu đồng). Theo đói, thời gian sắp tới phiên bản 5G của Galaxy A51 sẽ chính thức được ra mắt với mức giá 500 USD và hoạt động trên mạng 5G của T-Mobile và Verizon.</p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/aaaLinh/aaathang8/galaxy-3.jpg\" alt=\"\" data-bsinpicture=\"true\" />\r\n<div><iframe id=\"bs__cb38e92825948d536a7900eec26d3c9a\" src=\"https://new-cdn.blueseed.tv/img/2020/08/GoFood_Inpicture/inpicture-non-tvc.html?id=bs__cb38e92825948d536a7900eec26d3c9a\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" data-mce-fragment=\"1\"></iframe></div>\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Samsung ban đầu tuyên bố rằng dòng sản phẩm Galaxy A-series của họ sẽ có 5G vào tháng 4 và A51 là thiết bị thứ hai sau A71 , có giá khoảng 600 USD,  cho ra xử lý mạng mượt mà hơn. Dù thông số kỹ thuật kém ấn tượng hơn một chút so với A71, A51 vẫn có màn hình FHD + OLED Infinity-O 6,5 inch với camera đục lỗ, hệ thống camera tứ cực hoàn hảo ở mặt sau, dung lượng lưu trữ lê tới 128 GB, 4GB RAM và pin 4.000mAh. A51 có bộ xử lý tầm trung Exynos 9611 octa-core</p>\r\n<div id=\"playstream_out\" class=\"playstream_out\">\r\n<div class=\"video-container\">\r\n<div id=\"ad_container\" class=\"rtl\">\r\n<div><iframe src=\"https://imasdk.googleapis.com/js/core/bridge3.401.2_en.html#goog_685264869\" width=\"647\" height=\"360\" allowfullscreen=\"allowfullscreen\" data-mce-fragment=\"1\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-9376\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-9376-1\"></ins></ins></p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/aaaLinh/aaathang8/galaxy-1.png\" alt=\"\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Samsung cho biết A51 5G sẽ đến với T-Mobile, công ty vừa mới công bố mở rộng mạng 5G thêm 30% vào ngày 7/8. Galaxy A51 cũng sẽ đến với mạng Metro do T-Mobile cung cấp. Bắt đầu từ ngày 13/8, phiên bản A51 5G UW sẽ có mặt trên Verizon và mạng sóng milimet 5G , cũng hỗ trợ phiên bản 5G của Samsung Galaxy S20.</p>\r\n        ', '2020-08-12', 0, 0, 'Thường', 6, 'hiuhiu'),
(115, 'Hàng loạt iPhone giảm giá sâu lên tới 5 triệu đồng: iPhone 11 Pro, 11 Pro Max, XS Max', 'Dưới đây là những dòng iPhone đang được khuyến mãi sâu lên đến 5 triệu đồng, một ưu đãi tuyệt vời trong tháng 8 mà các iFan không thể bỏ qua.', '          <p>Hiện tại, nhu cầu mua sắm của người tiêu dùng đang có dấu hiệu giảm đi nhiều do tình hình dịch bệnh diễn biến phức tạp. Để đảm bảo an toàn và hạn chế tối đa khả năng lây nhiễm covid-19, thế giới di động áp dụng hình thức đặt hàng online giảm giá lên tới 5 triệu đồng. </p>\r\n<p><strong>Lưu ý:</strong> Khuyến mãi sẽ có thay đổi khi theo từng đợt hotsale diễn ra, chỉ áp ụng từ 1 - 13/8/2020.</p>\r\n<div id=\"playstream_out\" class=\"playstream_out\">\r\n<div class=\"video-container\">\r\n<div id=\"ad_container\" class=\"rtl\">\r\n<div><iframe src=\"https://imasdk.googleapis.com/js/core/bridge3.401.2_en.html#goog_899997758\" width=\"647\" height=\"360\" allowfullscreen=\"allowfullscreen\" data-mce-fragment=\"1\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<h2>iPhone XS Max 64 GB: 25.990.000 đồng</h2>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/Nhung-do/A-Cong-Nghe/Hang-loat-iphone-giam-gia-sau-len-toi-5-trieu-dong-iphone-11-pro-11-pro-max-xs-max-1.jpg\" alt=\"Hang-loat-iphone-giam-gia-sau-len-toi-5-trieu-dong-iphone-11-pro-11-pro-max-xs-max\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>- Khuyến mại: Trả góp 0% hoặc giảm 5 triệu khi đặt online, thời gian giao hàng từ 2 - 7 ngày.</p>\r\n<p>- Tặng 2 suất mua Đồng hồ thời trang giảm 40% (không áp dụng thêm khuyến mãi khác) (click xem chi tiết)</p>\r\n<h2><strong>iPhone XS Max 256 GB: 30.990.000 đồng</strong></h2>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/Nhung-do/A-Cong-Nghe/Hang-loat-iphone-giam-gia-sau-len-toi-5-trieu-dong-iphone-11-pro-11-pro-max-xs-max-2.jpg\" alt=\"Hang-loat-iphone-giam-gia-sau-len-toi-5-trieu-dong-iphone-11-pro-11-pro-max-xs-max\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>- Khuyến mại: Trả góp 0% hoặc giảm 5 triệu </p>\r\n<p>- Tặng 2 suất mua Đồng hồ thời trang giảm 40% (không áp dụng thêm khuyến mãi khác) (click xem chi tiết)</p>\r\n<h2><strong>iPhone 11 Pro 64GB: 30.990.000 đồng</strong></h2>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/Nhung-do/A-Cong-Nghe/Hang-loat-iphone-giam-gia-sau-len-toi-5-trieu-dong-iphone-11-pro-11-pro-max-xs-max-3.jpg\" alt=\"Hang-loat-iphone-giam-gia-sau-len-toi-5-trieu-dong-iphone-11-pro-11-pro-max-xs-max\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>- Khuyến mại: Trả góp 0% hoặc giảm 4 triệu khi đặt online, giao hàng từ ngày 1 - 13/8/2020.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5134\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-5134-1\"></ins></ins></p>\r\n<p>- Tặng 2 suất mua Đồng hồ thời trang giảm 40% (không áp dụng thêm khuyến mãi khác) (click xem chi tiết)</p>\r\n<h2><strong>iPhone 11 Pro 256GB: 34.990.000 đồng</strong></h2>\r\n<p>- Khuyến mãi: Trả góp 0% hoặc giảm 4 triệu khi đặt online, giao hàng từ ngày 1 - 13/8/2020.</p>\r\n<p>- Tặng 2 suất mua Đồng hồ thời trang giảm 40% (không áp dụng thêm khuyến mãi khác) (click xem chi tiết)</p>\r\n<h2><strong>iPhone 11 Pro Max 64 GB: 33.990.000 đồng</strong></h2>\r\n<p>- Khuyến mãi: Trả góp 0% hoặc giảm 4.5 triệu khi đặt online từ ngày 1 - 13/8/2020.</p>\r\n<p>- Tặng 2 suất mua Đồng hồ thời trang giảm 40% (không áp dụng thêm khuyến mãi khác) (click xem chi tiết)</p>\r\n<h2><strong>iPhone 11 Pro Max 512 GB: 41.990.000</strong></h2>\r\n<p>- Khuyến mãi: Trả góp 0% hoặc giảm 4 triệu khi đặt online từ ngày 4 - 13/8/2020.</p>\r\n<p>- Tặng 2 suất mua Đồng hồ thời trang giảm 40% (không áp dụng thêm khuyến mãi khác) (click xem chi tiết)</p>\r\n        ', '2020-08-12', 0, 0, 'Thường', 6, 'binbin'),
(116, 'Ngắm nhìn Huawei Mate 40 với camera \"siêu khủng\"', 'Huawei Mate 40 hứa hẹn sẽ mang đến cho chúng ta nhiều những bất ngờ mới khi sở hữu cụm camera siêu khủng.', '          <p class=\"p1\">Loạt ảnh render mới của siêu phẩm Huawei Mate 40 vừa được OnLeaks tiết lộ. Về kiểu dáng thiết kế, Mate 40 gần như tương đồng phiên bản trước đó, tuy nhiên, hệ thống camera đã được hoàn thiện hầm hố hơn, lồi hơn so với Mate 30. Cụm camera này bao gồm 3 ống kính, 1 cảm biến được bố trí trong module hình tròn, bên cạnh là đèn flashLED.</p>\r\n<p class=\"p1\"> </p>\r\n<div id=\"playstream_out\" class=\"playstream_out\">\r\n<div class=\"video-container\">\r\n<div id=\"ad_container\" class=\"rtl\">\r\n<div><iframe src=\"https://imasdk.googleapis.com/js/core/bridge3.401.2_en.html#goog_431396249\" allowfullscreen=\"allowfullscreen\" data-mce-fragment=\"1\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<p class=\"p1\"><img src=\"https://media.techz.vn/media2019/source/NguyenKhoa/photo-1-1596488657440109374257.jpg\" alt=\"photo-1-1596488657440109374257\" data-bsinpicture=\"true\" /></p>\r\n<p class=\"p1\"> </p>\r\n<p class=\"p1\">Mặt trước Mate 40 trang bị màn hình 6.4 inch với camera selfie \"nốt rùi kép\", màn hình thác nước, kích thước tổng thể bao gồm 158.6 x 72.5 x 8.9mm. Mate 40 sẽ vận hành trên nền tảng Android 10 cùng giao diện EMUI, nhưng chắc chắn một điều rằng, Mate 40 sẽ không hỗ trợ các dịch vụ của Google.<span class=\"Apple-converted-space\"> </span></p>\r\n        ', '2020-08-12', 0, 0, 'Thường', 6, 'binbin'),
(117, 'OPPO Reno4 gây chấn động với mức giá cực tốt khiến đối thủ chóng mặt', 'Reno4 và Reno4 Pro chính thức trình làng tại Việt Nam với giá cực hời lần lượt là 8,49 và 11,99 triệu đồng.', '          <p>Sau sự kiện, hàng loạt tín đồ công nghệ vui mừng vì bộ đôi sản phẩm có giá cực tốt, ngoài dự kiến. “Tân binh” smartphone còn đi kèm với bộ quà khuyến mãi siêu khủng khuấy động cả thị trường smartphone. Chỉ trong vòng 1 tuần từ ngày 02/08- 07/08, khi đặt trước Reno4, khách hàng sẽ rước ngay bộ quà trị giá 1,490,000 đồng lúc nhận máy, gồm loa Bluetooth OPPO (trị giá 990,000 đồng), tặng ngay 500,000 đồng và chương trình trả góp 0% lãi suất. </p>\r\n<p>Trong khi đó, khi đặt trước Reno4 Pro, người dùng sẽ được ưu đãi kèm theo bộ quà 2,990,000VND cực hấp dẫn, bao gồm: OPPO Enco W31 (trị giá 1,990,000 đồng), tăng ngay 1,000,000 đồng cùng chương trình trả góp 0%.</p>\r\n<p><img src=\"https://media.techz.vn/media2019/source/conghau/OPPO/OPPO%20Reno%204%20m%E1%BB%9F%20h%E1%BB%99p/Reno4%20(13%20of%2014).jpg\" alt=\"Reno4 (13 of 14)\" /></p>\r\n<p>Trước đó, dù chưa chính thức biết giá, nhờ hàng loạt nâng cấp, Reno4 đã tạo nên sức hút lớn, khiến hàng ngày khách hàng ngóng chờ sự xuất hiện. Hơn 2,500 đơn hàng có cọc đã được ghi nhận dù vẫn chưa biết giá chính thức. Nhiều chuỗi cửa hàng bán lẻ quyết định cho trải nghiệm máy và mở trang đặt hàng sớm hơn thời gian dự kiến. Có lẽ đây là một trong những lần hiếm hoi mà smartphone mới hấp dẫn người dùng đến vậy. </p>\r\n<p>OPPO Reno4 và Reno4 Pro nhận được nhiều sự quan tâm nhờ tập trung phát triển những tính năng hữu ích, tăng cường trải nghiệm tốt hơn cho người dùng. Cụm 4 camera chính có độ phân giải 48 MP sở hữu tính năng AI Color Portrait độc đáo, cho phép chụp ảnh chân dung với background trắng đen, chỉ giữ lại màu chủ thể. Chân dung màu AI còn hỗ trợ khi người dùng quay video, mang lại nét nghệ thuật và tô đậm phong cách cá nhân, giúp người trẻ thoải mái thể hiện bản thân, đúng với tinh thần thông điệp “Tôi nổi bật, tạo khác biệt”. </p>\r\n        ', '2020-08-12', 0, 0, 'Thường', 6, 'binbin'),
(118, 'Tin buồn: iPhone 5G có thể sẽ không được ra mắt trong tháng 9 này', 'Không chỉ iPhone, hàng loạt điện thoại 5G có thể không thể ra mắt theo đúng hẹn trong năm nay', '          <p>Theo báo cáo thu nhập quý 3 của Qualcomm sẽ sự chậm trễ đối với các iPhone 5G sắp ra mắt của Apple. Các công ty nhấn mạnh rằng đây cũng là hậu quả vì việc trì hoãn ra các sản phẩm mới trong năm nay.</p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/aaaLinh/aaathang7/12%3D3.jpg\" alt=\"\" data-bsinpicture=\"true\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Nhìn vào lịch phát hành điện thoại sắp tới, có thể thấy Qualcomm đang nói về iPhone 5G sắp ra mắt, dự kiến ​​sẽ ra mắt vào mùa thu này. Thông thường, Apple sẽ phát hành iPhone mới vào tháng 9. Đã có tin đồn về sự chậm trễ cho dòng sản phẩm 5G của Apple do chậm trễ sản xuất vì đại dịch COVID-19.</p>\r\n        ', '2020-08-12', 0, 0, 'Thường', 6, 'hiuhiu'),
(119, 'Tiết lộ cấu hình Samsung Galaxy Note20', 'Galaxy Note20 là một trong 5 sản phẩm Samsung sẽ ra mắt vào ngày 5/8 tới đây.', '          <p>Samsung Galaxy Note20 được cho là sẽ sở hữu ngoại hình vuông vắn thay vì bo viền. Mặt trước của máy vẫn sử dụng màn hình dạng Infinity-O, là màn hình Super AMOLED 6,7 inch cho Galaxy Note20 và 6,9 inch cho Galaxy Note20 Ultra. Phần viền màn hình cũng mỏng hơn bao giờ hết. </p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/1BeCuat7/ss2.jpg\" alt=\"\" data-bsinpicture=\"true\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Báo cáo từ Winfuture.de cho biết màn hình Galaxy Note20 sẽ dùng tấm nền độ phân giải Full HD, tốc độ làm tươi 60 Hz. Trong khi đó, Galaxy Note20 Ultra có độ phân giải 2K, mật độ điểm ảnh 508 ppi, tốc độ làm tươi tối đa 120 Hz. Tấm nền sẽ được bảo vệ bằng kính Gorilla Glass Victus thế hệ mới của Corning, có khả năng chống trầy gấp đôi Gorilla Glass 6 và có thể đảm bảo an toàn cho máy khi thả rơi từ độ cao 2 mét.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-3889\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-3889-1\"></ins></ins></p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/1BeCuat7/ss3.jpg\" alt=\"\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Theo thông tin đăng ký của Samsung trên website của FCC, Galaxy Note20 sẽ được trang bị chip Snapdragon 865, hỗ trợ Wi-Fi 6 và kết nối 5G, RAM ít nhất 12 GB, bộ nhớ từ 256 GB, mở rộng lưu trữ qua khe thẻ microSD tối đa 1 TB. Bên cạnh đó, các tính năng như chống nước IP68, cảm biến vân tay bên trong màn hình, sạc nhanh và sạc ngược không dây... gần như sẽ có mặt.</p>\r\n        ', '2020-08-12', 0, 0, 'Thường', 6, 'binbin'),
(120, 'Realme C15 ra mắt: Sở hữu loạt trang bị khủng, giá từ 138 USD', 'Realme C15 chính thức trình làng, gây chú ý với loạt trang bị đáng giá.', '          <p>Chiếc Realme C15 vừa được ra mắt là phiên bản nâng cấp của chiếc Realme C11. Máy có kích thước 164.5 x 75.9 x 9.8mm và nặng 209 gam. Màn hình IPS LCD 6.5 inch, độ phân giải HD, thiết kế kiểu giọt nước để chứa camera 8MP, khẩu độ f/2.0.</p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/1BeCuat7/realme4.jpg\" alt=\"\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Mặt sau của máy gây ấn tượng với cụm camera hình vuông nằm ở góc trên cùng bên trái, bao gồm cảm biến chính 13MP + ống kính 8MP cho góc chụp siêu rộng + camera macro 2MP + cảm biến độ sâu 2MP. Bộ 4 camera này cung cấp cho người dùng các tính năng như Super NightScape, chế độ chụp ảnh siêu rộng, HDR, time-lapse, quay chậm và quay video 1080p 30fps. Cảm biến vân tay nằm chính giữa mặt lưng.</p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/1BeCuat7/realme2.jpg\" alt=\"\" />\r\n<figcaption>Loạt trang bị đáng giá của Realme C15</figcaption>\r\n</figure>\r\n<p>\"Sức mạnh\" của Realme C15 đến từ con chip Helio G35 RAM tối đa 4GB và 64GB ROM. Đặc biệt, điểm nhấn lớn nhất của Realme C15 đến từ viên pin có dung lượng lên tới 6.000mAh, hỗ trợ công nghệ sạc nhanh 18W. Máy cũng hỗ trợ tính năng sạc ngược để cung cấp năng lượng cho các điện thoại khác.</p>\r\n<p><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-3850\" class=\"982a9496\" data-key=\"e3bd3760de9867264c2fcaf1c355808a\"><ins id=\"982a9496-e3bd3760de9867264c2fcaf1c355808a-2-3850-1\"></ins></ins></p>\r\n<figure class=\"image align-center\"><img src=\"https://media.techz.vn/media2019/source/1BeCuat7/realme3.jpg\" alt=\"\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n<p>Nhiều khả năng Realme C15 sẽ bắt đầu được bán ra tại Indonesia từ ngày 29/7, với giá khởi điểm 138 USD (khoảng 3.2 triệu đồng) cho bản 3GB RAM + 64GB bộ nhớ. Trong khi đó, phiên bản 4GB RAM + 64GB bộ nhớ được bán ra với giá 151 USD (khoảng 3.5 triệu đồng), bản 4GB RAM + 128GB bộ nhớ có giá bán là 172 USD (tương đương 4 triệu đồng).</p>\r\n        ', '2020-08-12', 1, 0, 'Thường', 6, 'binbin');
